import React, { useState } from 'react';
import {
    ChevronRight, ChevronLeft, MousePointer,
    Palette, Type, Layout, Zap, Image as ImageIcon, Upload, Settings
} from 'lucide-react';
import './PropertiesPanel.css';

const COLOR_PRESETS = {
    '--primary': '#667eea',
    '--primary-dark': '#5568d3',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#764ba2',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
};

const PropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle }) => {
    const [activeTab, setActiveTab] = useState('design');
    const [imagePreview, setImagePreview] = useState(null);

    if (isCollapsed) {
        return (
            <div className="properties-panel-collapsed">
                <button onClick={onToggle} className="toggle-button" title="Mở thuộc tính">
                    <ChevronLeft size={18} />
                </button>
            </div>
        );
    }

    if (!selectedElement || !selectedElement.json) {
        return (
            <div className="properties-panel">
                <div className="panel-header">
                    <h3 className="panel-title">Thuộc tính</h3>
                    <button onClick={onToggle} className="toggle-button" title="Đóng">
                        <ChevronRight size={18} />
                    </button>
                </div>
                <div className="panel-empty">
                    <MousePointer className="empty-icon" size={48} strokeWidth={1.5} />
                    <p className="empty-text">Chọn một section để chỉnh sửa</p>
                </div>
            </div>
        );
    }

    const handleStyleChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [property]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleComponentDataChange = (key, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    [key]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
                handleComponentDataChange('backgroundImage', reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleGradientChange = (gradient) => {
        handleStyleChange('background', gradient);
    };

    const renderDesignTab = () => (
        <div className="panel-section">
            <h4 className="section-title">
                <Palette size={16} strokeWidth={1.5} />
                Thiết kế
            </h4>
            <div className="input-group">
                <div>
                    <label className="input-label">Loại nền</label>
                    <select
                        value={selectedElement.json.componentData?.backgroundType || 'color'}
                        onChange={(e) => handleComponentDataChange('backgroundType', e.target.value)}
                        className="input-select"
                    >
                        <option value="color">Màu đơn</option>
                        <option value="image">Hình ảnh</option>
                        <option value="gradient">Gradient</option>
                    </select>
                </div>

                {selectedElement.json.componentData?.backgroundType === 'color' && (
                    <div>
                        <label className="input-label">Màu nền</label>
                        <input
                            type="color"
                            value={selectedElement.json.styles?.backgroundColor || '#ffffff'}
                            onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                            className="input-color"
                        />
                        <div className="color-preset-grid">
                            {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                <div
                                    key={key}
                                    className={`color-preset-item ${selectedElement.json.styles?.backgroundColor === value ? 'active' : ''}`}
                                    style={{ backgroundColor: value }}
                                    onClick={() => handleStyleChange('backgroundColor', value)}
                                    title={key}
                                />
                            ))}
                        </div>
                    </div>
                )}

                {selectedElement.json.componentData?.backgroundType === 'image' && (
                    <>
                        <div>
                            <label className="input-label">Tải ảnh nền</label>
                            <input
                                type="file"
                                accept="image/*"
                                onChange={handleImageUpload}
                                style={{ display: 'none' }}
                                id="background-image-upload"
                            />
                            <label htmlFor="background-image-upload" className="upload-button">
                                <Upload size={16} strokeWidth={1.5} />
                                Chọn ảnh
                            </label>
                            {(imagePreview || selectedElement.json.componentData?.backgroundImage) && (
                                <div className="image-preview">
                                    <img
                                        src={imagePreview || selectedElement.json.componentData?.backgroundImage}
                                        alt="Background Preview"
                                        style={{ maxWidth: '100%', maxHeight: '100px' }}
                                    />
                                </div>
                            )}
                        </div>
                        <div>
                            <label className="input-label">Background Position</label>
                            <select
                                value={selectedElement.json.styles?.backgroundPosition || 'center'}
                                onChange={(e) => handleStyleChange('backgroundPosition', e.target.value)}
                                className="input-select"
                            >
                                <option value="center">Giữa</option>
                                <option value="top">Trên</option>
                                <option value="bottom">Dưới</option>
                                <option value="left">Trái</option>
                                <option value="right">Phải</option>
                            </select>
                        </div>
                    </>
                )}

                {selectedElement.json.componentData?.backgroundType === 'gradient' && (
                    <div>
                        <label className="input-label">Gradient</label>
                        <input
                            type="text"
                            value={selectedElement.json.styles?.background || 'linear-gradient(90deg, #667eea, #764ba2)'}
                            onChange={(e) => handleGradientChange(e.target.value)}
                            className="input-field"
                            placeholder="linear-gradient(90deg, #667eea, #764ba2)"
                        />
                    </div>
                )}

                <div>
                    <label className="input-label">Lớp phủ (Overlay)</label>
                    <input
                        type="color"
                        value={selectedElement.json.componentData?.overlayColor || '#000000'}
                        onChange={(e) => handleComponentDataChange('overlayColor', e.target.value)}
                        className="input-color"
                    />
                    <label className="input-label">Độ mờ lớp phủ</label>
                    <input
                        type="range"
                        min="0"
                        max="1"
                        step="0.1"
                        value={selectedElement.json.componentData?.overlayOpacity || 0}
                        onChange={(e) => handleComponentDataChange('overlayOpacity', parseFloat(e.target.value))}
                        className="input-range"
                    />
                </div>

                <div>
                    <label className="input-label">Viền (Border)</label>
                    <input
                        type="text"
                        value={selectedElement.json.styles?.border || 'none'}
                        onChange={(e) => handleStyleChange('border', e.target.value)}
                        className="input-field"
                        placeholder="1px solid #000"
                    />
                </div>
                <div>
                    <label className="input-label">Bo góc (Border Radius)</label>
                    <input
                        type="text"
                        value={selectedElement.json.styles?.borderRadius || '0px'}
                        onChange={(e) => handleStyleChange('borderRadius', e.target.value)}
                        className="input-field"
                        placeholder="8px"
                    />
                </div>
                <div>
                    <label className="input-label">Đổ bóng (Box Shadow)</label>
                    <input
                        type="text"
                        value={selectedElement.json.styles?.boxShadow || 'none'}
                        onChange={(e) => handleStyleChange('boxShadow', e.target.value)}
                        className="input-field"
                        placeholder="0 4px 6px rgba(0,0,0,0.1)"
                    />
                </div>

                <div>
                    <label className="input-label">Padding</label>
                    <input
                        type="text"
                        value={selectedElement.json.styles?.padding || '0'}

                        onChange={(e) => handleStyleChange('padding', e.target.value)}
                        className="input-field"
                        placeholder="20px"
                    />
                </div>
                <div>
                    <label className="input-label">Chiều cao</label>
                    <input
                        type="text"
                        value={selectedElement.json.size?.height || '400px'}
                        onChange={(e) => onUpdateElement({ ...selectedElement, size: { ...selectedElement.json.size, height: e.target.value } })}
                        className="input-field"
                        placeholder="400px"
                    />
                </div>
            </div>
        </div>
    );

    const renderAnimationTab = () => (
        <div className="panel-section">
            <h4 className="section-title">
                <Zap size={16} strokeWidth={1.5} />
                Hiệu ứng
            </h4>
            <div className="input-group">
                <div>
                    <label className="input-label">Hiệu ứng khi load</label>
                    <select
                        value={selectedElement.json.componentData?.animation?.onLoad || 'none'}
                        onChange={(e) => handleComponentDataChange('animation', { ...selectedElement.json.componentData?.animation, onLoad: e.target.value })}
                        className="input-select"
                    >
                        <option value="none">Không có</option>
                        <option value="fadeIn">Fade In</option>
                        <option value="slideUp">Slide Up</option>
                        <option value="zoomIn">Zoom In</option>
                    </select>
                </div>
                <div>
                    <label className="input-label">Hiệu ứng khi scroll</label>
                    <select
                        value={selectedElement.json.componentData?.animation?.onScroll || 'none'}
                        onChange={(e) => handleComponentDataChange('animation', { ...selectedElement.json.componentData?.animation, onScroll: e.target.value })}
                        className="input-select"
                    >
                        <option value="none">Không có</option>
                        <option value="fadeInUp">Fade In Up</option>
                        <option value="scaleUp">Scale Up</option>
                    </select>
                </div>
                <div>
                    <label className="input-label">Thời gian hiệu ứng (ms)</label>
                    <input
                        type="number"
                        value={selectedElement.json.componentData?.animation?.duration || 1000}
                        onChange={(e) => handleComponentDataChange('animation', { ...selectedElement.json.componentData?.animation, duration: parseInt(e.target.value) })}
                        className="input-field"
                        placeholder="1000"
                    />
                </div>
            </div>
        </div>
    );

    const renderRenameTab = () => (
        <div className="panel-section">
            <h4 className="section-title">
                <Type size={16} strokeWidth={1.5} />
                Đổi tên Section
            </h4>
            <div className="input-group">
                <div>
                    <label className="input-label">Tên Section</label>
                    <input
                        type="text"
                        value={selectedElement.json.componentData?.title || ''}
                        onChange={(e) => handleComponentDataChange('title', e.target.value)}
                        className="input-field"
                        placeholder="Nhập tên section"
                    />
                </div>
            </div>
        </div>
    );

    const renderAdvancedTab = () => (
        <div className="panel-section">
            <h4 className="section-title">
                <Settings size={16} strokeWidth={1.5} />
                Thuộc tính khác
            </h4>
            <div className="input-group">
                <div>
                    <label className="input-label">Margin</label>
                    <input
                        type="text"
                        value={selectedElement.json.styles?.margin || '0'}
                        onChange={(e) => handleStyleChange('margin', e.target.value)}
                        className="input-field"
                        placeholder="0px"
                    />
                </div>
                <div>
                    <label className="input-label">Z-Index</label>
                    <input
                        type="number"
                        value={selectedElement.json.position?.z || 1}
                        onChange={(e) => onUpdateElement({ ...selectedElement, position: { ...selectedElement.json.position, z: parseInt(e.target.value) } })}
                        className="input-field"
                        placeholder="1"
                    />
                </div>
            </div>
        </div>
    );

    return (
        <div className="properties-panel">
            <div className="panel-header">
                <h3 className="panel-title">{selectedElement.json.componentData?.title || selectedElement.json.type}</h3>
                <button onClick={onToggle} className="toggle-button" title="Đóng">
                    <ChevronRight size={18} />
                </button>
            </div>

            <div className="panel-tabs">
                <button
                    className={`tab-button ${activeTab === 'design' ? 'active' : ''}`}
                    onClick={() => setActiveTab('design')}
                >
                    Thiết kế
                </button>
                <button
                    className={`tab-button ${activeTab === 'animation' ? 'active' : ''}`}
                    onClick={() => setActiveTab('animation')}
                >
                    Hiệu ứng
                </button>
                <button
                    className={`tab-button ${activeTab === 'rename' ? 'active' : ''}`}
                    onClick={() => setActiveTab('rename')}
                >
                    Đổi tên
                </button>
                <button
                    className={`tab-button ${activeTab === 'advanced' ? 'active' : ''}`}
                    onClick={() => setActiveTab('advanced')}
                >
                    Nâng cao
                </button>
            </div>

            <div className="panel-content">
                {activeTab === 'design' && renderDesignTab()}
                {activeTab === 'animation' && renderAnimationTab()}
                {activeTab === 'rename' && renderRenameTab()}
                {activeTab === 'advanced' && renderAdvancedTab()}
            </div>
        </div>
    );
};

export default PropertiesPanel;
