import React, { useState } from 'react';
import Login from '../components/Login';
import Register from '../components/Register';
import '../styles/AuthPage.css'; // CSS cho tab switch

const AuthPage = () => {
    const [activeTab, setActiveTab] = useState('login');

    return (
        <div className="auth-page">
            <div className="tab-container">
                <label className="tab_label" onClick={() => setActiveTab('login')}>Login</label>
                <label className="tab_label" onClick={() => setActiveTab('register')}>Register</label>
                <div className={`indicator ${activeTab}`}></div>
            </div>

            <div className="tab-content">
                {activeTab === 'login' && <Login />}
                {activeTab === 'register' && <Register />}
            </div>
        </div>
    );
};

export default AuthPage;
