import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import api from "@landinghub/api";
import DogLoader from "../components/DogLoader";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";

const CreateLandingPage = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // AI mode
    const [pageDescription, setPageDescription] = useState("");
    const [aiGeneratedContent, setAiGeneratedContent] = useState("");

    // Mode switching
    const [editorMode, setEditorMode] = useState("ai"); // "ai" | "dragdrop"

    // GrapesJS
    const editorRef = useRef(null);

    const navigate = useNavigate();

    // -------------------- Auth check --------------------
    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            console.error("No token found, redirecting to /auth");
            navigate("/auth");
            return;
        }

        try {
            const decoded = jwtDecode(token);
            if (!decoded.userId) {
                throw new Error("Invalid token: userId not found");
            }
            api.defaults.headers.common["Authorization"] = `Bearer ${token}`;
        } catch (err) {
            console.error("Error decoding token:", err);
            navigate("/auth");
        }
    }, [navigate]);

    // -------------------- AI Create --------------------
    const handleCreatePageAI = async () => {
        if (!pageDescription) {
            setError("Vui lòng nhập mô tả landing page");
            return;
        }
        try {
            setLoading(true);
            const response = await api.post("/api/pages", {
                description: pageDescription,
            });
            setAiGeneratedContent(response.data.html || "");

            navigate(`/pages/edit/${response.data.id}`, {
                state: {
                    page: response.data,
                    isNew: true,
                },
            });
        } catch (err) {
            console.error("Create error:", err);
            setError(
                "Lỗi khi tạo landing page: " +
                (err.response?.data?.msg || err.message)
            );
        } finally {
            setLoading(false);
        }
    };

    // -------------------- Drag & Drop --------------------
    useEffect(() => {
        if (editorMode === "dragdrop" && !editorRef.current) {
            editorRef.current = grapesjs.init({
                container: "#gjs",
                fromElement: false,
                width: "auto",
                height: "80vh",
                storageManager: false,
                plugins: ["gjs-preset-webpage"],
                pluginsOpts: {
                    "gjs-preset-webpage": {},
                },
            });
        }
    }, [editorMode]);

    const handleSaveDragDrop = async () => {
        if (!editorRef.current) return;

        const html = editorRef.current.getHtml();
        const css = editorRef.current.getCss();

        try {
            setLoading(true);
            const response = await api.post("/api/pages", {
                html,
                css,
            });

            navigate(`/pages/edit/${response.data.id}`, {
                state: {
                    page: response.data,
                    isNew: true,
                },
            });
        } catch (err) {
            console.error("Save error:", err);
            setError(
                "Lỗi khi lưu landing page: " +
                (err.response?.data?.msg || err.message)
            );
        } finally {
            setLoading(false);
        }
    };

    // -------------------- UI --------------------
    if (loading) {
        return <DogLoader />;
    }

    return (
        <div className="create-landing-page">
            <div className="create-header flex justify-between items-center">
                <button onClick={() => navigate("/pages")} className="back-button">
                    ← Quay lại
                </button>
                <h1>Tạo Landing Page Mới</h1>

                {/* Mode switch */}
                <div className="mode-switch flex gap-2">
                    <button
                        onClick={() => setEditorMode("ai")}
                        className={`px-3 py-1 rounded ${
                            editorMode === "ai" ? "bg-blue-600 text-white" : "bg-gray-200"
                        }`}
                    >
                        AI Editor
                    </button>
                    <button
                        onClick={() => setEditorMode("dragdrop")}
                        className={`px-3 py-1 rounded ${
                            editorMode === "dragdrop"
                                ? "bg-blue-600 text-white"
                                : "bg-gray-200"
                        }`}
                    >
                        Drag & Drop Editor
                    </button>
                </div>
            </div>

            {error && (
                <div className="error bg-red-100 text-red-700 p-2 my-2 rounded">
                    {error}
                    <button onClick={() => setError(null)} className="ml-2 text-sm">
                        Đóng
                    </button>
                </div>
            )}

            {/* -------------------- AI Mode -------------------- */}
            {editorMode === "ai" && (
                <div className="create-content">
                    <div className="create-form">
                        <label htmlFor="description">Mô tả Landing Page</label>
                        <textarea
                            id="description"
                            value={pageDescription}
                            onChange={(e) => setPageDescription(e.target.value)}
                            placeholder="Nhập mô tả chi tiết về landing page bạn muốn tạo..."
                            rows="10"
                            className="description-textarea w-full border p-2 rounded"
                        />

                        <div className="create-actions flex gap-2 mt-3">
                            <button
                                onClick={handleCreatePageAI}
                                disabled={!pageDescription || loading}
                                className="create-button primary bg-blue-600 text-white px-4 py-2 rounded"
                            >
                                {loading ? "Đang tạo..." : "Tạo Landing Page"}
                            </button>
                            <button
                                onClick={() => navigate("/pages")}
                                className="create-button secondary bg-gray-300 px-4 py-2 rounded"
                            >
                                Hủy
                            </button>
                        </div>
                    </div>

                    {aiGeneratedContent && (
                        <div className="preview-section mt-4">
                            <h3 className="font-bold mb-2">Preview</h3>
                            <div
                                className="border p-2"
                                dangerouslySetInnerHTML={{ __html: aiGeneratedContent }}
                            />
                        </div>
                    )}
                </div>
            )}

            {/* -------------------- Drag & Drop Mode -------------------- */}
            {editorMode === "dragdrop" && (
                <div className="dragdrop-content">
                    <div id="gjs" className="border rounded" />
                    <div className="flex gap-2 mt-3">
                        <button
                            onClick={handleSaveDragDrop}
                            className="bg-green-600 text-white px-4 py-2 rounded"
                        >
                            Lưu Landing Page
                        </button>
                        <button
                            onClick={() => navigate("/pages")}
                            className="bg-gray-300 px-4 py-2 rounded"
                        >
                            Hủy
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CreateLandingPage;
