import React, { useState } from 'react';
import { ChevronRight, ChevronLeft, MousePointer, Type, Zap, Sparkles, Palette, Box, Sun, Settings, Maximize } from 'lucide-react';
import '../../../styles/ElementPropertiesPanel.css';

const FONT_PRESETS = [
    { name: 'Arial', value: 'Arial, sans-serif' },
    { name: 'Helvetica', value: 'Helvetica, sans-serif' },
    { name: 'Roboto', value: 'Roboto, sans-serif' },
    { name: 'Open Sans', value: 'Open Sans, sans-serif' },
    { name: 'Poppins', value: 'Poppins, sans-serif' },
    { name: 'Montserrat', value: 'Montserrat, sans-serif' },
    { name: 'Inter', value: 'Inter, sans-serif' },
];

const FONT_WEIGHT_PRESETS = [
    { name: 'Light (300)', value: '300' },
    { name: 'Regular (400)', value: '400' },
    { name: 'Medium (500)', value: '500' },
    { name: 'Semibold (600)', value: '600' },
    { name: 'Bold (700)', value: '700' },
    { name: 'Extra Bold (800)', value: '800' },
];

const COLOR_PRESETS = ['#000000', '#ffffff', '#f3f4f6', '#1f2937', '#374151', '#667eea', '#764ba2', '#2563eb', '#3b82f6', '#06b6d4', '#10b981', '#22c55e', '#84cc16', '#eab308', '#f59e0b', '#ef4444', '#dc2626', '#ec4899', '#a855f7', '#8b5cf6'];

const GRADIENT_PRESETS = [
    { name: 'Không có', value: '', preview: '#2563eb' },
    { name: 'Tím - Hồng', value: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', preview: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' },
    { name: 'Xanh dương', value: 'linear-gradient(135deg, #2563eb 0%, #06b6d4 100%)', preview: 'linear-gradient(135deg, #2563eb 0%, #06b6d4 100%)' },
    { name: 'Xanh lá', value: 'linear-gradient(135deg, #10b981 0%, #84cc16 100%)', preview: 'linear-gradient(135deg, #10b981 0%, #84cc16 100%)' },
    { name: 'Cam - Vàng', value: 'linear-gradient(135deg, #f59e0b 0%, #eab308 100%)', preview: 'linear-gradient(135deg, #f59e0b 0%, #eab308 100%)' },
    { name: 'Đỏ - Hồng', value: 'linear-gradient(135deg, #ef4444 0%, #ec4899 100%)', preview: 'linear-gradient(135deg, #ef4444 0%, #ec4899 100%)' },
    { name: 'Cầu vồng', value: 'linear-gradient(90deg, #667eea, #764ba2, #f093fb, #4facfe)', preview: 'linear-gradient(90deg, #667eea, #764ba2, #f093fb, #4facfe)' },
    { name: 'Đen xám', value: 'linear-gradient(135deg, #1f2937 0%, #4b5563 100%)', preview: 'linear-gradient(135deg, #1f2937 0%, #4b5563 100%)' },
    { name: 'Vàng gold', value: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)', preview: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)' },
];

const ANIMATION_PRESETS = [
    { name: 'Không chọn', value: 'none' },
    { name: 'bounce', value: 'bounce' },
    { name: 'pulse', value: 'pulse' },
    { name: 'shake', value: 'shake' },
    { name: 'fadeIn', value: 'fadeIn' },
    { name: 'zoomIn', value: 'zoomIn' },
    { name: 'slideInUp', value: 'slideInUp' },
];

const EVENT_TYPES = [
    { name: 'Không chọn', value: 'none' },
    { name: 'Mở URL', value: 'navigate' },
    { name: 'Gửi Form', value: 'submitForm' },
    { name: 'Gọi API', value: 'triggerApi' },
    { name: 'Mở Popup', value: 'openPopup' },
    { name: 'Đóng Popup', value: 'closePopup' },
    { name: 'Cuộn đến Section', value: 'scrollToSection' },
];
const PropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle }) => {
    const [activeTab, setActiveTab] = useState('design');
    const [imagePreview, setImagePreview] = useState(null);

    // [Existing isCollapsed and no selectedElement handling remains unchanged]

    const { type, componentData = {}, styles = {}, size = {}, position = {} } = selectedElement.json;

    const handleStyleChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [property]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleComponentDataChange = (key, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    [key]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleSizeChange = (dimension, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                size: {
                    ...selectedElement.json.size,
                    [dimension]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handlePositionChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                position: {
                    ...selectedElement.json.position,
                    [property]: parseInt(value),
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
                // Store the image in componentData for persistence
                handleComponentDataChange('backgroundImage', reader.result);
                // Update styles for rendering
                handleStyleChange('backgroundImage', `url(${reader.result})`);
                handleStyleChange('backgroundSize', 'cover');
                handleStyleChange('backgroundPosition', 'center');
            };
            reader.readAsDataURL(file);
        }
    };

    const renderDesignTab = () => {
        const isSection = type === 'section';
        const isPopup = type === 'popup';
        const isText = type === 'text';
        const isButton = type === 'button';
        const isLine = type === 'line';

        return (
            <div className="panel-section">
                <h4 className="section-title">
                    <Palette size={16} strokeWidth={1.5} />
                    Thiết kế
                </h4>
                <div className="input-group">
                    {(isSection || isPopup) && (
                        <>
                            <div>
                                <label className="input-label">Loại nền</label>
                                <select
                                    value={componentData.backgroundType || 'color'}
                                    onChange={(e) => handleComponentDataChange('backgroundType', e.target.value)}
                                    className="input-select"
                                >
                                    <option value="color">Màu đơn</option>
                                    <option value="image">Hình ảnh</option>
                                    <option value="gradient">Gradient</option>
                                </select>
                            </div>

                            {componentData.backgroundType === 'color' && (
                                <div>
                                    <label className="input-label">Màu nền</label>
                                    <div className="color-input-group">
                                        <input
                                            type="color"
                                            value={styles.backgroundColor || '#ffffff'}
                                            onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                            className="color-picker"
                                        />
                                        <input
                                            type="text"
                                            value={styles.backgroundColor || '#ffffff'}
                                            onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                            className="input-field"
                                            placeholder="#ffffff"
                                        />
                                    </div>
                                    <div className="color-preset-grid">
                                        {COLOR_PRESETS.map((color) => (
                                            <div
                                                key={color}
                                                className={`color-preset-item ${styles.backgroundColor === color ? 'active' : ''}`}
                                                style={{ backgroundColor: color }}
                                                onClick={() => handleStyleChange('backgroundColor', color)}
                                                title={color}
                                            />
                                        ))}
                                    </div>
                                </div>
                            )}

                            {componentData.backgroundType === 'image' && (
                                <>
                                    <div>
                                        <label className="input-label">Tải ảnh nền</label>
                                        <input
                                            type="file"
                                            accept="image/*"
                                            onChange={handleImageUpload}
                                            style={{ display: 'none' }}
                                            id="background-image-upload"
                                        />
                                        <label htmlFor="background-image-upload" className="upload-button">
                                            <Upload size={16} strokeWidth={1.5} />
                                            Chọn ảnh
                                        </label>
                                        {(imagePreview || componentData.backgroundImage) && (
                                            <div className="image-preview" style={{ marginTop: '8px' }}>
                                                <img
                                                    src={imagePreview || componentData.backgroundImage}
                                                    alt="Background Preview"
                                                    style={{ maxWidth: '100%', maxHeight: '100px', borderRadius: '6px', objectFit: 'cover' }}
                                                />
                                            </div>
                                        )}
                                    </div>
                                    <div>
                                        <label className="input-label">Kích thước ảnh</label>
                                        <div className="preset-buttons">
                                            {BACKGROUND_SIZE_PRESETS.map((preset) => (
                                                <button
                                                    key={preset.value}
                                                    onClick={() => handleStyleChange('backgroundSize', preset.value)}
                                                    className={`preset-btn ${styles.backgroundSize === preset.value ? 'active' : ''}`}
                                                >
                                                    {preset.name}
                                                </button>
                                            ))}
                                        </div>
                                    </div>
                                    <div>
                                        <label className="input-label">Vị trí ảnh nền</label>
                                        <select
                                            value={styles.backgroundPosition || 'center'}
                                            onChange={(e) => handleStyleChange('backgroundPosition', e.target.value)}
                                            className="input-select"
                                        >
                                            <option value="center">Giữa</option>
                                            <option value="top">Trên</option>
                                            <option value="bottom">Dưới</option>
                                            <option value="left">Trái</option>
                                            <option value="right">Phải</option>
                                            <option value="top left">Trên trái</option>
                                            <option value="top right">Trên phải</option>
                                            <option value="bottom left">Dưới trái</option>
                                            <option value="bottom right">Dưới phải</option>
                                        </select>
                                    </div>
                                </>
                            )}

                            {componentData.backgroundType === 'gradient' && (
                                <div>
                                    <label className="input-label">Gradient</label>
                                    <div className="gradient-preset-grid">
                                        {GRADIENT_PRESETS.map((gradient, idx) => (
                                            <div
                                                key={idx}
                                                className={`gradient-preset-item ${styles.background === gradient ? 'active' : ''}`}
                                                style={{ background: gradient }}
                                                onClick={() => handleStyleChange('background', gradient)}
                                                title={gradient}
                                            />
                                        ))}
                                    </div>
                                    <input
                                        type="text"
                                        value={styles.background || 'linear-gradient(90deg, #667eea, #764ba2)'}
                                        onChange={(e) => handleStyleChange('background', e.target.value)}
                                        className="input-field"
                                        placeholder="linear-gradient(90deg, #667eea, #764ba2)"
                                        style={{ marginTop: '8px' }}
                                    />
                                </div>
                            )}

                            {/* [Remaining section/popup properties like overlay, border radius, box shadow, padding, height, and width remain unchanged] */}
                        </>
                    )}

                    {/* [Existing text, button, and line handling remains unchanged] */}
                </div>
            </div>
        );
    };

    // [renderAnimationTab and renderAdvancedTab remain unchanged]

    return (
        <div className="properties-panel">
            <div className="panel-header">
                <input
                    type="text"
                    value={componentData.title || ''}
                    onChange={(e) => handleComponentDataChange('title', e.target.value)}
                    className="panel-title-input"
                    placeholder={`Tên ${type === 'section' ? 'Section' : type === 'popup' ? 'Popup' : 'Element'}`}
                />
                <button onClick={onToggle} className="toggle-button" title="Đóng">
                    <ChevronRight size={18} />
                </button>
            </div>

            <div className="panel-tabs">
                <button
                    className={`tab-button ${activeTab === 'design' ? 'active' : ''}`}
                    onClick={() => setActiveTab('design')}
                >
                    Thiết kế
                </button>
                <button
                    className={`tab-button ${activeTab === 'animation' ? 'active' : ''}`}
                    onClick={() => setActiveTab('animation')}
                >
                    Hiệu ứng
                </button>
                <button
                    className={`tab-button ${activeTab === 'advanced' ? 'active' : ''}`}
                    onClick={() => setActiveTab('advanced')}
                >
                    Nâng cao
                </button>
            </div>

            <div className="panel-content">
                {activeTab === 'design' && renderDesignTab()}
                {activeTab === 'animation' && renderAnimationTab()}
                {activeTab === 'advanced' && renderAdvancedTab()}
            </div>
        </div>
    );
};

export default PropertiesPanel;
