import React, { useState, useEffect, useContext, useCallback, useMemo } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/AdminMarketplace.css'; // File CSS bạn sẽ cần cập nhật
import DogLoader from '../components/Loader';
import {
    Eye,
    Star,
    Trash2,
    Download,
    RefreshCw,
    BadgeCheck,
    BadgeX,
    ShieldAlert,
    Info,
    SlidersHorizontal,
    Package,
    Clock,
    BadgeDollarSign,
    CheckCircle2,
    XCircle,
    Archive,
    Wallet,
    Undo2
} from 'lucide-react';

const AdminMarketplace = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [actionLoading, setActionLoading] = useState(false);
    const [userRole, setUserRole] = useState(null);
    const [pages, setPages] = useState([]);
    const [stats, setStats] = useState(null);
    const [transactions, setTransactions] = useState([]);
    const [refundRequests, setRefundRequests] = useState([]);
    const [selectedStatus, setSelectedStatus] = useState('PENDING');
    const [selectedPage, setSelectedPage] = useState(null);
    const [showPreviewModal, setShowPreviewModal] = useState(false);
    const [selectedPages, setSelectedPages] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [rejectReason, setRejectReason] = useState('');
    const [suspendReason, setSuspendReason] = useState('');
    const [showRejectModal, setShowRejectModal] = useState(false);
    const [showSuspendModal, setShowSuspendModal] = useState(false);
    const [pageToReject, setPageToReject] = useState(null);
    const [pageToSuspend, setPageToSuspend] = useState(null);
    const [currentTab, setCurrentTab] = useState('pages');
    const [page, setPage] = useState(1);
    const [limit] = useState(20);
    const [totalPages, setTotalPages] = useState(1);
    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    const statusOptions = useMemo(() => [
        { value: 'all', label: 'Tất cả' },
        { value: 'PENDING', label: 'Chờ duyệt' },
        { value: 'ACTIVE', label: 'Đã duyệt' },
        { value: 'REJECTED', label: 'Bị từ chối' },
        { value: 'SUSPENDED', label: 'Tạm ngưng' }
    ], []);

    const transactionStatusOptions = useMemo(() => [
        { value: 'all', label: 'Tất cả' },
        { value: 'PENDING', label: 'Chờ xử lý' },
        { value: 'COMPLETED', label: 'Hoàn thành' },
        { value: 'REFUND_PENDING', label: 'Chờ hoàn tiền' }
    ], []);

    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                toast.error('Vui lòng đăng nhập để tiếp tục');
                navigate('/auth');
                setLoading(false);
                return;
            }
            try {
                const decodedToken = jwtDecode(token);
                if (decodedToken.role !== 'admin') {
                    toast.error('Bạn không có quyền truy cập trang này');
                    navigate('/dashboard');
                    return;
                }
                setUserRole(decodedToken.role);
            } catch (err) {
                console.error('Lỗi giải mã token:', err);
                toast.error('Phiên đăng nhập không hợp lệ');
                navigate('/auth');
            } finally {
                setLoading(false);
            }
        };

        if (user?.role === 'admin') {
            setUserRole(user.role);
            setLoading(false);
        } else {
            initializeAuth();
        }
    }, [user, navigate]);

    useEffect(() => {
        AOS.init({ duration: 600, once: true });
    }, []);

    const loadPages = useCallback(async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            const url = selectedStatus === 'PENDING'
                ? `${API_BASE_URL}/api/admin/marketplace/pending?page=${page}&limit=${limit}`
                : `${API_BASE_URL}/api/admin/marketplace/pages?page=${page}&limit=${limit}${selectedStatus !== 'all' ? `&status=${selectedStatus}` : ''}${searchTerm ? `&q=${encodeURIComponent(searchTerm)}` : ''}`;

            const response = await axios.get(url, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setPages(response.data.data || []);
            setTotalPages(response.data.pagination?.totalPages || 1);
        } catch (err) {
            console.error('Load pages error:', err);
            toast.error(err.response?.data?.message || 'Không thể tải danh sách marketplace pages');
        } finally {
            setLoading(false);
        }
    }, [selectedStatus, page, limit, searchTerm, API_BASE_URL]);

    const loadStats = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/admin/marketplace/stats`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setStats(response.data.data);
        } catch (err) {
            console.error('Load stats error:', err);
            toast.error(err.response?.data?.message || 'Không thể tải thống kê');
        }
    }, [API_BASE_URL]);

    const loadTransactions = useCallback(async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            const url = `${API_BASE_URL}/api/admin/marketplace/transactions?page=${page}&limit=${limit}${selectedStatus !== 'all' ? `&status=${selectedStatus}` : ''}${searchTerm ? `&q=${encodeURIComponent(searchTerm)}` : ''}`;
            const response = await axios.get(url, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setTransactions(response.data.data || []);
            setTotalPages(response.data.pagination?.totalPages || 1);
        } catch (err) {
            console.error('Load transactions error:', err);
            toast.error(err.response?.data?.message || 'Không thể tải danh sách giao dịch');
        } finally {
            setLoading(false);
        }
    }, [selectedStatus, page, limit, searchTerm, API_BASE_URL]);

    const loadRefundRequests = useCallback(async () => {
        setLoading(true);
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/admin/marketplace/refunds`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setRefundRequests(response.data.data || []);
        } catch (err) {
            console.error('Load refund requests error:', err);
            toast.error(err.response?.data?.message || 'Không thể tải danh sách yêu cầu hoàn tiền');
        } finally {
            setLoading(false);
        }
    }, [API_BASE_URL]);

    useEffect(() => {
        if (userRole === 'admin') {
            if (currentTab === 'pages') {
                loadPages();
                loadStats();
            } else if (currentTab === 'transactions') {
                loadTransactions();
            } else if (currentTab === 'refunds') {
                loadRefundRequests();
            }
        }
    }, [userRole, currentTab, loadPages, loadStats, loadTransactions, loadRefundRequests]);

    const handleApprove = async (id) => {
        if (!window.confirm('Bạn có chắc muốn duyệt landing page này?')) return;
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await axios.post(
                `${API_BASE_URL}/api/admin/marketplace/pages/${id}/approve`,
                {},
                { headers: { Authorization: `Bearer ${token}` } }
            );
            toast.success('Đã duyệt thành công');
            loadPages();
            loadStats();
        } catch (err) {
            console.error('Approve error:', err);
            toast.error(err.response?.data?.message || 'Không thể duyệt');
        } finally {
            setActionLoading(false);
        }
    };

    const openRejectModal = (id) => {
        setPageToReject(id);
        setShowRejectModal(true);
    };

    const handleReject = async () => {
        if (!rejectReason.trim()) {
            toast.warning('Vui lòng nhập lý do từ chối');
            return;
        }
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await axios.post(
                `${API_BASE_URL}/api/admin/marketplace/pages/${pageToReject}/reject`,
                { reason: rejectReason },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            toast.success('Đã từ chối landing page');
            setShowRejectModal(false);
            setRejectReason('');
            setPageToReject(null);
            loadPages();
            loadStats();
        } catch (err) {
            console.error('Reject error:', err);
            toast.error(err.response?.data?.message || 'Không thể từ chối');
        } finally {
            setActionLoading(false);
        }
    };

    const openSuspendModal = (id) => {
        setPageToSuspend(id);
        setShowSuspendModal(true);
    };

    const handleSuspend = async () => {
        if (!suspendReason.trim()) {
            toast.warning('Vui lòng nhập lý do tạm ngưng');
            return;
        }
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await axios.post(
                `${API_BASE_URL}/api/admin/marketplace/pages/${pageToSuspend}/suspend`,
                { reason: suspendReason },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            toast.success('Đã tạm ngưng landing page');
            setShowSuspendModal(false);
            setSuspendReason('');
            setPageToSuspend(null);
            loadPages();
            loadStats();
        } catch (err) {
            console.error('Suspend error:', err);
            toast.error(err.response?.data?.message || 'Không thể tạm ngưng');
        } finally {
            setActionLoading(false);
        }
    };

    const handleToggleFeatured = async (id) => {
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await axios.post(
                `${API_BASE_URL}/api/admin/marketplace/pages/${id}/toggle-featured`,
                {},
                { headers: { Authorization: `Bearer ${token}` } }
            );
            toast.success('Đã cập nhật trạng thái featured');
            loadPages();
        } catch (err) {
            console.error('Toggle featured error:', err);
            toast.error(err.response?.data?.message || 'Không thể cập nhật');
        } finally {
            setActionLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm('Bạn có chắc muốn xóa landing page này? Hành động này không thể hoàn tác!')) return;
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`${API_BASE_URL}/api/admin/marketplace/pages/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            toast.success('Đã xóa thành công');
            loadPages();
            loadStats();
        } catch (err) {
            console.error('Delete error:', err);
            toast.error(err.response?.data?.message || 'Không thể xóa');
        } finally {
            setActionLoading(false);
        }
    };

    const handleProcessRefund = async (transactionId) => {
        if (!window.confirm('Bạn có chắc muốn hoàn tiền cho giao dịch này?')) return;
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await axios.post(
                `${API_BASE_URL}/api/admin/marketplace/refunds/process`,
                { transaction_id: transactionId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            toast.success('Đã hoàn tiền thành công');
            loadRefundRequests();
        } catch (err) {
            console.error('Process refund error:', err);
            toast.error(err.response?.data?.message || 'Không thể hoàn tiền');
        } finally {
            setActionLoading(false);
        }
    };

    const handleRejectRefund = async (transactionId, reason) => {
        if (!reason || !reason.trim()) {
            toast.warning('Vui lòng nhập lý do từ chối hoàn tiền');
            return;
        }
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await axios.post(
                `${API_BASE_URL}/api/admin/marketplace/refunds/reject`,
                { transaction_id: transactionId, reason },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            toast.success('Đã từ chối yêu cầu hoàn tiền');
            loadRefundRequests();
        } catch (err) {
            console.error('Reject refund error:', err);
            toast.error(err.response?.data?.message || 'Không thể từ chối hoàn tiền');
        } finally {
            setActionLoading(false);
        }
    };

    const getStatusBadge = useCallback((status) => {
        const badges = {
            DRAFT: { color: '#6b7280', label: 'Bản nháp', icon: <Archive size={14} /> },
            PENDING: { color: '#f59e0b', label: 'Chờ duyệt', icon: <Clock size={14} /> },
            ACTIVE: { color: '#10b981', label: 'Đã duyệt', icon: <CheckCircle2 size={14} /> },
            REJECTED: { color: '#ef4444', label: 'Từ chối', icon: <XCircle size={14} /> },
            SUSPENDED: { color: '#f97316', label: 'Tạm ngưng', icon: <ShieldAlert size={14} /> },
            COMPLETED: { color: '#10b981', label: 'Hoàn thành', icon: <CheckCircle2 size={14} /> },
            REFUND_PENDING: { color: '#f59e0b', label: 'Chờ hoàn tiền', icon: <Undo2 size={14} /> }
        };
        const badge = badges[status] || { color: '#6b7280', label: status, icon: <Info size={14} /> };

        return (
            <span className="status-badge-luxury" style={{ backgroundColor: `${badge.color}20`, color: badge.color }}>
                {badge.icon}
                {badge.label}
            </span>
        );
    }, []);

    const formatPrice = useCallback((price) => {
        if (typeof price !== 'number') return 'N/A';
        return new Intl.NumberFormat('vi-VN', { style: 'currency', currency: 'VND' }).format(price);
    }, []);

    const formatDate = useCallback((date) => {
        if (!date) return 'N/A';
        return new Date(date).toLocaleString('vi-VN');
    }, []);

    const handleSelectPage = useCallback((pageId) => {
        setSelectedPages(prev => prev.includes(pageId) ? prev.filter(id => id !== pageId) : [...prev, pageId]);
    }, []);

    const handleSelectAll = useCallback(() => {
        setSelectedPages(selectedPages.length === pages.length ? [] : pages.map(p => p._id));
    }, [selectedPages, pages]);

    const handleBulkApprove = async () => {
        if (selectedPages.length === 0) {
            toast.warning('Vui lòng chọn ít nhất một trang');
            return;
        }
        if (!window.confirm(`Duyệt ${selectedPages.length} trang đã chọn?`)) return;
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await Promise.all(selectedPages.map(id =>
                axios.post(
                    `${API_BASE_URL}/api/admin/marketplace/pages/${id}/approve`,
                    {},
                    { headers: { Authorization: `Bearer ${token}` } }
                )
            ));
            toast.success(`Đã duyệt ${selectedPages.length} trang thành công`);
            setSelectedPages([]);
            loadPages();
            loadStats();
        } catch (err) {
            console.error('Bulk approve error:', err);
            toast.error(err.response?.data?.message || 'Có lỗi xảy ra khi duyệt hàng loạt');
        } finally {
            setActionLoading(false);
        }
    };

    const handleBulkDelete = async () => {
        if (selectedPages.length === 0) {
            toast.warning('Vui lòng chọn ít nhất một trang');
            return;
        }
        if (!window.confirm(`Xóa ${selectedPages.length} trang đã chọn? Hành động này không thể hoàn tác!`)) return;
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            await Promise.all(selectedPages.map(id =>
                axios.delete(`${API_BASE_URL}/api/admin/marketplace/pages/${id}`, {
                    headers: { Authorization: `Bearer ${token}` }
                })
            ));
            toast.success(`Đã xóa ${selectedPages.length} trang thành công`);
            setSelectedPages([]);
            loadPages();
            loadStats();
        } catch (err) {
            console.error('Bulk delete error:', err);
            toast.error(err.response?.data?.message || 'Có lỗi xảy ra khi xóa hàng loạt');
        } finally {
            setActionLoading(false);
        }
    };

    const handleExportData = async () => {
        setActionLoading(true);
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/admin/marketplace/export`, {
                headers: { Authorization: `Bearer ${token}` },
                responseType: 'blob'
            });
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `marketplace-export-${new Date().toISOString()}.csv`);
            document.body.appendChild(link);
            link.click();
            link.remove();
            toast.success('Đã xuất dữ liệu thành công');
        } catch (err) {
            console.error('Export error:', err);
            toast.error(err.response?.data?.message || 'Không thể xuất dữ liệu');
        } finally {
            setActionLoading(false);
        }
    };

    const filteredPages = useMemo(() => pages, [pages]);

    if (loading && !stats) {
        return <DogLoader />;
    }

    return (
        <div className="admin-marketplace-container luxury-theme">
            <Sidebar role={userRole} />
            <div className="admin-marketplace-main">
                <Header />
                <div className="admin-marketplace-content">
                    <div className="admin-marketplace-header" data-aos="fade-down">
                        <div>
                            <h1>Marketplace Dashboard</h1>
                            <p>Tổng quan, quản lý và điều hành các hoạt động trên sàn giao dịch.</p>
                        </div>
                    </div>

                    <div className="tabs-luxury" data-aos="fade-up">
                        <button className={`tab-item ${currentTab === 'pages' ? 'active' : ''}`} onClick={() => setCurrentTab('pages')}>
                            <Package size={16} /> Landing Pages
                        </button>
                        <button className={`tab-item ${currentTab === 'transactions' ? 'active' : ''}`} onClick={() => setCurrentTab('transactions')}>
                            <Wallet size={16} /> Giao dịch
                        </button>
                        <button className={`tab-item ${currentTab === 'refunds' ? 'active' : ''}`} onClick={() => setCurrentTab('refunds')}>
                            <Undo2 size={16} /> Yêu cầu hoàn tiền
                        </button>
                    </div>

                    {stats && currentTab === 'pages' && (
                        <div className="stats-grid-luxury" data-aos="fade-up">
                            <div className="stat-card-luxury">
                                <div className="stat-icon-wrapper" style={{ background: 'linear-gradient(135deg, #4338ca, #6d28d9)' }}><Package /></div>
                                <div className="stat-info">
                                    <div className="stat-value">{stats.overview?.totalPages || 0}</div>
                                    <div className="stat-label">Tổng số trang</div>
                                </div>
                            </div>
                            <div className="stat-card-luxury">
                                <div className="stat-icon-wrapper" style={{ background: 'linear-gradient(135deg, #f59e0b, #f97316)' }}><Clock /></div>
                                <div className="stat-info">
                                    <div className="stat-value">{stats.overview?.pendingPages || 0}</div>
                                    <div className="stat-label">Đang chờ duyệt</div>
                                </div>
                            </div>
                            <div className="stat-card-luxury">
                                <div className="stat-icon-wrapper" style={{ background: 'linear-gradient(135deg, #10b981, #14b8a6)' }}><CheckCircle2 /></div>
                                <div className="stat-info">
                                    <div className="stat-value">{stats.overview?.activePages || 0}</div>
                                    <div className="stat-label">Đang hoạt động</div>
                                </div>
                            </div>
                            <div className="stat-card-luxury">
                                <div className="stat-icon-wrapper" style={{ background: 'linear-gradient(135deg, #1d4ed8, #2563eb)' }}><BadgeDollarSign /></div>
                                <div className="stat-info">
                                    <div className="stat-value">{formatPrice(stats.overview?.totalRevenue || 0)}</div>
                                    <div className="stat-label">Tổng doanh thu</div>
                                </div>
                            </div>
                        </div>
                    )}

                    {currentTab === 'pages' && (
                        <>
                            <div className="admin-toolbar-luxury" data-aos="fade-up">
                                <div className="search-box-luxury">
                                    <SlidersHorizontal size={20} />
                                    <input
                                        type="text"
                                        placeholder="Tìm kiếm theo tên, danh mục, người bán..."
                                        value={searchTerm}
                                        onChange={(e) => setSearchTerm(e.target.value)}
                                        className="search-input-luxury"
                                    />
                                </div>
                                <div className="toolbar-actions">
                                    <select value={selectedStatus} onChange={(e) => setSelectedStatus(e.target.value)} className="status-filter-luxury">
                                        {statusOptions.map(opt => (
                                            <option key={opt.value} value={opt.value}>{opt.label}</option>
                                        ))}
                                    </select>
                                    <button className="toolbar-btn-luxury icon-btn" onClick={loadPages} title="Làm mới" disabled={actionLoading}>
                                        <RefreshCw size={18} />
                                    </button>
                                    <button className="toolbar-btn-luxury" onClick={handleExportData} title="Xuất dữ liệu" disabled={actionLoading}>
                                        <Download size={18} /> Xuất CSV
                                    </button>
                                </div>
                            </div>

                            {selectedPages.length > 0 && (
                                <div className="bulk-actions-bar" data-aos="fade-up">
                                    <span>Đã chọn <strong>{selectedPages.length}</strong> trang</span>
                                    <div className="bulk-buttons">
                                        <button className="bulk-btn approve" onClick={handleBulkApprove} disabled={actionLoading}>
                                            <BadgeCheck size={16} /> Duyệt tất cả
                                        </button>
                                        <button className="bulk-btn delete" onClick={handleBulkDelete} disabled={actionLoading}>
                                            <Trash2 size={16} /> Xóa tất cả
                                        </button>
                                    </div>
                                </div>
                            )}

                            <div className="table-container" data-aos="fade-up">
                                <div className="table-header-luxury">
                                    <div className="col-select">
                                        <input type="checkbox" onChange={handleSelectAll} checked={selectedPages.length === pages.length && pages.length > 0} />
                                    </div>
                                    <div className="col-info">Thông tin trang</div>
                                    <div className="col-stats">Thống kê</div>
                                    <div className="col-seller">Người bán</div>
                                    <div className="col-price">Giá</div>
                                    <div className="col-actions">Hành động</div>
                                </div>
                                <div className="pages-list-luxury">
                                    {loading ? (
                                        <DogLoader />
                                    ) : filteredPages.length === 0 ? (
                                        <div className="empty-state-luxury">
                                            <Archive size={48} />
                                            <p>{searchTerm ? 'Không tìm thấy kết quả phù hợp' : 'Chưa có landing page nào trong mục này'}</p>
                                        </div>
                                    ) : (
                                        filteredPages.map(page => (
                                            <div key={page._id} className="page-item-luxury">
                                                <div className="col-select">
                                                    <input type="checkbox" checked={selectedPages.includes(page._id)} onChange={() => handleSelectPage(page._id)} />
                                                </div>
                                                <div className="col-info">
                                                    <img src={page.main_screenshot || '/placeholder.png'} alt={page.title} className="page-thumbnail" />
                                                    <div className="page-details">
                                                        <h4>{page.title}</h4>
                                                        <p>{page.category}</p>
                                                        {page.is_featured && <span className="featured-tag"><Star size={12} /> Featured</span>}
                                                    </div>
                                                </div>
                                                <div className="col-stats">
                                                    <span>👁️ {page.views}</span>
                                                    <span>🛒 {page.sold_count}</span>
                                                    <span>⭐ {(page.rating || 0).toFixed(1)}</span>
                                                </div>
                                                <div className="col-seller">
                                                    <p>{page.seller_id?.name || page.seller_id?.email || 'N/A'}</p>
                                                    <span>{formatDate(page.created_at)}</span>
                                                </div>
                                                <div className="col-price">{formatPrice(page.price)}</div>
                                                <div className="col-actions">
                                                    {getStatusBadge(page.status)}
                                                    <div className="action-buttons-group">
                                                        <button className="action-btn" onClick={() => { setSelectedPage(page); setShowPreviewModal(true); }} disabled={actionLoading}><Eye size={16} /></button>
                                                        {page.status === 'PENDING' && (
                                                            <>
                                                                <button className="action-btn approve" onClick={() => handleApprove(page._id)} disabled={actionLoading}><BadgeCheck size={16} /></button>
                                                                <button className="action-btn reject" onClick={() => openRejectModal(page._id)} disabled={actionLoading}><BadgeX size={16} /></button>
                                                            </>
                                                        )}
                                                        {page.status === 'ACTIVE' && (
                                                            <>
                                                                <button className="action-btn suspend" onClick={() => openSuspendModal(page._id)} disabled={actionLoading}><ShieldAlert size={16} /></button>
                                                                <button className="action-btn feature" onClick={() => handleToggleFeatured(page._id)} disabled={actionLoading}><Star size={16} /></button>
                                                            </>
                                                        )}
                                                        <button className="action-btn delete" onClick={() => handleDelete(page._id)} disabled={actionLoading}><Trash2 size={16} /></button>
                                                    </div>
                                                    {page.rejection_reason && <div className="rejection-reason-tooltip"><Info size={14} /> {page.rejection_reason}</div>}
                                                </div>
                                            </div>
                                        ))
                                    )}
                                </div>
                            </div>

                            {totalPages > 1 && (
                                <div className="pagination" data-aos="fade-up">
                                    <button onClick={() => setPage(prev => Math.max(prev - 1, 1))} disabled={page === 1 || actionLoading}>
                                        Trước
                                    </button>
                                    <span>Trang {page} / {totalPages}</span>
                                    <button onClick={() => setPage(prev => Math.min(prev + 1, totalPages))} disabled={page === totalPages || actionLoading}>
                                        Sau
                                    </button>
                                </div>
                            )}
                        </>
                    )}

                    {/* Các tab khác (Giao dịch, Hoàn tiền) sẽ được thêm vào đây với cấu trúc tương tự nếu cần */}

                    {showPreviewModal && selectedPage && (
                        <div className="modal-overlay" onClick={() => setShowPreviewModal(false)}>
                            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                                <h2>{selectedPage.title}</h2>
                                <img src={selectedPage.main_screenshot} alt={selectedPage.title} style={{ width: '100%', marginBottom: '20px', borderRadius: '8px' }} />
                                <p><strong>Mô tả:</strong> {selectedPage.description}</p>
                                <p><strong>Danh mục:</strong> {selectedPage.category}</p>
                                <p><strong>Giá:</strong> {formatPrice(selectedPage.price)}</p>
                                <p><strong>Người bán:</strong> {selectedPage.seller_id?.name || selectedPage.seller_id?.email}</p>
                                <button onClick={() => setShowPreviewModal(false)} disabled={actionLoading}>Đóng</button>
                            </div>
                        </div>
                    )}

                    {showRejectModal && (
                        <div className="modal-overlay" onClick={() => setShowRejectModal(false)}>
                            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                                <h2>Từ chối landing page</h2>
                                <p style={{ marginBottom: '15px', color: '#9CA3AF' }}>Vui lòng nhập lý do từ chối để người bán có thể cải thiện:</p>
                                <textarea
                                    placeholder="Ví dụ: Nội dung không phù hợp, vi phạm bản quyền, chất lượng kém..."
                                    value={rejectReason}
                                    onChange={(e) => setRejectReason(e.target.value)}
                                    rows={5}
                                    className="modal-textarea"
                                />
                                <div className="modal-actions">
                                    <button onClick={() => { setShowRejectModal(false); setRejectReason(''); setPageToReject(null); }} className="modal-btn-cancel" disabled={actionLoading}>
                                        Hủy
                                    </button>
                                    <button onClick={handleReject} className="modal-btn-confirm reject" disabled={actionLoading}>
                                        Xác nhận từ chối
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}

                    {showSuspendModal && (
                        <div className="modal-overlay" onClick={() => setShowSuspendModal(false)}>
                            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                                <h2>Tạm ngưng landing page</h2>
                                <p style={{ marginBottom: '15px', color: '#9CA3AF' }}>Vui lòng nhập lý do tạm ngưng:</p>
                                <textarea
                                    placeholder="Ví dụ: Vi phạm chính sách, cần kiểm tra lại..."
                                    value={suspendReason}
                                    onChange={(e) => setSuspendReason(e.target.value)}
                                    rows={5}
                                    className="modal-textarea"
                                />
                                <div className="modal-actions">
                                    <button onClick={() => { setShowSuspendModal(false); setSuspendReason(''); setPageToSuspend(null); }} className="modal-btn-cancel" disabled={actionLoading}>
                                        Hủy
                                    </button>
                                    <button onClick={handleSuspend} className="modal-btn-confirm suspend" disabled={actionLoading}>
                                        Xác nhận tạm ngưng
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}

                    {actionLoading && (
                        <div className="loading-overlay">
                            <DogLoader />
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default AdminMarketplace;
