import React, {useState, useEffect, useContext, Suspense} from 'react';
import api from '@landinghub/api';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import '../styles/Dashboard.css';
import { useLocation } from 'react-router-dom';
import DogLoader from "../components/DogLoader";
const Pages = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const location = useLocation();
    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await api.get('/api/user/info');
                setUserData(res.data);
            } catch (err) {
                console.error('Error fetching user data:', err);
                setError('Không thể tải dữ liệu người dùng.');
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    if (loading) return <DogLoader />;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;
    useEffect(() => {
        if (user?.role) {
            setUserRole(user.role);
        } else if (localStorage.getItem('token')) {
            try {
                const { jwtDecode } = require('jwt-decode');
                const decodedToken = jwtDecode(localStorage.getItem('token'));
                setUserRole(decodedToken.role);
            } catch (err) {
                console.error('Error decoding token:', err);
            }
        }
        setLoading(false);
    }, [user]);

    const renderDashboard = () => {
        if (loading) return <DogLoader />;

        switch (userRole) {
            case 'admin':
                return (
                    <Suspense>
                        <AdminDashboard />
                    </Suspense>
                );
            case 'user':
                return (
                    <Suspense >
                        <UserDashboard />
                    </Suspense>
                );
            default:
                return <div>Role không hợp lệ hoặc chưa đăng nhập</div>;
        }
    };

    const isCompact = location.pathname !== '/dashboard';

    return (
        <div className="dashboard-container">
            <Header role={userRole} />
            <div className="dashboard-main">
                <Sidebar role={userRole} isCompact={isCompact} />
                <div className="dashboard-content">{renderDashboard()}</div>
            </div>
        </div>
    );
};

export default React.memo(Pages);
