import React, { useState } from 'react';
import '../styles/PreviewModal.css';

const PreviewModal = ({ previewTemplate, setShowPreview, handleUseTemplate }) => {
    const [viewMode, setViewMode] = useState('desktop');

    const handleClose = (e) => {
        e.stopPropagation();
        console.log('Closing preview modal');
        setShowPreview(false);
    };

    return (
        <div className="modal-overlay modal-overlay--preview" onClick={handleClose}>
            <div className="modal-content modal-content--preview" onClick={e => e.stopPropagation()}>
                <div className="modal-header modal-header--preview">
                    <div>
                        <h2 className="modal-title">
                            <i className="fas fa-eye"></i> Xem trước: {previewTemplate?.template?.name || 'Template'}
                        </h2>
                        <p style={{ margin: '0.5rem 0 0 0', color: '#64748b', fontSize: '0.875rem' }}>
                            {previewTemplate?.template?.category || 'Không xác định'}
                        </p>
                    </div>
                    <button
                        onClick={handleClose}
                        className="btn btn--close"
                        aria-label="Đóng modal"
                    >
                        <i className="fas fa-times"></i>
                    </button>
                </div>
                <div className="modal-body">
                    <div className="preview-tabs">
                        <button
                            className={`preview-tab ${viewMode === 'desktop' ? 'active' : ''}`}
                            onClick={() => setViewMode('desktop')}
                        >
                            Desktop
                        </button>
                        <button
                            className={`preview-tab ${viewMode === 'mobile' ? 'active' : ''}`}
                            onClick={() => setViewMode('mobile')}
                        >
                            Mobile
                        </button>
                    </div>
                    <div className={`preview-container ${viewMode}`}>
                        <div className="preview-frame">
                            {previewTemplate?.html ? (
                                viewMode === 'desktop' ? (
                                    <div className="desktop-preview">
                                        <div className="desktop-header">
                                            <span className="control-btn red"></span>
                                            <span className="control-btn yellow"></span>
                                            <span className="control-btn green"></span>
                                        </div>
                                        <iframe
                                            srcDoc={previewTemplate.html}
                                            className="modal-iframe"
                                            title="Template Preview"
                                            sandbox="allow-scripts allow-same-origin allow-popups"
                                            style={{ width: '100%', height: 'calc(100% - 32px)', overflow: 'auto' }}
                                        />
                                    </div>
                                ) : (
                                    <div className="mobile-preview">
                                        <div className="mobile-frame">
                                            <iframe
                                                srcDoc={previewTemplate.html}
                                                className="modal-iframe"
                                                title="Template Preview"
                                                sandbox="allow-scripts allow-same-origin allow-popups"
                                                style={{ width: '100%', height: 'calc(100% - 24px)', overflow: 'auto', transform: 'scale(0.95)', transformOrigin: 'top left' }}
                                            />
                                            <div className="mobile-home-button"></div>
                                        </div>
                                    </div>
                                )
                            ) : (
                                <div className="preview-placeholder">
                                    <p>Không có nội dung để hiển thị. Vui lòng kiểm tra file HTML hoặc API.</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
                <div className="modal-footer">
                    <button
                        className="btn btn-primary"
                        onClick={() => {
                            setShowPreview(false);
                            handleUseTemplate(previewTemplate?.template?.id, previewTemplate?.template?.name);
                        }}
                        disabled={!previewTemplate?.template?.id}
                    >
                        <i className="fas fa-rocket"></i> Sử dụng
                    </button>
                </div>
            </div>
        </div>
    );
};

export default PreviewModal;
