import React, { useState, useCallback, useMemo, useEffect } from 'react';
import { useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import '../../styles/ComponentLibrary.css'
const ItemTypes = {
    COMPONENT: 'component',
    TEMPLATE: 'template',
};

const EXTENDED_LIBRARY = {
    elements: {
        name: 'Phần tử',
        icon: 'fas fa-cube',
        subCategories: [
            {
                id: 'text',
                name: 'Văn bản',
                templates: [
                    {
                        id: 'text-basic',
                        name: 'Cơ bản',
                        icon: 'far fa-font',
                        description: 'Văn bản đơn giản, dễ chỉnh sửa',
                        level: 'basic',
                        json: {
                            type: 'heading',
                            content: 'Tiêu đề mẫu',
                            styles: {color: '#333', fontSize: '24px'},
                        },
                    },
                    {
                        id: 'text-advanced',
                        name: 'Nâng cao',
                        icon: 'far fa-pen',
                        description: 'Văn bản với hiệu ứng động và tùy chỉnh',
                        level: 'advanced',
                        variants: ['bold', 'italic', 'animated'],
                        json: {
                            type: 'paragraph',
                            content: 'Văn bản nâng cao với hiệu ứng',
                            styles: {color: '#555', fontStyle: 'italic', animation: 'fadeIn 1s'},
                        },
                    },
                ],
            },
            {
                id: 'buttons',
                name: 'Nút bấm',
                templates: [
                    {
                        id: 'button-basic',
                        name: 'Nút cơ bản',
                        icon: 'far fa-square',
                        description: 'Nút hành động đơn giản, dễ sử dụng',
                        level: 'basic',
                        json: {
