const DraggableComponent = React.memo(({ item, isSection = false, onDrag, onDragEnd }) => {
    const [{ isDragging }, drag] = useDrag(() => ({
        type: isSection ? ItemTypes.SECTION : ItemTypes.ELEMENT,
        item: { componentData: item },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    const renderPreview = () => {
        const { type, content, styles } = item.json;
        const styleProps = {
            ...styles,
            opacity: isDragging ? 0.5 : 1,
            margin: '5px',
            maxWidth: '100%',
            boxSizing: 'border-box',
        };

        const getTemplateClass = () => {
            switch (type) {
                case 'form':
                    return 'lpb-template-form';
                case 'gallery':
                case 'carousel':
                    return 'lpb-template-gallery';
                default:
                    return '';
            }
        };

        switch (type) {
            case 'section':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <h3>{content.title || 'Section'}</h3>
                        {content.subtitle && <p>{content.subtitle}</p>}
                    </div>
                );
            case 'heading':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <h2>{content}</h2>
                    </div>
                );
            case 'paragraph':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <p>{content}</p>
                    </div>
                );
            case 'button':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <button style={styles}>{content}</button>
                    </div>
                );
            case 'button-group':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {content.map((btn, index) => (
                            <button key={index} style={{ ...styles, marginRight: index < content.length - 1 ? '10px' : 0 }}>
                                {btn}
                            </button>
                        ))}
                    </div>
                );
            case 'form':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <form>
                            {content.fields.map((field, index) => (
                                <input
                                    key={index}
                                    type="text"
                                    placeholder={field}
                                    style={{ marginBottom: '8px', width: '100%', ...styles }}
                                />
                            ))}
                            <button type="submit" style={{ ...styles, marginTop: '8px' }}>Submit</button>
                        </form>
                    </div>
                );
            case 'image':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <img src={content} alt={item.name} style={{ ...styles, maxWidth: '100%', height: 'auto' }} />
                    </div>
                );
            case 'gallery':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {content.map((src, index) => (
                            <img
                                key={index}
                                src={src}
                                alt={`Gallery ${index}`}
                                style={{ width: '100px', marginRight: '8px', ...styles }}
                            />
                        ))}
                    </div>
                );
            case 'product-list':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {content.map((prod, index) => (
                            <div key={index} style={{ marginBottom: '10px', ...styles }}>
                                {prod.title}
                            </div>
                        ))}
                    </div>
                );
            case 'popup':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <div>{content}</div>
                    </div>
                );
            default:
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {item.name}
                    </div>
                );
        }
    };

    return renderPreview();
});

const ComponentLibrary = React.memo(({ isCollapsed, onToggle, addSection, addElement }) => {
    const [activeCategory, setActiveCategory] = useState('sections');
    const [activeSubCategory, setActiveSubCategory] = useState(null);
    const [isDragging, setIsDragging] = useState(false);

    const categories = Object.keys(EXTENDED_LIBRARY).map((key) => ({
        id: key,
        ...EXTENDED_LIBRARY[key],
    }));

    const handleToggle = useCallback(() => {
        onToggle();
    }, [onToggle]);

    const handleCategoryChange = useCallback((categoryId) => {
        setActiveCategory(categoryId);
        setActiveSubCategory(null);
        toast.info(`Đã chuyển sang danh mục ${EXTENDED_LIBRARY[categoryId]?.name}`);
    }, []);

    const handleSubCategoryChange = useCallback((subId) => {
        setActiveSubCategory(subId === activeSubCategory ? null : subId);
        toast.info(`Đã chọn danh mục con ${EXTENDED_LIBRARY[activeCategory]?.subCategories.find((sub) => sub.id === subId)?.name}`);
    }, [activeCategory, activeSubCategory]);

    const filteredItems = useMemo(() => {
        const categoryData = EXTENDED_LIBRARY[activeCategory];
        if (!categoryData || !activeSubCategory) return [];
        const subCategory = categoryData.subCategories.find((sub) => sub.id === activeSubCategory);
        return subCategory ? subCategory.templates : [];
    }, [activeCategory, activeSubCategory]);

    categories.forEach((cat, index) => {
        useHotkeys(`ctrl+${index + 1}`, () => handleCategoryChange(cat.id), [handleCategoryChange]);
    });

    if (isCollapsed) {
        return (
            <div className="lpb-component-library lpb-component-library-collapsed">
                <div className="lpb-component-library-header">
                    <button onClick={handleToggle} className="lpb-component-library-toggle">
                        <i className="fas fa-chevron-right"></i>
                    </button>
                </div>
                {categories.map((cat) => (
                    <button
                        key={cat.id}
                        className="lpb-component-library-icon-collapsed"
                        onClick={() => {
                            handleCategoryChange(cat.id);
                            handleToggle();
                        }}
                    >
                        <i className={cat.icon}></i>
                    </button>
                ))}
            </div>
        );
    }

    return (
        <div className="lpb-component-library">
            <div className="lpb-component-library-wrapper">
                <div className="lpb-component-library-category-nav">
                    <div className="lpb-component-library-header">
                        <h2 className="lpb-component-library-title">Thư viện</h2>
                        <button onClick={handleToggle} className="lpb-component-library-toggle">
                            <i className="fas fa-chevron-left"></i>
                        </button>
                    </div>
                    {categories.map((cat) => (
                        <button
                            key={cat.id}
                            onClick={() => handleCategoryChange(cat.id)}
                            className={`lpb-component-library-category-btn ${activeCategory === cat.id ? 'lpb-component-library-category-btn-active' : ''}`}
                        >
                            {cat.name}
                        </button>
                    ))}
                </div>
                <div className="lpb-component-library-subcategory-nav">
                    {EXTENDED_LIBRARY[activeCategory]?.subCategories.map((sub) => (
                        <button
                            key={sub.id}
                            onClick={() => handleSubCategoryChange(sub.id)}
                            className={`lpb-component-library-subcategory-btn ${activeSubCategory === sub.id ? 'lpb-component-library-subcategory-btn-active' : ''}`}
                        >
                            {sub.name}
                        </button>
                    ))}
                </div>
                <div className="lpb-component-library-template-column">
                    {filteredItems.length > 0 ? (
                        <div className="lpb-component-library-template-list">
                            {filteredItems.map((item) => (
                                <DraggableComponent
                                    key={item.id}
                                    item={item}
                                    isSection={activeCategory === 'sections' || activeCategory === 'templates'}
                                    onDrag={() => setIsDragging(true)}
                                    onDragEnd={() => setIsDragging(false)}
                                />
                            ))}
                        </div>
                    ) : (
                        <div className="lpb-component-library-empty">Chọn một danh mục con để xem templates</div>
                    )}
                </div>
            </div>
        </div>
    );
});

export default ComponentLibrary;
