// F:\landinghub-iconic\apps\web\src\components\Sidebar.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

const Sidebar = ({ role }) => {
    const navigate = useNavigate();

    const userMenu = [
        { name: 'Tổng quan', path: '/dashboard' },
        { name: 'Quản lý Landing Page', path: '/dashboard/pages' },
        { name: 'Quản lý Leads', path: '/dashboard/leads' },
        { name: 'Thanh toán & Premium', path: '/dashboard/payments' },
        { name: 'Báo cáo', path: '/dashboard/reports' },
        { name: 'Quảng cáo & Marketing', path: '/dashboard/marketing' },
        { name: 'Tương tác Khách hàng', path: '/dashboard/chatbot' },
    ];

    const adminMenu = [
        { name: 'Tổng quan Hệ thống', path: '/dashboard' },
        { name: 'Quản lý Tài khoản', path: '/dashboard/users' },
        { name: 'Kiểm tra Thanh toán', path: '/dashboard/payments' },
        { name: 'Quản lý Mẫu Marketplace', path: '/dashboard/templates' },
        { name: 'Báo cáo Hệ thống', path: '/dashboard/reports' },
    ];

    const menu = role === 'admin' ? adminMenu : userMenu;

    return (
        <aside className="dashboard-sidebar">
            <ul>
                {menu.map((item) => (
                    <li key={item.name} onClick={() => navigate(item.path)}>{item.name}</li>
                ))}
            </ul>
        </aside>
    );
};

export default React.memo(Sidebar);
