import React, { useRef, useCallback, useState, useEffect } from 'react';
import { useDrop } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import Guidelines from './Guidelines';
import Rulers from './Rulers';
import SelectionOverlay from './SelectionOverlay';
import '../../styles/Canvas.css';
import '@fortawesome/fontawesome-free/css/all.min.css';

const ItemTypes = {
    ELEMENT: 'element',
};

// Utility functions
const getCanvasPosition = (mouseX, mouseY, canvasRef, zoomLevel = 100, canvasBounds) => {
    if (!canvasRef?.current) {
        return { x: 0, y: 0 };
    }

    const canvasRect = canvasRef.current.getBoundingClientRect();
    const scale = zoomLevel / 100;
    const x = (mouseX - canvasRect.left) / scale;
    const y = (mouseY - canvasRect.top) / scale;

    // Clamp to prevent negative or out of bounds
    const clampedX = Math.max(0, Math.min(x, canvasBounds.width));
    const clampedY = Math.max(0, Math.min(y, canvasBounds.height));

    return { x: clampedX, y: clampedY };
};

const snapToGrid = (x, y, gridSize = 10, snapPoints = [], scale = 1, enableSnapping = true) => {
    if (!enableSnapping || !Number.isFinite(gridSize) || gridSize <= 0) {
        return { x, y };
    }

    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    const threshold = 10 / scale; // Tăng threshold để ổn định

    let minDistX = Infinity, minDistY = Infinity;
    let bestX = snappedX, bestY = snappedY;

    snapPoints.forEach((point) => {
        const distX = Math.abs(x - point.x);
        const distY = Math.abs(y - point.y);
        if (distX < threshold && distX < minDistX) {
            minDistX = distX;
            bestX = point.x;
        }
        if (distY < threshold && distY < minDistY) {
            minDistY = distY;
            bestY = point.y;
        }
    });

    return { x: bestX, y: bestY };
};

const getElementBounds = (element) => ({
    left: element.position?.desktop?.x || 0,
    top: element.position?.desktop?.y || 0,
    right: (element.position?.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position?.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position?.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position?.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

// Draggable Element Component
const DraggableElement = React.memo(({
                                         element,
                                         isSelected,
                                         onSelectElement,
                                         onUpdatePosition,
                                         viewMode,
                                         zoomLevel,
                                         gridSize,
                                         onContextMenu,
                                         canvasRef,
                                         snapPoints,
                                         enableSnapping = true,
                                         canvasBounds,
                                     }) => {
    const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {} } = element;
    const elementRef = useRef(null);
    const [isDragging, setIsDragging] = useState(false);
    const [dragStart, setDragStart] = useState({ x: 0, y: 0, offsetX: 0, offsetY: 0 });

    const scale = zoomLevel / 100;

    const handleMouseDown = useCallback((e) => {
        e.preventDefault();
        e.stopPropagation();

        const currentPos = position[viewMode] || { x: 0, y: 0 };
        const mouseCanvasPos = getCanvasPosition(e.clientX, e.clientY, canvasRef, zoomLevel, canvasBounds);

        setDragStart({
            x: mouseCanvasPos.x,
            y: mouseCanvasPos.y,
            offsetX: mouseCanvasPos.x - currentPos.x,
            offsetY: mouseCanvasPos.y - currentPos.y,
        });

        setIsDragging(true);
        onSelectElement(id, e.ctrlKey);
        document.body.style.userSelect = 'none';
    }, [position, viewMode, canvasRef, zoomLevel, onSelectElement, id, canvasBounds]);

    const handleMouseMove = useCallback((e) => {
        if (!isDragging) return;

        const mouseCanvasPos = getCanvasPosition(e.clientX, e.clientY, canvasRef, zoomLevel, canvasBounds);
        const newPos = {
            x: mouseCanvasPos.x - dragStart.offsetX,
            y: mouseCanvasPos.y - dragStart.offsetY,
        };

        const snapped = snapToGrid(newPos.x, newPos.y, gridSize, snapPoints, scale, enableSnapping);

        // Clamp to canvas bounds
        const boundedX = Math.max(0, Math.min(snapped.x, canvasBounds.width - size.width));
        const boundedY = Math.max(0, Math.min(snapped.y, canvasBounds.height - size.height));

        onUpdatePosition(id, { [viewMode]: { x: boundedX, y: boundedY } }, false);
    }, [isDragging, dragStart, canvasRef, zoomLevel, gridSize, snapPoints, onUpdatePosition, id, viewMode, scale, enableSnapping, canvasBounds, size]);

    const handleMouseUp = useCallback(() => {
        if (isDragging) {
            setIsDragging(false);
            const currentPos = position[viewMode] || { x: 0, y: 0 };
            const boundedX = Math.max(0, Math.min(currentPos.x, canvasBounds.width - size.width));
            const boundedY = Math.max(0, Math.min(currentPos.y, canvasBounds.height - size.height));
            onUpdatePosition(id, { [viewMode]: { x: boundedX, y: boundedY } }, true);
            document.body.style.userSelect = '';
        }
    }, [isDragging, position, viewMode, onUpdatePosition, id, canvasBounds, size]);

    useEffect(() => {
        if (isDragging) {
            document.addEventListener('mousemove', handleMouseMove);
            document.addEventListener('mouseup', handleMouseUp);
        }
        return () => {
            document.removeEventListener('mousemove', handleMouseMove);
            document.removeEventListener('mouseup', handleMouseUp);
        };
    }, [isDragging, handleMouseMove, handleMouseUp]);

    const handleContextMenu = useCallback((e) => {
        e.preventDefault();
        e.stopPropagation();
        const viewportWidth = window.innerWidth;
        const viewportHeight = window.innerHeight;
        const menuWidth = 120;
        const menuHeight = 100;
        const adjustedX = Math.min(e.clientX, viewportWidth - menuWidth);
        const adjustedY = Math.min(e.clientY, viewportHeight - menuHeight);
        onContextMenu(id, { x: adjustedX, y: adjustedY });
    }, [onContextMenu, id]);

    const renderContent = () => {
        switch (type) {
            case 'heading':
                return (
                    <h2 style={{
                        fontSize: componentData.fontSize || '1.5rem',
                        color: componentData.color || '#1f2937',
                        margin: 0,
                        userSelect: 'none',
                        pointerEvents: 'none',
                    }}>
                        {componentData.content || 'Tiêu đề'}
                    </h2>
                );
            case 'paragraph':
                return (
                    <p style={{
                        fontSize: componentData.fontSize || '1rem',
                        color: componentData.color || '#1f2937',
                        margin: 0,
                        userSelect: 'none',
                        pointerEvents: 'none',
                    }}>
                        {componentData.content || 'Đoạn văn'}
                    </p>
                );
            case 'button':
                return (
                    <button style={{
                        padding: '8px 16px',
                        borderRadius: '8px',
                        background: componentData.background || '#2563eb',
                        color: componentData.color || '#fff',
                        border: 'none',
                        cursor: 'pointer',
                        userSelect: 'none',
                        pointerEvents: 'none',
                    }}>
                        {componentData.content || 'Nút'}
                    </button>
                );
            case 'image':
                return (
                    <img
                        src={componentData.src || 'https://via.placeholder.com/150'}
                        alt="Image"
                        style={{
                            maxWidth: '100%',
                            height: 'auto',
                            userSelect: 'none',
                            pointerEvents: 'none',
                        }}
                        draggable={false}
                    />
                );
            case 'form':
                return (
                    <form style={{
                        display: 'flex',
                        flexDirection: 'column',
                        gap: '10px',
                        userSelect: 'none'
                    }}>
                        <input
                            type="text"
                            placeholder="Input"
                            style={{ padding: '8px', borderRadius: '8px', border: '1px solid #ccc' }}
                            readOnly
                            tabIndex={-1}
                        />
                        <button
                            type="button"
                            style={{
                                background: '#2563eb',
                                color: '#fff',
                                padding: '8px 16px',
                                borderRadius: '8px',
                                border: 'none',
                                pointerEvents: 'none'
                            }}
                        >
                            Submit
                        </button>
                    </form>
                );
            default:
                return <div style={{ userSelect: 'none' }}>{componentData.content || 'Unknown'}</div>;
        }
    };

    return (
        <div
            className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
            style={{
                position: 'absolute',
                left: position[viewMode]?.x || 0,
                top: position[viewMode]?.y || 0,
                width: size.width,
                height: size.height,
                zIndex: isDragging ? 1000 : (position.z || 1),
                cursor: isDragging ? 'grabbing' : 'grab',
                opacity: isDragging ? 0.8 : 1,
                border: isSelected ? '2px solid #3b82f6' : '1px solid transparent',
                boxSizing: 'border-box',
                ...styles,
            }}
            onMouseDown={handleMouseDown}
            onContextMenu={handleContextMenu}
        >
            {renderContent()}
        </div>
    );
});

// Main Canvas Component
const Canvas = React.memo(({
                               pageData,
                               selectedIds,
                               onSelectElement,
                               onUpdatePosition,
                               onUpdateSize,
                               onDeleteElement,
                               onAddElement,
                               onGroupElements,
                               viewMode,
                               zoomLevel,
                               gridSize,
                               showGrid,
                           }) => {
    const canvasRef = useRef(null);
    const [guidelines, setGuidelines] = useState([]);
    const [contextMenu, setContextMenu] = useState(null);

    const scale = zoomLevel / 100;

    const canvasBounds = {
        width: pageData?.canvas?.width || 1200,
        height: pageData?.canvas?.height || 1000,
    };

    const snapPoints = React.useMemo(() => {
        if (!pageData?.elements) return [];

        const points = [];
        pageData.elements.forEach(el => {
            const bounds = getElementBounds(el);
            points.push(
                { x: bounds.left, y: bounds.top },
                { x: bounds.right, y: bounds.bottom },
                { x: bounds.centerX, y: bounds.centerY },
                { x: bounds.left, y: bounds.centerY },
                { x: bounds.right, y: bounds.centerY },
                { x: bounds.centerX, y: bounds.top },
                { x: bounds.centerX, y: bounds.bottom }
            );
        });

        points.push(
            { x: 0, y: 0 },
            { x: canvasBounds.width, y: 0 },
            { x: 0, y: canvasBounds.height },
            { x: canvasBounds.width, y: canvasBounds.height },
            { x: canvasBounds.width / 2, y: canvasBounds.height / 2 }
        );

        return points;
    }, [pageData?.elements, canvasBounds.width, canvasBounds.height]);

    const [{ isOver }, drop] = useDrop(() => ({
        accept: [ItemTypes.ELEMENT],
        hover: (item, monitor) => {
            const clientOffset = monitor.getClientOffset();
            if (!clientOffset || !canvasRef.current) return;

            const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef, zoomLevel, canvasBounds);
            const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints, scale, true);

            const guidelineThreshold = 2 / scale;

            const newGuidelines = [];
            snapPoints.forEach(point => {
                if (Math.abs(snapped.x - point.x) < guidelineThreshold) {
                    newGuidelines.push({ vertical: true, position: point.x });
                }
                if (Math.abs(snapped.y - point.y) < guidelineThreshold) {
                    newGuidelines.push({ vertical: false, position: point.y });
                }
            });

            setGuidelines(newGuidelines);
        },
        drop: (item, monitor) => {
            const clientOffset = monitor.getClientOffset();
            if (!clientOffset || !canvasRef.current || !item.json) {
                toast.error('Không thể thêm phần tử!');
                return;
            }

            const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef, zoomLevel, canvasBounds);
            const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints, scale, true);

            const newElement = {
                id: `${item.id}-${Date.now()}`,
                type: item.json.type,
                componentData: item.json.componentData || {},
                position: {
                    [viewMode]: snapped,
                    desktop: snapped,
                    tablet: snapped,
                    mobile: snapped,
                },
                size: item.json.size || { width: 200, height: 50 },
                styles: item.json.styles || {},
            };

            onAddElement(newElement);
            setGuidelines([]);
            toast.success('Đã thêm phần tử!');
        },
        collect: (monitor) => ({
            isOver: !!monitor.isOver(),
        }),
    }), [canvasRef, zoomLevel, gridSize, showGrid, snapPoints, viewMode, onAddElement, scale, canvasBounds]);

    const handleCanvasClick = useCallback((e) => {
        if (e.target === canvasRef.current) {
            onSelectElement([]);
            setContextMenu(null);
        }
    }, [onSelectElement]);

    const handleContextMenu = useCallback((id, position) => {
        const viewportWidth = window.innerWidth;
        const viewportHeight = window.innerHeight;
        const menuWidth = 120;
        const menuHeight = 100;
        const adjustedX = Math.min(position.x, viewportWidth - menuWidth);
        const adjustedY = Math.min(position.y, viewportHeight - menuHeight);
        setContextMenu({ id, position: { x: adjustedX, y: adjustedY } });
    }, []);

    const handleKeyDown = useCallback((e) => {
        if (!selectedIds.length) return;

        const nudgeDistance = e.shiftKey ? 10 : 1;

        switch (e.key) {
            case 'ArrowUp':
                e.preventDefault();
                selectedIds.forEach(id => {
                    const element = pageData.elements.find(el => el.id === id);
                    if (element) {
                        const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                        onUpdatePosition(id, { [viewMode]: { x: currentPos.x, y: Math.max(0, currentPos.y - nudgeDistance) } }, true);
                    }
                });
                break;
            case 'ArrowDown':
                e.preventDefault();
                selectedIds.forEach(id => {
                    const element = pageData.elements.find(el => el.id === id);
                    if (element) {
                        const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                        onUpdatePosition(id, { [viewMode]: { x: currentPos.x, y: Math.min(canvasBounds.height - element.size.height, currentPos.y + nudgeDistance) } }, true);
                    }
                });
                break;
            case 'ArrowLeft':
                e.preventDefault();
                selectedIds.forEach(id => {
                    const element = pageData.elements.find(el => el.id === id);
                    if (element) {
                        const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                        onUpdatePosition(id, { [viewMode]: { x: Math.max(0, currentPos.x - nudgeDistance), y: currentPos.y } }, true);
                    }
                });
                break;
            case 'ArrowRight':
                e.preventDefault();
                selectedIds.forEach(id => {
                    const element = pageData.elements.find(el => el.id === id);
                    if (element) {
                        const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                        onUpdatePosition(id, { [viewMode]: { x: Math.min(canvasBounds.width - element.size.width, currentPos.x + nudgeDistance), y: currentPos.y } }, true);
                    }
                });
                break;
            case 'Delete':
            case 'Backspace':
                e.preventDefault();
                selectedIds.forEach(id => onDeleteElement(id));
                break;
            case 'Escape':
                e.preventDefault();
                onSelectElement([]);
                setContextMenu(null);
                break;
        }
    }, [selectedIds, onUpdatePosition, pageData.elements, viewMode, canvasBounds, onDeleteElement, onSelectElement]);

    useHotkeys('ctrl+a', (e) => {
        e.preventDefault();
        if (pageData?.elements) {
            onSelectElement(pageData.elements.map(el => el.id));
        }
    }, [pageData?.elements, onSelectElement]);

    useHotkeys('ctrl+g', (e) => {
        e.preventDefault();
        if (selectedIds.length > 1) {
            onGroupElements(selectedIds);
        }
    }, [selectedIds, onGroupElements]);

    return (
        <div
            className="lpb-canvas-wrapper"
            style={{ outline: 'none', position: 'relative', overflow: 'auto' }}
        >
            <div
                ref={(node) => {
                    canvasRef.current = node;
                    drop(node);
                }}
                className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                style={{
                    position: 'relative',
                    width: canvasBounds.width,
                    height: canvasBounds.height,
                    minHeight: '100vh',
                    background: pageData?.canvas?.background || '#ffffff',
                    transform: `scale(${scale})`,
                    transformOrigin: 'top left',
                    cursor: isOver ? 'copy' : 'default',
                    boxShadow: '0 0 0 1px #e5e7eb',
                }}
                onClick={handleCanvasClick}
                onKeyDown={handleKeyDown}
                tabIndex={0}
            >
                <Rulers
                    canvasWidth={canvasBounds.width}
                    canvasHeight={canvasBounds.height}
                    zoomLevel={zoomLevel}
                />

                <Guidelines
                    guidelines={guidelines}
                    zoomLevel={zoomLevel}
                />

                {showGrid && (
                    <div
                        className="lpb-canvas-grid"
                        style={{
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            backgroundImage: `
                                linear-gradient(to right, #e5e7eb 1px, transparent 1px),
                                linear-gradient(to bottom, #e5e7eb 1px, transparent 1px)
                            `,
                            backgroundSize: `${gridSize}px ${gridSize}px`,
                            pointerEvents: 'none',
                            opacity: 0.5,
                        }}
                    />
                )}

                {pageData?.elements?.map((element) => (
                    <DraggableElement
                        key={element.id}
                        element={element}
                        isSelected={selectedIds.includes(element.id)}
                        onSelectElement={onSelectElement}
                        onUpdatePosition={onUpdatePosition}
                        viewMode={viewMode}
                        zoomLevel={zoomLevel}
                        gridSize={showGrid ? gridSize : Infinity}
                        onContextMenu={handleContextMenu}
                        canvasRef={canvasRef}
                        snapPoints={snapPoints}
                        canvasBounds={canvasBounds}
                    />
                ))}

                <SelectionOverlay
                    selectedElements={pageData?.elements?.filter(el => selectedIds.includes(el.id)) || []}
                    onResize={onUpdateSize}
                    zoomLevel={zoomLevel}
                />

                {contextMenu && (
                    <div
                        className="lpb-context-menu"
                        style={{
                            position: 'fixed',
                            left: contextMenu.position.x,
                            top: contextMenu.position.y,
                            background: 'white',
                            border: '1px solid #d1d5db',
                            borderRadius: '6px',
                            boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
                            zIndex: 10000,
                            minWidth: '120px',
                        }}
                        onClick={() => setContextMenu(null)}
                    >
                        <div
                            className="lpb-context-menu-item"
                            style={{
                                padding: '8px 12px',
                                cursor: 'pointer',
                                borderBottom: '1px solid #f3f4f6',
                                fontSize: '14px',
                            }}
                            onClick={() => {
                                onDeleteElement(contextMenu.id);
                                setContextMenu(null);
                            }}
                        >
                            <i className="fas fa-trash" style={{ marginRight: '8px' }}></i>
                            Delete
                        </div>
                        <div
                            className="lpb-context-menu-item"
                            style={{
                                padding: '8px 12px',
                                cursor: 'pointer',
                                fontSize: '14px',
                            }}
                            onClick={() => {
                                const element = pageData.elements.find(el => el.id === contextMenu.id);
                                if (element) {
                                    const duplicated = {
                                        ...element,
                                        id: `${element.id}-copy-${Date.now()}`,
                                        position: {
                                            ...element.position,
                                            [viewMode]: {
                                                x: (element.position[viewMode]?.x || 0) + 20,
                                                y: (element.position[viewMode]?.y || 0) + 20,
                                            }
                                        }
                                    };
                                    onAddElement(duplicated);
                                }
                                setContextMenu(null);
                            }}
                        >
                            <i className="fas fa-copy" style={{ marginRight: '8px' }}></i>
                            Duplicate
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
});

export default Canvas;
