import React, { useCallback, useMemo } from 'react';
import { motion } from 'framer-motion';
import '../../styles/PropertiesPanel.css';

// Danh sách màu sắc từ CSS_VARIABLES trong CreateLanding.js
const COLOR_PRESETS = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
};

const PropertiesPanel = ({ selectedElement, onUpdateElement, onUpdateCanvas, isCollapsed, onToggle, pageData }) => {
    // Validation functions
    const validateStyle = useCallback((property, value) => {
        if (['padding', 'margin', 'borderRadius'].includes(property)) {
            if (!value.match(/^(\d+px\s*){1,4}$|^(\d+px\s*){1,3}\d+px$|^0$|^auto$/)) {
                alert(`Giá trị ${property} không hợp lệ. Vui lòng nhập dạng như "10px" hoặc "10px 20px".`);
                return false;
            }
        }
        if (property === 'border' && !value.match(/^(\d+px\s+(solid|dashed|dotted)\s+#?[0-9a-fA-F]{3,8})$|^none$/)) {
            alert('Viền không hợp lệ. Vui lòng nhập dạng như "1px solid #000" hoặc "none".');
            return false;
        }
        if (property === 'boxShadow' && !value.match(/^(\d+px\s+){3}rgba?\(\d+,\s*\d+,\s*\d+,\s*[\d.]+\)$|^none$/)) {
            alert('Box shadow không hợp lệ. Vui lòng nhập dạng như "0 4px 6px rgba(0,0,0,0.1)" hoặc "none".');
            return false;
        }
        if (['width', 'height'].includes(property) && !value.match(/^\d+px$|^\d+%$|^auto$/)) {
            alert(`Giá trị ${property} không hợp lệ. Vui lòng nhập dạng như "100px", "50%", hoặc "auto".`);
            return false;
        }
        return true;
    }, []);

    const validatePosition = useCallback((value) => {
        if (!value.match(/^\d+px$/)) {
            alert('Vị trí không hợp lệ. Vui lòng nhập dạng như "100px".');
            return false;
        }
        return true;
    }, []);

    const handleStyleChange = useCallback(
        (property, value) => {
            if (!validateStyle(property, value)) return;
            const updated = {
                ...selectedElement,
                json: {
                    ...selectedElement.json,
                    styles: {
                        ...selectedElement.json.styles,
                        [property]: value,
                    },
                },
            };
            onUpdateElement(updated);
        },
        [selectedElement, onUpdateElement]
    );

    const handleComponentDataChange = useCallback(
        (key, value) => {
            const updated = {
                ...selectedElement,
                json: {
                    ...selectedElement.json,
                    componentData: {
                        ...selectedElement.json.componentData,
                        [key]: value,
                    },
                },
            };
            onUpdateElement(updated);
        },
        [selectedElement, onUpdateElement]
    );

    const handleChildChange = useCallback(
        (childId, property, value) => {
            if (selectedElement.isChild) {
                const updated = {
                    ...selectedElement,
                    json: {
                        ...selectedElement.json,
                        [property]: value,
                    },
                };
                onUpdateElement(updated);
                return;
            }

            if (!selectedElement.json.children || !Array.isArray(selectedElement.json.children)) {
                console.warn('No children array found for selected element:', selectedElement);
                return;
            }

            const updated = {
                ...selectedElement,
                json: {
                    ...selectedElement.json,
                    children: selectedElement.json.children.map((child) =>
                        child.id === childId ? { ...child, [property]: value } : child
                    ),
                },
            };
            onUpdateElement(updated);
        },
        [selectedElement, onUpdateElement]
    );

    const handlePositionChange = useCallback(
        (axis, value) => {
            if (!validatePosition(value)) return;
            const updated = {
                ...selectedElement,
                json: {
                    ...selectedElement.json,
                    position: {
                        ...selectedElement.json.position,
                        desktop: {
                            ...selectedElement.json.position?.desktop,
                            [axis]: parseFloat(value) || 0,
                        },
                        tablet: {
                            ...selectedElement.json.position?.tablet,
                            [axis]: parseFloat(value) || 0,
                        },
                        mobile: {
                            ...selectedElement.json.position?.mobile,
                            [axis]: parseFloat(value) || 0,
                        },
                    },
                },
            };
            onUpdateElement(updated);
        },
        [selectedElement, onUpdateElement]
    );

    const handleCanvasChange = useCallback(
        (property, value) => {
            if (property === 'width' && !value.match(/^\d+px$|^\d+%$/)) {
                alert('Chiều rộng canvas phải có dạng như "1200px" hoặc "100%".');
                return;
            }
            onUpdateCanvas({ [property]: value });
        },
        [onUpdateCanvas]
    );

    const handleDataSourceChange = useCallback(
        (key, value) => {
            const updated = {
                ...selectedElement,
                json: {
                    ...selectedElement.json,
                    componentData: {
                        ...selectedElement.json.componentData,
                        dataSource: {
                            ...selectedElement.json.componentData?.dataSource,
                            [key]: value,
                        },
                    },
                },
            };
            onUpdateElement(updated);
        },
        [selectedElement, onUpdateElement]
    );

    const handleEventChange = useCallback(
        (eventType, eventConfig) => {
            const updated = {
                ...selectedElement,
                json: {
                    ...selectedElement.json,
                    componentData: {
                        ...selectedElement.json.componentData,
                        events: {
                            ...selectedElement.json.componentData?.events,
                            [eventType]: eventConfig,
                        },
                    },
                },
            };
            onUpdateElement(updated);
        },
        [selectedElement, onUpdateElement]
    );

    const isSection = selectedElement.json.type === 'section' || selectedElement.json.type === 'container' || selectedElement.json.type === 'group';
    const isChild = selectedElement.isChild;
    const elementType = selectedElement.json.type;

    // Component-specific properties
    const componentConfig = useMemo(() => {
        switch (elementType) {
            case 'section':
            case 'container':
            case 'group':
                return {
                    showPosition: true,
                    showSize: true,
                    showLayout: true,
                    showAppearance: true,
                    showContent: false,
                    showTypography: false,
                    showImage: false,
                    showForm: false,
                    showDataSource: true,
                    showEvents: ['onClick'],
                };
            case 'heading':
            case 'paragraph':
                return {
                    showPosition: isChild,
                    showSize: !isChild,
                    showLayout: false,
                    showAppearance: true,
                    showContent: true,
                    showTypography: true,
                    showImage: false,
                    showForm: false,
                    showDataSource: true,
                    showEvents: ['onClick', 'onHover'],
                };
            case 'button':
                return {
                    showPosition: isChild,
                    showSize: !isChild,
                    showLayout: false,
                    showAppearance: true,
                    showContent: true,
                    showTypography: true,
                    showImage: false,
                    showForm: false,
                    showDataSource: true,
                    showEvents: ['onClick', 'onHover'],
                    showHoverStyles: true,
                };
            case 'image':
                return {
                    showPosition: isChild,
                    showSize: !isChild,
                    showLayout: false,
                    showAppearance: true,
                    showContent: false,
                    showTypography: false,
                    showImage: true,
                    showForm: false,
                    showDataSource: true,
                    showEvents: ['onClick'],
                };
            case 'form':
                return {
                    showPosition: !isChild,
                    showSize: !isChild,
                    showLayout: true,
                    showAppearance: true,
                    showContent: true,
                    showTypography: false,
                    showImage: false,
                    showForm: true,
                    showDataSource: true,
                    showEvents: ['onSubmit'],
                };
            case 'popup':
            case 'modal':
                return {
                    showPosition: !isChild,
                    showSize: !isChild,
                    showLayout: true,
                    showAppearance: true,
                    showContent: true,
                    showTypography: false,
                    showImage: false,
                    showForm: false,
                    showDataSource: true,
                    showEvents: ['onClick'],
                };
            default:
                return {
                    showPosition: isChild,
                    showSize: !isChild,
                    showLayout: isSection,
                    showAppearance: true,
                    showContent: false,
                    showTypography: false,
                    showImage: false,
                    showForm: false,
                    showDataSource: false,
                    showEvents: [],
                };
        }
    }, [elementType, isChild, isSection]);

    return (
        <motion.div
            className={`properties-panel ${isCollapsed ? 'properties-panel-collapsed' : ''}`}
            initial={{ x: isCollapsed ? 300 : 0 }}
            animate={{ x: isCollapsed ? 300 : 0 }}
            transition={{ duration: 0.3 }}
        >
            {isCollapsed ? (
                <motion.button
                    onClick={onToggle}
                    className="toggle-button"
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    title="Mở bảng thuộc tính"
                >
                    <i className="fas fa-chevron-left"></i>
                </motion.button>
            ) : !selectedElement || !selectedElement.json ? (
                <div className="properties-panel">
                    <div className="panel-header">
                        <h3 className="panel-title">Thuộc tính</h3>
                        <motion.button
                            onClick={onToggle}
                            className="toggle-button"
                            whileHover={{ scale: 1.1 }}
                            whileTap={{ scale: 0.9 }}
                            title="Thu gọn bảng thuộc tính"
                        >
                            <i className="fas fa-chevron-right"></i>
                        </motion.button>
                    </div>
                    <motion.div
                        className="panel-empty"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ duration: 0.2 }}
                    >
                        <i className="fas fa-mouse-pointer empty-icon"></i>
                        <p className="empty-text">Chọn một element để chỉnh sửa thuộc tính</p>
                    </motion.div>
                </div>
            ) : (
                <div className="properties-panel">
                    <div className="panel-header">
                        <h3 className="panel-title">
                            Thuộc tính{' '}
                            {isSection
                                ? 'Section/Container/Group'
                                : isChild
                                ? 'Child Element'
                                : elementType.charAt(0).toUpperCase() + elementType.slice(1)}
                        </h3>
                        <motion.button
                            onClick={onToggle}
                            className="toggle-button"
                            whileHover={{ scale: 1.1 }}
                            whileTap={{ scale: 0.9 }}
                            title="Thu gọn bảng thuộc tính"
                        >
                            <i className="fas fa-chevron-right"></i>
                        </motion.button>
                    </div>
                    <motion.div
                        className="panel-content"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ duration: 0.2 }}
                    >
                        {/* Canvas Settings */}
                        {!isChild && (
                            <div className="panel-section">
                                <h4 className="section-title">Cài đặt Canvas</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Màu nền Canvas</label>
                                        <input
                                            type="color"
                                            value={pageData.canvas?.background || '#ffffff'}
                                            onChange={(e) => handleCanvasChange('background', e.target.value)}
                                            className="input-color"
                                            title="Chọn màu nền cho canvas"
                                        />
                                        <select
                                            value={pageData.canvas?.background || '#ffffff'}
                                            onChange={(e) => handleCanvasChange('background', e.target.value)}
                                            className="input-select"
                                            style={{ marginTop: '8px' }}
                                        >
                                            <option value="">Chọn màu từ preset</option>
                                            {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                                <option key={key} value={value}>
                                                    {key}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                    <div>
                                        <label className="input-label">Font mặc định</label>
                                        <select
                                            value={pageData.canvas?.fontFamily || 'Inter, Arial, sans-serif'}
                                            onChange={(e) => handleCanvasChange('fontFamily', e.target.value)}
                                            className="input-select"
                                            title="Chọn font mặc định cho canvas"
                                        >
                                            <option value="Inter, Arial, sans-serif">Inter</option>
                                            <option value="'Roboto', sans-serif">Roboto</option>
                                            <option value="'Open Sans', sans-serif">Open Sans</option>
                                            <option value="'Lato', sans-serif">Lato</option>
                                        </select>
                                    </div>
                                    <div>
                                        <label className="input-label">Chiều rộng Canvas</label>
                                        <input
                                            type="text"
                                            value={pageData.canvas?.width || '1200px'}
                                            onChange={(e) => handleCanvasChange('width', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 1200px"
                                            title="Nhập chiều rộng canvas (px hoặc %)"
                                        />
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Data Source */}
                        {componentConfig.showDataSource && (
                            <div className="panel-section">
                                <h4 className="section-title">Nguồn dữ liệu</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Loại dữ liệu</label>
                                        <select
                                            value={selectedElement.json.componentData?.dataSource?.type || 'static'}
                                            onChange={(e) => handleDataSourceChange('type', e.target.value)}
                                            className="input-select"
                                            title="Chọn loại nguồn dữ liệu"
                                        >
                                            <option value="static">Tĩnh</option>
                                            <option value="api">API</option>
                                            <option value="state">State</option>
                                        </select>
                                    </div>
                                    {selectedElement.json.componentData?.dataSource?.type === 'api' && (
                                        <>
                                            <div>
                                                <label className="input-label">API URL</label>
                                                <input
                                                    type="text"
                                                    value={selectedElement.json.componentData?.dataSource?.url || ''}
                                                    onChange={(e) => handleDataSourceChange('url', e.target.value)}
                                                    className="input-field"
                                                    placeholder="e.g., https://api.example.com/data"
                                                    title="URL của API"
                                                />
                                            </div>
                                            <div>
                                                <label className="input-label">Key dữ liệu</label>
                                                <input
                                                    type="text"
                                                    value={selectedElement.json.componentData?.dataSource?.key || ''}
                                                    onChange={(e) => handleDataSourceChange('key', e.target.value)}
                                                    className="input-field"
                                                    placeholder="e.g., title"
                                                    title="Key trong response API"
                                                />
                                            </div>
                                        </>
                                    )}
                                    {selectedElement.json.componentData?.dataSource?.type === 'state' && (
                                        <div>
                                            <label className="input-label">State Key</label>
                                            <input
                                                type="text"
                                                value={selectedElement.json.componentData?.dataSource?.stateKey || ''}
                                                onChange={(e) => handleDataSourceChange('stateKey', e.target.value)}
                                                className="input-field"
                                                placeholder="e.g., contentKey"
                                                title="Key trong state"
                                            />
                                        </div>
                                    )}
                                </div>
                            </div>
                        )}

                        {/* Events */}
                        {componentConfig.showEvents.length > 0 && (
                            <div className="panel-section">
                                <h4 className="section-title">Sự kiện</h4>
                                {componentConfig.showEvents.map((eventType) => (
                                    <div key={eventType} className="input-group">
                                        <div>
                                            <label className="input-label">{eventType}</label>
                                            <select
                                                value={selectedElement.json.componentData?.events?.[eventType]?.type || ''}
                                                onChange={(e) =>
                                                    handleEventChange(eventType, { type: e.target.value, url: '', apiUrl: '', popupId: '' })
                                                }
                                                className="input-select"
                                                title={`Chọn hành động cho ${eventType}`}
                                            >
                                                <option value="">Không</option>
                                                <option value="navigate">Navigate</option>
                                                {elementType === 'form' && eventType === 'onSubmit' && (
                                                    <option value="submitForm">Submit Form</option>
                                                )}
                                                <option value="showPopup">Show Popup</option>
                                            </select>
                                        </div>
                                        {selectedElement.json.componentData?.events?.[eventType]?.type === 'navigate' && (
                                            <div>
                                                <label className="input-label">URL</label>
                                                <input
                                                    type="text"
                                                    value={selectedElement.json.componentData?.events?.[eventType]?.url || ''}
                                                    onChange={(e) =>
                                                        handleEventChange(eventType, {
                                                            ...selectedElement.json.componentData?.events?.[eventType],
                                                            url: e.target.value,
                                                        })
                                                    }
                                                    className="input-field"
                                                    placeholder="e.g., /page"
                                                    title="URL để điều hướng"
                                                />
                                            </div>
                                        )}
                                        {selectedElement.json.componentData?.events?.[eventType]?.type === 'submitForm' && (
                                            <div>
                                                <label className="input-label">API URL</label>
                                                <input
                                                    type="text"
                                                    value={selectedElement.json.componentData?.events?.[eventType]?.apiUrl || ''}
                                                    onChange={(e) =>
                                                        handleEventChange(eventType, {
                                                            ...selectedElement.json.componentData?.events?.[eventType],
                                                            apiUrl: e.target.value,
                                                        })
                                                    }
                                                    className="input-field"
                                                    placeholder="e.g., /api/submit"
                                                    title="URL để gửi form"
                                                />
                                            </div>
                                        )}
                                        {selectedElement.json.componentData?.events?.[eventType]?.type === 'showPopup' && (
                                            <div>
                                                <label className="input-label">Popup ID</label>
                                                <input
                                                    type="text"
                                                    value={selectedElement.json.componentData?.events?.[eventType]?.popupId || ''}
                                                    onChange={(e) =>
                                                        handleEventChange(eventType, {
                                                            ...selectedElement.json.componentData?.events?.[eventType],
                                                            popupId: e.target.value,
                                                        })
                                                    }
                                                    className="input-field"
                                                    placeholder="e.g., popup-1"
                                                    title="ID của popup"
                                                />
                                            </div>
                                        )}
                                    </div>
                                ))}
                            </div>
                        )}

                        {/* Position */}
                        {componentConfig.showPosition && (
                            <div className="panel-section">
                                <h4 className="section-title">Vị trí</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Left (X)</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.position?.desktop?.x || '0'}
                                            onChange={(e) => handlePositionChange('x', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 80px"
                                            title="Vị trí ngang của phần tử"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Top (Y)</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.position?.desktop?.y || '0'}
                                            onChange={(e) => handlePositionChange('y', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 55px"
                                            title="Vị trí dọc của phần tử"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Position Type</label>
                                        <select
                                            value={selectedElement.json.styles?.position || 'relative'}
                                            onChange={(e) => handleStyleChange('position', e.target.value)}
                                            className="input-select"
                                            title="Chọn kiểu định vị"
                                        >
                                            <option value="relative">Relative</option>
                                            <option value="absolute">Absolute</option>
                                            <option value="fixed">Fixed</option>
                                        </select>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Size */}
                        {componentConfig.showSize && (
                            <div className="panel-section">
                                <h4 className="section-title">Kích thước</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Chiều rộng</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.size?.width || 'auto'}
                                            onChange={(e) => handleStyleChange('width', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 1200px, 100%"
                                            title="Nhập chiều rộng của phần tử"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Chiều cao</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.size?.height || 'auto'}
                                            onChange={(e) => handleStyleChange('height', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., auto, 400px"
                                            title="Nhập chiều cao của phần tử"
                                        />
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Content */}
                        {componentConfig.showContent && (
                            <div className="panel-section">
                                <h4 className="section-title">Nội dung</h4>
                                <textarea
                                    value={
                                        isChild
                                            ? selectedElement.json.content
                                            : selectedElement.json.componentData?.content ||
                                              selectedElement.json.componentData?.title ||
                                              ''
                                    }
                                    onChange={(e) =>
                                        isChild
                                            ? handleChildChange(selectedElement.json.id, 'content', e.target.value)
                                            : handleComponentDataChange(
                                                  elementType === 'form' || elementType === 'popup' || elementType === 'modal'
                                                      ? 'title'
                                                      : 'content',
                                                  e.target.value
                                              )
                                    }
                                    className="input-textarea"
                                    rows={3}
                                    placeholder="Nhập nội dung cho phần tử"
                                    title="Nội dung hiển thị của phần tử"
                                />
                            </div>
                        )}

                        {/* Image */}
                        {componentConfig.showImage && (
                            <div className="panel-section">
                                <h4 className="section-title">Hình ảnh</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Nguồn (URL)</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.componentData?.src || ''}
                                            onChange={(e) => handleComponentDataChange('src', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., https://example.com/image.jpg"
                                            title="URL của hình ảnh"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Văn bản thay thế (Alt)</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.componentData?.alt || ''}
                                            onChange={(e) => handleComponentDataChange('alt', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., Hình ảnh mẫu"
                                            title="Văn bản mô tả cho hình ảnh"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Object Fit</label>
                                        <select
                                            value={selectedElement.json.styles?.objectFit || 'cover'}
                                            onChange={(e) => handleStyleChange('objectFit', e.target.value)}
                                            className="input-select"
                                            title="Chọn kiểu hiển thị hình ảnh"
                                        >
                                            <option value="cover">Cover</option>
                                            <option value="contain">Contain</option>
                                            <option value="fill">Fill</option>
                                            <option value="scale-down">Scale Down</option>
                                            <option value="none">None</option>
                                        </select>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Form */}
                        {componentConfig.showForm && (
                            <div className="panel-section">
                                <h4 className="section-title">Cấu hình Form</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Tiêu đề Form</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.componentData?.title || ''}
                                            onChange={(e) => handleComponentDataChange('title', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., Contact Us"
                                            title="Tiêu đề của form"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Nút Submit</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.componentData?.buttonText || 'Submit'}
                                            onChange={(e) => handleComponentDataChange('buttonText', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., Submit"
                                            title="Nội dung nút submit"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Fields (JSON)</label>
                                        <textarea
                                            value={JSON.stringify(selectedElement.json.componentData?.fields || [], null, 2)}
                                            onChange={(e) => {
                                                try {
                                                    const fields = JSON.parse(e.target.value);
                                                    handleComponentDataChange('fields', fields);
                                                } catch {
                                                    alert('JSON không hợp lệ. Vui lòng kiểm tra lại.');
                                                }
                                            }}
                                            className="input-textarea"
                                            rows={5}
                                            placeholder='e.g., [{"type":"text","placeholder":"Name"}]'
                                            title="Cấu hình các field của form (JSON)"
                                        />
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Typography */}
                        {componentConfig.showTypography && (
                            <div className="panel-section">
                                <h4 className="section-title">Typography</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Font Size</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.styles?.fontSize || '16px'}
                                            onChange={(e) => handleStyleChange('fontSize', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 16px"
                                            title="Kích thước chữ"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Font Weight</label>
                                        <select
                                            value={selectedElement.json.styles?.fontWeight || 'normal'}
                                            onChange={(e) => handleStyleChange('fontWeight', e.target.value)}
                                            className="input-select"
                                            title="Độ đậm của chữ"
                                        >
                                            <option value="normal">Normal</option>
                                            <option value="bold">Bold</option>
                                            <option value="600">Semi Bold</option>
                                            <option value="300">Light</option>
                                        </select>
                                    </div>
                                    <div>
                                        <label className="input-label">Font Family</label>
                                        <select
                                            value={selectedElement.json.styles?.fontFamily || 'Inter, Arial, sans-serif'}
                                            onChange={(e) => handleStyleChange('fontFamily', e.target.value)}
                                            className="input-select"
                                            title="Chọn font chữ"
                                        >
                                            <option value="Inter, Arial, sans-serif">Inter</option>
                                            <option value="'Roboto', sans-serif">Roboto</option>
                                            <option value="'Open Sans', sans-serif">Open Sans</option>
                                            <option value="'Lato', sans-serif">Lato</option>
                                        </select>
                                    </div>
                                    <div>
                                        <label className="input-label">Text Align</label>
                                        <select
                                            value={selectedElement.json.styles?.textAlign || 'left'}
                                            onChange={(e) => handleStyleChange('textAlign', e.target.value)}
                                            className="input-select"
                                            title="Căn chỉnh văn bản"
                                        >
                                            <option value="left">Left</option>
                                            <option value="center">Center</option>
                                            <option value="right">Right</option>
                                            <option value="justify">Justify</option>
                                        </select>
                                    </div>
                                    <div>
                                        <label className="input-label">Line Height</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.styles?.lineHeight || '1.5'}
                                            onChange={(e) => handleStyleChange('lineHeight', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 1.5"
                                            title="Chiều cao dòng"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Letter Spacing</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.styles?.letterSpacing || '0'}
                                            onChange={(e) => handleStyleChange('letterSpacing', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 1px"
                                            title="Khoảng cách giữa các chữ cái"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Text Transform</label>
                                        <select
                                            value={selectedElement.json.styles?.textTransform || 'none'}
                                            onChange={(e) => handleStyleChange('textTransform', e.target.value)}
                                            className="input-select"
                                            title="Chuyển đổi kiểu chữ"
                                        >
                                            <option value="none">None</option>
                                            <option value="uppercase">Uppercase</option>
                                            <option value="lowercase">Lowercase</option>
                                            <option value="capitalize">Capitalize</option>
                                        </select>
                                    </div>
                                    <div>
                                        <label className="input-label">Text Color</label>
                                        <input
                                            type="color"
                                            value={selectedElement.json.styles?.color || '#000000'}
                                            onChange={(e) => handleStyleChange('color', e.target.value)}
                                            className="input-color"
                                            title="Chọn màu chữ"
                                        />
                                        <select
                                            value={selectedElement.json.styles?.color || '#000000'}
                                            onChange={(e) => handleStyleChange('color', e.target.value)}
                                            className="input-select"
                                            style={{ marginTop: '8px' }}
                                            title="Chọn màu chữ từ preset"
                                        >
                                            <option value="">Chọn màu từ preset</option>
                                            {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                                <option key={key} value={value}>
                                                    {key}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Hover Styles for Button */}
                        {componentConfig.showHoverStyles && (
                            <div className="panel-section">
                                <h4 className="section-title">Hover Styles</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Hover Background</label>
                                        <input
                                            type="color"
                                            value={selectedElement.json.styles?.hoverBackground || '#4f46e5'}
                                            onChange={(e) => handleStyleChange('hoverBackground', e.target.value)}
                                            className="input-color"
                                            title="Màu nền khi hover"
                                        />
                                        <select
                                            value={selectedElement.json.styles?.hoverBackground || '#4f46e5'}
                                            onChange={(e) => handleStyleChange('hoverBackground', e.target.value)}
                                            className="input-select"
                                            style={{ marginTop: '8px' }}
                                            title="Chọn màu nền hover từ preset"
                                        >
                                            <option value="">Chọn màu từ preset</option>
                                            {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                                <option key={key} value={value}>
                                                    {key}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                    <div>
                                        <label className="input-label">Hover Color</label>
                                        <input
                                            type="color"
                                            value={selectedElement.json.styles?.hoverColor || '#ffffff'}
                                            onChange={(e) => handleStyleChange('hoverColor', e.target.value)}
                                            className="input-color"
                                            title="Màu chữ khi hover"
                                        />
                                        <select
                                            value={selectedElement.json.styles?.hoverColor || '#ffffff'}
                                            onChange={(e) => handleStyleChange('hoverColor', e.target.value)}
                                            className="input-select"
                                            style={{ marginTop: '8px' }}
                                            title="Chọn màu chữ hover từ preset"
                                        >
                                            <option value="">Chọn màu từ preset</option>
                                            {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                                <option key={key} value={value}>
                                                    {key}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Layout */}
                        {componentConfig.showLayout && (
                            <div className="panel-section">
                                <h4 className="section-title">Layout</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Display</label>
                                        <select
                                            value={selectedElement.json.styles?.display || 'block'}
                                            onChange={(e) => handleStyleChange('display', e.target.value)}
                                            className="input-select"
                                            title="Kiểu hiển thị của phần tử"
                                        >
                                            <option value="block">Block</option>
                                            <option value="flex">Flex</option>
                                            <option value="grid">Grid</option>
                                            <option value="inline">Inline</option>
                                            <option value="inline-block">Inline-Block</option>
                                        </select>
                                    </div>
                                    {(selectedElement.json.styles?.display === 'flex' || selectedElement.json.styles?.display === 'grid') && (
                                        <>
                                            <div>
                                                <label className="input-label">Flex Direction</label>
                                                <select
                                                    value={selectedElement.json.styles?.flexDirection || 'row'}
                                                    onChange={(e) => handleStyleChange('flexDirection', e.target.value)}
                                                    className="input-select"
                                                    title="Hướng sắp xếp của flex"
                                                >
                                                    <option value="row">Row</option>
                                                    <option value="column">Column</option>
                                                    <option value="row-reverse">Row Reverse</option>
                                                    <option value="column-reverse">Column Reverse</option>
                                                </select>
                                            </div>
                                            <div>
                                                <label className="input-label">Justify Content</label>
                                                <select
                                                    value={selectedElement.json.styles?.justifyContent || 'flex-start'}
                                                    onChange={(e) => handleStyleChange('justifyContent', e.target.value)}
                                                    className="input-select"
                                                    title="Căn chỉnh nội dung theo trục chính"
                                                >
                                                    <option value="flex-start">Flex Start</option>
                                                    <option value="flex-end">Flex End</option>
                                                    <option value="center">Center</option>
                                                    <option value="space-between">Space Between</option>
                                                    <option value="space-around">Space Around</option>
                                                    <option value="space-evenly">Space Evenly</option>
                                                </select>
                                            </div>
                                            <div>
                                                <label className="input-label">Align Items</label>
                                                <select
                                                    value={selectedElement.json.styles?.alignItems || 'stretch'}
                                                    onChange={(e) => handleStyleChange('alignItems', e.target.value)}
                                                    className="input-select"
                                                    title="Căn chỉnh nội dung theo trục phụ"
                                                >
                                                    <option value="stretch">Stretch</option>
                                                    <option value="flex-start">Flex Start</option>
                                                    <option value="flex-end">Flex End</option>
                                                    <option value="center">Center</option>
                                                    <option value="baseline">Baseline</option>
                                                </select>
                                            </div>
                                            {selectedElement.json.styles?.display === 'grid' && (
                                                <>
                                                    <div>
                                                        <label className="input-label">Grid Template Columns</label>
                                                        <input
                                                            type="text"
                                                            value={selectedElement.json.styles?.gridTemplateColumns || '1fr'}
                                                            onChange={(e) => handleStyleChange('gridTemplateColumns', e.target.value)}
                                                            className="input-field"
                                                            placeholder="e.g., 1fr 1fr"
                                                            title="Cấu hình cột grid"
                                                        />
                                                    </div>
                                                    <div>
                                                        <label className="input-label">Grid Gap</label>
                                                        <input
                                                            type="text"
                                                            value={selectedElement.json.styles?.gridGap || '10px'}
                                                            onChange={(e) => handleStyleChange('gridGap', e.target.value)}
                                                            className="input-field"
                                                            placeholder="e.g., 10px"
                                                            title="Khoảng cách giữa các ô grid"
                                                        />
                                                    </div>
                                                </>
                                            )}
                                        </>
                                    )}
                                </div>
                            </div>
                        )}

                        {/* Appearance */}
                        {componentConfig.showAppearance && (
                            <div className="panel-section">
                                <h4 className="section-title">Giao diện</h4>
                                <div className="input-group">
                                    <div>
                                        <label className="input-label">Background Color</label>
                                        <input
                                            type="color"
                                            value={selectedElement.json.styles?.backgroundColor || '#ffffff'}
                                            onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                            className="input-color"
                                            title="Chọn màu nền cho phần tử"
                                        />
                                        <select
                                            value={selectedElement.json.styles?.backgroundColor || '#ffffff'}
                                            onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                            className="input-select"
                                            style={{ marginTop: '8px' }}
                                            title="Chọn màu nền từ preset"
                                        >
                                            <option value="">Chọn màu từ preset</option>
                                            {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                                <option key={key} value={value}>
                                                    {key}
                                                </option>
                                            ))}
                                        </select>
                                    </div>
                                    <div>
                                        <label className="input-label">Border</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.styles?.border || 'none'}
                                            onChange={(e) => handleStyleChange('border', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 1px solid #000"
                                            title="Định dạng viền (e.g., 1px solid #000)"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Padding</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.styles?.padding || '0'}
                                            onChange={(e) => handleStyleChange('padding', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 10px"
                                            title="Khoảng cách bên trong (e.g., 10px hoặc 10px 20px)"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Border Radius</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.styles?.borderRadius || '0'}
                                            onChange={(e) => handleStyleChange('borderRadius', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 8px"
                                            title="Bo góc của phần tử"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Opacity</label>
                                        <input
                                            type="number"
                                            step="0.1"
                                            min="0"
                                            max="1"
                                            value={selectedElement.json.styles?.opacity || '1'}
                                            onChange={(e) => handleStyleChange('opacity', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 1"
                                            title="Độ trong suốt (0-1)"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Box Shadow</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.styles?.boxShadow || 'none'}
                                            onChange={(e) => handleStyleChange('boxShadow', e.target.value)}
                                            className="input-field"
                                            placeholder="e.g., 0 4px 6px rgba(0,0,0,0.1)"
                                            title="Định dạng bóng (e.g., 0 4px 6px rgba(0,0,0,0.1))"
                                        />
                                    </div>
                                </div>
                            </div>
                        )}
                    </motion.div>
                </div>
            )}
        </motion.div>
    );
};

export default PropertiesPanel;
