const buttonComponents = [
    {
        id: 'btn-primary',
        name: 'Nút chính',
        icon: 'fas fa-square',
        category: 'buttons',
        json: {
            type: 'button',
            content: 'Nhấn vào đây',
            variant: 'primary',
            styles: {
                backgroundColor: '#007bff',
                color: 'white',
                padding: '12px 30px',
                borderRadius: '6px',
                border: 'none',
                fontSize: '16px',
                fontWeight: '600',
                cursor: 'pointer'
            },
            actions: {
                onClick: 'scrollToSection',
                target: '#contact'
            }
        }
    },
    {
        id: 'btn-secondary',
        name: 'Nút phụ',
        icon: 'far fa-square',
        category: 'buttons',
        json: {
            type: 'button',
            content: 'Tìm hiểu thêm',
            variant: 'secondary',
            styles: {
                backgroundColor: 'transparent',
                color: '#007bff',
                padding: '12px 30px',
                borderRadius: '6px',
                border: '2px solid #007bff',
                fontSize: '16px',
                fontWeight: '600',
                cursor: 'pointer'
            }
        }
    }
];

export default buttonComponents;
