// F:\landinghub-iconic\apps\web\src\pages\AdminDashboard.js
import React, { useState, useEffect } from 'react';
import api from '@landinghub/api';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [payments, setPayments] = useState([]);
    const [templates, setTemplates] = useState([]);
    const [reports, setReports] = useState({});
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const usersRes = await api.get('/api/users');
                const paymentsRes = await api.get('/api/payments');
                const templatesRes = await api.get('/api/templates');
                const reportsRes = await api.get('/api/reports/system');
                setUsers(usersRes.data);
                setPayments(paymentsRes.data);
                setTemplates(templatesRes.data);
                setReports(reportsRes.data);
            } catch (err) {
                console.error('Error fetching admin data:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    if (loading) return <div>Đang tải...</div>;

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>
            <section>
                <h2>Tổng quan Hệ thống</h2>
                <p>Số Nhà môi giới: {reports.total_brokers || 0}</p>
                <p>Số Leads Tổng: {reports.total_leads || 0}</p>
                <p>Doanh thu Tổng: {reports.total_revenue || 0}</p>
            </section>
        </div>
    );
};

export default React.memo(AdminDashboard);
