export const EXTENDED_LIBRARY = {
    sections: [
        {
            id: 'contact-section',
            name: 'Contact Section',
            icon: 'fa-address-card',
            description: 'A section for contact information',
            level: 'section',
            json: {
                type: 'section',
                componentData: {},
                position: {
                    desktop: { x: 80, y: 400 },
                    tablet: { x: 80, y: 400 },
                    mobile: { x: 80, y: 400 },
                },
                size: { width: 800, height: 120 },
                styles: {
                    background: 'rgb(31, 41, 55)',
                    color: 'rgb(255, 255, 255)',
                    padding: '20px',
                    textAlign: 'center',
                    borderRadius: '8px',
                    display: 'flex',
                    flexDirection: 'column',
                    gap: '10px',
                    height: '100%',
                },
                children: [
                    {
                        id: 'title-1',
                        type: 'heading',
                        content: 'Liên hệ với chúng tôi',
                        styles: { fontSize: '1.8rem', margin: '0' },
                    },
                    {
                        id: 'subtitle-1',
                        type: 'paragraph',
                        content: '© 2025 Công ty của bạn',
                        styles: { fontSize: '1.2rem', margin: '0' },
                    },
                    {
                        id: 'contact-1',
                        type: 'paragraph',
                        content: 'Email: info@company.com | Phone: 0123 456 789',
                        styles: { fontSize: '0.9rem', margin: '0' },
                    },
                ],
            },
        },
    ],
    elements: [],
    popups: [],
    templates: [],
};
