import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/MySales.css';
import DogLoader from '../components/Loader';

const MySales = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [pages, setPages] = useState([]);
    const [stats, setStats] = useState(null);
    const [selectedStatus, setSelectedStatus] = useState('all');

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    const statusOptions = [
        { value: 'all', label: 'T·∫•t c·∫£' },
        { value: 'DRAFT', label: 'B·∫£n nh√°p' },
        { value: 'PENDING', label: 'Ch·ªù duy·ªát' },
        { value: 'ACTIVE', label: 'ƒêang b√°n' },
        { value: 'REJECTED', label: 'B·ªã t·ª´ ch·ªëi' },
        { value: 'SUSPENDED', label: 'T·∫°m ng∆∞ng' }
    ];

    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/auth');
                setLoading(false);
                return;
            }
            try {
                const decodedToken = jwtDecode(token);
                if (!decodedToken.role || !decodedToken.userId) {
                    navigate('/auth');
                    setLoading(false);
                    return;
                }
                setUserRole(decodedToken.role);
            } catch (err) {
                console.error('L·ªói gi·∫£i m√£ token:', err);
                navigate('/auth');
            } finally {
                setLoading(false);
            }
        };

        if (user?.role) {
            setUserRole(user.role);
            setLoading(false);
        } else {
            initializeAuth();
        }
    }, [user, navigate]);

    useEffect(() => {
        AOS.init({ duration: 600, once: true });
    }, []);

    useEffect(() => {
        if (userRole) {
            loadMyPages();
            loadStats();
        }
    }, [userRole, selectedStatus]);

    const loadMyPages = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const params = selectedStatus !== 'all' ? `?status=${selectedStatus}` : '';

            const response = await axios.get(`${API_BASE_URL}/api/marketplace/my/pages${params}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

