import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import Guidelines from './Guidelines';
import SelectionOverlay from './SelectionOverlay';
import QuickEditToolkit from './QuickEditToolkit';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
    CHILD_ELEMENT: 'childElement',
};

// Calculate precise coordinates within canvas or section
const getCanvasPosition = (mouseX, mouseY, containerElement, zoomLevel) => {
    if (!containerElement) {
        console.error('Container element is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }

    const rect = containerElement.getBoundingClientRect();
    const scrollX = containerElement.scrollLeft || 0;
    const scrollY = containerElement.scrollTop || 0;

    const rawX = (mouseX - rect.left + scrollX) / (zoomLevel / 100);
    const rawY = (mouseY - rect.top + scrollY) / (zoomLevel / 100);

    return { x: Math.max(0, rawX), y: Math.max(0, rawY) };
};

// Snap to grid with snap points
const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    return { x: snappedX, y: snappedY };
};

// Calculate element bounds
const getElementBounds = (element) => ({
    left: element.position.desktop?.x || 0,
    top: element.position.desktop?.y || 0,
    right: (element.position.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

// Render component content
const renderComponentContent = (type, componentData = {}, styles = {}, children = []) => {
    console.log('renderComponentContent:', { type, componentData, children });

    if (!type || typeof type !== 'string') {
        return (
            <div style={{ color: '#ff0000', padding: '10px', border: '1px dashed #ff0000', borderRadius: '4px', ...styles }}>
                Unknown Component: {type || 'Không xác định'}
            </div>
        );
    }

    if (type === 'section' && componentData.structure === 'ladi-standard') {
        return (
            <div className="ladi-section" style={{ ...styles, width: '100%', height: '100%' }}>
                <div className="ladi-section-background" style={{
                    backgroundColor: componentData.backgroundColor || 'transparent',
                    backgroundImage: componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none',
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    zIndex: 0
                }}></div>
                <div className="ladi-overlay" style={{
                    backgroundColor: componentData.overlayColor || 'transparent',
                    opacity: componentData.overlayOpacity || 0,
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    zIndex: 0
                }}></div>
                <div className="ladi-container" style={{
                    position: 'relative',
                    zIndex: 1,
                    padding: componentData.padding || '20px',
                    width: '100%',
                    height: '100%',
                }}>
                    {children.map((child, index) => (
                        <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            </div>
        );
    }

    switch (type) {
        case 'heading':
            const headingLevel = componentData.level && ['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(componentData.level)
                ? componentData.level
                : 'h2';
            const HeadingTag = headingLevel;
            return (
                <HeadingTag style={{
                    fontSize: componentData.fontSize || '1.5rem',
                    color: componentData.color || '#1f2937',
                    margin: componentData.margin || '0',
                    padding: componentData.padding || '0',
                    fontWeight: componentData.fontWeight || '700',
                    textAlign: componentData.textAlign || 'left',
                    ...styles
                }}>
                    {componentData.content || 'Tiêu đề'}
                </HeadingTag>
            );
        case 'paragraph':
            return (
                <p style={{
                    fontSize: componentData.fontSize || '1rem',
                    color: componentData.color || '#1f2937',
                    margin: componentData.margin || '0',
                    padding: componentData.padding || '0',
                    fontWeight: componentData.fontWeight || '400',
                    lineHeight: componentData.lineHeight || '1.6',
                    textAlign: componentData.textAlign || 'left',
                    ...styles
                }}>
                    {componentData.content || 'Đoạn văn'}
                </p>
            );
        case 'button':
            return (
                <button style={{
                    padding: componentData.padding || '8px 16px',
                    borderRadius: componentData.borderRadius || '8px',
                    background: componentData.background || '#2563eb',
                    color: componentData.color || '#fff',
                    border: componentData.border || 'none',
                    cursor: 'pointer',
                    fontSize: componentData.fontSize || '1rem',
                    fontWeight: componentData.fontWeight || '600',
                    textAlign: componentData.textAlign || 'center',
                    ...styles
                }}>
                    {componentData.content || 'Nút'}
                </button>
            );
        case 'image':
            return (
                <img
                    src={componentData.src || 'https://via.placeholder.com/150?text=Image'}
                    alt={componentData.alt || 'Hình ảnh'}
                    style={{
                        maxWidth: componentData.maxWidth || '100%',
                        height: componentData.height || 'auto',
                        borderRadius: componentData.borderRadius || '0',
                        ...styles
                    }}
                    draggable={componentData.draggable ?? false}
                />
            );
        case 'form':
            return (
                <div style={{
                    display: 'flex',
                    flexDirection: componentData.direction || 'column',
                    gap: componentData.gap || '10px',
                    ...styles
                }}>
                    {componentData.title && !children.some(child => child?.type === 'heading') && (
                        <h3 style={{
                            margin: componentData.titleMargin || '0',
                            fontSize: componentData.titleFontSize || '1.2rem',
                            color: componentData.titleColor || '#1f2937',
                            fontWeight: componentData.titleFontWeight || '600'
                        }}>
                            {componentData.title}
                        </h3>
                    )}
                    {Array.isArray(componentData.fields) && componentData.fields.length > 0 ? (
                        componentData.fields.map((field, index) => (
                            <input
                                key={index}
                                type={field.type || 'text'}
                                placeholder={field.placeholder || field.label || 'Nhập...'}
                                style={{
                                    padding: field.padding || '8px',
                                    borderRadius: field.borderRadius || '8px',
                                    border: field.border || '1px solid #ccc',
                                    fontSize: field.fontSize || '1rem'
                                }}
                            />
                        ))
                    ) : (
                        !children.some(child => child?.type === 'input') && (
                            <input
                                type={componentData.inputType || 'text'}
                                placeholder={componentData.placeholder || 'Nhập...'}
                                style={{
                                    padding: componentData.inputPadding || '8px',
                                    borderRadius: componentData.inputBorderRadius || '8px',
                                    border: componentData.inputBorder || '1px solid #ccc',
                                    fontSize: componentData.inputFontSize || '1rem'
                                }}
                            />
                        )
                    )}
                    {!children.some(child => child?.type === 'button') && (
                        <button
                            type="submit"
                            style={{
                                background: componentData.buttonBackground || '#2563eb',
                                color: componentData.buttonColor || '#fff',
                                padding: componentData.buttonPadding || '8px 16px',
                                borderRadius: componentData.buttonBorderRadius || '8px',
                                border: componentData.buttonBorder || 'none',
                                cursor: 'pointer',
                                fontSize: componentData.buttonFontSize || '1rem',
                                fontWeight: componentData.buttonFontWeight || '600'
                            }}
                        >
                            {componentData.buttonText || 'Gửi'}
                        </button>
                    )}
                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );
        case 'section':
        case 'container':
            return (
                <div style={{
                    display: componentData.layoutType || 'flex',
                    flexDirection: componentData.direction || 'column',
                    gap: componentData.gap || '10px',
                    padding: componentData.padding || '0',
                    background: componentData.background || 'transparent',
                    ...styles
                }}>
                    {componentData.title && !children.some(child => child?.type === 'heading') && (
                        <h2 style={{
                            fontSize: componentData.titleFontSize || '1.8rem',
                            margin: componentData.titleMargin || '0',
                            color: componentData.titleColor || '#1f2937',
                            fontWeight: componentData.titleFontWeight || '700'
                        }}>
                            {componentData.title}
                        </h2>
                    )}
                    {componentData.subtitle && !children.some(child => child?.type === 'paragraph') && (
                        <p style={{
                            fontSize: componentData.subtitleFontSize || '1.2rem',
                            margin: componentData.subtitleMargin || '0',
                            color: componentData.subtitleColor || '#4b5563'
                        }}>
                            {componentData.subtitle}
                        </p>
                    )}
                    {componentData.content && !children.some(child => child?.type === 'paragraph') && (
                        <p style={{
                            fontSize: componentData.contentFontSize || '1rem',
                            margin: componentData.contentMargin || '0',
                            color: componentData.contentColor || '#4b5563'
                        }}>
                            {componentData.content}
                        </p>
                    )}
                    {Array.isArray(componentData.features) && componentData.features.length > 0 && !children.some(child => child?.type === 'list') && (
                        <ul style={{
                            listStyle: 'none',
                            padding: componentData.featuresPadding || 0,
                            margin: componentData.featuresMargin || '0',
                            display: 'flex',
                            flexDirection: componentData.featuresDirection || 'column',
                            gap: componentData.featuresGap || '8px'
                        }}>
                            {componentData.features.map((feature, index) => (
                                <li key={index} style={{
                                    fontSize: componentData.featureFontSize || '1rem',
                                    color: componentData.featureColor || '#1f2937'
                                }}>
                                    {feature}
                                </li>
                            ))}
                        </ul>
                    )}
                    {componentData.ctaText && !children.some(child => child?.type === 'button') && (
                        <button style={{
                            padding: componentData.ctaPadding || '10px 20px',
                            borderRadius: componentData.ctaBorderRadius || '8px',
                            background: componentData.ctaBackground || '#2563eb',
                            color: componentData.ctaColor || '#fff',
                            border: componentData.ctaBorder || 'none',
                            cursor: 'pointer',
                            fontSize: componentData.ctaFontSize || '1rem',
                            fontWeight: componentData.ctaFontWeight || '600'
                        }}>
                            {componentData.ctaText}
                        </button>
                    )}
                    {componentData.contact && !children.some(child => child?.type === 'paragraph') && (
                        <p style={{
                            fontSize: componentData.contactFontSize || '0.9rem',
                            margin: componentData.contactMargin || '0',
                            color: componentData.contactColor || '#4b5563'
                        }}>
                            {componentData.contact}
                        </p>
                    )}
                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );
        case 'divider':
            return (
                <hr style={{
                    border: 'none',
                    background: componentData.color || '#e5e7eb',
                    height: componentData.thickness || '2px',
                    width: componentData.width || '100%',
                    margin: componentData.margin || '0',
                    ...styles
                }} />
            );
        case 'spacer':
            return (
                <div style={{
                    background: componentData.background || 'transparent',
                    height: componentData.height || '40px',
                    width: componentData.width || '100%',
                    ...styles
                }} />
            );
        case 'popup':
            return (
                <div style={{
                    display: 'flex',
                    flexDirection: componentData.direction || 'column',
                    gap: componentData.gap || '10px',
                    textAlign: componentData.textAlign || 'center',
                    padding: componentData.padding || '20px',
                    background: componentData.background || 'rgba(255, 255, 255, 0.9)',
                    borderRadius: componentData.borderRadius || '12px',
                    ...styles
                }}>
                    {componentData.title && !children.some(child => child?.type === 'heading') && (
                        <h3 style={{
                            fontSize: componentData.titleFontSize || '1.5rem',
                            margin: componentData.titleMargin || '0',
                            color: componentData.titleColor || '#1f2937',
                            fontWeight: componentData.titleFontWeight || '600'
                        }}>
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && !children.some(child => child?.type === 'paragraph') && (
                        <p style={{
                            fontSize: componentData.contentFontSize || '1rem',
                            margin: componentData.contentMargin || '0',
                            color: componentData.contentColor || '#4b5563'
                        }}>
                            {componentData.content}
                        </p>
                    )}
                    {componentData.buttonText && !children.some(child => child?.type === 'button') && (
                        <button style={{
                            padding: componentData.buttonPadding || '10px 20px',
                            borderRadius: componentData.buttonBorderRadius || '8px',
                            background: componentData.buttonBackground || '#2563eb',
                            color: componentData.buttonColor || '#fff',
                            border: componentData.buttonBorder || 'none',
                            cursor: 'pointer',
                            fontSize: componentData.buttonFontSize || '1rem',
                            fontWeight: componentData.buttonFontWeight || '600'
                        }}>
                            {componentData.buttonText}
                        </button>
                    )}
                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );
        case 'modal':
            return (
                <div style={{
                    display: 'flex',
                    flexDirection: componentData.direction || 'column',
                    gap: componentData.gap || '10px',
                    padding: componentData.padding || '20px',
                    background: componentData.background || 'rgba(255, 255, 255, 0.9)',
                    borderRadius: componentData.borderRadius || '12px',
                    ...styles
                }}>
                    {componentData.title && !children.some(child => child?.type === 'heading') && (
                        <h3 style={{
                            fontSize: componentData.titleFontSize || '1.5rem',
                            margin: componentData.titleMargin || '0',
                            color: componentData.titleColor || '#1f2937',
                            fontWeight: componentData.titleFontWeight || '600'
                        }}>
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && !children.some(child => child?.type === 'paragraph') && (
                        <p style={{
                            fontSize: componentData.contentFontSize || '1rem',
                            margin: componentData.contentMargin || '0',
                            color: componentData.contentColor || '#4b5563'
                        }}>
                            {componentData.content}
                        </p>
                    )}
                    {componentData.closeButton && !children.some(child => child?.type === 'button') && (
                        <button style={{
                            padding: componentData.buttonPadding || '8px 16px',
                            borderRadius: componentData.buttonBorderRadius || '8px',
                            background: componentData.buttonBackground || '#6b7280',
                            color: componentData.buttonColor || '#fff',
                            border: componentData.buttonBorder || 'none',
                            cursor: 'pointer',
                            alignSelf: 'flex-end',
                            fontSize: componentData.buttonFontSize || '1rem',
                            fontWeight: componentData.buttonFontWeight || '600'
                        }}>
                            {componentData.buttonText || 'Đóng'}
                        </button>
                    )}
                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );
        case 'card':
            return (
                <div style={{
                    padding: componentData.padding || '20px',
                    background: componentData.background || '#ffffff',
                    borderRadius: componentData.borderRadius || '8px',
                    boxShadow: componentData.boxShadow || '0 2px 8px rgba(0,0,0,0.1)',
                    ...styles
                }}>
                    {componentData.title && (
                        <h3 style={{
                            fontSize: componentData.titleFontSize || '1.2rem',
                            margin: componentData.titleMargin || '0',
                            color: componentData.titleColor || '#1f2937'
                        }}>
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && (
                        <p style={{
                            fontSize: componentData.contentFontSize || '1rem',
                            margin: componentData.contentMargin || '10px 0 0',
                            color: componentData.contentColor || '#4b5563'
                        }}>
                            {componentData.content}
                        </p>
                    )}
                </div>
            );
        case 'testimonial':
            return (
                <div style={{
                    padding: componentData.padding || '20px',
                    background: componentData.background || '#f9fafb',
                    borderRadius: componentData.borderRadius || '8px',
                    ...styles
                }}>
                    {componentData.quote && (
                        <p style={{
                            fontSize: componentData.quoteFontSize || '1rem',
                            margin: componentData.quoteMargin || '0',
                            color: componentData.quoteColor || '#1f2937',
                            fontStyle: 'italic'
                        }}>
                            "{componentData.quote}"
                        </p>
                    )}
                    {componentData.author && (
                        <p style={{
                            fontSize: componentData.authorFontSize || '0.9rem',
                            margin: componentData.authorMargin || '10px 0 0',
                            color: componentData.authorColor || '#4b5563',
                            textAlign: 'right'
                        }}>
                            - {componentData.author}
                        </p>
                    )}
                </div>
            );
        case 'menu':
            return (
                <div style={{
                    display: componentData.layoutType || 'flex',
                    gap: componentData.gap || '20px',
                    ...styles
                }}>
                    {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                        <a
                            key={index}
                            href={item.url || '#'}
                            style={{
                                color: componentData.color || '#ffffff',
                                textDecoration: 'none',
                                fontSize: componentData.fontSize || '1rem'
                            }}
                        >
                            {item.label}
                        </a>
                    ))}
                </div>
            );
        case 'grid':
            return (
                <div style={{
                    display: 'grid',
                    gridTemplateColumns: componentData.columns ? `repeat(${componentData.columns}, 1fr)` : 'repeat(2, 1fr)',
                    gap: componentData.gap || '20px',
                    ...styles
                }}>
                    {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                        <div key={index} style={{
                            background: componentData.itemBackground || '#ffffff',
                            borderRadius: componentData.itemBorderRadius || '8px',
                            padding: componentData.itemPadding || '10px'
                        }}>
                            {item.image && (
                                <img
                                    src={item.image}
                                    alt={item.title || 'Product'}
                                    style={{ maxWidth: '100%', height: 'auto' }}
                                />
                            )}
                            {item.title && (
                                <h4 style={{
                                    fontSize: componentData.titleFontSize || '1rem',
                                    margin: componentData.titleMargin || '10px 0 0',
                                    color: componentData.titleColor || '#1f2937'
                                }}>
                                    {item.title}
                                </h4>
                            )}
                            {item.price && (
                                <p style={{
                                    fontSize: componentData.priceFontSize || '0.9rem',
                                    margin: componentData.priceMargin || '5px 0 0',
                                    color: componentData.priceColor || '#4b5563'
                                }}>
                                    {item.price}
                                </p>
                            )}
                        </div>
                    ))}
                </div>
            );
        case 'list':
            return (
                <ul style={{
                    listStyleType: componentData.listStyleType || 'none',
                    padding: componentData.padding || '0',
                    ...styles
                }}>
                    {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                        <li key={index} style={{
                            fontSize: componentData.itemFontSize || '1rem',
                            margin: componentData.itemMargin || '5px 0',
                            color: componentData.itemColor || '#1f2937'
                        }}>
                            {item.title || item}
                        </li>
                    ))}
                </ul>
            );
        default:
            return (
                <div style={{
                    color: componentData.errorColor || '#ff0000',
                    padding: '10px',
                    border: '1px dashed #ff0000',
                    borderRadius: '4px',
                    ...styles
                }}>
                    Unknown Component: {type}
                </div>
            );
    }
};

// ChildElement Component
const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         componentData = {},
         styles = {},
         isSelected,
         onSelectChild,
         zoomLevel,
         gridSize,
         showGrid,
         onUpdateChildPosition,
         onMoveChild,
         position,
         size,
         viewMode,
     }) => {
        const dragRef = useRef(null);

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.CHILD_ELEMENT,
            item: () => {
                console.log('ChildElement drag started:', { childId: id, parentId });
                return { childId: id, parentId, isExisting: true, position, size };
            },
            end: (item, monitor) => {
                if (!monitor.didDrop()) {
                    console.log('ChildElement drag ended without drop, clearing preview');
                    setDragPreview(null);
                }
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }));

        const handleClick = (e) => {
            e.stopPropagation();
            console.log('ChildElement clicked:', { parentId, id, isSelected });
            if (typeof onSelectChild === 'function') {
                onSelectChild(parentId, id);
            } else {
                console.warn('onSelectChild is not a function');
            }
        };

        useEffect(() => {
            drag(dragRef);
        }, [drag]);

        return (
            <div
                className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''} ${isDragging ? 'lpb-child-element-dragging' : ''}`}
                style={{
                    position: 'absolute',
                    left: position[viewMode]?.x || 0,
                    top: position[viewMode]?.y || 0,
                    width: size?.width || 200,
                    height: size?.height || 50,
                    zIndex: isDragging ? 1000 : (position.z || 10),
                    cursor: isDragging ? 'grabbing' : 'grab',
                    opacity: isDragging ? 0.5 : 1,
                    ...styles,
                }}
                onClick={handleClick}
            >
                <div ref={dragRef}>
                    {renderComponentContent(type, componentData, styles)}
                </div>
                {isSelected && (
                    <QuickEditToolkit
                        element={{ id, type, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => console.log('Edit child:', id)}
                        onDuplicate={() => console.log('Duplicate child:', id)}
                        onDelete={() => console.log('Delete child:', id)}
                        onClose={() => onSelectChild(parentId, null)}
                    />
                )}
            </div>
        );
    }
);

// Element Component
const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onAddChild,
         onUpdateChildPosition,
         onMoveChild,
         showGrid,
         setDragPreview,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: viewMode === 'mobile' ? 375 : 1200, height: 400 }, styles = {}, children = [] } = element;
        const elementRef = useRef(null);
        const containerRef = useRef(null);

        const handleMouseDown = useCallback(
            (e) => {
                e.stopPropagation();
                onSelectElement(id, e.ctrlKey);
                onSelectChild(id, null);
            },
            [id, onSelectElement, onSelectChild]
        );

        const handleContextMenu = useCallback(
            (e) => {
                e.preventDefault();
                e.stopPropagation();
                onContextMenu(id, { x: e.clientX, y: e.clientY });
            },
            [id, onContextMenu]
        );

        const snapPoints = [
            { x: 0, y: 0 },
            { x: size.width, y: size.height },
            { x: size.width / 2, y: size.height / 2 },
        ];
        children.forEach((child) => {
            const bounds = getElementBounds(child);
            snapPoints.push(
                { x: bounds.left, y: bounds.top },
                { x: bounds.right, y: bounds.bottom },
                { x: bounds.centerX, y: bounds.centerY },
                { x: bounds.left, y: bounds.centerY },
                { x: bounds.right, y: bounds.centerY },
                { x: bounds.centerX, y: bounds.top },
                { x: bounds.centerX, y: bounds.bottom }
            );
        });

        if (type === 'section' && componentData.structure === 'ladi-standard') {
            const [{ isOverContainer, canDropContainer }, drop] = useDrop(() => ({
                accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
                canDrop: (item, monitor) => {
                    console.log('Checking canDrop for ladi-container:', {
                        item,
                        itemType: monitor.getItemType(),
                        type,
                        structure: componentData?.structure,
                        parentId: item.parentId,
                        elementId: id,
                    });
                    if (!type || type !== 'section' || componentData?.structure !== 'ladi-standard') {
                        return false;
                    }
                    if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT && item.parentId && item.parentId !== id) {
                        return false;
                    }
                    return true;
                },
                drop: (item, monitor) => {
                    if (!monitor.canDrop()) {
                        toast.error('Không thể thả vào container này!');
                        setDragPreview(null);
                        return { moved: false };
                    }
                    const clientOffset = monitor.getClientOffset();
                    if (!clientOffset || !containerRef.current) {
                        toast.error('Không thể xác định vị trí thả!');
                        setDragPreview(null);
                        return { moved: false };
                    }

                    const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                    const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                    if (monitor.getItemType() === ItemTypes.ELEMENT) {
                        const newId = `${item.id}-${Date.now()}`;
                        const newChild = {
                            id: newId,
                            type: item.json.type,
                            componentData: item.json.componentData || {},
                            position: {
                                [viewMode]: snapped,
                                desktop: snapped,
                                tablet: snapped,
                                mobile: snapped,
                            },
                            size: item.json.size || { width: 200, height: 50 },
                            styles: item.json.styles || {},
                            children: [],
                        };
                        onAddChild(id, newChild);
                        toast.success('Đã thêm thành phần con!');
                        setDragPreview(null);
                        return { moved: true, newPosition: snapped };
                    } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                        if (item.parentId === id) {
                            onUpdateChildPosition(id, item.childId, snapped);
                            toast.success('Đã di chuyển thành phần con trong section!');
                        } else {
                            onMoveChild(item.parentId, item.childId, id, snapped);
                            toast.success('Đã di chuyển thành phần con sang section khác!');
                        }
                        setDragPreview(null);
                        return { moved: true, newPosition: snapped };
                    }
                    setDragPreview(null);
                    return { moved: false };
                },
                collect: (monitor) => ({
                    isOverContainer: monitor.isOver(),
                    canDropContainer: monitor.canDrop(),
                }),
            }), [id, type, componentData, viewMode, zoomLevel, gridSize, showGrid, onAddChild, onUpdateChildPosition, onMoveChild]);

            useEffect(() => {
                drop(containerRef);
            }, [drop]);

            return (
                <div
                    ref={elementRef}
                    className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''}`}
                    style={{
                        position: 'absolute',
                        width: viewMode === 'mobile' ? '375px' : '1200px',
                        height: size.height,
                        left: '50%',
                        transform: 'translateX(-50%)',
                        zIndex: position.z || 1,
                        userSelect: 'none',
                        ...styles,
                    }}
                    onMouseDown={handleMouseDown}
                    onContextMenu={handleContextMenu}
                >
                    <div className="ladi-section" style={{ width: '100%', height: '100%' }}>
                        <div className="ladi-section-background" style={{
                            backgroundColor: componentData.backgroundColor || 'transparent',
                            backgroundImage: componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none',
                            backgroundSize: 'cover',
                            backgroundPosition: 'center',
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            zIndex: 0
                        }}></div>
                        <div className="ladi-overlay" style={{
                            backgroundColor: componentData.overlayColor || 'transparent',
                            opacity: componentData.overlayOpacity || 0,
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            zIndex: 0
                        }}></div>
                        <div
                            ref={containerRef}
                            className={`ladi-container ${isOverContainer && canDropContainer ? 'lpb-child-element-hover' : ''}`}
                            style={{
                                position: 'relative',
                                zIndex: 1,
                                padding: componentData.padding || '20px',
                                width: '100%',
                                height: '100%',
                                border: isOverContainer && canDropContainer ? '2px dashed #2563eb' : 'none',
                            }}
                        >
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    zoomLevel={zoomLevel}
                                    gridSize={gridSize}
                                    showGrid={showGrid}
                                    onUpdateChildPosition={onUpdateChildPosition}
                                    onMoveChild={onMoveChild}
                                    viewMode={viewMode}
                                    setDragPreview={setDragPreview}
                                />
                            ))}
                        </div>
                    </div>
                </div>
            );
        }

        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: () => {
                console.log('Element drag started:', { id, position: position[viewMode] });
                return {
                    id,
                    elementSize: size,
                    elementPosition: position[viewMode] || { x: 0, y: 0 },
                    isExisting: true,
                };
            },
            end: (item, monitor) => {
                if (!monitor.didDrop()) {
                    console.log('Element drag ended without drop, clearing preview');
                    setDragPreview(null);
                }
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, position, viewMode]);

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    drag(node);
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                style={{
                    position: 'absolute',
                    transform: `translate(${position[viewMode]?.x || 0}px, ${position[viewMode]?.y || 0}px)`,
                    width: viewMode === 'mobile' ? '375px' : '1200px',
                    height: size.height,
                    zIndex: position.z || 1,
                    opacity: isDragging ? 0.5 : 1,
                    cursor: isDragging ? 'grabbing' : 'grab',
                    userSelect: 'none',
                    ...styles,
                }}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {renderComponentContent(type, componentData, styles, children)}
            </div>
        );
    }
);

// Canvas Component
const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         onGroupElements,
         viewMode,
         zoomLevel,
         gridSize,
         showGrid,
         onEditElement,
         onDuplicateElement,
         onMoveElementUp,
         onMoveElementDown,
         onSelectChild,
         selectedChildId,
         onAddChild,
         onUpdateChildPosition,
         onMoveChild,
         guideLine,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);
        const [contextMenu, setContextMenu] = useState(null);
        const [dragPreview, setDragPreview] = useState(null);
        const [showPopup, setShowPopup] = useState(false);

        const canvasBounds = canvasRef.current
            ? {
                width: canvasRef.current.offsetWidth / (zoomLevel / 100),
                height: canvasRef.current.offsetHeight / (zoomLevel / 100),
            }
            : { width: 1200, height: 1000 };

        const getSnapPoints = useCallback(() => {
            const points = [
                { x: 0, y: 0 },
                { x: viewMode === 'mobile' ? 375 : 1200, y: canvasBounds.height },
                { x: (viewMode === 'mobile' ? 375 : 1200) / 2, y: canvasBounds.height / 2 },
            ];

            pageData.elements.forEach((el) => {
                const bounds = getElementBounds(el);
                points.push(
                    { x: bounds.left, y: bounds.top },
                    { x: bounds.right, y: bounds.bottom },
                    { x: bounds.centerX, y: bounds.centerY },
                    { x: bounds.left, y: bounds.centerY },
                    { x: bounds.right, y: bounds.centerY },
                    { x: bounds.centerX, y: bounds.top },
                    { x: bounds.centerX, y: bounds.bottom }
                );
                el.children?.forEach((child) => {
                    const childBounds = getElementBounds(child);
                    points.push(
                        { x: childBounds.left, y: childBounds.top },
                        { x: childBounds.right, y: childBounds.bottom },
                        { x: childBounds.centerX, y: childBounds.centerY },
                        { x: childBounds.left, y: childBounds.centerY },
                        { x: childBounds.right, y: childBounds.centerY },
                        { x: childBounds.centerX, y: childBounds.top },
                        { x: childBounds.centerX, y: childBounds.bottom }
                    );
                });
            });

            return points;
        }, [pageData.elements, canvasBounds, viewMode]);

        const [{ isOver, dragItem }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    setDragPreview(null);
                    return;
                }

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT && item.json) {
                    setDragPreview({
                        id: 'preview',
                        x: snapped.x,
                        y: snapped.y,
                        size: item.json.size || { width: viewMode === 'mobile' ? 375 : 1200, height: 400 },
                        type: item.json.type,
                        componentData: item.json.componentData || { structure: 'ladi-standard' },
                        styles: item.json.styles,
                    });
                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    setDragPreview({
                        id: item.childId,
                        x: snapped.x,
                        y: snapped.y,
                        size: item.size || { width: 200, height: 50 },
                        type: pageData.elements
                            .find((el) => el.id === item.parentId)
                            ?.children.find((child) => child.id === item.childId)?.type,
                        componentData: pageData.elements
                            .find((el) => el.id === item.parentId)
                            ?.children.find((child) => child.id === item.childId)?.componentData,
                        styles: pageData.elements
                            .find((el) => el.id === item.parentId)
                            ?.children.find((child) => child.id === item.childId)?.styles,
                    });
                }

                const newGuidelines = [];
                pageData.elements.forEach((el) => {
                    if (el.id === item.id) return;
                    const bounds = getElementBounds(el);
                    if (Math.abs(snapped.x - bounds.left) < 15) newGuidelines.push({ vertical: true, position: bounds.left });
                    if (Math.abs(snapped.x - bounds.right) < 15) newGuidelines.push({ vertical: true, position: bounds.right });
                    if (Math.abs(snapped.x - bounds.centerX) < 15) newGuidelines.push({ vertical: true, position: bounds.centerX });
                    if (Math.abs(snapped.y - bounds.top) < 15) newGuidelines.push({ vertical: false, position: bounds.top });
                    if (Math.abs(snapped.y - bounds.bottom) < 15) newGuidelines.push({ vertical: false, position: bounds.bottom });
                    if (Math.abs(snapped.y - bounds.centerY) < 15) newGuidelines.push({ vertical: false, position: bounds.centerY });
                    el.children?.forEach((child) => {
                        const childBounds = getElementBounds(child);
                        if (Math.abs(snapped.x - childBounds.left) < 15) newGuidelines.push({ vertical: true, position: childBounds.left });
                        if (Math.abs(snapped.x - childBounds.right) < 15) newGuidelines.push({ vertical: true, position: childBounds.right });
                        if (Math.abs(snapped.x - childBounds.centerX) < 15) newGuidelines.push({ vertical: true, position: childBounds.centerX });
                        if (Math.abs(snapped.y - childBounds.top) < 15) newGuidelines.push({ vertical: false, position: childBounds.top });
                        if (Math.abs(snapped.y - childBounds.bottom) < 15) newGuidelines.push({ vertical: false, position: childBounds.bottom });
                        if (Math.abs(snapped.y - childBounds.centerY) < 15) newGuidelines.push({ vertical: false, position: childBounds.centerY });
                    });
                });

                if (Math.abs(snapped.x) < 15) newGuidelines.push({ vertical: true, position: 0 });
                if (Math.abs(snapped.x - (viewMode === 'mobile' ? 375 : 1200)) < 15) newGuidelines.push({ vertical: true, position: viewMode === 'mobile' ? 375 : 1200 });
                if (Math.abs(snapped.x - (viewMode === 'mobile' ? 375 : 1200) / 2) < 15) newGuidelines.push({ vertical: true, position: (viewMode === 'mobile' ? 375 : 1200) / 2 });
                if (Math.abs(snapped.y) < 15) newGuidelines.push({ vertical: false, position: 0 });
                if (Math.abs(snapped.y - canvasBounds.height) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height });
                if (Math.abs(snapped.y - canvasBounds.height / 2) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height / 2 });

                setGuidelines(newGuidelines);
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả!');
                    setDragPreview(null);
                    setGuidelines([]);
                    return { moved: false };
                }

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    const sourceSection = pageData.elements.find((el) => el.id === item.parentId);
                    if (!sourceSection) {
                        console.error('Source section not found:', item.parentId);
                        setDragPreview(null);
                        setGuidelines([]);
                        return { moved: false };
                    }
                    onMoveChild(item.parentId, item.childId, null, snapped);
                    toast.success('Đã di chuyển thành phần con ra canvas!');
                    setDragPreview(null);
                    setGuidelines([]);
                    return { moved: true, newPosition: snapped };
                } else if (monitor.getItemType() === ItemTypes.ELEMENT) {
                    if (item.json.type !== 'section') {
                        toast.error('Chỉ có thể thả section lên canvas!');
                        setDragPreview(null);
                        setGuidelines([]);
                        return { moved: false };
                    }
                    const newElement = {
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: item.json.componentData || { structure: 'ladi-standard' },
                        position: {
                            [viewMode]: snapped,
                            desktop: snapped,
                            tablet: snapped,
                            mobile: snapped,
                        },
                        size: { ...item.json.size, width: viewMode === 'mobile' ? 375 : 1200 },
                        styles: item.json.styles || {},
                        children: item.json.children || []
                    };
                    onAddElement(newElement);
                    toast.success('Đã thêm section mới!');
                    setDragPreview(null);
                    setGuidelines([]);
                    return { moved: true, newPosition: snapped };
                }

                setDragPreview(null);
                setGuidelines([]);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOver: monitor.isOver(),
                dragItem: monitor.getItem(),
            }),
        }), [pageData.elements, viewMode, zoomLevel, gridSize, showGrid, canvasBounds, onAddElement, onMoveChild]);

        const handleContextMenu = useCallback((id, position) => {
            setContextMenu({ id, position });
        }, []);

        const handleCanvasClick = useCallback(
            (e) => {
                if (e.target === canvasRef.current) {
                    onSelectElement([]);
                    onSelectChild(null, null);
                    setContextMenu(null);
                    setDragPreview(null);
                }
            },
            [onSelectElement, onSelectChild]
        );

        const handleKeyDown = useCallback(
            (e) => {
                const ids = Array.isArray(selectedIds) ? selectedIds : [];
                if (!ids.length) return;

                e.preventDefault();
                e.stopPropagation();

                const step = e.shiftKey ? 10 : 1;

                ids.forEach((id) => {
                    const element = pageData.elements.find((el) => el.id === id);
                    if (element && element.type !== 'section') {
                        const newY = e.key === 'ArrowUp' ? Math.max(0, (element.position[viewMode]?.y || 0) - step) : (element.position[viewMode]?.y || 0) + step;
                        const newX = e.key === 'ArrowLeft' ? Math.max(0, (element.position[viewMode]?.x || 0) - step) : (element.position[viewMode]?.x || 0) + step;
                        switch (e.key) {
                            case 'ArrowUp':
                            case 'ArrowDown':
                                onUpdatePosition(id, { [viewMode]: { x: element.position[viewMode]?.x || 0, y: newY } }, 'absolute');
                                break;
                            case 'ArrowLeft':
                            case 'ArrowRight':
                                onUpdatePosition(id, { [viewMode]: { x: newX, y: element.position[viewMode]?.y || 0 } }, 'absolute');
                                break;
                            case 'Delete':
                            case 'Backspace':
                                onDeleteElement(id);
                                break;
                            case 'Escape':
                                onSelectElement([]);
                                onSelectChild(null, null);
                                setContextMenu(null);
                                setDragPreview(null);
                                setShowPopup(false);
                                break;
                            default:
                                break;
                        }
                    }
                });
            },
            [selectedIds, onUpdatePosition, onDeleteElement, onSelectElement, onSelectChild, viewMode, pageData.elements]
        );

        useHotkeys('ctrl+a', (e) => {
            e.preventDefault();
            onSelectElement(pageData.elements.map((el) => el.id));
        }, [pageData.elements, onSelectElement]);

        useHotkeys('ctrl+g', (e) => {
            e.preventDefault();
            const ids = Array.isArray(selectedIds) ? selectedIds : [];
            if (ids.length > 1) {
                onGroupElements(ids);
            }
        }, [selectedIds, onGroupElements]);

        useEffect(() => {
            const handleClickOutside = () => {
                setContextMenu(null);
                setDragPreview(null);
                setShowPopup(false);
            };
            if (contextMenu || showPopup) {
                document.addEventListener('click', handleClickOutside);
                return () => document.removeEventListener('click', handleClickOutside);
            }
        }, [contextMenu, showPopup]);

        const handleAddSection = (section) => {
            const lastSectionY = pageData.elements
                .filter((el) => el.type === 'section')
                .reduce((maxY, el) => {
                    const y = el.position?.[viewMode]?.y || 0;
                    const height = el.size?.height || 400;
                    return Math.max(maxY, y + height);
                }, 0);

            const newElement = {
                id: `${section.id}-${Date.now()}`,
                type: section.json.type,
                componentData: section.json.componentData || { structure: 'ladi-standard' },
                position: {
                    [viewMode]: { x: 0, y: lastSectionY + 10 },
                    desktop: { x: 0, y: lastSectionY + 10 },
                    tablet: { x: 0, y: lastSectionY + 10 },
                    mobile: { x: 0, y: lastSectionY + 10 },
                },
                size: { ...section.json.size, width: viewMode === 'mobile' ? 375 : 1200 },
                styles: section.json.styles || {},
                children: section.json.children || []
            };
            onAddElement(newElement);
            toast.success('Đã thêm section mới!');
            setShowPopup(false);
        };

        const renderDragPreview = () => {
            if (!dragPreview) return null;

            const { x, y, size, type, componentData, styles } = dragPreview;
            const previewStyle = {
                position: 'absolute',
                transform: `translate(${x}px, ${y}px)`,
                width: size.width,
                height: size.height,
                border: '2px dashed #3b82f6',
                background: 'rgba(59, 130, 246, 0.1)',
                pointerEvents: 'none',
                zIndex: 1000,
                opacity: 0.5,
                ...styles,
            };

            return (
                <div className="lpb-drag-preview" style={previewStyle}>
                    {renderComponentContent(type, componentData || {}, {})}
                </div>
            );
        };

        const renderAddSectionButton = () => {
            if (pageData.elements.length > 0) return null;
            return (
                <div
                    style={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        zIndex: 1000,
                    }}
                >
                    <button
                        onClick={(e) => {
                            e.stopPropagation();
                            setShowPopup(true);
                        }}
                        style={{
                            padding: '12px 24px',
                            background: '#2563eb',
                            color: '#fff',
                            border: 'none',
                            borderRadius: '8px',
                            fontSize: '1rem',
                            fontWeight: '600',
                            cursor: 'pointer',
                        }}
                    >
                        Add Section
                    </button>
                </div>
            );
        };

        const renderSectionPopup = () => {
            if (!showPopup) return null;
            return (
                <div
                    className="lpb-section-popup"
                    style={{
                        position: 'fixed',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        background: '#fff',
                        borderRadius: '12px',
                        boxShadow: '0 4px 16px rgba(0,0,0,0.2)',
                        zIndex: 10000,
                        width: '600px',
                        maxHeight: '80vh',
                        overflowY: 'auto',
                        padding: '20px',
                    }}
                    onClick={(e) => e.stopPropagation()}
                >
                    <h2 style={{ fontSize: '1.5rem', marginBottom: '20px', textAlign: 'center' }}>
                        Select a Section
                    </h2>
                    {sections.subCategories.map((category) => (
                        <div key={category.id} style={{ marginBottom: '20px' }}>
                            <h3 style={{ fontSize: '1.2rem', marginBottom: '10px' }}>{category.name}</h3>
                            <div style={{
                                display: 'grid',
                                gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
                                gap: '20px',
                            }}>
                                {category.templates.map((template) => (
                                    <div
                                        key={template.id}
                                        style={{
                                            border: '1px solid #e5e7eb',
                                            borderRadius: '8px',
                                            padding: '10px',
                                            textAlign: 'center',
                                            cursor: 'pointer',
                                            background: '#fff',
                                            transition: 'transform 0.2s',
                                        }}
                                        onClick={() => handleAddSection(template)}
                                        onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.02)'}
                                        onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
                                    >
                                        <img
                                            src={template.previewImage}
                                            alt={template.name}
                                            style={{
                                                width: '100%',
                                                height: '150px',
                                                objectFit: 'cover',
                                                borderRadius: '4px',
                                                marginBottom: '10px',
                                            }}
                                        />
                                        <h4 style={{ fontSize: '1rem', margin: '0 0 5px' }}>{template.name}</h4>
                                        <p style={{ fontSize: '0.9rem', color: '#4b5563', margin: '0' }}>
                                            {template.description}
                                        </p>
                                        <button
                                            style={{
                                                marginTop: '10px',
                                                padding: '8px 16px',
                                                background: '#2563eb',
                                                color: '#fff',
                                                border: 'none',
                                                borderRadius: '4px',
                                                cursor: 'pointer',
                                            }}
                                        >
                                            Add
                                        </button>
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}
                    <button
                        onClick={() => setShowPopup(false)}
                        style={{
                            position: 'absolute',
                            top: '10px',
                            right: '10px',
                            padding: '5px 10px',
                            background: '#6b7280',
                            color: '#fff',
                            border: 'none',
                            borderRadius: '4px',
                            cursor: 'pointer',
                        }}
                    >
                        Close
                    </button>
                </div>
            );
        };

        return (
            <div className="lpb-canvas-wrapper" onKeyDown={handleKeyDown} tabIndex={0} style={{ outline: 'none' }}>
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    onClick={handleCanvasClick}
                    style={{
                        width: '100%',
                        height: pageData.canvas.height ? `${pageData.canvas.height}px` : 'auto',
                        background: pageData.canvas.background,
                        transform: `scale(${zoomLevel / 100})`,
                        transformOrigin: 'top center',
                        margin: '0 auto',
                        position: 'relative',
                    }}
                >
                    <Guidelines guidelines={guidelines} zoomLevel={zoomLevel} />
                    {pageData.elements.map((element) => (
                        <Element
                            key={element.id}
                            element={element}
                            isSelected={selectedIds.includes(element.id)}
                            onSelectElement={onSelectElement}
                            onUpdatePosition={onUpdatePosition}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={gridSize}
                            onContextMenu={handleContextMenu}
                            canvasBounds={canvasBounds}
                            canvasRef={canvasRef}
                            onSelectChild={onSelectChild}
                            selectedChildId={selectedChildId}
                            onAddChild={onAddChild}
                            onUpdateChildPosition={onUpdateChildPosition}
                            onMoveChild={onMoveChild}
                            showGrid={showGrid}
                            setDragPreview={setDragPreview}
                        />
                    ))}
                    {renderDragPreview()}
                    {guideLine?.show && (
                        <>
                            {/* Đường kẻ ngang (x) */}
                            <div
                                style={{
                                    position: 'absolute',
                                    left: '50%',
                                    transform: 'translateX(-50%)',
                                    top: guideLine.y,
                                    width: viewMode === 'mobile' ? '375px' : '1200px',
                                    height: '1px',
                                    borderBottom: '1px dashed #2563eb',
                                    opacity: 0.5,
                                    zIndex: 1001,
                                }}
                            />
                            {/* Đường kẻ dọc (y1) */}
                            <div
                                style={{
                                    position: 'absolute',
                                    left: '50%',
                                    transform: 'translateX(-50%)',
                                    top: 0,
                                    width: '1px',
                                    height: pageData.canvas.height ? `${pageData.canvas.height}px` : '100%',
                                    backgroundColor: '#2563eb',
                                    opacity: 0.5,
                                    zIndex: 1001,
                                }}
                            />
                            {/* Đường kẻ dọc (y2) */}
                            <div
                                style={{
                                    position: 'absolute',
                                    left: viewMode === 'mobile' ? 'calc(50% + 187.5px)' : 'calc(50% + 600px)',
                                    top: 0,
                                    width: '1px',
                                    height: pageData.canvas.height ? `${pageData.canvas.height}px` : '100%',
                                    backgroundColor: '#2563eb',
                                    opacity: 0.5,
                                    zIndex: 1001,
                                }}
                            />
                        </>
                    )}
                    <SelectionOverlay
                        selectedElements={pageData.elements.filter((el) => Array.isArray(selectedIds) && selectedIds.includes(el.id))}
                        onResize={onUpdateSize}
                        zoomLevel={zoomLevel}
                    />
                    {contextMenu && (
                        <div
                            className="lpb-context-menu"
                            style={{
                                position: 'fixed',
                                left: contextMenu.position.x,
                                top: contextMenu.position.y,
                                zIndex: 10000,
                                background: '#fff',
                                border: '1px solid #ccc',
                                borderRadius: '4px',
                                boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
                                minWidth: '120px',
                            }}
                        >
                            <div
                                className="lpb-context-menu-item"
                                style={{ padding: '8px 12px', cursor: 'pointer' }}
                                onClick={() => {
                                    onDeleteElement(contextMenu.id);
                                    setContextMenu(null);
                                }}
                            >
                                Delete
                            </div>
                            <div
                                className="lpb-context-menu-item"
                                style={{ padding: '8px 12px', cursor: 'pointer' }}
                                onClick={() => {
                                    onGroupElements([contextMenu.id]);
                                    setContextMenu(null);
                                }}
                            >
                                Group
                            </div>
                        </div>
                    )}
                    {renderAddSectionButton()}
                    {renderSectionPopup()}
                </div>
            </div>
        );
    }
);

export default Canvas;
