const getCanvasPosition = (mouseX, mouseY, canvasRef, zoomLevel = 100) => {
    if (!canvasRef || !canvasRef.current) {
        console.error('Canvas ref is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }

    const canvasRect = canvasRef.current.getBoundingClientRect();
    const x = (mouseX - canvasRect.left) / (zoomLevel / 100);
    const y = (mouseY - canvasRect.top) / (zoomLevel / 100);

    return { x: Math.max(0, x), y: Math.max(0, y) };
};

const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    if (gridSize === Infinity) return { x, y };

    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    // Snap to nearby elements (threshold 15px)
    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    return { x: snappedX, y: snappedY };
};

const getElementBounds = (element) => ({
    left: element.position.desktop?.x || 0,
    top: element.position.desktop?.y || 0,
    right: (element.position.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasRef, // Pass canvasRef from parent
         snapPoints,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {} } = element;
        const elementRef = useRef(null);
        const [dragStartOffset, setDragStartOffset] = useState({ x: 0, y: 0 });

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: { id, isExistingElement: true },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        const handleMouseDown = (e) => {
            e.stopPropagation();

            // Calculate offset from mouse to element top-left corner
            const elementRect = elementRef.current?.getBoundingClientRect();
            const mouseOffset = {
                x: (e.clientX - elementRect.left) / (zoomLevel / 100),
                y: (e.clientY - elementRect.top) / (zoomLevel / 100)
            };

            setDragStartOffset(mouseOffset);
            onSelectElement(id, e.ctrlKey);

            console.log('Mouse down offset:', mouseOffset);
        };

        const handleContextMenu = (e) => {
            e.preventDefault();
            e.stopPropagation();
            onContextMenu(id, { x: e.clientX, y: e.clientY });
        };

        // Store drag offset in drag item for Canvas drop handler to use
        useEffect(() => {
            if (isDragging) {
                // Store offset in a global way so drop handler can access it
                window.currentDragOffset = dragStartOffset;
            }
        }, [isDragging, dragStartOffset]);

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    drag(node);
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                style={{
                    position: 'absolute',
                    left: position[viewMode]?.x || 0,
                    top: position[viewMode]?.y || 0,
                    width: size.width,
                    height: size.height,
                    zIndex: position.z || 1,
                    cursor: isDragging ? 'grabbing' : 'grab',
                    opacity: isDragging ? 0.8 : 1,
                    ...styles,
                }}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {(() => {
                    switch (type) {
                        case 'heading':
                            return (
                                <h2 style={{
                                    fontSize: componentData.fontSize || '1.5rem',
                                    color: componentData.color || '#1f2937',
                                    margin: 0,
                                    userSelect: 'none'
                                }}>
                                    {componentData.content || 'Tiêu đề'}
                                </h2>
                            );
                        case 'paragraph':
                            return (
                                <p style={{
                                    fontSize: componentData.fontSize || '1rem',
                                    color: componentData.color || '#1f2937',
                                    margin: 0,
                                    userSelect: 'none'
                                }}>
                                    {componentData.content || 'Đoạn văn'}
                                </p>
                            );
                        case 'button':
                            return (
                                <button style={{
                                    padding: '8px 16px',
                                    borderRadius: '8px',
                                    background: componentData.background || '#2563eb',
                                    color: componentData.color || '#fff',
                                    border: 'none',
                                    cursor: 'pointer',
                                    userSelect: 'none'
                                }}>
                                    {componentData.content || 'Nút'}
                                </button>
                            );
                        case 'image':
                            return (
                                <img
                                    src={componentData.src || 'https://via.placeholder.com/150'}
                                    alt="Image"
                                    style={{
                                        maxWidth: '100%',
                                        height: 'auto',
                                        userSelect: 'none',
                                        pointerEvents: 'none'
                                    }}
                                />
                            );
                        case 'form':
                            return (
                                <form style={{
                                    display: 'flex',
                                    flexDirection: 'column',
                                    gap: '10px',
                                    userSelect: 'none'
                                }}>
                                    <input
                                        type="text"
                                        placeholder="Input"
                                        style={{ padding: '8px', borderRadius: '8px', border: '1px solid #ccc' }}
                                        readOnly
                                    />
                                    <button
                                        type="button"
                                        style={{
                                            background: '#2563eb',
                                            color: '#fff',
                                            padding: '8px 16px',
                                            borderRadius: '8px',
                                            border: 'none'
                                        }}
                                    >
                                        Submit
                                    </button>
                                </form>
                            );
                        default:
                            return <div style={{ userSelect: 'none' }}>{componentData.content || 'Unknown'}</div>;
                    }
                })()}
            </div>
        );
    }
);

const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         onGroupElements,
         viewMode,
         zoomLevel,
         gridSize,
         showGrid,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);
        const [contextMenu, setContextMenu] = useState(null);

        const canvasBounds = {
            width: pageData?.canvas?.width || 1200,
            height: pageData?.canvas?.height || 1000
        };

        // Generate snap points from existing elements
        const snapPoints = pageData?.elements ? pageData.elements.map(el => {
            const bounds = getElementBounds(el);
            return [
                { x: bounds.left, y: bounds.top },
                { x: bounds.right, y: bounds.bottom },
                { x: bounds.centerX, y: bounds.centerY },
                { x: bounds.left, y: bounds.centerY }, // left-center
                { x: bounds.right, y: bounds.centerY }, // right-center
                { x: bounds.centerX, y: bounds.top }, // top-center
                { x: bounds.centerX, y: bounds.bottom }, // bottom-center
            ];
        }).flat() : [];

        // Add canvas boundary snap points
        snapPoints.push(
            { x: 0, y: 0 }, // top-left
            { x: canvasBounds.width, y: 0 }, // top-right
            { x: 0, y: canvasBounds.height }, // bottom-left
            { x: canvasBounds.width, y: canvasBounds.height }, // bottom-right
            { x: canvasBounds.width / 2, y: canvasBounds.height / 2 }, // center
        );

        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) return;

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef, zoomLevel);
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                // Generate guidelines for visual feedback
                const newGuidelines = [];
                snapPoints.forEach(point => {
                    if (Math.abs(snapped.x - point.x) < 2) {
                        newGuidelines.push({ vertical: true, position: point.x });
                    }
                    if (Math.abs(snapped.y - point.y) < 2) {
                        newGuidelines.push({ vertical: false, position: point.y });
                    }
                });

                setGuidelines(newGuidelines);
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả!');
                    return;
                }

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef, zoomLevel);
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                console.group('Drop Debug');
                console.log('Item:', item);
                console.log('Client Offset:', clientOffset);
                console.log('Canvas Position:', pos);
                console.log('Snapped Position:', snapped);
                console.groupEnd();

                // Handle new element from library
                if (item.json && !item.isExistingElement) {
                    const newElement = {
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: item.json.componentData || {},
                        position: {
                            [viewMode]: snapped,
                            desktop: snapped,
                            tablet: snapped,
                            mobile: snapped
                        },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                    };

                    onAddElement(newElement);
                    toast.success('Đã thêm phần tử!');
                }

                // Handle existing element drag
                else if (item.isExistingElement && item.id) {
                    // Get the drag offset from the global variable
                    const dragOffset = window.currentDragOffset || { x: 0, y: 0 };

                    // Apply offset to get final position
                    const finalPosition = {
                        x: snapped.x - dragOffset.x,
                        y: snapped.y - dragOffset.y
                    };

                    // Apply final snapping with offset
                    const finalSnapped = snapToGrid(finalPosition.x, finalPosition.y, showGrid ? gridSize : Infinity, snapPoints);

                    onUpdatePosition(item.id, { [viewMode]: finalSnapped }, true);

                    // Clear the global offset
                    window.currentDragOffset = null;

                    toast.success('Đã di chuyển phần tử!');
                }

                setGuidelines([]);
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        const handleCanvasClick = (e) => {
            // Click on empty canvas to deselect all
            if (e.target === canvasRef.current) {
                onSelectElement([]);
            }
        };

        const handleContextMenu = (id, position) => {
            setContextMenu({ id, position });
        };

        const handleKeyDown = useCallback((e) => {
            if (!selectedIds.length) return;

            const nudgeDistance = e.shiftKey ? 10 : 1;

            switch (e.key) {
                case 'ArrowUp':
                    e.preventDefault();
                    selectedIds.forEach(id => {
                        const element = pageData.elements.find(el => el.id === id);
                        if (element) {
                            const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                            onUpdatePosition(id, {
                                [viewMode]: {
                                    x: currentPos.x,
                                    y: Math.max(0, currentPos.y - nudgeDistance)
                                }
                            }, true);
                        }
                    });
                    break;
                case 'ArrowDown':
                    e.preventDefault();
                    selectedIds.forEach(id => {
                        const element = pageData.elements.find(el => el.id === id);
                        if (element) {
                            const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                            onUpdatePosition(id, {
                                [viewMode]: {
                                    x: currentPos.x,
                                    y: currentPos.y + nudgeDistance
                                }
                            }, true);
                        }
                    });
                    break;
                case 'ArrowLeft':
                    e.preventDefault();
                    selectedIds.forEach(id => {
                        const element = pageData.elements.find(el => el.id === id);
                        if (element) {
                            const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                            onUpdatePosition(id, {
                                [viewMode]: {
                                    x: Math.max(0, currentPos.x - nudgeDistance),
                                    y: currentPos.y
                                }
                            }, true);
                        }
                    });
                    break;
                case 'ArrowRight':
                    e.preventDefault();
                    selectedIds.forEach(id => {
                        const element = pageData.elements.find(el => el.id === id);
                        if (element) {
                            const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                            onUpdatePosition(id, {
                                [viewMode]: {
                                    x: currentPos.x + nudgeDistance,
                                    y: currentPos.y
                                }
                            }, true);
                        }
                    });
                    break;
                case 'Delete':
                case 'Backspace':
                    e.preventDefault();
                    selectedIds.forEach(id => onDeleteElement(id));
                    break;
                case 'Escape':
                    e.preventDefault();
                    onSelectElement([]);
                    setContextMenu(null);
                    break;
            }
        }, [selectedIds, onUpdatePosition, onDeleteElement, onSelectElement, viewMode, pageData.elements]);

        // Hotkeys
        useHotkeys('ctrl+a', (e) => {
            e.preventDefault();
            onSelectElement(pageData.elements.map(el => el.id));
        }, [pageData.elements, onSelectElement]);

        useHotkeys('ctrl+g', (e) => {
            e.preventDefault();
            if (selectedIds.length > 1) {
                onGroupElements(selectedIds);
            }
        }, [selectedIds, onGroupElements]);

        return (
            <div
                className="lpb-canvas-wrapper"
                onKeyDown={handleKeyDown}
                tabIndex={0}
                style={{ outline: 'none' }}
            >
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    style={{
                        position: 'relative',
                        width: canvasBounds.width,
                        height: canvasBounds.height,
                        minHeight: '100vh',
                        background: pageData?.canvas?.background || '#ffffff',
                        transform: `scale(${zoomLevel / 100})`,
                        transformOrigin: 'top left',
                        cursor: isOver ? 'copy' : 'default',
                    }}
                    onClick={handleCanvasClick}
                >
                    <Rulers
                        canvasWidth={canvasBounds.width}
                        canvasHeight={canvasBounds.height}
                        zoomLevel={zoomLevel}
                    />

                    <Guidelines
                        guidelines={guidelines}
                        zoomLevel={zoomLevel}
                    />

                    {showGrid && (
                        <div
                            className="lpb-canvas-grid"
                            style={{
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                backgroundImage: `
                                    linear-gradient(to right, #e5e7eb 1px, transparent 1px),
                                    linear-gradient(to bottom, #e5e7eb 1px, transparent 1px)
                                `,
                                backgroundSize: `${gridSize}px ${gridSize}px`,
                                pointerEvents: 'none',
                                opacity: 0.5
                            }}
                        />
                    )}

                    {pageData?.elements?.map((element) => (
                        <Element
                            key={element.id}
                            element={element}
                            isSelected={selectedIds.includes(element.id)}
                            onSelectElement={onSelectElement}
                            onUpdatePosition={onUpdatePosition}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={showGrid ? gridSize : Infinity}
                            onContextMenu={handleContextMenu}
                            canvasRef={canvasRef}
                            snapPoints={snapPoints}
                        />
                    ))}

                    <SelectionOverlay
                        selectedElements={pageData?.elements?.filter(el => selectedIds.includes(el.id)) || []}
                        onResize={onUpdateSize}
                        zoomLevel={zoomLevel}
                    />

                    {contextMenu && (
                        <div
                            className="lpb-context-menu"
                            style={{
                                position: 'fixed',
                                left: contextMenu.position.x,
                                top: contextMenu.position.y,
                                background: 'white',
                                border: '1px solid #ccc',
                                borderRadius: '4px',
                                boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
                                zIndex: 10000
                            }}
                            onClick={() => setContextMenu(null)}
                        >
                            <div
                                className="lpb-context-menu-item"
                                style={{
                                    padding: '8px 12px',
                                    cursor: 'pointer',
                                    borderBottom: '1px solid #eee'
                                }}
                                onClick={() => {
                                    onDeleteElement(contextMenu.id);
                                    setContextMenu(null);
                                }}
                            >
                                Delete
                            </div>
                            <div
                                className="lpb-context-menu-item"
                                style={{
                                    padding: '8px 12px',
                                    cursor: 'pointer'
                                }}
                                onClick={() => {
                                    onGroupElements([contextMenu.id]);
                                    setContextMenu(null);
                                }}
                            >
                                Group
                            </div>
                        </div>
                    )}
                </div>
            </div>
        );
    }
);

export default Canvas;
