import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import api from '@landinghub/api';
import SidebarComponent from '../components/pages-content/SidebarComponent';
import HeaderComponent from '../components/pages-content/HeaderComponent';
import CreatePagePopup from '../components/pages-content/CreatePagePopup';
import DogLoader from '../components/Loader';
import { toast } from 'react-toastify';
import '../styles/Templates.css';

const Templates = () => {
    const [userData, setUserData] = useState(null);
    const [userId, setUserId] = useState(null);
    const [templates, setTemplates] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [categoryFilter, setCategoryFilter] = useState('Tất cả');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const navigate = useNavigate();

    // Lấy danh sách template từ API
    const fetchTemplates = useCallback(async () => {
        setLoading(true);
        try {
            const response = await api.get('/api/templates');
            console.log('API /api/templates response:', response);
            if (Array.isArray(response.data)) {
                setTemplates(response.data);
            } else {
                console.error('Response data is not an array:', response.data);
                setTemplates([]);
                toast.error('Dữ liệu template không hợp lệ');
            }
        } catch (err) {
            console.error('Fetch error:', err);
            setError('Không thể tải danh sách template: ' + (err.response?.data?.error || err.message));
            setTemplates([]);
            toast.error(err.response?.data?.error || err.message);
        } finally {
            setLoading(false);
        }
    }, []);

    // Kiểm tra auth
    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No token found in localStorage');
                setError('Vui lòng đăng nhập để tiếp tục');
                setLoading(false);
                navigate('/auth');
                return;
            }
            let decoded;
            try {
                decoded = jwtDecode(token);
                if (!decoded.userId) {
                    throw new Error('Invalid token: userId not found');
                }
            } catch (err) {
                console.error('Error decoding token:', err);
                setError('Phiên đăng nhập không hợp lệ');
                setLoading(false);
                navigate('/auth');
                return;
            }
            setUserId(decoded.userId);
            api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            try {
                const res = await api.get('/api/user/info');
                setUserData(res.data);
            } catch (err) {
                console.error('Error fetching user data:', err);
                setError('Không thể tải dữ liệu người dùng: ' + (err.response?.data?.error || err.message));
            }
        };
        initializeAuth();
    }, [navigate]);

    // Lấy template khi có userId
    useEffect(() => {
        if (userId) {
            fetchTemplates();
        }
    }, [userId, fetchTemplates]);

    // Lọc template dựa trên tìm kiếm và danh mục
    const filteredTemplates = templates.filter((template) => {
        const matchesSearch = template.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesCategory = categoryFilter === 'Tất cả' || template.category === categoryFilter;
        return matchesSearch && matchesCategory;
    });

    // Xử lý chọn mẫu
    const handleSelectTemplate = (template) => {
        setIsPopupOpen(true);
        // Lưu templateId để truyền vào CreatePagePopup
        setTemplates((prev) =>
            prev.map((t) =>
                t.id === template.id ? { ...t, selected: true } : { ...t, selected: false }
            )
        );
    };

    // Xử lý tạo landing page thành công
    const handleCreateSuccess = (page) => {
        console.log('Page created from template:', page);
        navigate(`/pages/create?id=${page.id || page._id}`);
    };

    if (loading) {
        return <DogLoader />;
    }

    if (error) {
        return <div className="error">{error}</div>;
    }

    return (
        <div className="templates-container">
            <SidebarComponent activeItem="Thư viện mẫu" />
            <div className="main-content">
                <HeaderComponent onCreateClick={() => setIsPopupOpen(true)} />
                <div className="controls">
                    <input
                        type="text"
                        placeholder="Tìm kiếm template..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="search-bar"
                    />
                    <select
                        value={categoryFilter}
                        onChange={(e) => setCategoryFilter(e.target.value)}
                        className="category-filter"
                    >
                        <option>Tất cả</option>
                        <option>Bất động sản</option>
                        <option>Thương mại</option>
                        <option>Sự kiện</option>
                    </select>
                </div>
                <div className="templates-grid">
                    {filteredTemplates.length > 0 ? (
                        filteredTemplates.map((template) => (
                            <div key={template.id} className="template-card">
                                <img
                                    src={template.thumbnail_url || 'https://via.placeholder.com/300x200'}
                                    alt={template.name}
                                    className="template-thumbnail"
                                />
                                <h3>{template.name}</h3>
                                <p>{template.category || 'Không có danh mục'}</p>
                                <button
                                    onClick={() => handleSelectTemplate(template)}
                                    className="select-template-button"
                                >
                                    Chọn mẫu
                                </button>
                            </div>
                        ))
                    ) : (
                        <p>Không tìm thấy template nào.</p>
                    )}
                </div>
                <CreatePagePopup
                    isOpen={isPopupOpen}
                    onClose={() => setIsPopupOpen(false)}
                    onCreateSuccess={handleCreateSuccess}
                    selectedTemplate={templates.find((t) => t.selected)?.id}
                />
            </div>
        </div>
    );
};

export default Templates;
