import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/Templates.css';
import DogLoader from '../components/Loader';

const Templates = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [templates, setTemplates] = useState([]);
    const [error, setError] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('Tất cả');
    const [usingTemplateId, setUsingTemplateId] = useState(null);
    const [previewTemplate, setPreviewTemplate] = useState(null);
    const [showPreview, setShowPreview] = useState(false);
    const [hasMore, setHasMore] = useState(true);
    const [page, setPage] = useState(1);

    const location = useLocation();
    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';
    const categories = ['Tất cả', 'Bất động sản', 'Thương mại điện tử', 'Nhà hàng', 'Sức khỏe', 'Giáo dục', 'Khác'];

    // COPY EXACT useEffect từ Pages.js
    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No token found in localStorage');
                navigate('/auth');
                setLoading(false);
                return;
            }

            try {
                const decodedToken = jwtDecode(token);
                if (!decodedToken.role || !decodedToken.userId) {
                    console.error('Invalid token: role or userId not found');
                    navigate('/auth');
                    setLoading(false);
                    return;
                }
                setUserRole(decodedToken.role);
            } catch (err) {
                console.error('Error decoding token:', err);
                navigate('/auth');
            } finally {
                setLoading(false);
            }
        };

        if (user?.role) {
            setUserRole(user.role);
            setLoading(false);
        } else {
            initializeAuth();
        }
    }, [user, navigate]);

    useEffect(() => {
        AOS.init({
            duration: 800,
            once: true,
            offset: 100,
        });
    }, []);

    // Load templates khi có role
    useEffect(() => {
        if (userRole && ['user', 'admin'].includes(userRole)) {
            setTemplates([]);
            setPage(1);
            setHasMore(true);
            loadTemplates(1);
        }
    }, [userRole, selectedCategory]);
    


    const loadMore = () => {
        if (!loading && hasMore) {
            const nextPage = page + 1;
            setPage(nextPage);
            loadTemplates(nextPage);
        }
    };

    const filteredTemplates = templates; // Filtering done via API

    const handlePreview = async (templateId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/templates/${templateId}/preview`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setPreviewTemplate(response.data);
            setShowPreview(true);
        } catch (err) {
            console.error('Error previewing template:', err);
            alert('Không thể xem trước template');
        }
    };

    const handleUseTemplate = async (templateId, templateName) => {
        const confirmed = window.confirm(
            `Bạn có muốn tạo landing page mới từ template "${templateName}" không?`
        );
        if (!confirmed) return;

        try {
            setUsingTemplateId(templateId);
            const token = localStorage.getItem('token');
            const response = await axios.post(
                `${API_BASE_URL}/api/templates/${templateId}/use`,
                {
                    name: `${templateName} - Copy`,
                    description: `Landing page từ template ${templateName}`
                },
                { headers: { 'Authorization': `Bearer ${token}` } }
            );

            if (response.data.success) {
                alert('✅ Đã tạo landing page thành công!');
                navigate(`/pages?id=${response.data.page.id}`);
            }
        } catch (err) {
            console.error('Error using template:', err);
            const errorMsg = err.response?.data?.error || 'Không thể sử dụng template';
            alert(`❌ ${errorMsg}`);
        } finally {
            setUsingTemplateId(null);
        }
    };

    const getPlaceholderImage = (templateName, category) => {
        const colors = {
            'Bất động sản': 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            'Thương mại điện tử': 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
            'Nhà hàng': 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
            'Sức khỏe': 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)',
            'Giáo dục': 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)',
            'Khác': 'linear-gradient(135deg, #30cfd0 0%, #330867 100%)'
        };
        return {
            background: colors[category] || colors['Khác'],
            text: templateName.charAt(0).toUpperCase()
        };
    };

    if (loading) return <DogLoader />;

    // FIX: Giống Dashboard.js - Sidebar MỞ ở /templates, ĐÓNG ở /dashboard
    const isCompact = location.pathname === '/dashboard';

    return (
        <div className="pages-container">
            <Header role={userRole} />
            <div className="pages-main">
                <Sidebar role={userRole} isCompact={isCompact} />

                <div className="pages-content">
                    <div className="templates-header">
                        <h1>Thư Viện Templates</h1>
                        <p>Chọn template phù hợp và tạo landing page chuyên nghiệp trong vài phút</p>
                    </div>

                    <div className="templates-filters">
                        {categories.map(category => (
                            <button
                                key={category}
                                className={`filter-btn ${selectedCategory === category ? 'active' : ''}`}
                                onClick={() => setSelectedCategory(category)}
                            >
                                {category}
                            </button>
                        ))}
                    </div>

                    {error && (
                        <div className="templates-error">
                            <i className="fas fa-exclamation-circle"></i>
                            <span>{error}</span>
                        </div>
                    )}

                    <InfiniteScroll
                        dataLength={templates.length}
                        next={loadMore}
                        hasMore={hasMore}
                        loader={<DogLoader />}
                        endMessage={
                            <p style={{ textAlign: 'center', color: '#999', margin: '40px 0' }}>
                                <b>Đã tải hết templates!</b>
                            </p>
                        }
                        className="templates-grid"
                    >
                        {templates.map(template => {
                            const placeholder = getPlaceholderImage(template.name, template.category);
                            return (
                                <div key={template.id} className="template-card" data-aos="fade-up">
                                    <div className="template-thumbnail">
                                        {template.thumbnail_url ? (
                                            <img
                                                src={template.thumbnail_url}
                                                alt={template.name}
                                                onError={(e) => {
                                                    e.target.style.display = 'none';
                                                    e.target.nextSibling.style.display = 'flex';
                                                }}
                                            />
                                        ) : null}
                                        <div
                                            className="template-placeholder"
                                            style={{
                                                background: placeholder.background,
                                                display: template.thumbnail_url ? 'none' : 'flex'
                                            }}
                                        >
                                            <span className="placeholder-text">{placeholder.text}</span>
                                        </div>

                                        <div className="template-overlay">
                                            <button className="btn-preview" onClick={() => handlePreview(template.id)}>
                                                <i className="fas fa-eye"></i> Xem trước
                                            </button>
                                            <button
                                                className="btn-use"
                                                onClick={() => handleUseTemplate(template.id, template.name)}
                                                disabled={usingTemplateId === template.id}
                                            >
                                                {usingTemplateId === template.id ? (
                                                    <>
                                                        <i className="fas fa-spinner fa-spin"></i> Đang tạo...
                                                    </>
                                                ) : (
                                                    <>
                                                        <i className="fas fa-rocket"></i> Sử dụng ngay
                                                    </>
                                                )}
                                            </button>
                                        </div>

                                        {template.price > 0 && (
                                            <div className="template-price-badge">
                                                {template.price.toLocaleString('vi-VN')}đ
                                            </div>
                                        )}
                                        {template.price === 0 && (
                                            <div className="template-free-badge">Miễn phí</div>
                                        )}
                                    </div>

                                    <div className="template-info">
                                        <h3 className="template-name">{template.name}</h3>
                                        <p className="template-description">
                                            {template.description || 'Không có mô tả'}
                                        </p>
                                        <div className="template-meta">
                      <span className="template-category">
                        <i className="fas fa-tag"></i> {template.category}
                      </span>
                                        </div>
                                    </div>
                                </div>
                            );
                        })}
                    </InfiniteScroll>

                    {showPreview && previewTemplate && (
                        <div className="preview-modal" onClick={() => setShowPreview(false)}>
                            <div className="preview-content" onClick={(e) => e.stopPropagation()}>
                                <div className="preview-header">
                                    <h2>{previewTemplate.template?.name}</h2>
                                    <button className="btn-close" onClick={() => setShowPreview(false)}>
                                        <i className="fas fa-times"></i>
                                    </button>
                                </div>
                                <div className="preview-body">
                                    <iframe
                                        srcDoc={previewTemplate.html}
                                        title="Template Preview"
                                        className="preview-iframe"
                                    />
                                </div>
                                <div className="preview-footer">
                                    <button
                                        className="btn-use-primary"
                                        onClick={() => {
                                            setShowPreview(false);
                                            handleUseTemplate(
                                                previewTemplate.template.id,
                                                previewTemplate.template.name
                                            );
                                        }}
                                    >
                                        <i className="fas fa-rocket"></i> Sử dụng template này
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default React.memo(Templates);
