import React, { useState } from 'react';
import api from '@landinghub/api';
import { useGoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import '../styles/login.css';
import LoadingDog from '../components/DogLoader';

const Login = () => {
    const navigate = useNavigate();

    const [form, setForm] = useState({ email: '', password: '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    // Login bằng Email/Password
    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);
        try {
            const res = await api.post('/api/auth/login', form);
            const token = res.data.token;
            localStorage.setItem('token', token);
            // Giải mã token để lấy role
            const decodedToken = jwtDecode(token);
            const role = decodedToken.role;
            redirectBasedOnRole(role);
        } catch (err) {
            setError(err.response?.data?.msg || 'Đăng nhập thất bại');
        } finally {
            setLoading(false);
        }
    };

    // Login bằng Google
    const handleGoogleLogin = useGoogleLogin({
        onSuccess: async (tokenResponse) => {
            try {
                console.log('Google token response:', tokenResponse);
                if (!tokenResponse.access_token) {
                    throw new Error('access_token is missing in tokenResponse');
                }
                const userInfo = await axios.get('https://www.googleapis.com/oauth2/v3/userinfo', {
                    headers: { Authorization: `Bearer ${tokenResponse.access_token}` },
                });
                console.log('Google user info:', userInfo.data);

                const res = await api.post('/api/auth/google/callback', {
                    email: userInfo.data.email,
                    name: userInfo.data.name,
                });
                const token = res.data.token;
                localStorage.setItem('token', token);
                // Giải mã token để lấy role
                const decodedToken = jwtDecode(token);
                const role = decodedToken.role;
                redirectBasedOnRole(role);
            } catch (err) {
                console.error('Google login error:', err.response?.data || err.message);
                setError(err.response?.data?.msg || 'Google login thất bại');
            }
        },
        onError: (error) => {
            console.error('Google login failed:', error);
            setError('Google login thất bại');
        },
        flow: 'implicit',
        scope: 'openid email profile',
    });

    const redirectBasedOnRole = (role) => {
        switch (role) {
            case 'admin':
                navigate('/dashboard');
                break;
            case 'user':
            default:
                navigate('/dashboard');
                break;
        }
    };

    return (
        <div className="login-container">
            <form className="form" onSubmit={handleLogin}>
                <h2 style={{ textAlign: 'center', marginBottom: 20 }}>Đăng Nhập</h2>

                {error && (
                    <div style={{ color: 'red', marginBottom: 12, textAlign: 'center' }}>
                        {error}
                    </div>
                )}

                <div className="flex-column">
                    <label>Email</label>
                    <div className="inputForm">
                        <svg xmlns="http://www.w3.org/2000/svg" width="20" viewBox="0 0 32 32" height="20">
                            <g data-name="Layer 3" id="Layer_3">
                                <path d="m30.853 13.87a15 15 0 0 0-29.729 4.082 15.1 15.1 0 0 0 12.876 12.918 15.6 15.6 0 0 0 2.016.13 14.85 14.85 0 0 0 7.715-2.145 1 1 0 1 0-1.031-1.711 13.007 13.007 0 1 1 5.458-6.529 2.149 2.149 0 0 1-4.158-.759v-10.856a1 1 0 0 0-2 0v1.726a8 8 0 1 0.2 10.325 4.135 4.135 0 0 0 7.83.274 15.2 15.2 0 0 0.823-7.455zm-14.853 8.13a6 6 0 1 1 6-6 6.006 6.006 0 0 1-6 6z"></path>
                            </g>
                        </svg>
                        <input
                            type="email"
                            name="email"
                            value={form.email}
                            onChange={handleChange}
                            placeholder="Enter your Email"
                            className="input"
                            required
                        />
                    </div>
                </div>

                <div className="flex-column">
                    <label>Password</label>
                    <div className="inputForm">
                        <svg xmlns="http://www.w3.org/2000/svg" width="20" viewBox="-64 0 512 512" height="20">
                            <path d="m336 512h-288c-26.453125 0-48-21.523438-48-48v-224c0-26.476562 21.546875-48 48-48h288c26.453125 0 48 21.523438 48 48v224c0 26.476562-21.546875 48-48 48zm-288-288c-8.8125 0-16 7.167969-16 16v224c0 8.832031 7.1875 16 16 16h288c8.8125 0 16-7.167969 16-16v-224c0-8.832031-7.1875-16-16-16zm0 0"></path>
                            <path d="m304 224c-8.832031 0-16-7.167969-16-16v-80c0-52.929688-43.070312-96-96-96s-96 43.070312-96 96v80c0 8.832031-7.167969 16-16 16s-16-7.167969-16-16v-80c0-70.59375 57.40625-128 128-128s128 57.40625 128 128v80c0 8.832031-7.167969 16-16 16zm0 0"></path>
                        </svg>
                        <input
                            type="password"
                            name="password"
                            value={form.password}
                            onChange={handleChange}
                            placeholder="Enter your Password"
                            className="input"
                            required
                            autoComplete="current-password"
                        />
                    </div>
                </div>

                <div className="flex-row" style={{ justifyContent: 'space-between', marginBottom: '12px' }}>
                    <div>
                        <input type="checkbox" id="remember" />
                        <label htmlFor="remember">Remember me</label>
                    </div>
                    <span className="span">Forgot password?</span>
                </div>

                <button type="submit" className="button-submit" disabled={loading}>
                    {loading ? 'Đang đăng nhập...' : 'Sign In'}
                </button>
                <p className="p line">Or With</p>

                <div className="flex-row" style={{ justifyContent: 'center' }}>
                    <button
                        type="button"
                        className="btn google"
                        onClick={() => handleGoogleLogin()}
                    >
                        <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="20"
                            height="20"
                            viewBox="0 0 512 512"
                        >
                            {/* Thêm SVG icon Google nếu có */}
                        </svg>
                        Google
                    </button>
                </div>
            </form>
        </div>
    );
};

export default Login;
