import React, { useState, useCallback, useEffect } from 'react';
import { toast } from 'react-toastify';
import api from '@landinghub/api';
import '../../styles/PropertiesPanel.css';

const COLOR_PRESETS = {
    '--primary': '#667eea',
    '--primary-dark': '#5568d3',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#764ba2',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
};

const PropertiesPanel = ({ selectedElement, onUpdateElement, onUpdateCanvas, isCollapsed, onToggle, pageData, pageId }) => {
    const [activeEventType, setActiveEventType] = useState('onClick');
    const [imagePreview, setImagePreview] = useState(null);
    const [isUploading, setIsUploading] = useState(false);
    const [isSaving, setIsSaving] = useState(false);
    const [activeBreakpoint, setActiveBreakpoint] = useState('desktop');

    // Validate input for dimensions (px, %, rem, etc.)
    const validateInput = useCallback((value, type) => {
        if (type === 'dimension' && value && !/^\d+(px|%|vw|vh|rem|em)$/.test(value)) {
            return 'Vui lòng nhập giá trị hợp lệ (ví dụ: 10px, 50%, 2rem)';
        }
        return null;
    }, []);

    // Handle style changes
    const handleStyleChange = useCallback((property, value) => {
        const validationError = validateInput(value, 'dimension');
        if (validationError) {
            toast.error(validationError);
            return;
        }
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [property]: value,
                },
            },
        };
        onUpdateElement(updated);
    }, [selectedElement, onUpdateElement]);

    // Handle component data changes
    const handleComponentDataChange = useCallback((key, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    [key]: value,
                },
            },
        };
        onUpdateElement(updated);
    }, [selectedElement, onUpdateElement]);

    // Handle position changes for breakpoints
    const handlePositionChange = useCallback((breakpoint, axis, value) => {
        const validationError = validateInput(value, 'dimension');
        if (validationError) {
            toast.error(validationError);
            return;
        }
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                position: {
                    ...selectedElement.json.position,
                    [breakpoint]: {
                        ...selectedElement.json.position?.[breakpoint],
                        [axis]: value,
                    },
                },
            },
        };
        onUpdateElement(updated);
    }, [selectedElement, onUpdateElement]);

    // Handle size changes for breakpoints
    const handleSizeChange = useCallback((breakpoint, dimension, value) => {
        const validationError = validateInput(value, 'dimension');
        if (validationError) {
            toast.error(validationError);
            return;
        }
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                size: {
                    ...selectedElement.json.size,
                    [breakpoint]: {
                        ...selectedElement.json.size?.[breakpoint],
                        [dimension]: value,
                    },
                },
            },
        };
        onUpdateElement(updated);
    }, [selectedElement, onUpdateElement]);

    // Handle image upload with backend API
    const handleImageUpload = useCallback(async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        setIsUploading(true);
        try {
            const formData = new FormData();
            formData.append('file', file);
            const response = await api.post('/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            const { url } = response.data;
            setImagePreview(url);
            handleComponentDataChange('src', url);
            toast.success('Upload ảnh thành công!');
        } catch (err) {
            console.error('Upload error:', err);
            toast.error('Lỗi khi upload ảnh: ' + (err.response?.data?.error || err.message));
        } finally {
            setIsUploading(false);
        }
    }, [handleComponentDataChange]);

    // Handle event changes
    const handleEventChange = useCallback((eventType, eventProperty, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    events: {
                        ...selectedElement.json.componentData?.events,
                        [eventType]: {
                            ...selectedElement.json.componentData?.events?.[eventType],
                            [eventProperty]: value,
                        },
                    },
                },
            },
        };
        onUpdateElement(updated);
    }, [selectedElement, onUpdateElement]);

    // Handle data source changes
    const handleDataSourceChange = useCallback((property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: {
                    ...selectedElement.json.componentData,
                    dataSource: {
                        ...selectedElement.json.componentData?.dataSource,
                        [property]: value,
                    },
                },
            },
        };
        onUpdateElement(updated);
    }, [selectedElement, onUpdateElement]);

    // Save changes to backend
    const handleSaveChanges = useCallback(async () => {
        if (!pageId || !selectedElement) {
            toast.error('Không thể lưu: Thiếu pageId hoặc element');
            return;
        }

        setIsSaving(true);
        try {
            const response = await api.post('/updatePage', {
                pageId,
                html: JSON.stringify(selectedElement.json),
            });
            if (response.data.success) {
                toast.success('Lưu thay đổi thành công!');
            } else {
                toast.error('Lưu thay đổi thất bại');
            }
        } catch (err) {
            console.error('Save error:', err);
            toast.error('Lỗi khi lưu thay đổi: ' + (err.response?.data?.error || err.message));
        } finally {
            setIsSaving(false);
        }
    }, [pageId, selectedElement]);

    // Check token on mount
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            toast.error('Vui lòng đăng nhập để tiếp tục');
            return;
        }
        api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    }, []);

    const isSection = selectedElement?.json?.type === 'section' || selectedElement?.json?.type === 'container';

    if (isCollapsed) {
        return (
            <div className="properties-panel-collapsed">
                <button onClick={onToggle} className="toggle-button" title="Mở bảng thuộc tính" aria-label="Mở bảng thuộc tính">
                    <i className="fas fa-chevron-left"></i>
                </button>
            </div>
        );
    }

    if (!selectedElement || !selectedElement.json) {
        return (
            <div className="properties-panel">
                <div className="panel-header">
                    <h3 className="panel-title">Thuộc tính</h3>
                    <button onClick={onToggle} className="toggle-button" title="Thu gọn" aria-label="Thu gọn bảng thuộc tính">
                        <i className="fas fa-chevron-right"></i>
                    </button>
                </div>
                <div className="panel-empty">
                    <i className="fas fa-mouse-pointer empty-icon"></i>
                    <p className="empty-text">Chọn một element để chỉnh sửa thuộc tính</p>
                </div>
            </div>
        );
    }

    return (
        <div className="properties-panel">
            <div className="panel-header">
                <h3 className="panel-title">{selectedElement.json.type}</h3>
                <button onClick={onToggle} className="toggle-button" title="Thu gọn" aria-label="Thu gọn bảng thuộc tính">
                    <i className="fas fa-chevron-right"></i>
                </button>
            </div>
            <div className="panel-content">
                {/* Canvas Settings */}
                {!selectedElement.isChild && (
                    <div className="panel-section">
                        <h4 className="section-title"><i className="fas fa-palette"></i> Canvas</h4>
                        <div className="input-group">
                            <div>
                                <label className="input-label">Màu nền Canvas</label>
                                <div className="color-input-group">
                                    <input
                                        type="color"
                                        value={pageData.canvas?.background || '#ffffff'}
                                        onChange={(e) => onUpdateCanvas({ background: e.target.value })}
                                        className="input-color"
                                        aria-label="Chọn màu nền canvas"
                                    />
                                    <input
                                        type="text"
                                        value={pageData.canvas?.background || '#ffffff'}
                                        onChange={(e) => onUpdateCanvas({ background: e.target.value })}
                                        className="input-field"
                                        placeholder="#ffffff"
                                        aria-label="Nhập mã màu nền canvas"
                                    />
                                </div>
                                <select
                                    value={pageData.canvas?.background || '#ffffff'}
                                    onChange={(e) => onUpdateCanvas({ background: e.target.value })}
                                    className="input-select"
                                    style={{ marginTop: '8px' }}
                                    aria-label="Chọn màu preset cho canvas"
                                >
                                    <option value="">Chọn màu preset</option>
                                    {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                        <option key={key} value={value}>{key}</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                    </div>
                )}

                {/* Position and Size */}
                <div className="panel-section">
                    <h4 className="section-title"><i className="fas fa-arrows-alt"></i> Vị trí & Kích thước</h4>
                    <div className="input-group">
                        <div>
                            <label className="input-label">Breakpoint</label>
                            <select
                                value={activeBreakpoint}
                                onChange={(e) => setActiveBreakpoint(e.target.value)}
                                className="input-select"
                                aria-label="Chọn breakpoint"
                            >
                                <option value="desktop">Desktop</option>
                                <option value="tablet">Tablet</option>
                                <option value="mobile">Mobile</option>
                            </select>
                        </div>
                        <div>
                            <label className="input-label">X</label>
                            <input
                                type="text"
                                value={selectedElement.json.position?.[activeBreakpoint]?.x || '0px'}
                                onChange={(e) => handlePositionChange(activeBreakpoint, 'x', e.target.value)}
                                className="input-field"
                                placeholder="0px"
                                aria-label={`Vị trí X cho ${activeBreakpoint}`}
                            />
                        </div>
                        <div>
                            <label className="input-label">Y</label>
                            <input
                                type="text"
                                value={selectedElement.json.position?.[activeBreakpoint]?.y || '0px'}
                                onChange={(e) => handlePositionChange(activeBreakpoint, 'y', e.target.value)}
                                className="input-field"
                                placeholder="0px"
                                aria-label={`Vị trí Y cho ${activeBreakpoint}`}
                            />
                        </div>
                        <div>
                            <label className="input-label">Chiều rộng</label>
                            <input
                                type="text"
                                value={selectedElement.json.size?.[activeBreakpoint]?.width || 'auto'}
                                onChange={(e) => handleSizeChange(activeBreakpoint, 'width', e.target.value)}
                                className="input-field"
                                placeholder="auto"
                                aria-label={`Chiều rộng cho ${activeBreakpoint}`}
                            />
                        </div>
                        <div>
                            <label className="input-label">Chiều cao</label>
                            <input
                                type="text"
                                value={selectedElement.json.size?.[activeBreakpoint]?.height || 'auto'}
                                onChange={(e) => handleSizeChange(activeBreakpoint, 'height', e.target.value)}
                                className="input-field"
                                placeholder="auto"
                                aria-label={`Chiều cao cho ${activeBreakpoint}`}
                            />
                        </div>
                    </div>
                </div>

                {/* Content */}
                {(selectedElement.json.type === 'heading' ||
                    selectedElement.json.type === 'paragraph' ||
                    selectedElement.json.type === 'button') && (
                    <div className="panel-section">
                        <h4 className="section-title"><i className="fas fa-edit"></i> Nội dung</h4>
                        <textarea
                            value={selectedElement.json.componentData?.content || ''}
                            onChange={(e) => handleComponentDataChange('content', e.target.value)}
                            className="input-textarea"
                            rows={3}
                            placeholder="Nhập nội dung..."
                            aria-label="Nội dung văn bản"
                        />
                    </div>
                )}

                {/* Image Upload */}
                {selectedElement.json.type === 'image' && (
                    <div className="panel-section">
                        <h4 className="section-title"><i className="fas fa-image"></i> Hình ảnh</h4>
                        <div className="input-group">
                            <div>
                                <label className="input-label">Upload ảnh</label>
                                <input
                                    type="file"
                                    accept="image/*"
                                    onChange={handleImageUpload}
                                    style={{ display: 'none' }}
                                    id="image-upload"
                                    aria-label="Tải lên hình ảnh"
                                />
                                <label htmlFor="image-upload" className="upload-button" disabled={isUploading}>
                                    <i className="fas fa-cloud-upload-alt"></i>
                                    {isUploading ? 'Đang tải...' : 'Tải ảnh lên'}
                                </label>
                                {(imagePreview || selectedElement.json.componentData?.src) && (
                                    <div className="image-preview">
                                        <img
                                            src={imagePreview || selectedElement.json.componentData?.src}
                                            alt="Preview"
                                            style={{ maxWidth: '100%', height: 'auto' }}
                                        />
                                    </div>
                                )}
                            </div>
                            <div>
                                <label className="input-label">Hoặc nhập URL</label>
                                <input
                                    type="text"
                                    value={selectedElement.json.componentData?.src || ''}
                                    onChange={(e) => handleComponentDataChange('src', e.target.value)}
                                    className="input-field"
                                    placeholder="https://example.com/image.jpg"
                                    aria-label="URL hình ảnh"
                                />
                            </div>
                            <div>
                                <label className="input-label">Alt Text</label>
                                <input
                                    type="text"
                                    value={selectedElement.json.componentData?.alt || ''}
                                    onChange={(e) => handleComponentDataChange('alt', e.target.value)}
                                    className="input-field"
                                    placeholder="Mô tả hình ảnh"
                                    aria-label="Văn bản thay thế cho hình ảnh"
                                />
                            </div>
                        </div>
                    </div>
                )}

                {/* Section/Container Settings */}
                {isSection && (
                    <div className="panel-section">
                        <h4 className="section-title"><i className="fas fa-layer-group"></i> Section Settings</h4>
                        <div className="input-group">
                            <div>
                                <label className="input-label">Background Image URL</label>
                                <input
                                    type="text"
                                    value={selectedElement.json.componentData?.backgroundImage || ''}
                                    onChange={(e) => handleComponentDataChange('backgroundImage', e.target.value)}
                                    className="input-field"
                                    placeholder="https://example.com/image.jpg"
                                    aria-label="URL hình nền"
                                />
                            </div>
                            <div>
                                <label className="input-label">Overlay Color</label>
                                <div className="color-input-group">
                                    <input
                                        type="color"
                                        value={selectedElement.json.componentData?.overlayColor || 'transparent'}
                                        onChange={(e) => handleComponentDataChange('overlayColor', e.target.value)}
                                        className="input-color"
                                        aria-label="Chọn màu overlay"
                                    />
                                    <input
                                        type="text"
                                        value={selectedElement.json.componentData?.overlayColor || 'transparent'}
                                        onChange={(e) => handleComponentDataChange('overlayColor', e.target.value)}
                                        className="input-field"
                                        placeholder="transparent"
                                        aria-label="Nhập mã màu overlay"
                                    />
                                </div>
                            </div>
                            <div>
                                <label className="input-label">Overlay Opacity</label>
                                <input
                                    type="number"
                                    min="0"
                                    max="1"
                                    step="0.1"
                                    value={selectedElement.json.componentData?.overlayOpacity || 0}
                                    onChange={(e) => handleComponentDataChange('overlayOpacity', parseFloat(e.target.value))}
                                    className="input-field"
                                    aria-label="Độ mờ overlay"
                                />
                            </div>
                        </div>
                    </div>
                )}

                {/* Data Source Configuration */}
                <div className="panel-section">
                    <h4 className="section-title"><i className="fas fa-database"></i> Nguồn dữ liệu</h4>
                    <div className="data-source-config">
                        <div className="input-group">
                            <div>
                                <label className="input-label">Loại nguồn</label>
                                <select
                                    value={selectedElement.json.componentData?.dataSource?.type || 'static'}
                                    onChange={(e) => handleDataSourceChange('type', e.target.value)}
                                    className="input-select"
                                    aria-label="Chọn loại nguồn dữ liệu"
                                >
                                    <option value="static">Static (Tĩnh)</option>
                                    <option value="api">API</option>
                                    <option value="state">State</option>
                                </select>
                            </div>
                            {selectedElement.json.componentData?.dataSource?.type === 'api' && (
                                <>
                                    <div>
                                        <label className="input-label">API URL</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.componentData?.dataSource?.url || ''}
                                            onChange={(e) => handleDataSourceChange('url', e.target.value)}
                                            className="input-field"
                                            placeholder="https://api.example.com/data"
                                            aria-label="URL API"
                                        />
                                    </div>
                                    <div>
                                        <label className="input-label">Data Key</label>
                                        <input
                                            type="text"
                                            value={selectedElement.json.componentData?.dataSource?.key || ''}
                                            onChange={(e) => handleDataSourceChange('key', e.target.value)}
                                            className="input-field"
                                            placeholder="data.items"
                                            aria-label="Khóa dữ liệu"
                                        />
                                    </div>
                                </>
                            )}
                            {selectedElement.json.componentData?.dataSource?.type === 'state' && (
                                <div>
                                    <label className="input-label">State Key</label>
                                    <input
                                        type="text"
                                        value={selectedElement.json.componentData?.dataSource?.stateKey || ''}
                                        onChange={(e) => handleDataSourceChange('stateKey', e.target.value)}
                                        className="input-field"
                                        placeholder="userInfo"
                                        aria-label="Khóa trạng thái"
                                    />
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Events Configuration */}
                <div className="panel-section">
                    <h4 className="section-title"><i className="fas fa-bolt"></i> Sự kiện</h4>
                    <div className="event-config">
                        <div className="event-type-selector">
                            {['onClick', 'onHover', 'onLoad'].map((type) => (
                                <button
                                    key={type}
                                    className={`event-type-btn ${activeEventType === type ? 'active' : ''}`}
                                    onClick={() => setActiveEventType(type)}
                                    aria-label={`Chọn sự kiện ${type}`}
                                >
                                    {type}
                                </button>
                            ))}
                        </div>
                        <div className="input-group">
                            <div>
                                <label className="input-label">Hành động</label>
                                <select
                                    value={selectedElement.json.componentData?.events?.[activeEventType]?.type || 'none'}
                                    onChange={(e) => handleEventChange(activeEventType, 'type', e.target.value)}
                                    className="input-select"
                                    aria-label="Chọn hành động sự kiện"
                                >
                                    <option value="none">Không có</option>
                                    <option value="navigate">Điều hướng</option>
                                    <option value="api">Gọi API</option>
                                    <option value="popup">Mở Popup</option>
                                    <option value="custom">Tùy chỉnh</option>
                                </select>
                            </div>
                            {selectedElement.json.componentData?.events?.[activeEventType]?.type === 'navigate' && (
                                <div>
                                    <label className="input-label">URL</label>
                                    <input
                                        type="text"
                                        value={selectedElement.json.componentData?.events?.[activeEventType]?.url || ''}
                                        onChange={(e) => handleEventChange(activeEventType, 'url', e.target.value)}
                                        className="input-field"
                                        placeholder="/page-url"
                                        aria-label="URL điều hướng"
                                    />
                                </div>
                            )}
                            {selectedElement.json.componentData?.events?.[activeEventType]?.type === 'api' && (
                                <div>
                                    <label className="input-label">API Endpoint</label>
                                    <input
                                        type="text"
                                        value={selectedElement.json.componentData?.events?.[activeEventType]?.apiUrl || ''}
                                        onChange={(e) => handleEventChange(activeEventType, 'apiUrl', e.target.value)}
                                        className="input-field"
                                        placeholder="https://api.example.com/action"
                                        aria-label="API Endpoint"
                                    />
                                </div>
                            )}
                            {selectedElement.json.componentData?.events?.[activeEventType]?.type === 'popup' && (
                                <div>
                                    <label className="input-label">Popup ID</label>
                                    <input
                                        type="text"
                                        value={selectedElement.json.componentData?.events?.[activeEventType]?.popupId || ''}
                                        onChange={(e) => handleEventChange(activeEventType, 'popupId', e.target.value)}
                                        className="input-field"
                                        placeholder="popup-1"
                                        aria-label="ID của popup"
                                    />
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Typography */}
                {(selectedElement.json.type === 'heading' || selectedElement.json.type === 'paragraph' || selectedElement.json.type === 'button') && (
                    <div className="panel-section">
                        <h4 className="section-title"><i className="fas fa-font"></i> Typography</h4>
                        <div className="input-group">
                            <div>
                                <label className="input-label">Kích thước chữ</label>
                                <input
                                    type="text"
                                    value={selectedElement.json.styles?.fontSize || '16px'}
                                    onChange={(e) => handleStyleChange('fontSize', e.target.value)}
                                    className="input-field"
                                    placeholder="16px"
                                    aria-label="Kích thước chữ"
                                />
                            </div>
                            <div>
                                <label className="input-label">Độ dày chữ</label>
                                <select
                                    value={selectedElement.json.styles?.fontWeight || '400'}
                                    onChange={(e) => handleStyleChange('fontWeight', e.target.value)}
                                    className="input-select"
                                    aria-label="Độ dày chữ"
                                >
                                    <option value="100">Thin</option>
                                    <option value="300">Light</option>
                                    <option value="400">Regular</option>
                                    <option value="600">Semibold</option>
                                    <option value="700">Bold</option>
                                    <option value="900">Black</option>
                                </select>
                            </div>
                            <div>
                                <label className="input-label">Chiều cao dòng</label>
                                <input
                                    type="text"
                                    value={selectedElement.json.styles?.lineHeight || '1.5'}
                                    onChange={(e) => handleStyleChange('lineHeight', e.target.value)}
                                    className="input-field"
                                    placeholder="1.5"
                                    aria-label="Chiều cao dòng"
                                />
                            </div>
                            <div>
                                <label className="input-label">Căn chỉnh văn bản</label>
                                <select
                                    value={selectedElement.json.styles?.textAlign || 'left'}
                                    onChange={(e) => handleStyleChange('textAlign', e.target.value)}
                                    className="input-select"
                                    aria-label="Căn chỉnh văn bản"
                                >
                                    <option value="left">Trái</option>
                                    <option value="center">Giữa</option>
                                    <option value="right">Phải</option>
                                    <option value="justify">Cân đều</option>
                                </select>
                            </div>
                            <div>
                                <label className="input-label">Màu chữ</label>
                                <div className="color-input-group">
                                    <input
                                        type="color"
                                        value={selectedElement.json.styles?.color || '#000000'}
                                        onChange={(e) => handleStyleChange('color', e.target.value)}
                                        className="input-color"
                                        aria-label="Chọn màu chữ"
                                    />
                                    <input
                                        type="text"
                                        value={selectedElement.json.styles?.color || '#000000'}
                                        onChange={(e) => handleStyleChange('color', e.target.value)}
                                        className="input-field"
                                        placeholder="#000000"
                                        aria-label="Nhập mã màu chữ"
                                    />
                                </div>
                                <div className="color-preset-grid">
                                    {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                        <div
                                            key={key}
                                            className={`color-preset-item ${selectedElement.json.styles?.color === value ? 'active' : ''}`}
                                            style={{ backgroundColor: value }}
                                            onClick={() => handleStyleChange('color', value)}
                                            title={key}
                                            role="button"
                                            aria-label={`Chọn màu ${key}`}
                                        />
                                    ))}
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                {/* Appearance */}
                <div className="panel-section">
                    <h4 className="section-title"><i className="fas fa-paint-brush"></i> Giao diện</h4>
                    <div className="input-group">
                        <div>
                            <label className="input-label">Màu nền</label>
                            <div className="color-input-group">
                                <input
                                    type="color"
                                    value={selectedElement.json.styles?.backgroundColor || '#ffffff'}
                                    onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                    className="input-color"
                                    aria-label="Chọn màu nền"
                                />
                                <input
                                    type="text"
                                    value={selectedElement.json.styles?.backgroundColor || '#ffffff'}
                                    onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                    className="input-field"
                                    placeholder="#ffffff"
                                    aria-label="Nhập mã màu nền"
                                />
                            </div>
                            <div className="color-preset-grid">
                                {Object.entries(COLOR_PRESETS).map(([key, value]) => (
                                    <div
                                        key={key}
                                        className={`color-preset-item ${selectedElement.json.styles?.backgroundColor === value ? 'active' : ''}`}
                                        style={{ backgroundColor: value }}
                                        onClick={() => handleStyleChange('backgroundColor', value)}
                                        title={key}
                                        role="button"
                                        aria-label={`Chọn màu nền ${key}`}
                                    />
                                ))}
                            </div>
                        </div>
                        <div>
                            <label className="input-label">Padding</label>
                            <input
                                type="text"
                                value={selectedElement.json.styles?.padding || '0'}
                                onChange={(e) => handleStyleChange('padding', e.target.value)}
                                className="input-field"
                                placeholder="10px"
                                aria-label="Padding"
                            />
                        </div>
                        <div>
                            <label className="input-label">Border Radius</label>
                            <input
                                type="text"
                                value={selectedElement.json.styles?.borderRadius || '0'}
                                onChange={(e) => handleStyleChange('borderRadius', e.target.value)}
                                className="input-field"
                                placeholder="8px"
                                aria-label="Border Radius"
                            />
                        </div>
                    </div>
                </div>

                {/* Save Button */}
                <div className="panel-section">
                    <button
                        onClick={handleSaveChanges}
                        className="save-button"
                        disabled={isUploading || isSaving}
                        aria-label="Lưu thay đổi"
                    >
                        <i className="fas fa-save"></i> {isSaving ? 'Đang lưu...' : 'Lưu thay đổi'}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default PropertiesPanel;
