import React, { useRef, useCallback, useState, useEffect } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import Guidelines from './Guidelines';
import Rulers from './Rulers';
import SelectionOverlay from './SelectionOverlay';
import '../../styles/Canvas.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

// Hàm tính tọa độ, loại bỏ offsetX không cần thiết
const getCanvasPosition = (mouseX, mouseY, canvasRef, zoomLevel, scrollX = 0, scrollY = 0) => {
    if (!canvasRef.current) {
        console.error('Canvas ref is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }
    const canvasRect = canvasRef.current.getBoundingClientRect();
    const x = Math.max(0, (mouseX - canvasRect.left + scrollX) / (zoomLevel / 100));
    const y = Math.max(0, (mouseY - canvasRect.top + scrollY) / (zoomLevel / 100));
    return { x, y };
};

const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    // Snap to nearby elements or canvas bounds (ngưỡng 15px)
    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    return { x: snappedX, y: snappedY };
};

const getElementBounds = (element) => ({
    left: element.position.desktop.x,
    top: element.position.desktop.y,
    right: element.position.desktop.x + element.size.width,
    bottom: element.position.desktop.y + element.size.height,
    centerX: element.position.desktop.x + element.size.width / 2,
    centerY: element.position.desktop.y + element.size.height / 2,
});

const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {} } = element;
        const ref = useRef(null);
        const previewRef = useRef(null);
        const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

        useEffect(() => {
            if (ref.current) {
                console.log('Element ref attached for id:', id, ref.current);
            } else {
                console.warn('Element ref not attached for id:', id);
            }
        }, [id]);

        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: { id },
            end: (item, monitor) => {
                const clientOffset = monitor.getClientOffset() || monitor.getSourceClientOffset();
                if (!clientOffset || !ref.current) {
                    console.error('Invalid clientOffset or ref in drag end for id:', id);
                    return;
                }
                const canvasRef = document.querySelector('.lpb-canvas') || ref.current.closest('.lpb-canvas');
                if (!canvasRef) {
                    console.error('Cannot find .lpb-canvas in drag end for id:', id);
                    return;
                }
                const pos = getCanvasPosition(
                    clientOffset.x,
                    clientOffset.y,
                    { current: canvasRef },
                    zoomLevel,
                    canvasRef.scrollLeft,
                    canvasRef.scrollTop
                );
                // Snap to grid and other elements
                const snapPoints = [
                    { x: 0, y: 0 }, // Canvas top-left
                    { x: canvasBounds.width, y: canvasBounds.height }, // Canvas bottom-right
                    { x: canvasBounds.width / 2, y: canvasBounds.height / 2 }, // Canvas center
                ];
                const snapped = snapToGrid(pos.x - dragOffset.x, pos.y - dragOffset.y, gridSize, snapPoints);
                console.group('Drag End Debug');
                console.log('Client Offset:', JSON.stringify(clientOffset, null, 2));
                console.log('Canvas Rect:', JSON.stringify(canvasRef.getBoundingClientRect(), null, 2));
                console.log('Scroll:', { scrollX: canvasRef.scrollLeft, scrollY: canvasRef.scrollTop });
                console.log('Canvas Position:', JSON.stringify(pos, null, 2));
                console.log('Drag Offset:', JSON.stringify(dragOffset, null, 2));
                console.log('Snapped Position:', JSON.stringify(snapped, null, 2));
                console.groupEnd();
                onUpdatePosition(id, { [viewMode]: snapped });
                console.log('Updated pageData:', JSON.stringify(element, null, 2));
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        useEffect(() => {
            if (previewRef.current) {
                preview(previewRef.current);
            }
        }, []);

        const handleMouseDown = (e) => {
            e.stopPropagation();
            if (!ref.current) {
                console.error('Element ref is null in handleMouseDown for id:', id);
                return;
            }
            const canvasRef = document.querySelector('.lpb-canvas') || ref.current.closest('.lpb-canvas');
            if (!canvasRef) {
                console.error('Cannot find .lpb-canvas in handleMouseDown for id:', id);
                return;
            }
            const elementRect = ref.current.getBoundingClientRect();
            const canvasRect = canvasRef.getBoundingClientRect();
            const offsetX = (e.clientX - elementRect.left) / (zoomLevel / 100);
            const offsetY = (e.clientY - elementRect.top) / (zoomLevel / 100);
            setDragOffset({ x: offsetX, y: offsetY });
            console.group('Mouse Down Debug');
            console.log('Mouse Position:', { x: e.clientX, y: e.clientY });
            console.log('Element Rect:', JSON.stringify(elementRect, null, 2));
            console.log('Canvas Rect:', JSON.stringify(canvasRect, null, 2));
            console.log('Scroll:', { scrollX: canvasRef.scrollLeft, scrollY: canvasRef.scrollTop });
            console.log('Offset:', { x: offsetX, y: offsetY });
            console.groupEnd();
            onSelectElement(id, e.ctrlKey);
        };

        const handleContextMenu = (e) => {
            e.preventDefault();
            e.stopPropagation();
            onContextMenu(id, { x: e.clientX, y: e.clientY });
        };

        return (
            <>
                <div
                    ref={drag}
                    className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                    style={{
                        transform: `translate(${position[viewMode]?.x || 0}px, ${position[viewMode]?.y || 0}px)`,
                        width: size.width,
                        height: size.height,
                        zIndex: position.z || 1,
                        ...styles,
                    }}
                    onMouseDown={handleMouseDown}
                    onContextMenu={handleContextMenu}
                >
                    {(() => {
                        switch (type) {
                            case 'heading':
                                return <h2 style={{ fontSize: componentData.fontSize || '1.5rem', color: componentData.color || '#1f2937' }}>{componentData.content || 'Tiêu đề'}</h2>;
                            case 'paragraph':
                                return <p style={{ fontSize: componentData.fontSize || '1rem', color: componentData.color || '#1f2937' }}>{componentData.content || 'Đoạn văn'}</p>;
                            case 'button':
                                return <button style={{ padding: '8px 16px', borderRadius: '8px', background: componentData.background || '#2563eb', color: componentData.color || '#fff' }}>{componentData.content || 'Nút'}</button>;
                            case 'image':
                                return <img src={componentData.src || 'https://via.placeholder.com/150'} alt="Image" style={{ maxWidth: '100%', height: 'auto' }} />;
                            case 'form':
                                return (
                                    <form style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                                        <input type="text" placeholder="Input" style={{ padding: '8px', borderRadius: '8px' }} />
                                        <button type="submit" style={{ background: '#2563eb', color: '#fff', padding: '8px 16px', borderRadius: '8px' }}>Submit</button>
                                    </form>
                                );
                            default:
                                return <div>{componentData.content || 'Unknown'}</div>;
                        }
                    })()}
                </div>
                {isDragging && (
                    <div
                        ref={previewRef}
                        className="lpb-canvas-element lpb-canvas-element-dragging"
                        style={{
                            transform: `translate(${position[viewMode]?.x || 0}px, ${position[viewMode]?.y || 0}px)`,
                            width: size.width,
                            height: size.height,
                            zIndex: 1000,
                            opacity: 0.5,
                            ...styles,
                        }}
                    >
                        {(() => {
                            switch (type) {
                                case 'heading':
                                    return <h2 style={{ fontSize: componentData.fontSize || '1.5rem', color: componentData.color || '#1f2937' }}>{componentData.content || 'Tiêu đề'}</h2>;
                                case 'paragraph':
                                    return <p style={{ fontSize: componentData.fontSize || '1rem', color: componentData.color || '#1f2937' }}>{componentData.content || 'Đoạn văn'}</p>;
                                case 'button':
                                    return <button style={{ padding: '8px 16px', borderRadius: '8px', background: componentData.background || '#2563eb', color: componentData.color || '#fff' }}>{componentData.content || 'Nút'}</button>;
                                case 'image':
                                    return <img src={componentData.src || 'https://via.placeholder.com/150'} alt="Image" style={{ maxWidth: '100%', height: 'auto' }} />;
                                case 'form':
                                    return (
                                        <form style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                                            <input type="text" placeholder="Input" style={{ padding: '8px', borderRadius: '8px' }} />
                                            <button type="submit" style={{ background: '#2563eb', color: '#fff', padding: '8px 16px', borderRadius: '8px' }}>Submit</button>
                                        </form>
                                    );
                                default:
                                    return <div>{componentData.content || 'Unknown'}</div>;
                            }
                        })()}
                    </div>
                )}
            </>
        );
    }
);

const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         onGroupElements,
         viewMode,
         zoomLevel,
         gridSize,
         showGrid,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);
        const [contextMenu, setContextMenu] = useState(null);

        useEffect(() => {
            if (canvasRef.current) {
                console.log('Canvas ref attached:', canvasRef.current);
            } else {
                console.warn('Canvas ref not attached');
            }
        }, []);

        const canvasBounds = canvasRef.current
            ? { width: canvasRef.current.getBoundingClientRect().width, height: canvasRef.current.getBoundingClientRect().height }
            : { width: 1200, height: 1000 };

        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) return;
                const pos = getCanvasPosition(
                    clientOffset.x,
                    clientOffset.y,
                    canvasRef,
                    zoomLevel,
                    canvasRef.current.scrollLeft,
                    canvasRef.current.scrollTop
                );
                const snapPoints = [
                    { x: 0, y: 0 }, // Canvas top-left
                    { x: canvasBounds.width, y: canvasBounds.height }, // Canvas bottom-right
                    { x: canvasBounds.width / 2, y: canvasBounds.height / 2 }, // Canvas center
                    ...pageData.elements.flatMap((el) => {
                        const bounds = getElementBounds(el);
                        return [
                            { x: bounds.left, y: bounds.top },
                            { x: bounds.right, y: bounds.bottom },
                            { x: bounds.centerX, y: bounds.centerY },
                        ];
                    }),
                ];
                const snapped = snapToGrid(pos.x, pos.y, gridSize, snapPoints);
                const newGuidelines = [];
                pageData.elements.forEach((el) => {
                    const bounds = getElementBounds(el);
                    if (Math.abs(snapped.x - bounds.left) < 15) newGuidelines.push({ vertical: true, position: bounds.left });
                    if (Math.abs(snapped.x - bounds.right) < 15) newGuidelines.push({ vertical: true, position: bounds.right });
                    if (Math.abs(snapped.x - bounds.centerX) < 15) newGuidelines.push({ vertical: true, position: bounds.centerX });
                    if (Math.abs(snapped.y - bounds.top) < 15) newGuidelines.push({ vertical: false, position: bounds.top });
                    if (Math.abs(snapped.y - bounds.bottom) < 15) newGuidelines.push({ vertical: false, position: bounds.bottom });
                    if (Math.abs(snapped.y - bounds.centerY) < 15) newGuidelines.push({ vertical: false, position: bounds.centerY });
                });
                // Add canvas boundary guidelines
                if (Math.abs(snapped.x) < 15) newGuidelines.push({ vertical: true, position: 0 });
                if (Math.abs(snapped.x - canvasBounds.width) < 15) newGuidelines.push({ vertical: true, position: canvasBounds.width });
                if (Math.abs(snapped.x - canvasBounds.width / 2) < 15) newGuidelines.push({ vertical: true, position: canvasBounds.width / 2 });
                if (Math.abs(snapped.y) < 15) newGuidelines.push({ vertical: false, position: 0 });
                if (Math.abs(snapped.y - canvasBounds.height) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height });
                if (Math.abs(snapped.y - canvasBounds.height / 2) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height / 2 });
                setGuidelines(newGuidelines);
                console.group('Hover Debug');
                console.log('Hover - Client Offset:', JSON.stringify(clientOffset, null, 2));
                console.log('Hover - Canvas Rect:', JSON.stringify(canvasRef.current.getBoundingClientRect(), null, 2));
                console.log('Hover - Scroll:', { scrollX: canvasRef.current.scrollLeft, scrollY: canvasRef.current.scrollTop });
                console.log('Hover - Canvas Position:', JSON.stringify(pos, null, 2));
                console.log('Hover - Snapped:', JSON.stringify(snapped, null, 2));
                console.log('Hover - Snap Points:', JSON.stringify(snapPoints, null, 2));
                console.log('Guidelines:', JSON.stringify(newGuidelines, null, 2));
                console.groupEnd();
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả!');
                    return;
                }
                const pos = getCanvasPosition(
                    clientOffset.x,
                    clientOffset.y,
                    canvasRef,
                    zoomLevel,
                    canvasRef.current.scrollLeft,
                    canvasRef.current.scrollTop
                );
                const snapPoints = [
                    { x: 0, y: 0 },
                    { x: canvasBounds.width, y: canvasBounds.height },
                    { x: canvasBounds.width / 2, y: canvasBounds.height / 2 },
                    ...pageData.elements.flatMap((el) => {
                        const bounds = getElementBounds(el);
                        return [
                            { x: bounds.left, y: bounds.top },
                            { x: bounds.right, y: bounds.bottom },
                            { x: bounds.centerX, y: bounds.centerY },
                        ];
                    }),
                ];
                const snapped = snapToGrid(pos.x, pos.y, gridSize, snapPoints);
                console.group('Drop Debug');
                console.log('Drop - Client Offset:', JSON.stringify(clientOffset, null, 2));
                console.log('Drop - Canvas Rect:', JSON.stringify(canvasRef.current.getBoundingClientRect(), null, 2));
                console.log('Drop - Scroll:', { scrollX: canvasRef.current.scrollLeft, scrollY: canvasRef.current.scrollTop });
                console.log('Drop - Canvas Position:', JSON.stringify(pos, null, 2));
                console.log('Drop - Snapped Position:', JSON.stringify(snapped, null, 2));
                console.log('Drop - Snap Points:', JSON.stringify(snapPoints, null, 2));
                console.groupEnd();
                if (item.json) {
                    onAddElement({
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: item.json.componentData,
                        position: { [viewMode]: snapped, desktop: snapped, tablet: snapped, mobile: snapped },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                    });
                    toast.success('Đã thêm phần tử!');
                } else if (item.id) {
                    onUpdatePosition(item.id, { [viewMode]: snapped });
                    toast.success('Đã di chuyển phần tử!');
                }
                setGuidelines([]);
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        const handleContextMenu = (id, position) => {
            setContextMenu({ id, position });
        };

        const handleKeyDown = useCallback((e) => {
            e.stopPropagation();
            if (!selectedIds.length) return;
            if (e.key === 'ArrowUp') {
                onUpdatePosition(selectedIds, { [viewMode]: { y: -1 * (e.shiftKey ? 10 : 1) } });
            } else if (e.key === 'ArrowDown') {
                onUpdatePosition(selectedIds, { [viewMode]: { y: 1 * (e.shiftKey ? 10 : 1) } });
            } else if (e.key === 'ArrowLeft') {
                onUpdatePosition(selectedIds, { [viewMode]: { x: -1 * (e.shiftKey ? 10 : 1) } });
            } else if (e.key === 'ArrowRight') {
                onUpdatePosition(selectedIds, { [viewMode]: { x: 1 * (e.shiftKey ? 10 : 1) } });
            } else if (e.key === 'Delete') {
                selectedIds.forEach((id) => onDeleteElement(id));
            } else if (e.key === 'Escape') {
                onSelectElement([]);
            }
        }, [selectedIds, onUpdatePosition, onDeleteElement, onSelectElement, viewMode]);

        useHotkeys('ctrl+a', () => onSelectElement(pageData.elements.map((el) => el.id)), [pageData.elements, onSelectElement]);
        useHotkeys('ctrl+g', () => onGroupElements(selectedIds), [selectedIds, onGroupElements]);

        return (
            <div className="lpb-canvas-wrapper" onKeyDown={handleKeyDown} tabIndex={0}>
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    style={{
                        minHeight: '100%',
                        background: pageData.canvas.background,
                        transform: `scale(${zoomLevel / 100})`,
                        transformOrigin: 'top left',
                    }}
                >
                    <Rulers canvasWidth={pageData.canvas.width} canvasHeight={1000} zoomLevel={zoomLevel} />
                    <Guidelines guidelines={guidelines} zoomLevel={zoomLevel} />
                    {pageData.elements.map((element) => (
                        <Element
                            key={element.id}
                            element={element}
                            isSelected={selectedIds.includes(element.id)}
                            onSelectElement={onSelectElement}
                            onUpdatePosition={onUpdatePosition}
                            onUpdateSize={onUpdateSize}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={showGrid ? gridSize : Infinity}
                            onContextMenu={handleContextMenu}
                            canvasBounds={canvasBounds}
                        />
                    ))}
                    <SelectionOverlay
                        selectedElements={pageData.elements.filter((el) => selectedIds.includes(el.id))}
                        onResize={onUpdateSize}
                        zoomLevel={zoomLevel}
                    />
                    {contextMenu && (
                        <div
                            className="lpb-context-menu"
                            style={{ left: contextMenu.position.x, top: contextMenu.position.y }}
                        >
                            <div className="lpb-context-menu-item" onClick={() => onDeleteElement(contextMenu.id)}>Delete</div>
                            <div className="lpb-context-menu-item" onClick={() => onGroupElements([contextMenu.id])}>Group</div>
                        </div>
                    )}
                </div>
                {/* Visual Debug: Canvas Boundary */}
                <div
                    style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        border: '2px solid red',
                        pointerEvents: 'none',
                        zIndex: 9999,
                    }}
                />
            </div>
        );
    }
);

export default Canvas;
