import React, { useRef, useCallback, useState, useEffect } from 'react';
import { useDrop, useDrag, DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '@landinghub/api';
import Guidelines from './Guidelines';
import SelectionOverlay from './SelectionOverlay';
import QuickEditToolkit from './QuickEditToolkit';
import ComponentLibrary from './ComponentLibrary';
import { EXTENDED_LIBRARY } from '../../constants/ExtendedLibrary';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
    CHILD_ELEMENT: 'childElement',
};

// Tính toán tọa độ chính xác trong canvas
const getCanvasPosition = (mouseX, mouseY, canvasElement, zoomLevel) => {
    if (!canvasElement) {
        console.error('Canvas element is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }

    const canvasRect = canvasElement.getBoundingClientRect();
    const scrollX = canvasElement.scrollLeft || 0;
    const scrollY = canvasElement.scrollTop || 0;

    const rawX = (mouseX - canvasRect.left + scrollX) / (zoomLevel / 100);
    const rawY = (mouseY - canvasRect.top + scrollY) / (zoomLevel / 100);

    const x = Math.max(0, rawX);
    const y = Math.max(0, rawY);

    return { x, y };
};

// Snap to grid với snap points
const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    return { x: snappedX, y: snappedY };
};

// Tính bounds của element
const getElementBounds = (element) => ({
    left: element.position.desktop?.x || 0,
    top: element.position.desktop?.y || 0,
    right: (element.position.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

// Render nội dung component
const renderComponentContent = (type, componentData, styles, children = []) => {
    console.log('renderComponentContent:', { type, componentData, children });
    switch (type) {
        case 'heading':
            return (
                <h2 style={{ fontSize: componentData.fontSize || '1.5rem', color: componentData.color || '#1f2937', margin: 0, padding: 0, ...styles }}>
                    {componentData.content || 'Tiêu đề'}
                </h2>
            );
        case 'paragraph':
            return (
                <p style={{ fontSize: componentData.fontSize || '1rem', color: componentData.color || '#1f2937', margin: 0, padding: 0, ...styles }}>
                    {componentData.content || 'Đoạn văn'}
                </p>
            );
        case 'button':
            return (
                <button style={{ padding: '8px 16px', borderRadius: '8px', background: componentData.background || '#2563eb', color: componentData.color || '#fff', border: 'none', cursor: 'pointer', ...styles }}>
                    {componentData.content || 'Nút'}
                </button>
            );
        case 'image':
            return (
                <img
                    src={componentData.src || 'https://via.placeholder.com/150'}
                    alt={componentData.alt || 'Image'}
                    style={{ maxWidth: '100%', height: 'auto', ...styles }}
                    draggable={false}
                />
            );
        case 'form':
            return (
                <form style={{ display: 'flex', flexDirection: 'column', gap: '10px', ...styles }}>
                    {componentData.title && !children.some(child => child.type === 'heading') && (
                        <h3 style={{ margin: 0, fontSize: '1.2rem' }}>{componentData.title}</h3>
                    )}
                    {componentData.fields ? (
                        componentData.fields.map((field, index) => (
                            <input
                                key={index}
                                type="text"
                                placeholder={field}
                                style={{ padding: '8px', borderRadius: '8px', border: '1px solid #ccc' }}
                            />
                        ))
                    ) : (
                        !children.some(child => child.type === 'input') && (
                            <input
                                type="text"
                                placeholder={componentData.placeholder || 'Input'}
                                style={{ padding: '8px', borderRadius: '8px', border: '1px solid #ccc' }}
                            />
                        )
                    )}
                    {!children.some(child => child.type === 'button') && (
                        <button
                            type="submit"
                            style={{
                                background: '#2563eb',
                                color: '#fff',
                                padding: '8px 16px',
                                borderRadius: '8px',
                                border: 'none',
                                cursor: 'pointer',
                            }}
                        >
                            {componentData.buttonText || 'Submit'}
                        </button>
                    )}
                </form>
            );
        case 'section':
        case 'container':
            return (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', ...styles }}>
                    {componentData.title && !children.some(child => child.type === 'heading') && (
                        <h2 style={{ fontSize: '1.8rem', margin: 0 }}>{componentData.title}</h2>
                    )}
                    {componentData.subtitle && !children.some(child => child.type === 'paragraph') && (
                        <p style={{ fontSize: '1.2rem', margin: 0 }}>{componentData.subtitle}</p>
                    )}
                    {componentData.content && !children.some(child => child.type === 'paragraph') && (
                        <p style={{ fontSize: '1rem', margin: 0 }}>{componentData.content}</p>
                    )}
                    {componentData.features && !children.some(child => child.type === 'list') && (
                        <ul style={{ listStyle: 'none', padding: 0, margin: 0, display: 'flex', flexDirection: 'column', gap: '8px' }}>
                            {componentData.features.map((feature, index) => (
                                <li key={index} style={{ fontSize: '1rem' }}>{feature}</li>
                            ))}
                        </ul>
                    )}
                    {componentData.ctaText && !children.some(child => child.type === 'button') && (
                        <button style={{ padding: '10px 20px', borderRadius: '8px', background: '#2563eb', color: '#fff', border: 'none', cursor: 'pointer' }}>
                            {componentData.ctaText}
                        </button>
                    )}
                    {componentData.contact && !children.some(child => child.type === 'paragraph') && (
                        <p style={{ fontSize: '0.9rem', margin: 0 }}>{componentData.contact}</p>
                    )}
                </div>
            );
        case 'divider':
            return <hr style={{ border: 'none', background: '#e5e7eb', height: '2px', ...styles }} />;
        case 'spacer':
            return <div style={{ background: 'transparent', ...styles }} />;
        case 'popup':
            return (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', textAlign: 'center', ...styles }}>
                    {componentData.title && !children.some(child => child.type === 'heading') && (
                        <h3 style={{ fontSize: '1.5rem', margin: 0 }}>{componentData.title}</h3>
                    )}
                    {componentData.content && !children.some(child => child.type === 'paragraph') && (
                        <p style={{ fontSize: '1rem', margin: 0 }}>{componentData.content}</p>
                    )}
                    {componentData.buttonText && !children.some(child => child.type === 'button') && (
                        <button style={{ padding: '10px 20px', borderRadius: '8px', background: '#2563eb', color: '#fff', border: 'none', cursor: 'pointer' }}>
                            {componentData.buttonText}
                        </button>
                    )}
                </div>
            );
        case 'modal':
            return (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', ...styles }}>
                    {componentData.title && !children.some(child => child.type === 'heading') && (
                        <h3 style={{ fontSize: '1.5rem', margin: 0 }}>{componentData.title}</h3>
                    )}
                    {componentData.content && !children.some(child => child.type === 'paragraph') && (
                        <p style={{ fontSize: '1rem', margin: 0 }}>{componentData.content}</p>
                    )}
                    {componentData.closeButton && !children.some(child => child.type === 'button') && (
                        <button style={{ padding: '8px 16px', borderRadius: '8px', background: '#6b7280', color: '#fff', border: 'none', cursor: 'pointer', alignSelf: 'flex-end' }}>
                            Close
                        </button>
                    )}
                </div>
            );
        default:
            return <div style={{ color: '#ff0000', ...styles }}>Unknown Component: {type}</div>;
    }
};

// Component ChildElement
const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         content,
         componentData = {},
         styles = {},
         index,
         isSelected,
         onSelectChild,
         onUpdateChildren,
         zoomLevel,
         gridSize,
         showGrid,
     }) => {
        const dragRef = useRef(null);
        const dropRef = useRef(null);

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.CHILD_ELEMENT,
            item: () => {
                console.log('ChildElement drag started:', { childId: id, parentId, index });
                return { childId: id, parentId, index, isExisting: true };
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }));

        const [{ isOver, canDrop }, drop] = useDrop(() => ({
            accept: ItemTypes.CHILD_ELEMENT,
            canDrop: (item) => item.parentId === parentId,
            drop: (item, monitor) => {
                if (!monitor.canDrop()) {
                    console.warn('Drop not allowed: Different parent or same position');
                    toast.error('Không thể di chuyển thành phần con sang section khác!');
                    return { moved: false };
                }
                console.log('ChildElement drop:', {
                    childId: item.childId,
                    parentId,
                    dragIndex: item.index,
                    dropIndex: index,
                });
                if (item.index !== index) {
                    console.log('Calling onUpdateChildren:', { parentId, dragIndex: item.index, dropIndex: index });
                    try {
                        onUpdateChildren(parentId, item.index, index);
                        toast.success('Đã di chuyển thành phần con!');
                        return { moved: true, parentId, dropIndex: index };
                    } catch (error) {
                        console.error('Error in onUpdateChildren:', error);
                        toast.error('Lỗi khi di chuyển thành phần con!');
                        return { moved: false };
                    }
                }
                return { moved: false };
            },
            collect: (monitor) => ({
                isOver: monitor.isOver(),
                canDrop: monitor.canDrop(),
            }),
        }));

        const handleClick = (e) => {
            e.stopPropagation();
            console.log('ChildElement clicked:', { parentId, id, isSelected });
            if (typeof onSelectChild === 'function') {
                onSelectChild(parentId, id);
            } else {
                console.warn('onSelectChild is not a function');
            }
        };

        useEffect(() => {
            drag(dragRef);
            drop(dropRef);
        }, [drag, drop]);

        return (
            <div
                ref={dropRef}
                className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''} ${isDragging ? 'lpb-child-element-dragging' : ''} ${isOver && canDrop ? 'lpb-child-element-hover' : ''}`}
                style={{
                    position: 'relative',
                    margin: '5px 0',
                    padding: '5px',
                    cursor: isDragging ? 'grabbing' : 'grab',
                    opacity: isDragging ? 0.5 : 1,
                    borderBottom: isOver && canDrop ? '2px solid #2563eb' : 'none',
                    ...styles,
                }}
                onClick={handleClick}
            >
                <div ref={dragRef}>
                    {renderComponentContent(type, { content, ...componentData }, styles)}
                </div>
                {isSelected && (
                    <QuickEditToolkit
                        element={{ id, type, content, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => console.log('Edit child:', id)}
                        onDuplicate={() => console.log('Duplicate child:', id)}
                        onDelete={() => console.log('Delete child:', id)}
                        onMoveUp={() => console.log('Move up child:', id)}
                        onMoveDown={() => console.log('Move down child:', id)}
                        onClose={() => onSelectChild(parentId, null)}
                    />
                )}
            </div>
        );
    }
);

// Component Element
const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onUpdateChildren,
         showGrid,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {}, children = [] } = element;
        const elementRef = useRef(null);
        const [isDragStarted, setIsDragStarted] = useState(false);

        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: () => {
                setIsDragStarted(true);
                console.log('Element drag started:', { id, position: position[viewMode] });
                return {
                    id,
                    elementSize: size,
                    elementPosition: position[viewMode] || { x: 0, y: 0 },
                    isExisting: true,
                };
            },
            end: (item, monitor) => {
                setIsDragStarted(false);
                const dropResult = monitor.getDropResult();
                if (!dropResult || !dropResult.moved) {
                    console.log('Drag cancelled or invalid drop for element:', id);
                    return;
                }
                console.log('Drag ended successfully for element:', id, 'New position:', dropResult.newPosition);
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, position, viewMode]);

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        const handleMouseDown = useCallback(
            (e) => {
                e.stopPropagation();
                onSelectElement(id, e.ctrlKey);
                onSelectChild(id, null);
            },
            [id, onSelectElement, onSelectChild]
        );

        const handleContextMenu = useCallback(
            (e) => {
                e.preventDefault();
                e.stopPropagation();
                onContextMenu(id, { x: e.clientX, y: e.clientY });
            },
            [id, onContextMenu]
        );

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    drag(node);
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                style={{
                    position: 'absolute',
                    transform: `translate(${position[viewMode]?.x || 0}px, ${position[viewMode]?.y || 0}px)`,
                    width: size.width,
                    height: size.height,
                    zIndex: position.z || 1,
                    opacity: isDragging ? 0.5 : 1,
                    cursor: isDragging ? 'grabbing' : 'grab',
                    userSelect: 'none',
                    ...styles,
                }}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {renderComponentContent(type, componentData, styles, children)}
                {children.map((child, index) => (
                    <ChildElement
                        key={child.id}
                        parentId={id}
                        id={child.id}
                        type={child.type}
                        content={child.content}
                        componentData={child.componentData || {}}
                        styles={child.styles || {}}
                        index={index}
                        isSelected={selectedChildId === child.id}
                        onSelectChild={onSelectChild}
                        onUpdateChildren={onUpdateChildren}
                        zoomLevel={zoomLevel}
                        gridSize={gridSize}
                        showGrid={showGrid}
                    />
                ))}
            </div>
        );
    }
);

// Component Canvas
const Canvas = ({ viewMode = 'desktop', zoomLevel = 100, gridSize = 10, showGrid = true }) => {
    const canvasRef = useRef(null);
    const [guidelines, setGuidelines] = useState([]);
    const [contextMenu, setContextMenu] = useState(null);
    const [dragPreview, setDragPreview] = useState(null);
    const [pageData, setPageData] = useState({ elements: [] });
    const [selectedIds, setSelectedIds] = useState([]);
    const [selectedChildId, setSelectedChildId] = useState(null);
    const [pageInfo, setPageInfo] = useState({ name: '', file_path: '', created_at: null, updated_at: null });
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();
    const location = useLocation();

    const pageId = new URLSearchParams(location.search).get('id');

    const canvasBounds = canvasRef.current
        ? {
            width: canvasRef.current.offsetWidth / (zoomLevel / 100),
            height: canvasRef.current.offsetHeight / (zoomLevel / 100),
        }
        : { width: 1200, height: 1000 };

    useEffect(() => {
        if (pageId) {
            const fetchPageData = async () => {
                try {
                    const response = await api.get(`/api/pages/${pageId}`);
                    console.log('Phản hồi lấy dữ liệu trang:', response.data);
                    if (response.data.success && response.data.page) {
                        const { name, file_path, html, created_at, updated_at } = response.data.page;
                        setPageInfo({ name, file_path, created_at, updated_at });
                        // Chuyển HTML thành pageData.elements
                        setPageData({ elements: parseHtmlToPageData(html) });
                    } else {
                        toast.error('Không thể tải dữ liệu trang');
                    }
                } catch (err) {
                    console.error('Lỗi lấy dữ liệu trang:', err);
                    toast.error('Lỗi khi tải trang: ' + (err.response?.data?.error || err.message));
                    navigate('/dashboard');
                } finally {
                    setLoading(false);
                }
            };
            fetchPageData();
        } else {
            setLoading(false);
        }
    }, [pageId, navigate]);

    const parseHtmlToPageData = (html) => {
        // Chuyển HTML thành cấu trúc pageData.elements
        // Đây là một ví dụ đơn giản, bạn có thể cải tiến bằng cách dùng parser như html-to-json
        if (!html) return [];
        return [
            {
                id: `section-${Date.now()}`,
                type: 'section',
                componentData: { content: 'Section mặc định' },
                position: { desktop: { x: 0, y: 0 }, tablet: { x: 0, y: 0 }, mobile: { x: 0, y: 0 } },
                size: { width: 1200, height: 600 },
                styles: { background: '#ffffff' },
                children: [
                    {
                        id: `child-${Date.now()}`,
                        type: 'paragraph',
                        content: html,
                        componentData: { content: html },
                        styles: { fontSize: '1rem', color: '#1f2937' },
                    },
                ],
            },
        ];
    };

    const generateHtml = () => {
        // Chuyển pageData.elements thành HTML
        return pageData.elements.map((element) => {
            const childrenHtml = element.children
                ? element.children.map(child => renderComponentContent(child.type, child.componentData, child.styles)).join('')
                : '';
            return `<div style="position: absolute; transform: translate(${element.position[viewMode]?.x || 0}px, ${element.position[viewMode]?.y || 0}px); width: ${element.size.width}px; height: ${element.size.height}px; ${Object.entries(element.styles).map(([k, v]) => `${k}: ${v};`).join(' ')}">${renderComponentContent(element.type, element.componentData, element.styles)}${childrenHtml}</div>`;
        }).join('');
    };

    const handleSave = async () => {
        if (!pageId) {
            toast.error('Không tìm thấy pageId');
            return;
        }

        const html = generateHtml();
        try {
            console.log('Lưu trang với HTML:', html);
            const response = await api.put(`/api/pages/${pageId}`, { html });
            console.log('Phản hồi lưu:', response.data);
            if (response.data.success) {
                toast.success('Lưu trang thành công!');
                setPageInfo((prev) => ({
                    ...prev,
                    updated_at: response.data.page.updated_at,
                }));
            } else {
                toast.error('Lưu trang thất bại');
            }
        } catch (err) {
            console.error('Lỗi lưu:', err);
            toast.error('Lỗi khi lưu trang: ' + (err.response?.data?.error || err.message));
        }
    };

    const formatDate = (date) => {
        if (!date) return 'Chưa có';
        return new Date(date).toLocaleString('vi-VN', {
            timeZone: 'Asia/Ho_Chi_Minh',
            year: 'numeric',
            month: '2-digit',
            day: '2-digit',
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit',
        });
    };

    const getSnapPoints = useCallback(() => {
        const points = [
            { x: 0, y: 0 },
            { x: canvasBounds.width, y: canvasBounds.height },
            { x: canvasBounds.width / 2, y: canvasBounds.height / 2 },
        ];

        pageData.elements.forEach((el) => {
            const bounds = getElementBounds(el);
            points.push(
                { x: bounds.left, y: bounds.top },
                { x: bounds.right, y: bounds.bottom },
                { x: bounds.centerX, y: bounds.centerY },
                { x: bounds.left, y: bounds.centerY },
                { x: bounds.right, y: bounds.centerY },
                { x: bounds.centerX, y: bounds.top },
                { x: bounds.centerX, y: bounds.bottom }
            );
        });

        return points;
    }, [pageData.elements, canvasBounds]);

    const [{ isOver, dragItem }, drop] = useDrop(() => ({
        accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
        hover: (item, monitor) => {
            if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                console.log('Bỏ qua CHILD_ELEMENT hover trong Canvas');
                return undefined;
            }
            const clientOffset = monitor.getClientOffset();
            if (!clientOffset || !canvasRef.current) return;

            const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
            const snapPoints = getSnapPoints();
            const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

            if (monitor.getItemType() === ItemTypes.EXISTING_ELEMENT && item.isExisting) {
                const draggingElement = pageData.elements.find((el) => el.id === item.id);
                if (draggingElement) {
                    setDragPreview({
                        id: item.id,
                        x: snapped.x,
                        y: snapped.y,
                        size: draggingElement.size || { width: 200, height: 50 },
                        type: draggingElement.type,
                        componentData: draggingElement.componentData,
                        styles: draggingElement.styles,
                    });
                }
            } else if (item.json) {
                setDragPreview({
                    id: 'preview',
                    x: snapped.x,
                    y: snapped.y,
                    size: item.json.size || { width: 200, height: 50 },
                    type: item.json.type,
                    componentData: item.json.componentData,
                    styles: item.json.styles,
                });
            }

            const newGuidelines = [];
            pageData.elements.forEach((el) => {
                if (el.id === item.id) return;
                const bounds = getElementBounds(el);

                if (Math.abs(snapped.x - bounds.left) < 15) newGuidelines.push({ vertical: true, position: bounds.left });
                if (Math.abs(snapped.x - bounds.right) < 15) newGuidelines.push({ vertical: true, position: bounds.right });
                if (Math.abs(snapped.x - bounds.centerX) < 15) newGuidelines.push({ vertical: true, position: bounds.centerX });
                if (Math.abs(snapped.y - bounds.top) < 15) newGuidelines.push({ vertical: false, position: bounds.top });
                if (Math.abs(snapped.y - bounds.bottom) < 15) newGuidelines.push({ vertical: false, position: bounds.bottom });
                if (Math.abs(snapped.y - bounds.centerY) < 15) newGuidelines.push({ vertical: false, position: bounds.centerY });
            });

            if (Math.abs(snapped.x) < 15) newGuidelines.push({ vertical: true, position: 0 });
            if (Math.abs(snapped.x - canvasBounds.width) < 15) newGuidelines.push({ vertical: true, position: canvasBounds.width });
            if (Math.abs(snapped.x - canvasBounds.width / 2) < 15) newGuidelines.push({ vertical: true, position: canvasBounds.width / 2 });
            if (Math.abs(snapped.y) < 15) newGuidelines.push({ vertical: false, position: 0 });
            if (Math.abs(snapped.y - canvasBounds.height) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height });
            if (Math.abs(snapped.y - canvasBounds.height / 2) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height / 2 });

            setGuidelines(newGuidelines);
        },
        drop: (item, monitor) => {
            if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                console.log('Bỏ qua CHILD_ELEMENT drop trong Canvas');
                return undefined;
            }
            const clientOffset = monitor.getClientOffset();
            if (!clientOffset || !canvasRef.current) {
                toast.error('Không thể xác định vị trí thả!');
                return { moved: false };
            }

            const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
            const snapPoints = getSnapPoints();
            const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

            console.group('Canvas Drop Debug');
            console.log('Vị trí thả:', snapped);
            console.log('Item:', item);
            console.log('Loại item:', monitor.getItemType());
            console.groupEnd();

            if (item.json && !item.isExisting) {
                const newElement = {
                    id: `${item.id}-${Date.now()}`,
                    type: item.json.type,
                    componentData: item.json.componentData,
                    position: {
                        [viewMode]: snapped,
                        desktop: snapped,
                        tablet: snapped,
                        mobile: snapped,
                    },
                    size: item.json.size || { width: 200, height: 50 },
                    styles: item.json.styles || {},
                    children: item.json.children || [],
                };
                setPageData((prev) => ({ elements: [...prev.elements, newElement] }));
                setDragPreview(null);
                setGuidelines([]);
                return { moved: true, newPosition: snapped };
            } else if (item.id && item.isExisting) {
                const elementToUpdate = pageData.elements.find((el) => el.id === item.id);
                if (elementToUpdate) {
                    const updatedElement = {
                        ...elementToUpdate,
                        position: {
                            ...elementToUpdate.position,
                            [viewMode]: snapped,
                        },
                    };
                    setPageData((prev) => ({
                        elements: prev.elements.map((el) => (el.id === item.id ? updatedElement : el)),
                    }));
                    toast.success('Đã di chuyển phần tử!');
                    console.log('Cập nhật vị trí với chế độ REPLACE:', item.id, updatedElement.position);
                } else {
                    console.error('Không tìm thấy phần tử:', item.id);
                }
                setDragPreview(null);
                setGuidelines([]);
                return { moved: true, newPosition: snapped };
            }

            setDragPreview(null);
            setGuidelines([]);
            return { moved: false };
        },
        collect: (monitor) => ({
            isOver: monitor.isOver(),
            dragItem: monitor.getItem(),
        }),
    }), [pageData.elements, viewMode, zoomLevel, gridSize, showGrid, canvasBounds]);

    const handleContextMenu = useCallback((id, position) => {
        setContextMenu({ id, position });
    }, []);

    const handleCanvasClick = useCallback(
        (e) => {
            if (e.target === canvasRef.current) {
                setSelectedIds([]);
                setSelectedChildId(null);
                setContextMenu(null);
            }
        },
        []
    );

    const handleKeyDown = useCallback(
        (e) => {
            const ids = Array.isArray(selectedIds) ? selectedIds : [];
            if (!ids.length) return;

            e.preventDefault();
            e.stopPropagation();

            const step = e.shiftKey ? 10 : 1;

            ids.forEach((id) => {
                const element = pageData.elements.find((el) => el.id === id);
                if (element) {
                    const newY = e.key === 'ArrowUp' ? Math.max(0, (element.position[viewMode]?.y || 0) - step) : (element.position[viewMode]?.y || 0) + step;
                    const newX = e.key === 'ArrowLeft' ? Math.max(0, (element.position[viewMode]?.x || 0) - step) : (element.position[viewMode]?.x || 0) + step;
                    switch (e.key) {
                        case 'ArrowUp':
                        case 'ArrowDown':
                            setPageData((prev) => ({
                                elements: prev.elements.map((el) =>
                                    el.id === id
                                        ? {
                                            ...el,
                                            position: { ...el.position, [viewMode]: { x: el.position[viewMode]?.x || 0, y: newY } },
                                        }
                                        : el
                                ),
                            }));
                            break;
                        case 'ArrowLeft':
                        case 'ArrowRight':
                            setPageData((prev) => ({
                                elements: prev.elements.map((el) =>
                                    el.id === id
                                        ? {
                                            ...el,
                                            position: { ...el.position, [viewMode]: { x: newX, y: el.position[viewMode]?.y || 0 } },
                                        }
                                        : el
                                ),
                            }));
                            break;
                        case 'Delete':
                        case 'Backspace':
                            setPageData((prev) => ({
                                elements: prev.elements.filter((el) => el.id !== id),
                            }));
                            break;
                        case 'Escape':
                            setSelectedIds([]);
                            setSelectedChildId(null);
                            setContextMenu(null);
                            break;
                        default:
                            break;
                    }
                }
            });
        },
        [selectedIds, viewMode, pageData.elements]
    );

    useHotkeys('ctrl+a', (e) => {
        e.preventDefault();
        setSelectedIds(pageData.elements.map((el) => el.id));
    }, [pageData.elements]);

    useHotkeys('ctrl+g', (e) => {
        e.preventDefault();
        const ids = Array.isArray(selectedIds) ? selectedIds : [];
        if (ids.length > 1) {
            setPageData((prev) => ({
                elements: [
                    ...prev.elements.filter((el) => !ids.includes(el.id)),
                    {
                        id: `group-${Date.now()}`,
                        type: 'section',
                        componentData: { content: 'Nhóm phần tử' },
                        position: { desktop: { x: 0, y: 0 }, tablet: { x: 0, y: 0 }, mobile: { x: 0, y: 0 } },
                        size: { width: 300, height: 200 },
                        styles: { background: '#f0f0f0' },
                        children: prev.elements.filter((el) => ids.includes(el.id)).map((el) => ({
                            id: el.id,
                            type: el.type,
                            content: el.componentData.content,
                            componentData: el.componentData,
                            styles: el.styles,
                        })),
                    },
                ],
            }));
        }
    }, [selectedIds]);

    useEffect(() => {
        const handleClickOutside = () => setContextMenu(null);
        if (contextMenu) {
            document.addEventListener('click', handleClickOutside);
            return () => document.removeEventListener('click', handleClickOutside);
        }
    }, [contextMenu]);

    const renderDragPreview = () => {
        if (!dragPreview) return null;

        const { x, y, size, type, componentData, styles } = dragPreview;
        const previewStyle = {
            position: 'absolute',
            transform: `translate(${x}px, ${y}px)`,
            width: size.width,
            height: size.height,
            border: '2px dashed #3b82f6',
            background: 'rgba(59, 130, 246, 0.1)',
            pointerEvents: 'none',
            zIndex: 1000,
            opacity: 0.5,
            ...styles,
        };

        return (
            <div className="lpb-drag-preview" style={previewStyle}>
                {renderComponentContent(type, componentData || {}, {})}
            </div>
        );
    };

    const handleSelectElement = (id, ctrlKey) => {
        if (ctrlKey) {
            setSelectedIds((prev) => (prev.includes(id) ? prev.filter((sid) => sid !== id) : [...prev, id]));
        } else {
            setSelectedIds([id]);
        }
    };

    const handleSelectChild = (parentId, childId) => {
        setSelectedChildId(childId);
        setSelectedIds(parentId ? [parentId] : []);
    };

    const handleUpdateChildren = (parentId, dragIndex, dropIndex) => {
        setPageData((prev) => ({
            elements: prev.elements.map((el) => {
                if (el.id === parentId) {
                    const newChildren = [...el.children];
                    const [movedChild] = newChildren.splice(dragIndex, 1);
                    newChildren.splice(dropIndex, 0, movedChild);
                    return { ...el, children: newChildren };
                }
                return el;
            }),
        }));
    };

    if (loading) {
        return <div>Đang tải...</div>;
    }

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="canvas-container">
                <h2>{pageInfo.name || 'Tạo Landing Page'}</h2>
                <div className="page-info">
                    <p><strong>URL S3:</strong> <a href={pageInfo.file_path} target="_blank" rel="noopener noreferrer">{pageInfo.file_path || 'Chưa có'}</a></p>
                    <p><strong>Ngày tạo:</strong> {formatDate(pageInfo.created_at)}</p>
                    <p><strong>Ngày cập nhật:</strong> {formatDate(pageInfo.updated_at)}</p>
                </div>
                <div className="canvas-content">
                    <ComponentLibrary library={EXTENDED_LIBRARY} />
                    <div
                        ref={(node) => {
                            canvasRef.current = node;
                            drop(node);
                        }}
                        className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                        onClick={handleCanvasClick}
                        onKeyDown={handleKeyDown}
                        tabIndex={0}
                        style={{ outline: 'none', minHeight: '600px', border: '1px solid #ccc' }}
                    >
                        <Guidelines guidelines={guidelines} zoomLevel={zoomLevel} />
                        {pageData.elements.map((element) => (
                            <Element
                                key={element.id}
                                element={element}
                                isSelected={selectedIds.includes(element.id)}
                                onSelectElement={handleSelectElement}
                                onUpdatePosition={(id, position) =>
                                    setPageData((prev) => ({
                                        elements: prev.elements.map((el) =>
                                            el.id === id ? { ...el, position } : el
                                        ),
                                    }))
                                }
                                viewMode={viewMode}
                                zoomLevel={zoomLevel}
                                gridSize={showGrid ? gridSize : Infinity}
                                onContextMenu={handleContextMenu}
                                canvasBounds={canvasBounds}
                                canvasRef={canvasRef}
                                onSelectChild={handleSelectChild}
                                selectedChildId={selectedChildId}
                                onUpdateChildren={handleUpdateChildren}
                                showGrid={showGrid}
                            />
                        ))}
                        {renderDragPreview()}
                        <SelectionOverlay
                            selectedElements={pageData.elements.filter((el) => Array.isArray(selectedIds) && selectedIds.includes(el.id))}
                            onResize={(id, size) =>
                                setPageData((prev) => ({
                                    elements: prev.elements.map((el) => (el.id === id ? { ...el, size } : el)),
                                }))
                            }
                            zoomLevel={zoomLevel}
                        />
                        {contextMenu && (
                            <div
                                className="lpb-context-menu"
                                style={{
                                    position: 'fixed',
                                    left: contextMenu.position.x,
                                    top: contextMenu.position.y,
                                    zIndex: 10000,
                                    background: '#fff',
                                    border: '1px solid #ccc',
                                    borderRadius: '4px',
                                    boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
                                    minWidth: '120px',
                                }}
                            >
                                <div
                                    className="lpb-context-menu-item"
                                    style={{ padding: '8px 12px', cursor: 'pointer' }}
                                    onClick={() => {
                                        setPageData((prev) => ({
                                            elements: prev.elements.filter((el) => el.id !== contextMenu.id),
                                        }));
                                        setContextMenu(null);
                                    }}
                                >
                                    Xóa
                                </div>
                                <div
                                    className="lpb-context-menu-item"
                                    style={{ padding: '8px 12px', cursor: 'pointer' }}
                                    onClick={() => {
                                        setPageData((prev) => ({
                                            elements: [
                                                ...prev.elements.filter((el) => el.id !== contextMenu.id),
                                                {
                                                    id: `group-${Date.now()}`,
                                                    type: 'section',
                                                    componentData: { content: 'Nhóm phần tử' },
                                                    position: { desktop: { x: 0, y: 0 }, tablet: { x: 0, y: 0 }, mobile: { x: 0, y: 0 } },
                                                    size: { width: 300, height: 200 },
                                                    styles: { background: '#f0f0f0' },
                                                    children: prev.elements.filter((el) => el.id === contextMenu.id).map((el) => ({
                                                        id: el.id,
                                                        type: el.type,
                                                        content: el.componentData.content,
                                                        componentData: el.componentData,
                                                        styles: el.styles,
                                                    })),
                                                },
                                            ],
                                        }));
                                        setContextMenu(null);
                                    }}
                                >
                                    Nhóm
                                </div>
                            </div>
                        )}
                    </div>
                </div>
                <button onClick={handleSave}>Lưu</button>
            </div>
        </DndProvider>
    );
};

export default Canvas;
