// DraggableComponent updated for compact view: smaller, icon + short name
const DraggableComponent = React.memo(({ item, isTemplate = false }) => {
    const [{ isDragging }, drag] = useDrag(() => ({
        type: isTemplate ? ItemTypes.TEMPLATE : ItemTypes.COMPONENT,
        item: { componentData: isTemplate ? item.structure : item },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    return (
        <div
            ref={drag}
            className="p-2 mb-1 border border-gray-200 rounded-md cursor-move hover:border-blue-300 hover:shadow-sm hover:bg-blue-50 transition-all duration-200 flex items-center gap-2 text-sm"
            style={{ opacity: isDragging ? 0.5 : 1 }}
            onMouseDown={() => toast.info(`Bắt đầu kéo ${item.name}`)}
            data-tooltip-id="component-tooltip"
            data-tooltip-content={item.description || `Kéo để thêm ${item.name}`}
        >
            <i className={`${item.icon} text-gray-500 text-lg`}></i>
            <span className="font-medium text-gray-800 truncate">{item.name}</span>
            {item.level && <span className="text-xs text-blue-500 ml-auto">{item.level.toUpperCase()}</span>}
        </div>
    );
});

const ComponentLibrary = React.memo(({ isCollapsed, onToggle }) => {
    const [activeCategory, setActiveCategory] = useState('elements');
    const [searchQuery, setSearchQuery] = useState('');
    const [expandedSubs, setExpandedSubs] = useState({}); // State for expanded sub-categories

    const categories = Object.keys(EXTENDED_LIBRARY).map((key) => ({
        id: key,
        ...EXTENDED_LIBRARY[key],
    }));

    // Toggle sub-category expansion
    const toggleSub = useCallback((catId, subId) => {
        setExpandedSubs((prev) => ({
            ...prev,
            [catId]: {
                ...prev[catId],
                [subId]: !prev[catId]?.[subId],
            },
        }));
    }, []);

    // Filter items based on search query
    const filteredItems = useMemo(() => {
        const categoryData = EXTENDED_LIBRARY[activeCategory];
        if (!categoryData) return [];

        let items = [];
        categoryData.subCategories.forEach((sub) => {
            if (expandedSubs[activeCategory]?.[sub.id]) {
                items = [...items, ...sub.templates];
            }
        });

        if (!searchQuery) return items;
        return items.filter((item) =>
            item.name.toLowerCase().includes(searchQuery.toLowerCase())
        );
    }, [activeCategory, searchQuery, expandedSubs]);

    // Handle category change
    const handleCategoryChange = useCallback((categoryId) => {
        setActiveCategory(categoryId);
        setSearchQuery('');
        toast.info(`Đã chuyển sang danh mục ${EXTENDED_LIBRARY[categoryId]?.name}`);
    }, []);

    // Keyboard shortcuts for categories
    categories.forEach((cat, index) => {
        useHotkeys(`ctrl+${index + 1}`, () => handleCategoryChange(cat.id), [handleCategoryChange]);
    });

    if (isCollapsed) {
        return (
            <div className="w-12 bg-white border-r border-gray-200 h-full flex flex-col items-center py-2 transition-all duration-300">
                <button
                    onClick={onToggle}
                    className="p-2 hover:bg-blue-50 rounded mb-4 text-gray-600 hover:text-blue-600"
                    data-tooltip-id="component-tooltip"
                    data-tooltip-content="Mở thư viện thành phần"
                >
                    <i className="fas fa-chevron-right"></i>
                </button>
                {categories.map((cat) => (
                    <button
                        key={cat.id}
                        className="p-2 hover:bg-blue-50 rounded mb-1 text-gray-600 hover:text-blue-600"
                        data-tooltip-id="component-tooltip"
                        data-tooltip-content={cat.name}
                        onClick={() => {
                            handleCategoryChange(cat.id);
                            onToggle(); // Auto-expand
                        }}
                    >
                        <i className={cat.icon}></i>
                    </button>
                ))}
            </div>
        );
    }

    const renderSubCategories = (categoryData) => {
        return categoryData.subCategories.map((sub) => (
            <div key={sub.id}>
                <button
                    onClick={() => toggleSub(activeCategory, sub.id)}
                    className="w-full p-2 text-left flex items-center justify-between hover:bg-gray-50 text-gray-600 text-sm font-medium border-b border-gray-100"
                >
                    <span>{sub.name}</span>
                    <i className={`fas fa-chevron-${expandedSubs[activeCategory]?.[sub.id] ? 'down' : 'right'}`}></i>
                </button>
                {expandedSubs[activeCategory]?.[sub.id] && (
                    <div className="pl-2 pt-1">
                        {sub.templates.map((template) => (
                            <DraggableComponent key={template.id} item={template} isTemplate={activeCategory === 'templates'} />
                        ))}
                    </div>
                )}
            </div>
        ));
    };

    return (
        <div className="w-64 bg-white border-r border-gray-200 h-full overflow-y-auto transition-all duration-300">
            <div className="p-4 border-b flex items-center justify-between">
                <h2 className="font-semibold text-gray-800">Thư viện thành phần</h2>
                <button
                    onClick={onToggle}
                    className="p-1 hover:bg-blue-50 rounded text-gray-600 hover:text-blue-600"
                    data-tooltip-id="component-tooltip"
                    data-tooltip-content="Thu gọn thư viện"
                >
                    <i className="fas fa-chevron-left"></i>
                </button>
            </div>

            <div className="flex flex-wrap border-b p-1 bg-gray-50">
                {categories.map((cat) => (
                    <button
                        key={cat.id}
                        onClick={() => handleCategoryChange(cat.id)}
                        className={`flex-1 p-2 text-center text-sm font-medium rounded-md m-1 transition-all duration-200 ${
                            activeCategory === cat.id
                                ? 'bg-blue-500 text-white'
                                : 'text-gray-600 hover:bg-blue-100'
                        }`}
                        data-tooltip-id="component-tooltip"
                        data-tooltip-content={`Danh mục ${cat.name}`}
                    >
                        {cat.name}
                    </button>
                ))}
            </div>

            <div className="p-2 border-b">
                <input
                    type="text"
                    placeholder="Tìm kiếm thành phần hoặc template..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full p-2 border border-gray-200 rounded-lg text-sm focus:outline-none focus:border-blue-500"
                />
            </div>

            <div className="p-2">
                {renderSubCategories(EXTENDED_LIBRARY[activeCategory])}
                {filteredItems.length === 0 && (
                    <div className="text-center text-gray-500 text-sm py-4">
                        Không tìm thấy thành phần hoặc template
                    </div>
                )}
            </div>
        </div>
    );
});

export default ComponentLibrary;
