import React, { useState, useMemo, useCallback, useEffect } from 'react';
import { useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import COMPONENT_LIBRARY from '../../constants/componentLibrary';

const ItemTypes = {
  COMPONENT: 'component',
  TEMPLATE: 'template',
};

const EXTENDED_LIBRARY = {
  elements: {
    name: 'Phần tử',
    icon: 'fas fa-cube',
    subCategories: [
      {
        id: 'text',
        name: 'Văn bản',
        templates: [
          { id: 'text-basic', name: 'Cơ bản', icon: 'fas fa-font', description: 'Văn bản đơn giản', level: 'basic' },
          { id: 'text-advanced', name: 'Nâng cao', icon: 'fas fa-text-height', description: 'Văn bản với hiệu ứng', level: 'advanced', variants: ['bold', 'italic', 'animated'] },
        ],
      },
      {
        id: 'buttons',
        name: 'Nút bấm',
