import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = ({ role }) => {
    const location = useLocation();

    const userMenu = [
        { name: 'Tổng quan', path: '/dashboard' },
        { name: 'Quản lý Landing Page', path: '/pages' },
        { name: 'Quản lý Leads', path: '/leads' },
        { name: 'Thanh toán & Premium', path: '/payments' },
        { name: 'Báo cáo', path: '/reports' },
        { name: 'Quảng cáo & Marketing', path: '/marketing' },
        { name: 'Tương tác Khách hàng', path: '/chatbot' },
    ];

    const adminMenu = [
        { name: 'Tổng quan Hệ thống', path: '/dashboard' },
        { name: 'Quản lý Tài khoản', path: '/users' },
        { name: 'Kiểm tra Thanh toán', path: '/payments' },
        { name: 'Quản lý Mẫu Marketplace', path: '/templates' },
        { name: 'Báo cáo Hệ thống', path: '/reports' },
    ];

    const menu = role === 'admin' ? adminMenu : userMenu;

    return (
        <aside className="sidebar">
            {menu.map((item) => {
                const isActive = location.pathname === item.path;
                return (
                    <Link
                        key={item.name}
                        to={item.path}
                        className={`sidebar-item ${isActive ? 'active' : ''}`}
                    >
                        {item.name}
                    </Link>
                );
            })}
        </aside>
    );
};

export default React.memo(Sidebar);
