import React, { useState, useEffect } from 'react';
import { Search, Plus, ChevronDown, MoreHorizontal, Eye, Clock, Globe } from 'lucide-react';
import api from '@landinghub/api';
import { useNavigate } from 'react-router-dom';
import '../styles/PageContent.css';

const SidebarComponent = () => {
    const sidebarItems = [
        { icon: 'üìÑ', label: 'Pages', active: true },
        { icon: 'üìß', label: 'Th∆∞ vi·ªán m·∫´u' },
        { icon: 'üë§', label: 'T√™n mi·ªÅn' },
    ];

    return (
        <div className="sidebar">
            <div className="sidebar-header">
                <h3>Landing Pages</h3>
            </div>
            <nav className="sidebar-nav">
                {sidebarItems.map((item, index) => (
                    <div key={index} className={`nav-item ${item.active ? 'active' : ''}`}>
                        <span className="nav-icon">{item.icon}</span>
                        <span className="nav-label">{item.label}</span>
                    </div>
                ))}
            </nav>
            <div className="filter-section">
                <div className="filter-header">
                    <span>L·ªçc theo Tags</span>
                    <div className="filter-actions">
                        <button className="filter-btn">+</button>
                        <button className="filter-btn">‚â°</button>
                    </div>
                </div>
                <div className="search-filter">
                    <Search className="search-icon" size={16} />
                    <input type="text" placeholder="T√¨m ki·∫øm" />
                </div>
                <button className="all-btn">T·∫•t c·∫£</button>
            </div>
        </div>
    );
};

const HeaderComponent = ({ onCreateClick }) => {
    return (
        <div className="content-header">
            <div>
                <h1>Landing Pages</h1>
                <p>Qu·∫£n l√Ω danh s√°ch Landing Page c·ªßa b·∫°n ƒë·ªÉ d·ªÖ d√†ng h∆°n v·ªõi vi·ªác g·∫Øn Tag, theo d√µi hi·ªáu su·∫•t.</p>
            </div>
            <button className="create-btn" onClick={onCreateClick}>
                <Plus size={16} />
                T·∫°o Landing Page
            </button>
        </div>
    );
};

const ControlsComponent = ({ searchTerm, setSearchTerm, statusFilter, setStatusFilter }) => {
    return (
        <div className="controls">
            <div className="search-box">
                <Search className="search-icon" size={16} />
                <input
                    type="text"
                    placeholder="T√¨m ki·∫øm Landing Page"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
            <div className="filters">
                <select
                    value={statusFilter}
                    onChange={(e) => setStatusFilter(e.target.value)}
                    className="filter-select"
                >
                    <option>Tr·∫°ng th√°i</option>
                    <option>ƒê√É XU·∫§T B·∫¢N</option>
                    <option>CH∆ØA XU·∫§T B·∫¢N</option>
                    <option>ARCHIVED</option>
                </select>
            </div>
        </div>
    );
};

const PaginationComponent = ({ filteredPages, landingPages }) => {
    return (
        <div className="pagination">
            <span>ƒêang hi·ªÉn th·ªã 1 ƒë·∫øn {filteredPages.length} c·ªßa {landingPages.length} b·∫£n ghi</span>
        </div>
    );
};

const PageContent = () => {
    const [landingPages, setLandingPages] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('Tr·∫°ng th√°i');
    const [menuOpen, setMenuOpen] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showCreateModal, setShowCreateModal] = useState(false);
    const [showEditorModal, setShowEditorModal] = useState(false);
    const [pageDescription, setPageDescription] = useState('');
    const [editingPage, setEditingPage] = useState(null);
    const navigate = useNavigate();

    const fetchLandingPages = async () => {
        try {
            setLoading(true);
            const response = await api.get('/api/pages');
            setLandingPages(response.data);
        } catch (err) {
            setError('Kh√¥ng th·ªÉ t·∫£i danh s√°ch landing page: ' + (err.response?.data?.error || err.message));
            console.error('Fetch error:', err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchLandingPages();
    }, []);

    const filteredPages = landingPages.filter((page) => {
        const matchesSearch = page.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesStatus = statusFilter === 'Tr·∫°ng th√°i' || page.status === statusFilter;
        return matchesSearch && matchesStatus;
    });

    const handleCreatePage = async () => {
        try {
            setLoading(true);
            const response = await api.post('/api/pages', { description: pageDescription });
            setLandingPages([...landingPages, response.data]);
            setShowCreateModal(false);
            setPageDescription('');
        } catch (err) {
            setError('L·ªói khi t·∫°o landing page: ' + (err.response?.data?.error || err.message));
            console.error('Create error:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleSavePage = async () => {
        if (editorRef.current) {
            const html = editorRef.current.getHtml();
            const css = editorRef.current.getCss();
            try {
                const response = await api.put(`/api/pages/${editingPage.id}`, { html, css });
                setLandingPages(landingPages.map((p) => (p.id === editingPage.id ? response.data : p)));
                setShowEditorModal(false);
                setEditingPage(null);
            } catch (err) {
                setError('L·ªói khi l∆∞u landing page: ' + (err.response?.data?.error || err.message));
                console.error('Save error:', err);
            }
        }
    };

    const handlePublishPage = async (pageId) => {
        try {
            const response = await api.post(`/api/pages/${pageId}/publish`);
            setLandingPages(landingPages.map((p) => (p.id === pageId ? response.data : p)));
        } catch (err) {
            setError('L·ªói khi tri·ªÉn khai landing page: ' + (err.response?.data?.error || err.message));
            console.error('Publish error:', err);
        }
    };

    const handleDeletePage = async (pageId) => {
        if (!window.confirm('B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a landing page n√†y?')) return;
        try {
            await api.delete(`/api/pages/${pageId}`);
            setLandingPages(landingPages.filter((p) => p.id !== pageId));
        } catch (err) {
            setError('L·ªói khi x√≥a landing page: ' + (err.response?.data?.error || err.message));
            console.error('Delete error:', err);
        }
    };

    const handlePreviewPage = (url) => {
        if (url) window.open(url, '_blank');
    };

    const handleEditPage = (page) => {
        setEditingPage(page);
        setShowEditorModal(true);
    };

    return (
        <div className="page-content">
            {loading && <div className="loading">ƒêang t·∫£i...</div>}
            {error && (
                <div className="error">
                    {error}
                    <button onClick={() => setError(null)} className="error-dismiss">ƒê√≥ng</button>
                </div>
            )}
            <SidebarComponent />
            <div className="main-content">
                <HeaderComponent onCreateClick={() => setShowCreateModal(true)} />
                <CreateModal
                    showCreateModal={showCreateModal}
                    setShowCreateModal={setShowCreateModal}
                    pageDescription={pageDescription}
                    setPageDescription={setPageDescription}
                    handleCreatePage={handleCreatePage}
                />
                <EditorModal
                    showEditorModal={showEditorModal}
                    setShowEditorModal={setShowEditorModal}
                    editingPage={editingPage}
                    handleSavePage={handleSavePage}
                />
                <ControlsComponent
                    searchTerm={searchTerm}
                    setSearchTerm={setSearchTerm}
                    statusFilter={statusFilter}
                    setStatusFilter={setStatusFilter}
                />
                <LandingList
                    landingPages={landingPages}
                    filteredPages={filteredPages}
                    handleMoreClick={handleMoreClick}
                    menuOpen={menuOpen}
                    handlePreviewPage={handlePreviewPage}
                    handleEditPage={handleEditPage}
                    handlePublishPage={handlePublishPage}
                    handleDeletePage={handleDeletePage}
                />
                <PaginationComponent filteredPages={filteredPages} landingPages={landingPages} />
            </div>
        </div>
    );
};

export default PageContent;
