    name: 'Thành phần',
    subCategories: [
        {
            id: 'heading',
            name: 'Tiêu đề',
            lucideIcon: Type,
            templates: [
                {
                    id: 'heading-1-arial',
                    name: 'Heading 1 (Arial)',
