import React, { useRef, useCallback, useState } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import QuickEditToolkit from './QuickEditToolkit';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

const Element = React.memo(
    ({ component, index, isSelected, onSelectElement, handleElementHover, onDuplicateElement, onDeleteElement, onMoveElement, updatePosition, canvasRef, sectionId, onInlineEdit, pageStructure, zoomLevel }) => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: { id: component.id, sectionId },
            end: (item, monitor) => {
                const delta = monitor.getDifferenceFromInitialOffset();
                if (delta && canvasRef.current && sectionId) {
                    const rect = canvasRef.current.getBoundingClientRect();
                    let newX = (component.json.styles.position?.x || 0) + delta.x / (zoomLevel / 100);
                    let newY = (component.json.styles.position?.y || 0) + delta.y / (zoomLevel / 100);
                    newX = Math.round(newX / 10) * 10;
                    newY = Math.round(newY / 10) * 10;
                    const section = pageStructure.find((s) => s.id === sectionId);
                    let adjustedY = newY;
                    if (section) {
                        section.elements.forEach((el) => {
                            if (el.id !== component.id) {
                                const elX = el.json.styles.position?.x || 0;
                                const elY = el.json.styles.position?.y || 0;
                                if (Math.abs(newX - elX) < 50 && Math.abs(newY - elY) < 50) {
                                    adjustedY = elY + 20;
                                }
                            }
                        });
                    }
                    updatePosition(component.id, sectionId, { x: newX, y: adjustedY });
                }
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        const handleMouseDown = (e) => {
            e.stopPropagation();
            onSelectElement(component);
        };

        const handleInlineEdit = (e) => {
            if (onInlineEdit && e.target.contentEditable === 'true') {
                onInlineEdit(component.id, sectionId, e.target.innerText);
            }
        };

        const renderContent = () => {
            const { type, content, styles = {}, isContainer = false } = component.json;
            const containerStyle = {
                ...styles,
                position: 'absolute',
                left: styles.position?.x || 0,
                top: styles.position?.y || 0,
                boxSizing: 'border-box', // Đảm bảo kích thước bao gồm padding và border
                width: styles.width || 'auto', // Áp dụng width từ JSON
                height: styles.height || 'auto', // Áp dụng height từ JSON
                maxWidth: '100%', // Giới hạn tối đa để tránh tràn
                transform: isSelected ? 'translate(-2px, -2px)' : 'none',
            };

            if (isContainer) {
                switch (type) {
                    case 'form':
                        return (
                            <div style={containerStyle} className="lpb-form-container">
                                <form onSubmit={(e) => e.preventDefault()} onMouseDown={(e) => e.stopPropagation()}>
                                    {(content?.fields || []).map((field, idx) => (
                                        <input
                                            key={idx}
                                            type="text"
                                            placeholder={field}
                                            style={{ marginBottom: '8px', width: '100%', ...styles }}
                                            onMouseDown={(e) => e.stopPropagation()}
                                        />
                                    ))}
                                    <button
                                        type="submit"
                                        style={{ marginTop: '8px', ...styles }}
                                        onMouseDown={(e) => e.stopPropagation()}
                                    >
                                        Submit
                                    </button>
                                </form>
                            </div>
                        );
                    case 'button-group':
                        return (
                            <div style={containerStyle} className="lpb-button-group-container">
                                {(content || []).map((btn, idx) => (
                                    <button
                                        key={idx}
                                        style={{ marginRight: idx < content.length - 1 ? '10px' : 0, ...styles }}
                                        onMouseDown={(e) => e.stopPropagation()}
                                    >
                                        {btn}
                                    </button>
                                ))}
                            </div>
                        );
                    case 'gallery':
                        return (
                            <div style={containerStyle} className="lpb-gallery-container">
                                {(content || []).map((src, idx) => (
                                    <img
                                        key={idx}
                                        src={src}
                                        alt={`Gallery ${idx}`}
                                        style={{ width: '100px', marginRight: idx < content.length - 1 ? '10px' : 0, ...styles }}
                                    />
                                ))}
                            </div>
                        );
                    case 'card':
                        return (
                            <div style={containerStyle} className="lpb-card-container">
                                <i className={content.icon} style={{ fontSize: '24px', color: '#4a90e2' }} />
                                <h3 style={{ margin: '10px 0', ...styles }}>{content.title}</h3>
                                <p style={{ margin: '0', ...styles }}>{content.description}</p>
                            </div>
                        );
                    default:
                        return <div style={containerStyle}>{component.name || 'Unknown'}</div>;
                }
            }

            switch (type) {
                case 'heading':
                    return (
                        <h2
                            style={containerStyle}
                            contentEditable
                            suppressContentEditableWarning
                            onBlur={handleInlineEdit}
                            onMouseDown={(e) => e.stopPropagation()}
                        >
                            {content}
                        </h2>
                    );
                case 'paragraph':
                    return (
                        <p
                            style={containerStyle}
                            contentEditable
                            suppressContentEditableWarning
                            onBlur={handleInlineEdit}
                            onMouseDown={(e) => e.stopPropagation()}
                        >
                            {content}
                        </p>
                    );
                case 'button':
                    return (
                        <button
                            style={containerStyle}
                            contentEditable
                            suppressContentEditableWarning
                            onBlur={handleInlineEdit}
                            onMouseDown={(e) => e.stopPropagation()}
                        >
                            {content}
                        </button>
                    );
                case 'image':
                    return <img src={content} alt="Image" style={containerStyle} />;
                default:
                    return <div style={containerStyle}>{component.name || 'Unknown'}</div>;
            }
        };

        return (
            <div
                ref={drag}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                onMouseEnter={(e) => handleElementHover(e, component)}
                onMouseDown={handleMouseDown}
                style={{
                    position: 'absolute',
                    left: component.json.styles.position?.x || 0,
                    top: component.json.styles.position?.y || 0,
                    zIndex: isSelected ? 10 : 1,
                    boxSizing: 'border-box',
                    ...component.json.styles, // Áp dụng toàn bộ styles từ JSON
                }}
            >
                {renderContent()}
            </div>
        );
    }
);

const Section = React.memo(
    ({ section, index, selectedElement, onSelectElement, handleElementHover, onDuplicateElement, onDeleteElement, onMoveElement, updatePosition, addElement, canvasRef, moveElement, onInlineEdit, onSelectSection, viewMode, pageStructure, zoomLevel }) => {
        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && canvasRef.current) {
                    const rect = canvasRef.current.getBoundingClientRect();
                    const y = clientOffset.y - rect.top;
                    item.dropPosition = {
                        x: Math.round((clientOffset.x - rect.left) / (zoomLevel / 100) / 10) * 10,
                        y: Math.round((y - 50) / (zoomLevel / 100) / 10) * 10 + 50,
                    };
                }
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả. Vui lòng thử lại!');
                    return;
                }
                const rect = canvasRef.current.getBoundingClientRect();
                let position = {
                    x: Math.round((clientOffset.x - rect.left) / (zoomLevel / 100) / 10) * 10,
                    y: Math.round((clientOffset.y - rect.top - 50) / (zoomLevel / 100) / 10) * 10 + 50,
                };
                const sectionElements = pageStructure.find((s) => s.id === section.id)?.elements || [];
                sectionElements.forEach((el) => {
                    if (el.id !== item.id) {
                        const elX = el.json.styles.position?.x || 0;
                        const elY = el.json.styles.position?.y || 0;
                        if (Math.abs(position.x - elX) < 50 && Math.abs(position.y - elY) < 50) {
                            position.y = elY + 20;
                        }
                    }
                });
                if (item.componentData) {
                    addElement(section.id, {
                        ...item.componentData,
                        json: {
                            ...item.componentData.json,
                            styles: {
                                ...item.componentData.json.styles,
                                position,
                            },
                        },
                    }, section.elements.length);
                } else {
                    updatePosition(item.id, section.id, position);
                    moveElement(section.id, item.id, section.elements.length);
                }
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        const handleSectionClick = (e) => {
            e.stopPropagation();
            onSelectSection(section);
        };

        return (
            <div
                ref={drop}
                className={`lpb-canvas-section ${isOver ? 'lpb-canvas-section-over' : ''} ${selectedElement?.id === section.id ? 'lpb-canvas-section-selected' : ''}`}
                style={{
                    width: section.json.styles?.width || '1200px',
                    height: section.json.styles?.height || 'auto',
                    minHeight: '200px',
                    position: 'relative',
                    margin: '0 auto 20px auto',
                    border: '1px solid #ddd',
                    padding: '20px',
                    boxSizing: 'border-box',
                }}
                onMouseDown={handleSectionClick}
            >
                <h3>{section.json.content.title || 'Section'}</h3>
                <div className="lpb-canvas-section-content">
                    {section.elements.map((element, idx) => (
                        <Element
                            key={element.id}
                            component={element}
                            index={idx}
                            isSelected={selectedElement?.id === element.id}
                            onSelectElement={onSelectElement}
                            handleElementHover={handleElementHover}
                            onDuplicateElement={onDuplicateElement}
                            onDeleteElement={onDeleteElement}
                            onMoveElement={onMoveElement}
                            updatePosition={updatePosition}
                            canvasRef={canvasRef}
                            sectionId={section.id}
                            onInlineEdit={onInlineEdit}
                            pageStructure={pageStructure}
                            zoomLevel={zoomLevel}
                        />
                    ))}
                </div>
            </div>
        );
    }
);

const Canvas = React.memo(
    ({
         pageStructure,
         selectedElement,
         onSelectElement,
         quickEditElement,
         quickEditPosition,
         onQuickEdit,
         onCloseQuickEdit,
         onDuplicateElement,
         onDeleteElement,
         onMoveElement,
         viewMode,
         addSection,
         addElement,
         moveElement,
         ItemTypes,
         zoomLevel,
         setPageStructure,
         updatePosition,
         onInlineEdit,
     }) => {
        const canvasRef = useRef(null);
        const [dropIndex, setDropIndex] = useState(null);

        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.SECTION],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && canvasRef.current) {
                    const rect = canvasRef.current.getBoundingClientRect();
                    const y = clientOffset.y - rect.top;
                    const index = Math.min(pageStructure.length, Math.floor(y / 200));
                    setDropIndex(index);
                    if (item.componentData) {
                        item.componentData.json.styles = {
                            ...item.componentData.json.styles,
                            width: '1200px',
                            height: 'auto',
                        };
                    }
                }
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả. Vui lòng thử lại!');
                    return;
                }
                const rect = canvasRef.current.getBoundingClientRect();
                const index = Math.min(pageStructure.length, Math.floor((clientOffset.y - rect.top) / 200));
                addSection({
                    ...item.componentData,
                    json: {
                        ...item.componentData.json,
                        styles: {
                            ...item.componentData.json.styles,
                            width: '1200px',
                            height: 'auto',
                        },
                    },
                }, index);
                setDropIndex(null);
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        const handleElementHover = useCallback(
            (e, component) => {
                if (quickEditElement?.id === component.id) return;
                const rect = e.currentTarget.getBoundingClientRect();
                const canvasRect = canvasRef.current?.getBoundingClientRect() || { left: 0, top: 0 };
                onQuickEdit(component, {
                    x: (rect.left - canvasRect.left) / (zoomLevel / 100),
                    y: (rect.top - canvasRect.top + 20) / (zoomLevel / 100),
                });
            },
            [quickEditElement, onQuickEdit, zoomLevel]
        );

        const handleSelectSection = useCallback(
            (section) => {
                onSelectElement(section);
            },
            [onSelectElement]
        );

        useHotkeys(
            'delete',
            () => {
                if (selectedElement) {
                    onDeleteElement(selectedElement);
                }
            },
            [selectedElement, onDeleteElement]
        );

        return (
            <div className="lpb-canvas-wrapper">
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    onMouseLeave={onCloseQuickEdit}
                    style={{ zoom: `${zoomLevel / 100}`, position: 'relative' }}
                >
                    <div className="lpb-canvas-content">
                        {pageStructure.length === 0 ? (
                            <div className="lpb-canvas-empty-state">
                                <i className="fas fa-layer-group"></i>
                                <p>Kéo thả section để bắt đầu</p>
                            </div>
                        ) : (
                            <>
                                {pageStructure.map((section, index) => (
                                    <React.Fragment key={section.id}>
                                        {dropIndex === index && (
                                            <div
                                                className="lpb-canvas-drop-zone"
                                                style={{
                                                    height: '50px',
                                                    background: 'rgba(0, 128, 255, 0.1)',
                                                    border: '2px dashed #0080ff',
                                                    margin: '10px auto',
                                                    width: '1200px',
                                                }}
                                            />
                                        )}
                                        <Section
                                            section={section}
                                            index={index}
                                            selectedElement={selectedElement}
                                            onSelectElement={onSelectElement}
                                            handleElementHover={handleElementHover}
                                            onDuplicateElement={onDuplicateElement}
                                            onDeleteElement={onDeleteElement}
                                            onMoveElement={onMoveElement}
                                            updatePosition={updatePosition}
                                            addElement={addElement}
                                            canvasRef={canvasRef}
                                            moveElement={moveElement}
                                            onInlineEdit={onInlineEdit}
                                            onSelectSection={handleSelectSection}
                                            viewMode={viewMode}
                                            pageStructure={pageStructure}
                                            zoomLevel={zoomLevel}
                                        />
                                    </React.Fragment>
                                ))}
                                {dropIndex === pageStructure.length && (
                                    <div
                                        className="lpb-canvas-drop-zone"
                                        style={{
                                            height: '50px',
                                            background: 'rgba(0, 128, 255, 0.1)',
                                            border: '2px dashed #0080ff',
                                            margin: '10px auto',
                                            width: '1200px',
                                        }}
                                    />
                                )}
                            </>
                        )}
                    </div>
                    <QuickEditToolkit
                        element={quickEditElement}
                        position={quickEditPosition}
                        onEdit={() => onSelectElement(quickEditElement)}
                        onDuplicate={() => onDuplicateElement(quickEditElement)}
                        onDelete={() => onDeleteElement(quickEditElement)}
                        onMoveUp={() => onMoveElement(quickEditElement, 'up')}
                        onMoveDown={() => onMoveElement(quickEditElement, 'down')}
                        onClose={onCloseQuickEdit}
                    />
                </div>
            </div>
        );
    }
);

export default Canvas;
