import React, { useState } from 'react';
import Login from '../components/Login';
import Register from '../components/Register';
import './AuthPage.css'; // CSS cho tab switch

const AuthPage = () => {
    const [activeTab, setActiveTab] = useState('login');

    return (
        <div className="auth-page">
            <div className="tab-container">
                <input
                    type="radio"
                    name="tab"
                    id="tab-login"
                    checked={activeTab === 'login'}
                    onChange={() => setActiveTab('login')}
                    className="tab"
                />
                <label className="tab_label" htmlFor="tab-login">
                    Login
                </label>

                <input
                    type="radio"
                    name="tab"
                    id="tab-register"
                    checked={activeTab === 'register'}
                    onChange={() => setActiveTab('register')}
                    className="tab"
                />
                <label className="tab_label" htmlFor="tab-register">
                    Register
                </label>

                <div className="indicator"></div>
            </div>

            <div className="tab-content">
                {activeTab === 'login' && <Login />}
                {activeTab === 'register' && <Register />}
            </div>
        </div>
    );
};

export default AuthPage;
