import React, { useState, useRef } from 'react';
import {
    ChevronRight, ChevronLeft, MousePointer,
    Type, Zap, Sparkles, Bold, Italic, Underline,
    AlignLeft, AlignCenter, AlignRight, AlignJustify,
    Palette, Box, Sun, Droplet, Image as ImageIcon, Upload, Trash2
} from 'lucide-react';
import { toast } from 'react-toastify';
import ImageManagerModal from '../ImageManagerModal';
import '../../../styles/ElementPropertiesPanel.css';
// 1. IMPORT LOGIC RESPONSIVE
import { getResponsiveValues, RESPONSIVE_DATA_KEYS } from '../../../utils/responsiveSync';
// 2. GIẢ SỬ BẠN CÓ IMAGE SERVICE
// import { validateImageFile, compressImage, uploadImage } from '../../../utils/imageService';


// ... (Giữ nguyên các hằng số FONT_PRESETS, ANIMATION_PRESETS, ...)
const FONT_PRESETS = [
    { name: 'Arial', value: 'Arial, sans-serif' },
    { name: 'Helvetica', value: 'Helvetica, sans-serif' },
    { name: 'Times New Roman', value: 'Times New Roman, serif' },
    { name: 'Georgia', value: 'Georgia, serif' },
    { name: 'Courier New', value: 'Courier New, monospace' },
    { name: 'Verdana', value: 'Verdana, sans-serif' },
    { name: 'Roboto', value: 'Roboto, sans-serif' },
    { name: 'Open Sans', value: 'Open Sans, sans-serif' },
    { name: 'Poppins', value: 'Poppins, sans-serif' },
    { name: 'Montserrat', value: 'Montserrat, sans-serif' },
    { name: 'Inter', value: 'Inter, sans-serif' },
    { name: 'Lato', value: 'Lato, sans-serif' },
    { name: 'Raleway', value: 'Raleway, sans-serif' },
    { name: 'Playfair Display', value: 'Playfair Display, serif' },
    { name: 'Merriweather', value: 'Merriweather, serif' },
    { name: 'Dancing Script', value: "'Dancing Script', cursive" },
    { name: 'Pacifico', value: "'Pacifico', cursive" },
    { name: 'Lobster', value: "'Lobster', cursive" }
];

const FONT_WEIGHT_PRESETS = [
    { name: 'Thin (100)', value: '100' },
    { name: 'Extra Light (200)', value: '200' },
    { name: 'Light (300)', value: '300' },
    { name: 'Regular (400)', value: '400' },
    { name: 'Medium (500)', value: '500' },
    { name: 'Semibold (600)', value: '600' },
    { name: 'Bold (700)', value: '700' },
    { name: 'Extra Bold (800)', value: '800' },
    { name: 'Black (900)', value: '900' }
];

const ANIMATION_PRESETS = [
    { name: 'Không chọn', value: 'none' },
    { name: 'bounce', value: 'bounce' },
    { name: 'flash', value: 'flash' },
    { name: 'pulse', value: 'pulse' },
    { name: 'rubberBand', value: 'rubberBand' },
    { name: 'shake', value: 'shake' },
    { name: 'swing', value: 'swing' },
    { name: 'tada', value: 'tada' },
    { name: 'wobble', value: 'wobble' },
    { name: 'bounceIn', value: 'bounceIn' },
    { name: 'bounceInDown', value: 'bounceInDown' },
    { name: 'bounceInLeft', value: 'bounceInLeft' },
    { name: 'bounceInRight', value: 'bounceInRight' },
    { name: 'bounceInUp', value: 'bounceInUp' },
    { name: 'fadeIn', value: 'fadeIn' },
    { name: 'fadeInDown', value: 'fadeInDown' },
    { name: 'fadeInLeft', value: 'fadeInLeft' },
    { name: 'fadeInRight', value: 'fadeInRight' },
    { name: 'fadeInUp', value: 'fadeInUp' },
    { name: 'slideInDown', value: 'slideInDown' },
    { name: 'slideInLeft', value: 'slideInLeft' },
    { name: 'slideInRight', value: 'slideInRight' },
    { name: 'slideInUp', value: 'slideInUp' },
    { name: 'zoomIn', value: 'zoomIn' },
    { name: 'zoomInDown', value: 'zoomInDown' },
    { name: 'zoomInLeft', value: 'zoomInLeft' },
    { name: 'zoomInRight', value: 'zoomInRight' },
    { name: 'zoomInUp', value: 'zoomInUp' }
];

const COLOR_PRESETS = [
    '#000000', '#ffffff', '#f3f4f6', '#1f2937', '#374151',
    '#667eea', '#764ba2', '#2563eb', '#3b82f6', '#06b6d4',
    '#10b981', '#22c55e', '#84cc16', '#eab308', '#f59e0b',
    '#ef4444', '#dc2626', '#ec4899', '#a855f7', '#8b5cf6'
];

const TEXT_SHADOW_PRESETS = [
    { name: 'Không có', value: 'none' },
    { name: 'Nhẹ', value: '1px 1px 2px rgba(0,0,0,0.1)' },
    { name: 'Vừa', value: '2px 2px 4px rgba(0,0,0,0.2)' },
    { name: 'Đậm', value: '3px 3px 6px rgba(0,0,0,0.3)' },
    { name: 'Neon Xanh', value: '0 0 10px #06b6d4, 0 0 20px #06b6d4, 0 0 30px #06b6d4' },
    { name: 'Neon Hồng', value: '0 0 10px #ec4899, 0 0 20px #ec4899, 0 0 30px #ec4899' },
    { name: '3D Đỏ', value: '1px 1px 0 #dc2626, 2px 2px 0 #b91c1c, 3px 3px 0 #991b1b' },
    { name: '3D Xanh', value: '1px 1px 0 #2563eb, 2px 2px 0 #1e40af, 3px 3px 0 #1e3a8a' }
];

const GRADIENT_PRESETS = [
    { name: 'Không có', value: '' },
    { name: 'Tím - Hồng', value: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' },
    { name: 'Xanh dương', value: 'linear-gradient(135deg, #2563eb 0%, #06b6d4 100%)' },
    { name: 'Xanh lá', value: 'linear-gradient(135deg, #10b981 0%, #84cc16 100%)' },
    { name: 'Cam - Vàng', value: 'linear-gradient(135deg, #f59e0b 0%, #eab308 100%)' },
    { name: 'Đỏ - Hồng', value: 'linear-gradient(135deg, #ef4444 0%, #ec4899 100%)' },
    { name: 'Cầu vồng', value: 'linear-gradient(90deg, #667eea, #764ba2, #f093fb, #4facfe)' }
];


// 3. NHẬN PROPS 'viewMode'
const ElementPropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle, pageId, viewMode }) => {
    const [activeTab, setActiveTab] = useState('design');
    const [showImageEditor, setShowImageEditor] = useState(true);
    const [showUploadModal, setShowUploadModal] = useState(false);
    const fileInputRef = useRef(null);

    if (isCollapsed) { /* ... (giữ nguyên code) ... */ }
    if (!selectedElement || !selectedElement.json) { /* ... (giữ nguyên code) ... */ }

    // 4. ĐỌC DỮ LIỆU BẰNG 'getResponsiveValues'
    const { type } = selectedElement.json;
    const {
        size: responsiveSize,
        styles: responsiveStyles,
        componentData: responsiveComponentData
    } = getResponsiveValues(selectedElement.json, viewMode);

    const isHeading = type === 'heading';
    const isParagraph = type === 'paragraph';
    const isGallery = type === 'gallery';

    if (!isHeading && !isParagraph && !isGallery) { /* ... (giữ nguyên code) ... */ }

    // 5. SỬA CÁC HÀM GHI (WRITE)
    const handleStyleChange = (property, value) => {
        const currentViewStyles = selectedElement.json.styles?.[viewMode] || selectedElement.json.styles?.desktop || {};
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [viewMode]: {
                        ...currentViewStyles,
                        [property]: value,
                    },
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleComponentDataChange = (key, value) => {
        let updatedComponentData;

        if (RESPONSIVE_DATA_KEYS.includes(key)) {
            // Đây là data responsive, lưu vào [viewMode]
            const currentViewData = selectedElement.json.componentData?.[viewMode] || selectedElement.json.componentData?.desktop || {};
            updatedComponentData = {
                ...selectedElement.json.componentData,
                [viewMode]: {
                    ...currentViewData,
                    [key]: value
                }
            };
        } else {
            // Đây là data global (như 'images'), lưu vào gốc
            updatedComponentData = {
                ...selectedElement.json.componentData,
                [key]: value
            };
        }

        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                componentData: updatedComponentData,
            },
        };
        onUpdateElement(updated);
    };

    const handleSizeChange = (dimension, value) => {
        const parsedValue = parseInt(value);
        if (isNaN(parsedValue) || parsedValue < 0) {
            toast.error('Kích thước phải là số dương');
            return;
        }
        const currentViewSize = selectedElement.json.size?.[viewMode] || selectedElement.json.size?.desktop || {};
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                size: {
                    ...selectedElement.json.size,
                    [viewMode]: {
                        ...currentViewSize,
                        [dimension]: parsedValue,
                    },
                },
            },
        };
        onUpdateElement(updated);
    };

    // Các hàm này (toggleFontStyle, applyGradientText...) gọi handleStyleChange đã được sửa
    // nên chúng sẽ hoạt động đúng
    const toggleFontStyle = (property, value) => {
        const currentValue = responsiveStyles[property]; // Đọc từ responsiveStyles
        handleStyleChange(property, currentValue === value ? 'normal' : value);
    };

    const applyGradientText = (gradient) => {
        if (gradient) {
            handleStyleChange('background', gradient);
            handleStyleChange('WebkitBackgroundClip', 'text');
            handleStyleChange('WebkitTextFillColor', 'transparent');
        } else {
            handleStyleChange('background', 'transparent');
            handleStyleChange('WebkitBackgroundClip', 'initial');
            handleStyleChange('WebkitTextFillColor', 'initial');
        }
    };

    // Các hàm gallery (handleImageSelect, handleImageRemove...) gọi handleComponentDataChange
    // đã được sửa (lưu 'images' vào global) nên sẽ hoạt động đúng
    const handleImageSelect = (image) => {
        if (!image?.url) { /* ... */ return; }
        const isValidUrl = /^https?:\/\/.*\.(?:png|jpg|jpeg|gif|webp)$/i.test(image.url);
        if (!isValidUrl) { /* ... */ return; }
        // Đọc từ data global
        const newImages = [...(selectedElement.json.componentData.images || []), image.url];
        handleComponentDataChange('images', newImages);
        toast.success('Đã thêm ảnh vào thư viện!');
        setShowUploadModal(false);
    };

    const handleImageRemove = (index) => {
        // Đọc từ data global
        const newImages = (selectedElement.json.componentData.images || []).filter((_, i) => i !== index);
        handleComponentDataChange('images', newImages);
        toast.success('Đã xóa ảnh khỏi thư viện!');
    };

    // Giả sử window.imageService được import và hoạt động
    const handleFileSelect = async (e) => {
        /* ... (logic file select của bạn có vẻ đúng, vì nó gọi handleComponentDataChange) ... */
        /* ... (nhớ kiểm tra window.imageService) ... */
    };


    // 6. SỬA RENDER FUNCTIONS ĐỂ ĐỌC TỪ RESPONSIVE VALUES
    const renderDesignTab = () => {
        if (isGallery) {
            return (
                <div className="panel-content">
                    <div className="panel-section">
                        {/* ... (Header) ... */}
                        {showImageEditor && (
                            <div className="input-group">
                                <div className="image-list">
                                    {/* Đọc 'images' từ data global (responsiveComponentData chứa cả global) */}
                                    {(responsiveComponentData.images || []).map((imageUrl, index) => (
                                        <div key={index} className="image-item">
                                            <img src={imageUrl} /* ... */ />
                                            <button onClick={() => handleImageRemove(index)} /* ... */ >
                                                <Trash2 size={16} />
                                            </button>
                                        </div>
                                    ))}
                                    {(!responsiveComponentData.images || responsiveComponentData.images.length === 0) && (
                                        <div className="no-images"> /* ... */ </div>
                                    )}
                                </div>
                                {/* ... (Các nút) ... */}
                                <div>
                                    <label className="input-label">Kiểu hiển thị</label>
                                    <select
                                        value={responsiveStyles.display || 'grid'}
                                        onChange={(e) => handleStyleChange('display', e.target.value)}
                                        className="input-select"
                                    >
                                        <option value="grid">Grid</option>
                                        <option value="flex">Flex (Carousel)</option>
                                    </select>
                                </div>
                                <div>
                                    <label className="input-label">Khoảng cách (Gap)</label>
                                    <input
                                        type="range" min="0" max="50" step="1"
                                        value={parseInt(responsiveStyles.gap) || 10}
                                        onChange={(e) => handleStyleChange('gap', e.target.value + 'px')}
                                        className="input-range"
                                    />
                                    <span className="range-value">{parseInt(responsiveStyles.gap || 10)}px</span>
                                </div>
                                <div>
                                    <label className="input-label">Bo góc ảnh</label>
                                    <input
                                        type="range" min="0" max="50" step="1"
                                        value={parseInt(responsiveStyles.borderRadius) || 8}
                                        onChange={(e) => handleStyleChange('borderRadius', e.target.value + 'px')}
                                        className="input-range"
                                    />
                                    <span className="range-value">{parseInt(responsiveStyles.borderRadius || 8)}px</span>
                                </div>
                                <div>
                                    <label className="input-label">Kích thước</label>
                                    <div className="size-inputs">
                                        <div className="size-input-item">
                                            <label className="input-label">W</label>
                                            <input
                                                type="number"
                                                value={responsiveSize.width || 380}
                                                onChange={(e) => handleSizeChange('width', e.target.value)}
                                                className="input-field"
                                            />
                                        </div>
                                        <div className="size-input-item">
                                            <label className="input-label">H</label>
                                            <input
                                                type="number"
                                                value={responsiveSize.height || 300}
                                                onChange={(e) => handleSizeChange('height', e.target.value)}
                                                className="input-field"
                                            />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            );
        }

        return (
            <div className="panel-content">
                <div className="panel-section">
                    <h4 className="section-title"><Type size={16} /> Nội dung</h4>
                    <div className="input-group">
                        <textarea
                            value={responsiveComponentData.content || ''}
                            onChange={(e) => handleComponentDataChange('content', e.target.value)}
                            placeholder={isHeading ? 'Nhập tiêu đề...' : 'Nhập nội dung đoạn văn...'}
                            rows={isHeading ? 2 : 4}
                            style={{ /* ... */ }}
                        />
                    </div>
                </div>

                <div className="panel-section">
                    <h4 className="section-title"><Type size={16} /> Font chữ</h4>
                    <div className="input-group">
                        <div>
                            <label className="input-label">Kiểu chữ</label>
                            <select
                                value={responsiveStyles.fontFamily || 'Arial, sans-serif'}
                                onChange={(e) => handleStyleChange('fontFamily', e.target.value)}
                                className="input-select"
                            >
                                {FONT_PRESETS.map(font => (
                                    <option key={font.value} value={font.value}>{font.name}</option>
                                ))}
                            </select>
                        </div>
                        <div>
                            <label className="input-label">Cỡ chữ</label>
                            <div className="input-with-unit">
                                <input
                                    type="number"
                                    value={parseInt(responsiveStyles.fontSize) || 16}
                                    onChange={(e) => handleStyleChange('fontSize', e.target.value + 'px')}
                                    className="input-field"
                                />
                                <span className="input-unit">px</span>
                            </div>
                        </div>
                        <div>
                            <label className="input-label">Độ đậm</label>
                            <select
                                value={responsiveStyles.fontWeight || '400'}
                                onChange={(e) => handleStyleChange('fontWeight', e.target.value)}
                                className="input-select"
                            >
                                {FONT_WEIGHT_PRESETS.map(weight => (
                                    <option key={weight.value} value={weight.value}>{weight.name}</option>
                                ))}
                            </select>
                        </div>
                        <div>
                            <label className="input-label">Định dạng</label>
                            <div className="format-buttons">
                                <button
                                    onClick={() => toggleFontStyle('fontWeight', 'bold')}
                                    className={`format-btn ${(responsiveStyles.fontWeight === 'bold' || responsiveStyles.fontWeight === '700') ? 'active' : ''}`}
                                    title="Bold"
                                >
                                    <Bold size={16} />
                                </button>
                                <button
                                    onClick={() => toggleFontStyle('fontStyle', 'italic')}
                                    className={`format-btn ${responsiveStyles.fontStyle === 'italic' ? 'active' : ''}`}
                                    title="Italic"
                                >
                                    <Italic size={16} />
                                </button>
                                <button
                                    onClick={() => toggleFontStyle('textDecoration', 'underline')}
                                    className={`format-btn ${responsiveStyles.textDecoration === 'underline' ? 'active' : ''}`}
                                    title="Underline"
                                >
                                    <Underline size={16} />
                                </button>
                                <button
                                    onClick={() => handleStyleChange('textTransform', responsiveStyles.textTransform === 'uppercase' ? 'none' : 'uppercase')}
                                    className={`format-btn ${responsiveStyles.textTransform === 'uppercase' ? 'active' : ''}`}
                                    title="Uppercase"
                                >
                                    <span style={{ fontSize: '11px', fontWeight: '700' }}>TT</span>
                                </button>
                            </div>
                        </div>
                        <div>
                            <label className="input-label">Căn chỉnh</label>
                            <div className="format-buttons">
                                <button
                                    onClick={() => handleStyleChange('textAlign', 'left')}
                                    className={`format-btn ${responsiveStyles.textAlign === 'left' ? 'active' : ''}`}
                                    title="Căn trái"
                                >
                                    <AlignLeft size={16} />
                                </button>
                                {/* ... (Tương tự cho AlignCenter, AlignRight, AlignJustify) ... */}
                            </div>
                        </div>
                        {/* ... (Các phần còn lại, nhớ đổi 'styles.' thành 'responsiveStyles.') ... */}
                         <div>
                            <label className="input-label">Khoảng cách dòng</label>
                            <input
                                type="range" min="0.8" max="3" step="0.1"
                                value={parseFloat(responsiveStyles.lineHeight) || 1.5}
                                onChange={(e) => handleStyleChange('lineHeight', e.target.value)}
                                className="input-range"
                            />
                            <span className="range-value">{parseFloat(responsiveStyles.lineHeight || 1.5).toFixed(1)}</span>
                        </div>
                        <div>
                            <label className="input-label">Khoảng cách chữ</label>
                            <input
                                type="range" min="-5" max="20" step="0.5"
                                value={parseFloat(responsiveStyles.letterSpacing) || 0}
                                onChange={(e) => handleStyleChange('letterSpacing', e.g.target.value + 'px')}
                                className="input-range"
                            />
                            <span className="range-value">{parseFloat(responsiveStyles.letterSpacing || 0).toFixed(1)}px</span>
                        </div>
                    </div>
                </div>

                <div className="panel-section">
                    <h4 className="section-title"><Palette size={16} /> Màu sắc</h4>
                    <div className="input-group">
                        <div>
                            <label className="input-label">Màu chữ</label>
                            <div className="color-input-group">
                                <input
                                    type="color"
                                    value={responsiveStyles.color || '#000000'}
                                    onChange={(e) => handleStyleChange('color', e.target.value)}
                                    className="color-picker"
                                />
                                <input
                                    type="text"
                                    value={responsiveStyles.color || '#000000'}
                                    onChange={(e) => handleStyleChange('color', e.target.value)}
                                    className="input-field"
                                    placeholder="#000000"
                                />
                            </div>
                             <div className="color-preset-grid">
                                {COLOR_PRESETS.map(color => (
                                    <div
                                        key={color}
                                        className={`color-preset-item ${responsiveStyles.color === color ? 'active' : ''}`}
                                        style={{ backgroundColor: color }}
                                        onClick={() => handleStyleChange('color', color)}
                                        title={color}
                                    />
                                ))}
                            </div>
                        </div>
                        <div>
                            <label className="input-label">Gradient chữ</label>
                            <select
                                value={responsiveStyles.background && responsiveStyles.WebkitBackgroundClip === 'text' ? responsiveStyles.background : ''}
                                onChange={(e) => applyGradientText(e.target.value)}
                                className="input-select"
                            >
                                {GRADIENT_PRESETS.map(preset => (
                                    <option key={preset.name} value={preset.value}>{preset.name}</option>
                                ))}
                            </select>
                        </div>
                        <div>
                            <label className="input-label">Màu nền</label>
                            <div className="color-input-group">
                                <input
                                    type="color"
                                    value={responsiveStyles.backgroundColor || '#ffffff'}
                                    onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                    className="color-picker"
                                />
                                <input
                                    type="text"
                                    value={responsiveStyles.backgroundColor || 'transparent'}
                                    onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                                    className="input-field"
                                    placeholder="transparent"
                                />
                            </div>
                        </div>
                    </div>
                </div>

                {/* ... (Sửa tương tự cho các section còn lại: Sun, Box, Kích thước) ... */}
                {/* ... (Ví dụ cho Kích thước) ... */}
                 <div className="panel-section">
                    <h4 className="section-title">Kích thước</h4>
                    <div className="input-group">
                        <div className="size-inputs">
                            <div className="size-input-item">
                                <label className="input-label">W</label>
                                <input
                                    type="number"
                                    value={responsiveSize.width || 400}
                                    onChange={(e) => handleSizeChange('width', e.target.value)}
                                    className="input-field"
                                />
                            </div>
                            <div className="size-input-item">
                                <label className="input-label">H</label>
                                <input
                                    type="number"
                                    value={responsiveSize.height || 80}
                                    onChange={(e) => handleSizeChange('height', e.target.value)}
                                    className="input-field"
                                />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    };

    const renderAnimationTab = () => {
        // ... (Code của tab này đã dùng handleComponentDataChange('animation', ...))
        // ... (Nó đã đúng vì 'animation' là responsive key)
        // ... (Chỉ cần đảm bảo đọc từ 'responsiveComponentData') ...
        return (
            <div className="panel-content">
                <div className="panel-section">
                    <h4 className="section-title">
                        <Sparkles size={16} strokeWidth={1.5} />
                        Hiệu ứng hiển thị
                    </h4>
                    <div className="input-group">
                        <div>
                            <label className="input-label">Hiệu ứng</label>
                            <select
                                value={responsiveComponentData.animation?.type || 'none'}
                                onChange={(e) => handleComponentDataChange('animation', {
                                    ...(responsiveComponentData.animation || {}),
                                    type: e.target.value
                                })}
                                className="input-select"
                            >
                                {ANIMATION_PRESETS.map(anim => (
                                    <option key={anim.value} value={anim.value}>{anim.name}</option>
                                ))}
                            </select>
                        </div>
                        {/* ... (Phần còn lại của Animation tab, đọc từ responsiveComponentData) ... */}
                    </div>
                </div>
            </div>
        );
    };

    return (
        <>
            <div className="element-properties-panel">
                {/* ... (Giữ nguyên header và tabs) ... */}
                {activeTab === 'design' && renderDesignTab()}
                {activeTab === 'animation' && renderAnimationTab()}
            </div>
            {isGallery && (
                <ImageManagerModal
                    isOpen={showUploadModal}
                    onClose={() => setShowUploadModal(false)}
                    onSelectImage={handleImageSelect}
                    pageId={pageId}
                />
            )}
        </>
    );
};

export default ElementPropertiesPanel;
