import React from 'react';
import { useDrag } from 'react-dnd';
import EXTENDED_LIBRARY from '../../constants/ExtendedLibrary';
import '../../styles/ComponentLibrary.css';

const ComponentLibrary = ({ isCollapsed, onToggle, addSection, addElement }) => {
    const ItemTypes = {
        SECTION: 'section',
        ELEMENT: 'element',
    };

    const DraggableComponent = ({ template, type }) => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: type === 'section' || type === 'popup' || type === 'template' ? ItemTypes.SECTION : ItemTypes.ELEMENT,
            item: {
                id: template.id,
                json: template.json,
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        return (
            <div
                ref={drag}
                className={`lpb-component-item ${isDragging ? 'lpb-component-dragging' : ''}`}
                style={{
                    padding: '10px',
                    margin: '5px 0',
                    background: '#f0f0f0',
                    border: '1px solid #ddd',
                    cursor: 'move',
                    opacity: isDragging ? 0.5 : 1,
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px',
                }}
            >
                <i className={template.icon}></i>
                <span>{template.name}</span>
                <small>({template.level})</small>
            </div>
        );
    };

    return (
        <div className={`lpb-component-library ${isCollapsed ? 'lpb-collapsed' : ''}`}>
            <div className="lpb-component-header">
                <h3>Thư viện thành phần</h3>
                <button onClick={onToggle} className="lpb-toggle-button">
                    {isCollapsed ? <i className="fas fa-chevron-right"></i> : <i className="fas fa-chevron-left"></i>}
                </button>
            </div>
            {!isCollapsed && (
                <div className="lpb-component-content">
                    {Object.entries(EXTENDED_LIBRARY).map(([categoryKey, category]) => (
                        <div key={categoryKey}>
                            <h4>
                                <i className={category.icon}></i> {category.name}
                            </h4>
                            {category.subCategories.map((subCategory) => (
                                <div key={subCategory.id} style={{ marginLeft: '10px' }}>
                                    <h5>{subCategory.name}</h5>
                                    {subCategory.templates.map((template) => (
                                        <DraggableComponent
                                            key={template.id}
                                            template={template}
                                            type={categoryKey === 'elements' ? 'element' : 'section'}
                                        />
                                    ))}
                                </div>
                            ))}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default ComponentLibrary;
