import React, { useState, useEffect, useRef } from 'react';
import {
  ChevronRight, ChevronLeft, Star, Sparkles,
  Palette, Box, Sun, Zap, Code, MousePointer2,
  ArrowRight, Play, Heart, User, Circle, Image as ImageIcon,
  Upload, Trash2, X, RefreshCw
} from 'lucide-react';
import { toast } from 'react-toastify';
import DOMPurify from 'dompurify';
import { uploadImage, getPageImages, deleteImage, validateImageFile, compressImage } from '../imageService';
import '../../../styles/ElementPropertiesPanel.css';

const ICON_PRESETS = [
  { name: 'Star', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon></svg>' },
  { name: 'Heart', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><path d="M20.84 4.61a5.5 5.5 0 0 0-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 0 0-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 0 0 0-7.78z"></path></svg>' },
  { name: 'Arrow Right', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><line x1="5" y1="12" x2="19" y2="12"></line><polyline points="12 5 19 12 12 19"></polyline></svg>' },
  { name: 'Play', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><polygon points="5 3 19 12 5 21 5 3"></polygon></svg>' },
  { name: 'User', icon: '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path><circle cx="12" cy="7" r="4"></circle></svg>' },
  { name: 'Facebook', imageUrl: 'https://cdn-icons-png.flaticon.com/512/124/124010.png' },
  { name: 'Zalo', imageUrl: 'https://cdn-icons-png.flaticon.com/512/124/124034.png' },
  { name: 'Messenger', imageUrl: 'https://cdn-icons-png.flaticon.com/512/5968/5968850.png' },
];

const COLOR_PRESETS = [
  '#000000', '#ffffff', '#f3f4f6', '#1f2937', '#374151',
  '#667eea', '#764ba2', '#2563eb', '#3b82f6', '#06b6d4',
  '#10b981', '#22c55e', '#84cc16', '#eab308', '#f59e0b',
  '#ef4444', '#dc2626', '#ec4899', '#a855f7', '#8b5cf6'
];

const HOVER_EFFECTS = [
  { name: 'None', value: 'none' },
  { name: 'Scale Up', value: 'scale', transform: 'scale(1.2)' },
  { name: 'Scale Down', value: 'scaleDown', transform: 'scale(0.9)' },
  { name: 'Rotate', value: 'rotate', transform: 'rotate(15deg)' },
  { name: 'Slide Right', value: 'slideRight', transform: 'translateX(5px)' },
  { name: 'Slide Up', value: 'slideUp', transform: 'translateY(-5px)' },
  { name: 'Pulse', value: 'pulse', animation: 'pulse 0.6s ease infinite' },
];

const ANIMATION_PRESETS = [
  { name: 'Không có', value: 'none' },
  { name: 'Fade In', value: 'fadeIn' },
  { name: 'Zoom In', value: 'zoomIn' },
  { name: 'Slide Up', value: 'slideUp' },
  { name: 'Rotate', value: 'rotate' },
  { name: 'Bounce', value: 'bounce' },
];

const IconPropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle, pageId }) => {
  const [activeTab, setActiveTab] = useState('icon');
  const [showSvgEditor, setShowSvgEditor] = useState(false);
  const [showImageEditor, setShowImageEditor] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [progress, setProgress] = useState(0);
  const fileInputRef = useRef(null);

  // Initialize editor based on element type
  useEffect(() => {
    if (selectedElement?.json?.componentData?.imageUrl) {
      setShowImageEditor(true);
      setShowSvgEditor(false);
    } else {
      setShowSvgEditor(true);
      setShowImageEditor(false);
    }
  }, [selectedElement]);

  // Load images when modal opens
  useEffect(() => {
    if (showUploadModal) {
      loadImages();
    }
  }, [showUploadModal, pageId]);

  const loadImages = async () => {
    setLoading(true);
    try {
      const response = await getPageImages(pageId);
      setImages(response.data || []);
    } catch (error) {
      console.error('Error loading images:', error);
      toast.error(`Lỗi tải ảnh: ${error.message}`, { position: 'bottom-right' });
      setImages([]);
    } finally {
      setLoading(false);
    }
  };

  // Handle file upload
  const handleFileSelect = async (e) => {
    const files = Array.from(e.target.files);
    if (files.length === 0) return;

    setUploading(true);
    setProgress(0);

    try {
      for (const file of files) {
        const validation = validateImageFile(file);
        if (!validation.valid) {
          toast.warn(`${file.name}: ${validation.error}`, { position: 'bottom-right' });
          continue;
        }

        const compressedFile = await compressImage(file);
        const response = await uploadImage(compressedFile, pageId, (progress) => {
          setProgress(progress);
        });

        if (response.success) {
          setImages(prev => [...prev, response.data]);
          handleComponentDataChange('imageUrl', response.data.url);
          toast.success(`Tải lên ${file.name} thành công`, { position: 'bottom-right' });
        }
      }
      setShowUploadModal(false);
    } catch (error) {
      console.error('Upload error:', error);
      toast.error(`Lỗi tải ảnh: ${error.message}`, { position: 'bottom-right' });
    } finally {
      setUploading(false);
      setProgress(0);
      if (fileInputRef.current) fileInputRef.current.value = '';
    }
  };

  // Handle image deletion
  const handleDeleteImage = async (imageKey) => {
    if (!window.confirm('Bạn có chắc chắn muốn xóa ảnh này?')) return;

    try {
      const success = await deleteImage(imageKey);
      if (success) {
        setImages(prev => prev.filter(img => img.key !== imageKey));
        if (componentData.imageUrl === images.find(img => img.key === imageKey)?.url) {
          handleComponentDataChange('imageUrl', '');
        }
        toast.success('Xóa ảnh thành công', { position: 'bottom-right' });
      }
    } catch (error) {
      console.error('Error deleting image:', error);
      toast.error(`Lỗi xóa ảnh: ${error.message}`, { position: 'bottom-right' });
    }
  };

  // Update styles
  const handleStyleChange = (property, value) => {
    const updated = {
      ...selectedElement,
      json: {
        ...selectedElement.json,
        styles: {
          ...styles,
          [property]: value,
        },
      },
    };
    onUpdateElement(updated);
  };

  // Update component data
  const handleComponentDataChange = (key, value) => {
    const updated = {
      ...selectedElement,
      json: {
        ...selectedElement.json,
        componentData: {
          ...componentData,
          [key]: key === 'icon' ? DOMPurify.sanitize(value) : value,
          ...(key === 'imageUrl' ? { icon: undefined } : key === 'icon' ? { imageUrl: undefined } : {}),
        },
      },
    };
    onUpdateElement(updated);
  };

  // Update size
  const handleSizeChange = (dimension, value) => {
    const parsedValue = parseInt(value);
    if (isNaN(parsedValue) || parsedValue < 0) {
      toast.error('Kích thước phải là số dương', { position: 'bottom-right' });
      return;
    }
    const updated = {
      ...selectedElement,
      json: {
        ...selectedElement.json,
        size: {
          ...size,
          [dimension]: parsedValue,
        },
      },
    };
    onUpdateElement(updated);
  };

  // Handle hover effects
  const handleHoverEffectChange = (effect) => {
    const hoverEffect = HOVER_EFFECTS.find(e => e.value === effect);
    const newStyles = { ...styles };
    if (!hoverEffect || effect === 'none') {
      delete newStyles[':hover'];
    } else {
      newStyles[':hover'] = {
        transform: hoverEffect.transform || 'none',
        animation: hoverEffect.animation || 'none',
      };
    }
    const updated = {
      ...selectedElement,
      json: { ...selectedElement.json, styles: newStyles },
    };
    onUpdateElement(updated);
  };

  const { componentData = {}, styles = {}, size = {} } = selectedElement.json;

  // Collapsed state
  if (isCollapsed) {
    return (
        <div className="element-properties-panel-collapsed">
          <button onClick={onToggle} className="toggle-button" title="Mở thuộc tính">
            <ChevronLeft size={18} />
          </button>
        </div>
    );
  }

  // No selected element or not an icon
  if (!selectedElement || !selectedElement.json || selectedElement.json.type !== 'icon') {
    return (
        <div className="element-properties-panel">
          <div className="panel-header">
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <Star size={16} color="#2563eb" />
              <h3 className="panel-title">Thuộc tính Icon</h3>
            </div>
            <button onClick={onToggle} className="toggle-button" title="Đóng">
              <ChevronRight size={18} />
            </button>
          </div>
          <div className="panel-empty">
            <Star className="empty-icon" size={48} strokeWidth={1.5} />
            <p className="empty-text">Chọn một icon để chỉnh sửa</p>
          </div>
        </div>
    );
  }

  // Icon tab
  const renderIconTab = () => (
      <div className="panel-content">
        {/* Icon Type Selector */}
        <div className="panel-section">
          <h4 className="section-title">
            <Code size={14} strokeWidth={1.5} />
            Loại Icon
          </h4>
          <div className="input-group">
            <select
                value={componentData.imageUrl ? 'image' : 'svg'}
                onChange={(e) => {
                  if (e.target.value === 'svg') {
                    setShowImageEditor(false);
                    setShowSvgEditor(true);
                    handleComponentDataChange('imageUrl', '');
                  } else {
                    setShowSvgEditor(false);
                    setShowImageEditor(true);
                    handleComponentDataChange('icon', '');
                  }
                }}
                className="input-select"
            >
              <option value="svg">SVG</option>
              <option value="image">Hình ảnh</option>
            </select>
          </div>
        </div>

        {/* SVG Editor */}
        {showSvgEditor && (
            <div className="panel-section">
              <div className="section-header">
                <h4 className="section-title">
                  <Code size={14} strokeWidth={1.5} />
                  Icon SVG
                </h4>
                <button
                    onClick={() => setShowSvgEditor(!showSvgEditor)}
                    className={`toggle-editor-btn ${showSvgEditor ? 'active' : ''}`}
                >
                  {showSvgEditor ? 'Ẩn Editor' : 'Sửa SVG'}
                </button>
              </div>

              {showSvgEditor && (
                  <>
                    <div className="icon-preview">
                      {componentData.icon ? (
                          <div
                              style={{ width: '64px', height: '64px', color: styles.color || '#000' }}
                              dangerouslySetInnerHTML={{ __html: DOMPurify.sanitize(componentData.icon) }}
                          />
                      ) : (
                          <Star size={32} color="#9ca3af" />
                      )}
                    </div>

                    <div className="input-group">
                      <label className="input-label">Mã SVG</label>
                      <textarea
                          value={componentData.icon || ''}
                          onChange={(e) => handleComponentDataChange('icon', e.target.value)}
                          placeholder="Dán mã SVG tại đây..."
                          rows={8}
                          className="input-field"
                          style={{ fontFamily: 'monospace' }}
                      />
                    </div>

                    <div className="input-group">
                      <label className="input-label">Icon có sẵn</label>
                      <div className="icon-preset-grid">
                        {ICON_PRESETS.filter(preset => preset.icon).map((preset) => (
                            <button
                                key={preset.name}
                                onClick={() => handleComponentDataChange('icon', preset.icon)}
                                className={`icon-preset-item ${componentData.icon === preset.icon ? 'active' : ''}`}
                                title={preset.name}
                            >
                              <div style={{ width: '20px', height: '20px' }} dangerouslySetInnerHTML={{ __html: preset.icon }} />
                            </button>
                        ))}
                      </div>
                    </div>
                  </>
              )}
            </div>
        )}

        {/* Image Editor */}
        {showImageEditor && (
            <div className="panel-section">
              <div className="section-header">
                <h4 className="section-title">
                  <ImageIcon size={14} strokeWidth={1.5} />
                  Hình ảnh
                </h4>
                <button
                    onClick={() => setShowImageEditor(!showImageEditor)}
                    className={`toggle-editor-btn ${showImageEditor ? 'active' : ''}`}
                >
                  {showImageEditor ? 'Ẩn Editor' : 'Sửa Hình ảnh'}
                </button>
              </div>

              {showImageEditor && (
                  <>
                    <div className="icon-preview">
                      {componentData.imageUrl ? (
                          <img
                              src={componentData.imageUrl}
                              alt={componentData.alt || 'Icon'}
                              style={{ width: '64px', height: '64px', objectFit: 'contain' }}
                          />
                      ) : (
                          <ImageIcon size={32} color="#9ca3af" />
                      )}
                    </div>

                    <button
                        onClick={() => setShowUploadModal(true)}
                        className="upload-btn"
                    >
                      <Upload size={16} />
                      Chọn từ thư viện
                    </button>

                    <div className="input-group">
                      <label className="input-label">URL Hình ảnh</label>
                      <input
                          type="text"
                          value={componentData.imageUrl || ''}
                          onChange={(e) => handleComponentDataChange('imageUrl', e.target.value)}
                          placeholder="https://example.com/image.jpg"
                          className="input-field"
                      />
                    </div>

                    <div className="input-group">
                      <label className="input-label">Văn bản thay thế (Alt Text)</label>
                      <input
                          type="text"
                          value={componentData.alt || ''}
                          onChange={(e) => handleComponentDataChange('alt', e.target.value)}
                          placeholder="Mô tả hình ảnh"
                          className="input-field"
                      />
                    </div>

                    <button
                        onClick={() => fileInputRef.current?.click()}
                        className="upload-btn"
                    >
                      <Upload size={16} />
                      Tải ảnh lên
                    </button>
                    <input
                        ref={fileInputRef}
                        type="file"
                        accept="image/*"
                        multiple
                        onChange={handleFileSelect}
                        style={{ display: 'none' }}
                    />

                    <div className="input-group">
                      <label className="input-label">Hình ảnh có sẵn</label>
                      <div className="icon-preset-grid">
                        {ICON_PRESETS.filter(preset => preset.imageUrl).map((preset) => (
                            <button
                                key={preset.name}
                                onClick={() => handleComponentDataChange('imageUrl', preset.imageUrl)}
                                className={`icon-preset-item ${componentData.imageUrl === preset.imageUrl ? 'active' : ''}`}
                                title={preset.name}
                            >
                              <img src={preset.imageUrl} alt={preset.name} style={{ width: '20px', height: '20px', objectFit: 'contain' }} />
                            </button>
                        ))}
                      </div>
                    </div>
                  </>
              )}
            </div>
        )}

        {/* Size */}
        <div className="panel-section">
          <h4 className="section-title">
            <Box size={14} strokeWidth={1.5} />
            Kích thước
          </h4>
          <div className="input-group">
            <div className="size-inputs">
              <div className="size-input-item">
                <label className="input-label">Chiều rộng</label>
                <div className="input-with-unit">
                  <input
                      type="number"
                      value={size.width || 50}
                      onChange={(e) => handleSizeChange('width', e.target.value)}
                      className="input-field"
                      min="0"
                  />
                  <span className="input-unit">px</span>
                </div>
              </div>
              <div className="size-input-item">
                <label className="input-label">Chiều cao</label>
                <div className="input-with-unit">
                  <input
                      type="number"
                      value={size.height || 50}
                      onChange={(e) => handleSizeChange('height', e.target.value)}
                      className="input-field"
                      min="0"
                  />
                  <span className="input-unit">px</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
  );

  // Effects tab
  const renderEffectsTab = () => (
      <div className="panel-content">
        {/* Colors */}
        <div className="panel-section">
          <h4 className="section-title">
            <Palette size={14} strokeWidth={1.5} />
            Màu sắc
          </h4>
          <div className="input-group">
            <label className="input-label">Màu Icon</label>
            <div className="color-input-group">
              <input
                  type="color"
                  value={styles.color || '#000000'}
                  onChange={(e) => handleStyleChange('color', e.target.value)}
                  className="color-picker"
                  disabled={componentData.imageUrl}
                  style={{ opacity: componentData.imageUrl ? 0.5 : 1 }}
              />
              <input
                  type="text"
                  value={styles.color || '#000000'}
                  onChange={(e) => {
                    const value = e.target.value;
                    if (/^#[0-9A-Fa-f]{6}$/.test(value)) {
                      handleStyleChange('color', value);
                    } else {
                      toast.error('Vui lòng nhập mã màu hex hợp lệ (VD: #FFFFFF)', { position: 'bottom-right' });
                    }
                  }}
                  className="input-field"
                  placeholder="#000000"
                  disabled={componentData.imageUrl}
                  style={{ opacity: componentData.imageUrl ? 0.5 : 1 }}
              />
            </div>
            <div className="color-preset-grid">
              {COLOR_PRESETS.map((color) => (
                  <div
                      key={color}
                      onClick={() => !componentData.imageUrl && handleStyleChange('color', color)}
                      className={`color-preset-item ${styles.color === color && !componentData.imageUrl ? 'active' : ''}`}
                      style={{ backgroundColor: color, opacity: componentData.imageUrl ? 0.5 : 1 }}
                      title={color}
                  />
              ))}
            </div>
          </div>
        </div>

        {/* Effects */}
        <div className="panel-section">
          <h4 className="section-title">
            <Sun size={14} strokeWidth={1.5} />
            Hiệu ứng
          </h4>
          <div className="input-group">
            <div>
              <label className="input-label">Bóng (Drop Shadow)</label>
              <input
                  type="text"
                  value={styles.filter || 'none'}
                  onChange={(e) => handleStyleChange('filter', e.target.value)}
                  placeholder="drop-shadow(0 4px 8px rgba(0,0,0,0.2))"
                  className="input-field"
              />
            </div>
            <div>
              <label className="input-label">Độ mờ (Opacity)</label>
              <div className="range-group">
                <input
                    type="range"
                    min="0"
                    max="1"
                    step="0.1"
                    value={styles.opacity || 1}
                    onChange={(e) => handleStyleChange('opacity', parseFloat(e.target.value))}
                    className="input-range"
                />
                <span className="range-value">{((styles.opacity || 1) * 100).toFixed(0)}%</span>
              </div>
            </div>
          </div>
        </div>

        {/* Hover Effects */}
        <div className="panel-section">
          <h4 className="section-title">
            <MousePointer2 size={14} strokeWidth={1.5} />
            Hiệu ứng Hover
          </h4>
          <div className="input-group">
            <select
                value={styles[':hover']?.transform ? HOVER_EFFECTS.find(e => e.transform === styles[':hover'].transform)?.value || 'none' : 'none'}
                onChange={(e) => handleHoverEffectChange(e.target.value)}
                className="input-select"
            >
              {HOVER_EFFECTS.map((effect) => (
                  <option key={effect.value} value={effect.value}>
                    {effect.name}
                  </option>
              ))}
            </select>
          </div>
        </div>
      </div>
  );

  // Animation tab
  const renderAnimationTab = () => (
      <div className="panel-content">
        <div className="panel-section">
          <h4 className="section-title">
            <Sparkles size={14} strokeWidth={1.5} />
            Hiệu ứng xuất hiện
          </h4>
          <div className="input-group">
            <div>
              <label className="input-label">Kiểu animation</label>
              <select
                  value={componentData.animation?.type || 'none'}
                  onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, type: e.target.value })}
                  className="input-select"
              >
                {ANIMATION_PRESETS.map(anim => (
                    <option key={anim.value} value={anim.value}>{anim.name}</option>
                ))}
              </select>
            </div>
            <div>
              <label className="input-label">Thời gian: {componentData.animation?.duration || 1000}ms</label>
              <div className="range-group">
                <input
                    type="range"
                    min="100"
                    max="3000"
                    step="100"
                    value={componentData.animation?.duration || 1000}
                    onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, duration: parseInt(e.target.value) })}
                    className="input-range"
                />
                <span className="range-value">{componentData.animation?.duration || 1000}ms</span>
              </div>
            </div>
            <div>
              <label className="input-label">Độ trễ: {componentData.animation?.delay || 0}ms</label>
              <div className="range-group">
                <input
                    type="range"
                    min="0"
                    max="3000"
                    step="100"
                    value={componentData.animation?.delay || 0}
                    onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, delay: parseInt(e.target.value) })}
                    className="input-range"
                />
                <span className="range-value">{componentData.animation?.delay || 0}ms</span>
              </div>
            </div>
            <div className="checkbox-group">
              <input
                  type="checkbox"
                  id="repeat-animation"
                  checked={componentData.animation?.repeat || false}
                  onChange={(e) => handleComponentDataChange('animation', { ...componentData.animation, repeat: e.target.checked })}
              />
              <label htmlFor="repeat-animation" className="checkbox-label">Lặp lại animation</label>
            </div>
          </div>
        </div>
      </div>
  );

  // Upload Modal
  const renderUploadModal = () => (
      <div className="modal-overlay">
        <div className="modal-content">
          <div className="modal-header">
            <h3 className="modal-title">Thư viện hình ảnh</h3>
            <button onClick={() => setShowUploadModal(false)} className="modal-close-btn">
              <X size={20} />
            </button>
          </div>

          <div className="modal-body">
            <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                multiple
                onChange={handleFileSelect}
                style={{ display: 'none' }}
            />
            <button
                onClick={() => fileInputRef.current?.click()}
                disabled={uploading}
                className={`upload-btn ${uploading ? 'disabled' : ''}`}
            >
              {uploading ? <RefreshCw size={18} className="animate-spin" /> : <Upload size={18} />}
              {uploading ? `Đang tải lên... ${progress}%` : 'Tải ảnh lên S3'}
            </button>
            <p className="modal-info-text">Hỗ trợ JPG, PNG, GIF, WebP - Tối đa 10MB</p>
            {uploading && (
                <div className="progress-bar">
                  <div className="progress-fill" style={{ width: `${progress}%` }} />
                </div>
            )}
          </div>

          <div className="modal-image-grid">
            {loading ? (
                <div className="loading-container">
                  <RefreshCw size={24} className="animate-spin" color="#3b82f6" />
                </div>
            ) : images.length === 0 ? (
                <div className="empty-container">
                  <ImageIcon size={48} strokeWidth={1.5} />
                  <p className="empty-text">Chưa có hình ảnh nào</p>
                </div>
            ) : (
                <div className="image-grid">
                  {images.map((image) => (
                      <div
                          key={image.key || image.id}
                          className={`image-item ${componentData.imageUrl === image.url ? 'active' : ''}`}
                          onClick={() => {
                            handleComponentDataChange('imageUrl', image.url);
                            setShowUploadModal(false);
                          }}
                      >
                        <img src={image.url} alt={image.name} className="image-preview" />
                        <div className="image-overlay">
                          <button
                              onClick={(e) => {
                                e.stopPropagation();
                                handleDeleteImage(image.key || image.id);
                              }}
                              className="delete-btn"
                          >
                            <Trash2 size={16} />
                          </button>
                        </div>
                        <div className="image-name">{image.name}</div>
                      </div>
                  ))}
                </div>
            )}
          </div>

          <div className="modal-footer">
            <button onClick={loadImages} className="refresh-btn">
              <RefreshCw size={16} />
              Làm mới
            </button>
            <button onClick={() => setShowUploadModal(false)} className="close-btn">
              Đóng
            </button>
          </div>
        </div>
      </div>
  );

  return (
      <>
        <div className="element-properties-panel">
          <div className="panel-header">
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <Star size={16} color="#2563eb" />
              <h3 className="panel-title">Thuộc tính Icon</h3>
            </div>
            <button onClick={onToggle} className="toggle-button" title="Đóng">
              <ChevronRight size={18} />
            </button>
          </div>

          <div className="panel-tabs">
            <button
                onClick={() => setActiveTab('icon')}
                className={`tab-button ${activeTab === 'icon' ? 'active' : ''}`}
            >
              <ImageIcon size={16} />
              <span>Icon</span>
            </button>
            <button
                onClick={() => setActiveTab('effects')}
                className={`tab-button ${activeTab === 'effects' ? 'active' : ''}`}
            >
              <Palette size={16} />
              <span>Hiệu ứng</span>
            </button>
            <button
                onClick={() => setActiveTab('animation')}
                className={`tab-button ${activeTab === 'animation' ? 'active' : ''}`}
            >
              <Sparkles size={16} />
              <span>Animation</span>
            </button>
          </div>

          {activeTab === 'icon' && renderIconTab()}
          {activeTab === 'effects' && renderEffectsTab()}
          {activeTab === 'animation' && renderAnimationTab()}
        </div>

        {showUploadModal && renderUploadModal()}
      </>
  );
};

export default IconPropertiesPanel;
