import React, { useRef, useCallback, useState, useEffect } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import Guidelines from './Guidelines';
import Rulers from './Rulers';
import SelectionOverlay from './SelectionOverlay';
import { throttle } from 'lodash'; // npm install lodash
import '../../styles/Canvas.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

// Hàm tính tọa độ trên Canvas
const getCanvasPosition = (mouseX, mouseY, canvasRef, zoomLevel, scrollX = 0, scrollY = 0) => {
    if (!canvasRef.current) {
        console.error('Canvas ref is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }
    const canvasRect = canvasRef.current.getBoundingClientRect();
    const x = (mouseX - canvasRect.left + scrollX) / (zoomLevel / 100);
    const y = (mouseY - canvasRect.top + scrollY) / (zoomLevel / 100);
    return { x: Math.max(0, x), y: Math.max(0, y) };
};

// Hàm snapping
const snapToGrid = (x, y, gridSize, snapPoints = [], canvasBounds) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    snappedX = Math.max(0, Math.min(snappedX, canvasBounds.width));
    snappedY = Math.max(0, Math.min(snappedY, canvasBounds.height));

    return { x: snappedX, y: snappedY };
};

// Hàm tính bounds của phần tử
const getElementBounds = (element) => ({
    left: element.position.desktop?.x || 0,
    top: element.position.desktop?.y || 0,
    right: (element.position.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasRef,
         snapPoints,
         canvasBounds,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {} } = element;
        const elementRef = useRef(null);
        const tempPositionRef = useRef(position[viewMode] || { x: 0, y: 0 });
        const offsetRef = useRef({ x: 0, y: 0 });

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: () => {
                const elementRect = elementRef.current?.getBoundingClientRect();
                const canvasRect = canvasRef.current?.getBoundingClientRect();
                if (!elementRect || !canvasRect) {
                    console.error('Cannot calculate offset for element:', id);
                    return { id, offset: { x: 0, y: 0 } };
                }
                const offset = {
                    x: (elementRect.left - canvasRect.left) / (zoomLevel / 100) - (position[viewMode]?.x || 0),
                    y: (elementRect.top - canvasRect.top) / (zoomLevel / 100) - (position[viewMode]?.y || 0),
                };
                offsetRef.current = offset;
                return { id, offset };
            },
            end: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) return;
                const pos = getCanvasPosition(
                    clientOffset.x,
                    clientOffset.y,
                    canvasRef,
                    zoomLevel,
                    canvasRef.current.scrollLeft,
                    canvasRef.current.scrollTop
                );
                const snapped = snapToGrid(pos.x - item.offset.x, pos.y - item.offset.y, gridSize, snapPoints, canvasBounds);
                onUpdatePosition(id, { [viewMode]: snapped }, true);
                tempPositionRef.current = snapped;
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        const handleMouseMove = useCallback(
            throttle((e) => {
                if (!isDragging || !canvasRef.current) return;
                const pos = getCanvasPosition(
                    e.clientX,
                    e.clientY,
                    canvasRef,
                    zoomLevel,
                    canvasRef.current.scrollLeft,
                    canvasRef.current.scrollTop
                );
                const snapped = snapToGrid(pos.x - offsetRef.current.x, pos.y - offsetRef.current.y, gridSize, snapPoints, canvasBounds);
                tempPositionRef.current = snapped;
                if (elementRef.current) {
                    elementRef.current.style.left = `${snapped.x}px`;
                    elementRef.current.style.top = `${snapped.y}px`;
                }
            }, 50),
            [isDragging, canvasRef, zoomLevel, gridSize, snapPoints, canvasBounds]
        );

        useEffect(() => {
            if (isDragging) {
                document.addEventListener('mousemove', handleMouseMove);
            }
            return () => {
                document.removeEventListener('mousemove', handleMouseMove);
            };
        }, [isDragging, handleMouseMove]);

        const handleMouseDown = (e) => {
            e.stopPropagation();
            onSelectElement(id, e.ctrlKey);
        };

        const handleContextMenu = (e) => {
            e.preventDefault();
            e.stopPropagation();
            onContextMenu(id, { x: e.clientX, y: e.clientY });
        };

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    drag(node);
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                style={{
                    position: 'absolute',
                    left: position[viewMode]?.x || 0,
                    top: position[viewMode]?.y || 0,
                    width: size.width,
                    height: size.height,
                    zIndex: position.z || 1,
                    cursor: isDragging ? 'grabbing' : 'grab',
                    opacity: isDragging ? 0.8 : 1,
                    ...styles,
                }}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {(() => {
                    switch (type) {
                        case 'heading':
                            return (
                                <h2 style={{ fontSize: componentData.fontSize || '1.5rem', color: componentData.color || '#1f2937', margin: 0, userSelect: 'none' }}>
                                    {componentData.content || 'Tiêu đề'}
                                </h2>
                            );
                        case 'paragraph':
                            return (
                                <p style={{ fontSize: componentData.fontSize || '1rem', color: componentData.color || '#1f2937', margin: 0, userSelect: 'none' }}>
                                    {componentData.content || 'Đoạn văn'}
                                </p>
                            );
                        case 'button':
                            return (
                                <button style={{ padding: '8px 16px', borderRadius: '8px', background: componentData.background || '#2563eb', color: componentData.color || '#fff', border: 'none', cursor: 'pointer', userSelect: 'none' }}>
                                    {componentData.content || 'Nút'}
                                </button>
                            );
                        case 'image':
                            return (
                                <img
                                    src={componentData.src || 'https://via.placeholder.com/150'}
                                    alt="Image"
                                    style={{ maxWidth: '100%', height: 'auto', userSelect: 'none', pointerEvents: 'none' }}
                                />
                            );
                        case 'form':
                            return (
                                <form style={{ display: 'flex', flexDirection: 'column', gap: '10px', userSelect: 'none' }}>
                                    <input type="text" placeholder="Input" style={{ padding: '8px', borderRadius: '8px', border: '1px solid #ccc' }} readOnly />
                                    <button type="button" style={{ background: '#2563eb', color: '#fff', padding: '8px 16px', borderRadius: '8px', border: 'none' }}>
                                        Submit
                                    </button>
                                </form>
                            );
                        default:
                            return <div style={{ userSelect: 'none' }}>{componentData.content || 'Unknown'}</div>;
                    }
                })()}
            </div>
        );
    }
);

const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         onGroupElements,
         viewMode,
         zoomLevel,
         gridSize,
         showGrid,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);
        const [contextMenu, setContextMenu] = useState(null);

        const canvasBounds = {
            width: pageData?.canvas?.width || 1200,
            height: pageData?.canvas?.height || 1000,
        };

        const snapPoints = React.useMemo(() => {
            const points = pageData?.elements?.flatMap((el) => {
                const bounds = getElementBounds(el);
                return [
                    { x: bounds.left, y: bounds.top },
                    { x: bounds.right, y: bounds.bottom },
                    { x: bounds.centerX, y: bounds.centerY },
                    { x: bounds.left, y: bounds.centerY },
                    { x: bounds.right, y: bounds.centerY },
                    { x: bounds.centerX, y: bounds.top },
                    { x: bounds.centerX, y: bounds.bottom },
                ];
            }) || [];
            points.push(
                { x: 0, y: 0 },
                { x: canvasBounds.width, y: 0 },
                { x: 0, y: canvasBounds.height },
                { x: canvasBounds.width, y: canvasBounds.height },
                { x: canvasBounds.width / 2, y: canvasBounds.height / 2 }
            );
            return points;
        }, [pageData?.elements, canvasBounds]);

        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) return;

                const pos = getCanvasPosition(
                    clientOffset.x,
                    clientOffset.y,
                    canvasRef,
                    zoomLevel,
                    canvasRef.current.scrollLeft,
                    canvasRef.current.scrollTop
                );
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints, canvasBounds);

                const newGuidelines = [];
                snapPoints.forEach((point) => {
                    if (Math.abs(snapped.x - point.x) < 15) {
                        newGuidelines.push({ vertical: true, position: point.x });
                    }
                    if (Math.abs(snapped.y - point.y) < 15) {
                        newGuidelines.push({ vertical: false, position: point.y });
                    }
                });
                setGuidelines(newGuidelines);
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả!');
                    return;
                }

                const pos = getCanvasPosition(
                    clientOffset.x,
                    clientOffset.y,
                    canvasRef,
                    zoomLevel,
                    canvasRef.current.scrollLeft,
                    canvasRef.current.scrollTop
                );
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints, canvasBounds);

                if (item.json) {
                    // Thêm phần tử mới từ ComponentLibrary
                    onAddElement({
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: item.json.componentData || {},
                        position: { [viewMode]: snapped, desktop: snapped, tablet: snapped, mobile: snapped },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                    });
                    toast.success('Đã thêm phần tử!');
                } else if (item.id) {
                    // Di chuyển phần tử hiện có
                    onUpdatePosition(item.id, { [viewMode]: snapped }, true);
                    toast.success('Đã di chuyển phần tử!');
                }
                setGuidelines([]);
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        const handleCanvasClick = (e) => {
            if (e.target === canvasRef.current) {
                onSelectElement([]);
                setContextMenu(null);
            }
        };

        const handleContextMenu = (id, position) => {
            setContextMenu({ id, position });
        };

        const handleKeyDown = useCallback(
            (e) => {
                if (!selectedIds.length) return;

                const nudgeDistance = e.shiftKey ? 10 : 1;

                switch (e.key) {
                    case 'ArrowUp':
                        e.preventDefault();
                        selectedIds.forEach((id) => {
                            const element = pageData.elements.find((el) => el.id === id);
                            if (element) {
                                const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                                onUpdatePosition(id, { [viewMode]: { x: currentPos.x, y: Math.max(0, currentPos.y - nudgeDistance) } }, true);
                            }
                        });
                        break;
                    case 'ArrowDown':
                        e.preventDefault();
                        selectedIds.forEach((id) => {
                            const element = pageData.elements.find((el) => el.id === id);
                            if (element) {
                                const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                                onUpdatePosition(id, { [viewMode]: { x: currentPos.x, y: Math.min(canvasBounds.height - element.size.height, currentPos.y + nudgeDistance) } }, true);
                            }
                        });
                        break;
                    case 'ArrowLeft':
                        e.preventDefault();
                        selectedIds.forEach((id) => {
                            const element = pageData.elements.find((el) => el.id === id);
                            if (element) {
                                const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                                onUpdatePosition(id, { [viewMode]: { x: Math.max(0, currentPos.x - nudgeDistance), y: currentPos.y } }, true);
                            }
                        });
                        break;
                    case 'ArrowRight':
                        e.preventDefault();
                        selectedIds.forEach((id) => {
                            const element = pageData.elements.find((el) => el.id === id);
                            if (element) {
                                const currentPos = element.position[viewMode] || { x: 0, y: 0 };
                                onUpdatePosition(id, { [viewMode]: { x: Math.min(canvasBounds.width - element.size.width, currentPos.x + nudgeDistance), y: currentPos.y } }, true);
                            }
                        });
                        break;
                    case 'Delete':
                    case 'Backspace':
                        e.preventDefault();
                        selectedIds.forEach((id) => onDeleteElement(id));
                        break;
                    case 'Escape':
                        e.preventDefault();
                        onSelectElement([]);
                        setContextMenu(null);
                        break;
                }
            },
            [selectedIds, onUpdatePosition, onDeleteElement, onSelectElement, viewMode, pageData.elements, canvasBounds]
        );

        useHotkeys('ctrl+a', (e) => {
            e.preventDefault();
            onSelectElement(pageData.elements.map((el) => el.id));
        }, [pageData.elements, onSelectElement]);

        useHotkeys('ctrl+g', (e) => {
            e.preventDefault();
            if (selectedIds.length > 1) {
                onGroupElements(selectedIds);
            }
        }, [selectedIds, onGroupElements]);

        return (
            <div className="lpb-canvas-wrapper" onKeyDown={handleKeyDown} tabIndex={0} style={{ outline: 'none' }}>
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    style={{
                        position: 'relative',
                        width: canvasBounds.width,
                        height: canvasBounds.height,
                        minHeight: '100vh',
                        background: pageData?.canvas?.background || '#ffffff',
                        transform: `scale(${zoomLevel / 100})`,
                        transformOrigin: 'top left',
                        cursor: isOver ? 'copy' : 'default',
                    }}
                    onClick={handleCanvasClick}
                >
                    <Rulers canvasWidth={canvasBounds.width} canvasHeight={canvasBounds.height} zoomLevel={zoomLevel} />
                    <Guidelines guidelines={guidelines} zoomLevel={zoomLevel} />
                    {showGrid && (
                        <div
                            className="lpb-canvas-grid"
                            style={{
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                backgroundImage: `
                                    linear-gradient(to right, #e5e7eb 1px, transparent 1px),
                                    linear-gradient(to bottom, #e5e7eb 1px, transparent 1px)
                                `,
                                backgroundSize: `${gridSize}px ${gridSize}px`,
                                pointerEvents: 'none',
                                opacity: 0.5,
                            }}
                        />
                    )}
                    {pageData?.elements?.map((element) => (
                        <Element
                            key={element.id}
                            element={element}
                            isSelected={selectedIds.includes(element.id)}
                            onSelectElement={onSelectElement}
                            onUpdatePosition={onUpdatePosition}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={showGrid ? gridSize : Infinity}
                            onContextMenu={handleContextMenu}
                            canvasRef={canvasRef}
                            snapPoints={snapPoints}
                            canvasBounds={canvasBounds}
                        />
                    ))}
                    <SelectionOverlay
                        selectedElements={pageData?.elements?.filter((el) => selectedIds.includes(el.id)) || []}
                        onResize={onUpdateSize}
                        zoomLevel={zoomLevel}
                    />
                    {contextMenu && (
                        <div
                            className="lpb-context-menu"
                            style={{
                                position: 'fixed',
                                left: contextMenu.position.x,
                                top: contextMenu.position.y,
                                background: 'white',
                                border: '1px solid #ccc',
                                borderRadius: '4px',
                                boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
                                zIndex: 10000,
                            }}
                            onClick={() => setContextMenu(null)}
                        >
                            <div
                                className="lpb-context-menu-item"
                                style={{ padding: '8px 12px', cursor: 'pointer', borderBottom: '1px solid #eee' }}
                                onClick={() => {
                                    onDeleteElement(contextMenu.id);
                                    setContextMenu(null);
                                }}
                            >
                                Delete
                            </div>
                            <div
                                className="lpb-context-menu-item"
                                style={{ padding: '8px 12px', cursor: 'pointer' }}
                                onClick={() => {
                                    onGroupElements([contextMenu.id]);
                                    setContextMenu(null);
                                }}
                            >
                                Group
                            </div>
                        </div>
                    )}
                </div>
            </div>
        );
    }
);

export default Canvas;
