import React from 'react';

const Background = ({ children }) => {
    return (
        <div style={{
            minHeight: '100vh',
            background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 30%, #f1f5f9 100%)',
            position: 'relative',
            overflow: 'hidden'
        }}>
            {/* 3D Geometric shapes like Windows 11 */}
            <div style={{ position: 'absolute', inset: 0, pointerEvents: 'none' }}>
                {/* Large blue circle on the left */}
                <div style={{
                    position: 'absolute',
                    top: '200px',
                    left: '-150px',
                    width: '400px',
                    height: '400px',
                    background: 'linear-gradient(135deg, #7c3aed 0%, #a855f7 50%, #c084fc 100%)',
                    borderRadius: '50%',
                    opacity: 0.6,
                    filter: 'blur(1px)',
                    boxShadow: '0 50px 100px rgba(124, 58, 237, 0.2)',
                    zIndex: 1
                }}></div>

                {/* Purple overlay shape */}
                <div style={{
                    position: 'absolute',
                    top: '150px',
                    left: '-100px',
                    width: '500px',
                    height: '500px',
                    background: 'linear-gradient(135deg, #ec4899 0%, #f472b6 50%, #f9a8d4 100%)',
                    borderRadius: '50% 30% 50% 30%',
                    opacity: 0.3,
                    transform: 'rotate(45deg)',
                    filter: 'blur(2px)',
                    zIndex: 2
                }}></div>

                {/* Small yellow circle top left */}
                <div style={{
                    position: 'absolute',
                    top: '50px',
                    left: '50px',
                    width: '200px',
                    height: '200px',
                    background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 50%, #d97706 100%)',
                    borderRadius: '50%',
                    opacity: 0.4,
                    filter: 'blur(1px)',
                    boxShadow: '0 30px 60px rgba(251, 191, 36, 0.1)',
                    zIndex: 1
                }}></div>

                {/* Pink blob bottom */}
                <div style={{
                    position: 'absolute',
                    bottom: '-100px',
                    left: '100px',
                    width: '300px',
                    height: '300px',
                    background: 'linear-gradient(135deg, #f472b6 0%, #ec4899 50%, #be185d 100%)',
                    borderRadius: '60% 40% 30% 70%',
                    opacity: 0.5,
                    transform: 'rotate(-20deg)',
                    filter: 'blur(1px)',
                    zIndex: 1
                }}></div>

                {/* Right side shapes */}
                <div style={{
                    position: 'absolute',
                    top: '300px',
                    right: '-200px',
                    width: '500px',
                    height: '400px',
                    background: 'linear-gradient(135deg, #06b6d4 0%, #0891b2 50%, #0e7490 100%)',
                    borderRadius: '40% 60% 50% 30%',
                    opacity: 0.2,
                    transform: 'rotate(30deg)',
                    filter: 'blur(3px)',
                    zIndex: 1
                }}></div>

                {/* Small accent shapes */}
                <div style={{
                    position: 'absolute',
                    top: '60%',
                    right: '20%',
                    width: '100px',
                    height: '100px',
                    background: 'linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)',
                    borderRadius: '50%',
                    opacity: 0.3,
                    filter: 'blur(1px)',
                    zIndex: 2
                }}></div>
            </div>

            {/* Top Menu Bar */}
            <nav style={{
                position: 'relative',
                zIndex: 10,
                background: 'rgba(255, 255, 255, 0.8)',
                backdropFilter: 'blur(20px)',
                borderBottom: '1px solid rgba(0, 0, 0, 0.05)',
                boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)'
            }}>
                <div style={{
                    maxWidth: '1200px',
                    margin: '0 auto',
                    padding: '12px 24px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between'
                }}>
                    {/* Logo */}
                    <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                        <img
                            src="https://via.placeholder.com/40x40/7c3aed/ffffff?text=L"
                            alt="Logo"
                            style={{
                                width: '40px',
                                height: '40px',
                                borderRadius: '8px',
                                boxShadow: '0 4px 12px rgba(124, 58, 237, 0.2)'
                            }}
                        />
                        <div>
                            <h1 style={{
                                fontSize: '20px',
                                fontWeight: '600',
                                color: '#1e293b',
                                margin: 0,
                                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
                            }}>Iconic</h1>
                            <p style={{
                                fontSize: '12px',
                                color: '#64748b',
                                margin: 0,
                                fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
                            }}>Digital Platform</p>
                        </div>
                    </div>

                    {/* Menu Items */}
                    <div style={{ display: 'flex', alignItems: 'center', gap: '32px' }}>
                        <a href="#" style={{
                            color: '#475569',
                            textDecoration: 'none',
                            fontSize: '14px',
                            fontWeight: '500',
                            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
                            transition: 'color 0.3s',
                            padding: '8px 12px',
                            borderRadius: '6px'
                        }}>Giới thiệu</a>
                        <a href="#" style={{
                            color: '#475569',
                            textDecoration: 'none',
                            fontSize: '14px',
                            fontWeight: '500',
                            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
                            transition: 'color 0.3s',
                            padding: '8px 12px',
                            borderRadius: '6px'
                        }}>Bài viết</a>
                        <a href="#" style={{
                            color: '#475569',
                            textDecoration: 'none',
                            fontSize: '14px',
                            fontWeight: '500',
                            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
                            transition: 'color 0.3s',
                            padding: '8px 12px',
                            borderRadius: '6px'
                        }}>Pages</a>
                        <a href="#" style={{
                            color: '#475569',
                            textDecoration: 'none',
                            fontSize: '14px',
                            fontWeight: '500',
                            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
                            transition: 'color 0.3s',
                            padding: '8px 12px',
                            borderRadius: '6px'
                        }}>Liên hệ</a>
                    </div>

                    {/* Right side buttons */}
                    <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                        <select style={{
                            background: 'transparent',
                            border: 'none',
                            color: '#64748b',
                            fontSize: '14px',
                            fontWeight: '500',
                            cursor: 'pointer',
                            outline: 'none',
                            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
                        }}>
                            <option>Tiếng việt</option>
                            <option>English</option>
                        </select>
                        <button style={{
                            background: 'transparent',
                            border: '1px solid #e2e8f0',
                            color: '#7c3aed',
                            padding: '8px 16px',
                            borderRadius: '6px',
                            fontSize: '14px',
                            fontWeight: '500',
                            cursor: 'pointer',
                            transition: 'all 0.3s',
                            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
                        }}>Đăng nhập</button>
                        <button style={{
                            background: 'linear-gradient(135deg, #7c3aed 0%, #a855f7 100%)',
                            border: 'none',
                            color: 'white',
                            padding: '8px 16px',
                            borderRadius: '6px',
                            fontSize: '14px',
                            fontWeight: '500',
                            cursor: 'pointer',
                            transition: 'all 0.3s',
                            boxShadow: '0 4px 12px rgba(124, 58, 237, 0.2)',
                            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
                        }}>Đăng ký</button>
                    </div>
                </div>
            </nav>

            {/* Main Content Area */}
            <div style={{
                position: 'relative',
                zIndex: 10,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                minHeight: 'calc(100vh - 70px)',
                padding: '40px 24px'
            }}>
                <div style={{ width: '100%', maxWidth: '480px' }}>
                    {/* Glass container for forms */}
                    <div style={{
                        background: 'rgba(255, 255, 255, 0.7)',
                        backdropFilter: 'blur(20px)',
                        borderRadius: '16px',
                        border: '1px solid rgba(255, 255, 255, 0.8)',
                        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.06)',
                        padding: '4px',
                        overflow: 'hidden'
                    }}>
                        {children}
                    </div>
                </div>
            </div>

            {/* CSS for hover effects */}
            <style>{`
        nav a:hover {
          background-color: rgba(124, 58, 237, 0.05) !important;
          color: #7c3aed !important;
        }
        
        nav button:first-of-type:hover {
          background-color: rgba(124, 58, 237, 0.05) !important;
          border-color: #7c3aed !important;
        }
        
        nav button:last-of-type:hover {
          transform: translateY(-1px);
          box-shadow: 0 6px 16px rgba(124, 58, 237, 0.3) !important;
        }

        /* Responsive */
        @media (max-width: 768px) {
