import React, { useRef, useEffect, useCallback, useMemo } from 'react';
import PropTypes from 'prop-types';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import SectionToolkit from './toolkitqick/SectionToolkit';
import { ItemTypes, getCanvasPosition, snapToGrid } from './helpers';
import eventController from '../../utils/EventUtils';
// Đảm bảo đường dẫn này chính xác!
import { getResponsiveValues } from '../../utils/responsiveSync';

/**
 * Utility to check if URL is valid
 */
// const isValidUrl = (url) => { ... }; // (Giữ nguyên)

/**
 * Reusable utility to generate locked styles for elements
 */
const getLockedStyles = (isLocked) =>
    useMemo(() => (isLocked ? { opacity: 0.7, cursor: 'not-allowed', pointerEvents: 'none' } : {}), [isLocked]);

/**
 * Get canvas width based on view mode
 */
const getCanvasWidth = (viewMode) => {
    switch (viewMode) {
        case 'mobile':
            return 375;
        case 'tablet':
            return 768;
        case 'desktop':
        default:
            return 1200;
    }
};


/**
 * ===================================================================
 * renderComponentContent - Hàm render nội dung (quan trọng)
 * (Đã sửa lỗi tách height/width)
 * ===================================================================
 */
export const renderComponentContent = (
    elementType,
    componentData = {},
    styles = {}, // `styles` ở đây đã được kế thừa (merged)
    children = [],
    isCanvas,
    onSelectChild,
    parentId,
    childId, // ID của chính element này (nếu nó là child)
    isTemplateMode,
    viewMode,
    onDeleteChild // Thêm prop này để truyền xuống
) => {
    // console.log(`Rendering elementType: ${elementType}, parentId: ${parentId}, childId: ${childId}`);

    // ----- SỬA LỖI "Section nhỏ": Tách height/width ra khỏi styles -----
    const { height, width, ...textStyles } = {
        pointerEvents: 'auto',
        userSelect: 'text',
        cursor: 'pointer',
        width: '100%',
        height: '100%',
        ...styles,
    };

    const { height: mediaHeight, width: mediaWidth, ...mediaStyles } = {
        pointerEvents: 'auto',
        cursor: 'pointer',
        width: '100%',
        height: '100%',
        objectFit: styles.objectFit || 'cover',
        ...styles,
    };
    // ----- KẾT THÚC SỬA LỖI -----

    switch (elementType) {
        case 'heading':
            return (
                <h1 style={textStyles} data-element-id={childId}>
                    {componentData.content || 'Default Heading'}
                </h1>
            );

        case 'paragraph':
            return (
                <p style={textStyles} data-element-id={childId}>
                    {componentData.content || 'Default Paragraph'}
                </p>
            );

        case 'gallery':
            const images = componentData.images || [];
            const galleryImages = Array.isArray(images) ? images : [];
            const { height: galleryHeight, width: galleryWidth, ...galleryStyles } = styles;

            return (
                <div
                    style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
                        gap: '16px',
                        padding: '16px',
                        maxWidth: '100%',
                        boxSizing: 'border-box',
                        width: '100%',
                        height: '100%',
                        overflow: 'auto',
                        ...galleryStyles,
                    }}
                    data-element-id={childId}
                >
                    {galleryImages.length > 0 ? (
                        galleryImages.map((image, index) => {
                            const imageSrc = typeof image === 'string'
                                ? image
                                : (image.src || image.url || 'https://via.placeholder.com/150?text=Image');
                            const imageAlt = typeof image === 'string'
                                ? `Image ${index + 1}`
                                : (image.alt || `Image ${index + 1}`);

                            return (
                                <img
                                    key={index}
                                    src={imageSrc}
                                    alt={imageAlt}
                                    style={{
                                        width: '100%',
                                        height: 'auto',
                                        objectFit: styles.objectFit || 'cover',
                                        borderRadius: styles.borderRadius || '8px',
                                        transition: styles[':hover'] ? 'all 0.3s ease' : 'none',
                                        cursor: 'pointer',
                                    }}
                                    onError={(e) => {
                                        e.target.src = 'https://via.placeholder.com/150?text=Error';
                                    }}
                                />
                            );
                        })
                    ) : (
                        <div
                            style={{
                                width: '100%',
                                height: '100%',
                                minHeight: '200px',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                backgroundColor: '#f3f4f6',
                                borderRadius: '8px',
                                color: '#9ca3af',
                            }}
                        >
                            <div style={{ textAlign: 'center' }}>
                                <i className="fas fa-images" style={{ fontSize: '48px', marginBottom: '8px' }} />
                                <p>Gallery Placeholder</p>
                            </div>
                        </div>
                    )}
                </div>
            );

        case 'icon':
            const { height: iconHeight, width: iconWidth, ...iconStyles } = styles;
            return (
                <div
                    style={{
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        width: '100%',
                        height: '100%',
                        ...iconStyles,
                    }}
                    data-element-id={childId}
                >
                    {componentData.imageUrl ? (
                        <img
                            src={componentData.imageUrl}
                            alt={componentData.alt || componentData.title || 'Icon'}
                            style={{
                                width: '100%',
                                height: '100%',
                                objectFit: 'contain',
                            }}
                            onError={(e) => {
                                e.target.style.display = 'none';
                                e.target.parentElement.innerHTML = '<i class="fas fa-exclamation-circle" style="font-size: 24px; color: #ef4444;"></i>';
                            }}
                        />
                    ) : componentData.icon && componentData.icon.includes('<svg') ? (
                        <div
                            dangerouslySetInnerHTML={{ __html: componentData.icon }}
                            style={{
                                width: '100%',
                                height: '100%',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                color: styles.color || '#000', // Cho phép SVG thừa hưởng màu
                            }}
                        />
                    ) : componentData.src ? (
                        <img
                            src={componentData.src}
                            alt={componentData.alt || 'Icon'}
                            style={{
                                width: '100%',
                                height: '100%',
                                objectFit: 'contain',
                            }}
                            onError={(e) => {
                                e.target.style.display = 'none';
                                e.target.parentElement.innerHTML = '<i class="fas fa-star" style="font-size: 24px; color: #f59e0b;"></i>';
                            }}
                        />
                    ) : (
                        <i
                            className={componentData.icon || 'fas fa-star'}
                            style={{
                                fontSize: styles.fontSize || '24px',
                                color: styles.color || '#000',
                            }}
                        />
                    )}
                </div>
            );

        case 'image':
            return (
                <img
                    src={componentData.src || 'https://via.placeholder.com/200?text=Image'}
                    alt={componentData.alt || 'Image'}
                    style={mediaStyles}
                    data-element-id={childId}
                    onError={(e) => {
                        e.target.src = 'https://via.placeholder.com/200?text=Error';
                    }}
                />
            );

        case 'button':
            // Tách các pseudo-classes (lớp giả) và height/width
            const { height: btnHeight, width: btnWidth, ':hover': hover, ':active': active, '@keyframes': keyframes, ...cleanStyles } = styles;

            const buttonStyles = {
                padding: '10px 20px',
                borderRadius: '4px',
                background: '#007bff',
                color: '#fff',
                border: 'none',
                cursor: 'pointer',
                fontFamily: 'inherit',
                fontSize: '16px',
                fontWeight: '500',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                width: '100%',
                height: '100%',
                transition: 'all 0.3s ease',
                boxShadow: 'none',
                ...cleanStyles, // Áp dụng style kế thừa
                pointerEvents: 'auto',
            };

            return (
                <button
                    style={buttonStyles}
                    onClick={(e) => {
                        e.stopPropagation(); // Ngăn click vào element cha
                        if (isCanvas && componentData.events?.onClick) {
                            eventController.handleEvent(componentData.events.onClick, childId || parentId, isCanvas);
                        }
                    }}
                    onMouseEnter={(e) => {
                        if (hover) {
                            Object.assign(e.target.style, hover);
                        }
                    }}
                    onMouseLeave={(e) => {
                        // Quay về style gốc (không hover)
                        Object.keys(hover || {}).forEach(key => e.target.style[key] = buttonStyles[key] || null);
                    }}
                    onMouseDown={(e) => {
                        if (active) {
                            Object.assign(e.target.style, active);
                        }
                    }}
                    onMouseUp={(e) => {
                         // Quay về style (không active)
                        Object.keys(active || {}).forEach(key => e.target.style[key] = buttonStyles[key] || null);
                    }}
                    data-element-id={childId}
                >
                    {componentData.content || 'Button'}
                </button>
            );

        case 'video':
            return (
                <video
                    controls
                    src={componentData.src || 'https://www.w3schools.com/html/mov_bbb.mp4'}
                    style={mediaStyles}
                    data-element-id={childId}
                >
                    Your browser does not support the video tag.
                </video>
            );

        // Section và Popup render children của chúng thông qua ChildElement
        // Chúng không nên được gọi trong renderComponentContent của 1 child
        case 'section':
        case 'popup':
            const { height: containerHeight, width: containerWidth, ...containerStyles } = styles;
            return (
                <div
                    className="ladi-container"
                    style={{
                        position: 'relative',
                        zIndex: 2,
                        width: '100%',
                        height: '100%',
                        ...containerStyles // Áp dụng padding, v.v.
                    }}
                >
                    {children.map((child) => {
                        if (!child || !child.type || !child.id) {
                            console.warn('Invalid child in renderComponentContent:', child);
                            return null;
                        }
                        return (
                            // eslint-disable-next-line no-use-before-define
                            <ChildElement
                                key={child.id}
                                parentId={parentId} // `parentId` ở đây là ID của section/popup
                                id={child.id}
                                type={child.type}
                                element={child} // Truyền toàn bộ object `child`
                                // componentData, styles, position, size sẽ được `ChildElement` tự tính toán
                                isSelected={childId === child.id} // `childId` ở đây là `selectedChildId`
                                onSelectChild={onSelectChild}
                                viewMode={viewMode}
                                onDeleteChild={onDeleteChild}
                                // Truyền các props bị thiếu từ file gốc
                                visible={child.visible}
                                locked={child.locked}
                            />
                        );
                    })}
                </div>
            );

        default:
            const { height: defHeight, width: defWidth, ...defStyles } = styles;
            return (
                <div
                    style={{
                        ...defStyles,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        backgroundColor: '#f3f4f6',
                        color: '#6b7280',
                        padding: '20px',
                        borderRadius: '8px',
                        width: '100%',
                        height: '100%',
                    }}
                >
                    Unsupported: {elementType}
                </div>
            );
    }
};


/**
 * ===================================================================
 * ChildElement
 * ===================================================================
 */
const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         element, // <- Nhận toàn bộ object `element`
         isSelected,
         onSelectChild,
         viewMode,
         onDeleteChild,
         // Props thừa từ file gốc (giờ đã nằm trong `element`)
         componentData: propComponentData,
         styles: propStyles,
         position: propPosition,
         size: propSize,
         visible: propVisible,
         locked: propLocked,
     }) => {
        const dragRef = useRef(null);

        // Ưu tiên `element` prop
        const data = element || {
            id, type, componentData: propComponentData, styles: propStyles,
            position: propPosition, size: propSize, visible: propVisible, locked: propLocked
        };
        const { componentData = {}, visible = true, locked = false } = data;


        // Get responsive values for current view mode
        const { size: responsiveSize, position: responsivePosition, styles: responsiveStyles } = useMemo(
            () => getResponsiveValues(data, viewMode),
            [data, viewMode]
        );

        const [{ isDragging }, drag] = useDrag({
            type: ItemTypes.CHILD_ELEMENT,
            canDrag: () => !locked && !componentData.locked,
            item: () => {
                if (locked || componentData.locked) {
                    toast.warning('Child element đã bị khóa!');
                    return null;
                }
                return { childId: id, parentId, isExisting: true, position: responsivePosition, size: responsiveSize };
            },
            collect: (monitor) => ({ isDragging: monitor.isDragging() }),
        }, [id, parentId, responsivePosition, responsiveSize, locked, componentData.locked]);

        const handleClick = useCallback(
            (e) => {
                e.stopPropagation(); // Ngăn click vào section/popup cha
                console.log(`ChildElement ${id} (type: ${type}) clicked, parentId: ${parentId}`);
                if (typeof onSelectChild === 'function') {
                    onSelectChild(parentId, id);
                    console.log(`onSelectChild called with parentId: ${parentId}, id: ${id}`);
                }
                // Logic event click (nếu có) sẽ được xử lý bên trong `renderComponentContent`
            },
            [parentId, id, type, onSelectChild]
        );

        const handleDelete = useCallback(
            (e) => {
                e.stopPropagation();
                e.preventDefault();
                if (typeof onDeleteChild === 'function' && !locked && !componentData.locked) {
                    onDeleteChild(parentId, id, e);
                    toast.success('Đã xóa thành phần con!');
                } else {
                    toast.warn('Không thể xóa: Element bị khóa!');
                }
            },
            [parentId, id, onDeleteChild, locked, componentData.locked]
        );

        useEffect(() => {
            if (!locked && !componentData.locked) {
                drag(dragRef);
            }
        }, [drag, locked, componentData.locked]);

        if (!visible || componentData.visible === false) {
            return null;
        }

        const lockedStyles = getLockedStyles(locked || componentData.locked);

        // ----- SỬA LỖI LOGIC: Bỏ `getElementWidth/Height` -----
        const wrapperStyles = useMemo(
            () => ({
                position: 'absolute',
                left: responsivePosition.x || 0,
                top: responsivePosition.y || 0,
                // Luôn dùng giá trị từ `responsiveSize`
                width: responsiveSize.width ? `${responsiveSize.width}px` : 'auto',
                height: responsiveSize.height ? `${responsiveSize.height}px` : 'auto',
                zIndex: isDragging ? 1000 : responsivePosition.z || 20,
                cursor: locked || componentData.locked ? 'not-allowed' : isDragging ? 'grabbing' : 'pointer',
                opacity: isDragging ? 0.5 : 1,
                pointerEvents: 'auto', // Cho phép click
                ...lockedStyles,
            }),
            [responsivePosition, isDragging, locked, componentData.locked, lockedStyles, responsiveSize]
        );

        // Tách height/width khỏi `responsiveStyles`
        const { height, width, ...contentStyles } = responsiveStyles;

        return (
            <div
                ref={dragRef} // Gắn drag ref vào wrapper
                className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''} ${
                    isDragging ? 'lpb-child-element-dragging' : ''
                } ${locked || componentData.locked ? 'lpb-element-locked' : ''}`}
                style={wrapperStyles}
                onClick={handleClick} // Xử lý click chọn
            >
                {/* Render nội dung */}
                {renderComponentContent(
                    data.type,
                    data.componentData || {},
                    contentStyles, // SỬA: Truyền contentStyles (đã bỏ height/width)
                    data.children || [],
                    true, // isCanvas = true (để bật event)
                    onSelectChild,
                    parentId, // SỬA: Phải là parentId (section/popup)
                    id, // childId (ID của chính nó)
                    false, // isTemplateMode
                    viewMode
                    // onDeleteChild không được truyền ở đây, nó dùng cho nút xóa
                )}

                {/* Overlay khóa */}
                {(locked || componentData.locked) && (
                    <div
                        style={{
                            position: 'absolute',
                            top: '2px',
                            right: '2px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '2px 6px',
                            borderRadius: '3px',
                            fontSize: '10px',
                            pointerEvents: 'none',
                        }}
                    >
                        <i className="fas fa-lock" />
                    </div>
                )}
                {/* Nút xóa */}
                {isSelected && (
                    <button
                        onClick={handleDelete}
                        style={{
                            position: 'absolute',
                            top: '2px',
                            left: '2px',
                            zIndex: 10002,
                            borderRadius: '50%',
                            background: '#ff7b7b',
                            border: 'none',
                            color: '#ffffff',
                            fontSize: '16px',
                            cursor: 'pointer',
                            padding: '6px',
                            width: '30px',
                            height: '30px',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                        }}
                    >
                        <i className="fas fa-trash-alt" />
                    </button>
                )}
            </div>
        );
    }
);


/**
 * ===================================================================
 * Element (Section/Popup/Element Gốc)
 * ===================================================================
 */
const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize, // <--- THÊM PROP NÀY VÀO
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onAddChild,
         onUpdateChildPosition,
         onMoveChild,
         showGrid,
         setDragPreview,
         visiblePopups: visibleElements = [], // Giữ tên gốc từ file
         onClosePopup: onCloseElement, // Giữ tên gốc từ file
         onSaveTemplate,
         onToggleVisibility,
         onEditElement,
         onMoveElementUp,
         onMoveElementDown,
         onDeleteElement,
         onDeleteChild,
     }) => {
        const {
            id,
            type,
            componentData = {},
            children = [],
            visible = true,
            locked = false,
        } = element;

        // Get responsive values for current view mode
        const { size: responsiveSize, position: responsivePosition, styles: responsiveStyles } = useMemo(
            () => getResponsiveValues(element, viewMode),
            [element, viewMode]
        );

        const canvasWidth = getCanvasWidth(viewMode);

        const elementRef = useRef(null);
        const containerRef = useRef(null); // Dùng cho dropzone của section/popup

        const [{ isDragging }, drag, preview] = useDrag({
            type: ItemTypes.EXISTING_ELEMENT,
            canDrag: () => !locked && type !== 'section' && type !== 'popup',
            item: () => {
                if (locked) {
                    toast.warning('Element đã bị khóa!');
                    return null;
                }
                return {
                    id,
                    elementSize: responsiveSize,
                    elementPosition: responsivePosition,
                    isExisting: true,
                };
            },
            collect: (monitor) => ({ isDragging: monitor.isDragging() }),
        }, [id, responsiveSize, responsivePosition, locked, type]);

        // Snap points cho children bên trong section/popup
        const snapPoints = useMemo(() => {
            const points = [
                { x: 0, y: 0 },
                { x: responsiveSize?.width || 0, y: responsiveSize?.height || 0 },
                { x: (responsiveSize?.width || 0) / 2, y: (responsiveSize?.height || 0) / 2 },
            ];
            (children || []).forEach((child) => {
                if (!child || !child.position || !child.size) return;
                const childResponsive = getResponsiveValues(child, viewMode);
                const bounds = {
                    left: childResponsive.position.x,
                    top: childResponsive.position.y,
                    right: childResponsive.position.x + (childResponsive.size?.width || 0),
                    bottom: childResponsive.position.y + (childResponsive.size?.height || 0),
                    centerX: childResponsive.position.x + (childResponsive.size?.width || 0) / 2,
                    centerY: childResponsive.position.y + (childResponsive.size?.height || 0) / 2,
                };
                points.push(
                    { x: bounds.left, y: bounds.top },
                    { x: bounds.right, y: bounds.bottom },
                    { x: bounds.centerX, y: bounds.centerY }
                );
            });
            return points;
        }, [responsiveSize, children, viewMode]);

        // Drop logic cho Section và Popup (kết hợp)
        const [{ isOver, canDrop }, drop] = useDrop({
            accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
            canDrop: (item, monitor) => {
                if (type !== 'section' && type !== 'popup') return false;
                if (type === 'section' && componentData?.structure !== 'ladi-standard') return false;

                // SỬA LỖI LOGIC DROP: Chỉ cho phép kéo trong cùng 1 parent
                // Nếu bạn muốn cho phép kéo giữa các section, hãy xóa điều kiện `&& item.parentId === id`
                if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT && item.parentId && item.parentId !== id) {
                    return false; // Chặn kéo child từ section khác
                }
                return true;
            },
            drop: (item, monitor) => {
                if (!monitor.canDrop() || !containerRef.current) return { moved: false };

                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return { moved: false };

                const containerRect = containerRef.current.getBoundingClientRect();
                const scale = zoomLevel / 100;
                let x = (clientOffset.x - containerRect.left) / scale;
                let y = (clientOffset.y - containerRect.top) / scale;

                const pos = { x, y };
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT) { // Thả element mới từ thư viện
                    const newId = `${item.id}-${Date.now()}`;
                    const newChild = {
                        id: newId,
                        type: item.json.type,
                        componentData: item.json.componentData || {},
                        position: snapped,
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: item.json.children || [],
                        visible: true,
                        locked: false,
                    };
                    onAddChild(id, newChild); // `handleAddChild` sẽ tự "responsify"
                    toast.success(`Đã thêm ${item.json.type} vào ${type}!`);
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped, parentId: id };

                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) { // Kéo thả child có sẵn
                    if (item.parentId === id) { // Kéo trong cùng 1 container
                        onUpdateChildPosition(id, item.childId, snapped);
                        toast.success(`Đã di chuyển thành phần con trong ${type}!`);
                    } else {
                        // Bị chặn bởi canDrop, nhưng nếu bạn mở khóa, logic này sẽ chạy
                        onMoveChild(item.parentId, item.childId, id, snapped);
                        toast.success(`Đã di chuyển thành phần con sang ${type}!`);
                    }
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped, parentId: id };
                }
                setDragPreview(null);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOver: monitor.isOver(),
                canDrop: monitor.canDrop(),
            }),
        }, [id, type, componentData, viewMode, zoomLevel, gridSize, showGrid, onAddChild, onUpdateChildPosition, onMoveChild, snapPoints]);

        const lockedStyles = getLockedStyles(locked);

        // Animation styles (Đã đúng)
        const animationStyles = useMemo(
            () => ({
                animation: componentData.animation?.onLoad
                    ? `${componentData.animation.onLoad} ${componentData.animation?.duration || 1000}ms ease`
                    : componentData.animation?.type
                        ? `${componentData.animation.type} ${componentData.animation?.duration || 1000}ms ease ${componentData.animation?.delay || 0}ms ${componentData.animation?.repeat ? 'infinite' : ''}`
                        : 'none',
            }),
            [componentData.animation]
        );

        // ----- SỬA LỖI CHIỀU CAO: `sectionStyles` -----
        const sectionStyles = useMemo(() => {
            // Tách height/width ra khỏi responsiveStyles
            const { height, width, ...restStyles } = responsiveStyles;

            return {
                position: 'absolute',
                top: responsivePosition.y || 0,
                width: `${canvasWidth}px`, // Section luôn rộng 100%
                left: '50%',
                transform: 'translateX(-50%)',
                zIndex: responsivePosition.z || 1,
                userSelect: 'none',
                overflow: 'visible',
                ...restStyles, // Áp dụng style (BG, padding, v.v.)
                ...animationStyles,
                ...lockedStyles,
                // Ghi đè height từ `responsiveSize` LÊN TRÊN CÙNG
                height: responsiveSize?.height ? `${responsiveSize.height}px` : 'auto',
            };
        }, [responsivePosition, responsiveSize, canvasWidth, responsiveStyles, animationStyles, lockedStyles]);

        // ----- SỬA LỖI CHIỀU CAO: `popupStyles` -----
        const popupStyles = useMemo(
            () => {
                // Tách height/width ra khỏi responsiveStyles
                const { height, width, ...restStyles } = responsiveStyles;

                return {
                    position: 'absolute',
                    top: responsivePosition.y || '50%',
                    left: responsivePosition.x || '50%',
                    transform: (responsivePosition.x && responsivePosition.y) ? 'none' : 'translate(-50%, -50%)',
                    maxWidth: viewMode === 'mobile' ? '90%' : '600px', // Giữ lại max-width
                    minHeight: '200px',
                    maxHeight: '90vh',
                    zIndex: 1001,
                    borderRadius: '12px',
                    boxShadow: isSelected ? '0 20px 60px rgba(0, 0, 0, 0.3), 0 0 0 3px #3b82f6' : '0 8px 32px rgba(0, 0, 0, 0.25)',
                    overflow: 'hidden',
                    display: 'flex',
                    flexDirection: 'column',
                    transition: 'all 0.3s ease, opacity 0.3s ease, transform 0.3s ease',
                    opacity: 1,
                    transformOrigin: 'center',
                    cursor: locked ? 'not-allowed' : 'default',
                    ...restStyles, // Áp dụng style (BG, padding, v.v.)
                    ...lockedStyles,
                    ...animationStyles,
                    // Ghi đè height/width từ `responsiveSize` LÊN TRÊN CÙNG
                    width: responsiveSize?.width ? `${responsiveSize.width}px` : (viewMode === 'mobile' ? '90%' : '600px'),
                    height: responsiveSize?.height ? `${responsiveSize.height}px` : 'auto',
                };
            },
            [viewMode, responsiveSize, responsivePosition, isSelected, responsiveStyles, lockedStyles, animationStyles]
        );
        // ----- KẾT THÚC SỬA LỖI -----

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        useEffect(() => {
            // Gắn drop ref vào container
            if (type === 'section' || type === 'popup') {
                drop(containerRef);
            }
        }, [drop, type]);

        const handleMouseDown = useCallback(
            (e) => {
                if (locked) {
                    toast.warning('Element đã bị khóa!');
                    return;
                }
                // Ngăn chặn nếu click vào 1 child element
                if (e.target.closest('.lpb-child-element')) {
                    return;
                }
                if (!e.target.closest('button')) {
                    e.stopPropagation();
                    if (typeof onSelectElement === 'function') {
                        onSelectElement([id], e.ctrlKey);
                    }
                    onSelectChild(id, null); // Bỏ chọn child khi click vào cha
                    console.log(`Element ${id} selected`);
                }
            },
            [id, locked, onSelectElement, onSelectChild]
        );

        const handleClick = useCallback(
            (e) => {
                e.stopPropagation();
                if (componentData.events?.onClick) {
                    eventController.handleEvent(componentData.events.onClick, id, true);
                }
            },
            [id, componentData.events]
        );

        const handleContextMenu = useCallback(
            (e) => {
                e.preventDefault();
                e.stopPropagation();
                onContextMenu(id, { x: e.clientX, y: e.clientY });
            },
            [id, onContextMenu]
        );

        if (!visible) {
            return null;
        }

        const isPopup = type === 'popup';
        // Logic hiển thị popup (Đã đúng)
        const shouldShowElement = type === 'section' || isSelected || visibleElements.includes(id);
        if (isPopup && !shouldShowElement) {
            return null;
        }

        // ================== RENDER SECTION ==================
        if (type === 'section' && componentData?.structure === 'ladi-standard') {
            const { height, width, ...containerStyles } = responsiveStyles;

            return (
                <div
                    ref={elementRef}
                    data-element-id={id}
                    className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                    style={sectionStyles} // Đã sửa: styles này đã bao gồm height đúng
                    onMouseDown={locked ? undefined : handleMouseDown}
                    onContextMenu={handleContextMenu}
                >
                    {locked && (
                        <div style={{ /* lock badge styles */ }}>
                            <i className="fas fa-lock" /> Section Locked
                        </div>
                    )}

                    <div className="ladi-section" style={{
                        width: '100%',
                        height: '100%',
                        position: 'relative',
                        isolation: 'isolate',
                        ...containerStyles, // Áp dụng style (BG, v.v.)
                    }}>

                        {/* Overlay (Đã đúng) */}
                        <div
                            className="ladi-overlay"
                            style={{
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                zIndex: 1,
                                pointerEvents: 'none',
                                backgroundColor: componentData.overlayColor || 'transparent',
                                opacity: componentData.overlayOpacity !== undefined ? componentData.overlayOpacity : 0,
                            }}
                        />

                        {/* Container chứa children (Dropzone) */}
                        <div
                            ref={containerRef}
                            className={`ladi-container ${isOver && canDrop ? 'lpb-child-element-hover' : ''}`}
                            style={{
                                position: 'relative',
                                zIndex: 10,
                                padding: containerStyles.padding || '20px', // Lấy padding từ style
                                width: '100%',
                                height: '100%',
                                border: isOver && canDrop ? '2px dashed #2563eb' : 'none',
                                pointerEvents: 'auto',
                            }}
                        >
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    element={child} // Truyền toàn bộ object child
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    viewMode={viewMode}
                                    onDeleteChild={onDeleteChild}
                                />
                            ))}
                        </div>
                    </div>

                    {isSelected && (
                        <SectionToolkit
                            element={element}
                            position={{ x: 0, y: 0 }}
                            onEdit={() => onEditElement(id)}
                            onSaveTemplate={() => onSaveTemplate(id)}
                            onMoveUp={() => onMoveElementUp(id)}
                            onMoveDown={() => onMoveElementDown(id)}
                            onToggleVisibility={() => onToggleVisibility(id)}
                            onDelete={() => onDeleteElement(id)}
                        />
                    )}
                </div>
            );
        }

        // ================== RENDER POPUP ==================
        if (type === 'popup') {
            const { height, width, ...contentContainerStyles } = responsiveStyles;
            return (
                <>
                    {/* Backdrop */}
                    <div
                        className="lpb-popup-backdrop"
                        style={{
                            position: 'fixed',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            background: 'rgba(0, 0, 0, 0.5)',
                            zIndex: 1000,
                            pointerEvents: 'auto',
                            opacity: 1,
                            transition: 'opacity 0.3s ease',
                        }}
                        onClick={(e) => {
                            e.stopPropagation();
                            if (typeof onCloseElement === 'function') {
                                onCloseElement(id);
                                eventController.dispatch('element-close', { elementId: id });
                            }
                        }}
                    />
                    {/* Popup Container */}
                    <div
                        ref={elementRef}
                        data-element-id={id}
                        className={`lpb-popup-container ${isSelected ? 'lpb-popup-selected' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                        style={popupStyles} // Đã sửa: styles này đã bao gồm height/width đúng
                        onMouseDown={locked ? undefined : handleMouseDown}
                        onContextMenu={handleContextMenu}
                    >
                        {/* Header (Đã đúng) */}
                        <div
                            className="lpb-popup-header"
                            style={{
                                padding: '16px 20px',
                                borderBottom: '1px solid #e5e7eb',
                                background: 'linear-gradient(to bottom, #f9fafb, #ffffff)',
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                flexShrink: 0,
                            }}
                        >
                            <h3 style={{ margin: 0, fontSize: '16px', fontWeight: '600', color: '#111827' }}>
                                {componentData.title || 'Popup Title'}
                            </h3>
                            <button
                                className="lpb-popup-close"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    if (typeof onCloseElement === 'function') {
                                        onCloseElement(id);
                                        eventController.dispatch('element-close', { elementId: id });
                                    }
                                }}
                                style={{
                                    width: '28px',
                                    height: '28px',
                                    border: 'none',
                                    background: 'transparent',
                                    cursor: 'pointer',
                                    borderRadius: '6px',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    transition: 'all 0.15s ease',
                                    color: '#6b7280',
                                }}
                            >
                                <i className="fas fa-times" style={{ fontSize: '14px' }} />
                            </button>
                        </div>

                        {/* Content (Dropzone) */}
                        <div
                            ref={containerRef}
                            className={`lpb-popup-content ${isOver && canDrop ? 'lpb-popup-dropzone-active' : ''}`}
                            style={{
                                position: 'relative',
                                flex: 1,
                                padding: '20px', // Padding mặc định
                                overflowY: 'auto',
                                overflowX: 'hidden',
                                minHeight: '200px',
                                border: isOver && canDrop ? '2px dashed #2563eb' : 'none',
                                background: isOver && canDrop ? 'rgba(37, 99, 235, 0.05)' : 'transparent',
                                transition: 'all 0.2s ease',
                                ...contentContainerStyles, // Áp dụng BG, padding, v.v.
                            }}
                        >
                            {children.length === 0 && (
                                <div
                                    className="lpb-popup-empty"
                                    style={{
                                        display: 'flex',
                                        flexDirection: 'column',
                                        alignItems: 'center',
                                        justifyContent: 'center',
                                        minHeight: '200px',
                                        color: '#9ca3af',
                                        textAlign: 'center',
                                        padding: '40px 20px',
                                    }}
                                >
                                    <i
                                        className="fas fa-hand-pointer"
                                        style={{ fontSize: '48px', marginBottom: '16px', opacity: 0.3, color: '#d1d5db' }}
                                    />
                                    <p style={{ margin: '0 0 8px', fontSize: '14px', fontWeight: '500', color: '#6b7280' }}>
                                        Kéo thả elements vào đây
                                    </p>
                                    <small style={{ fontSize: '12px', color: '#9ca3af' }}>
                                        Popup có thể chứa bất kỳ element nào
                                    </small>
                                </div>
                            )}
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    element={child} // Truyền toàn bộ object child
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    viewMode={viewMode}
                                    onDeleteChild={onDeleteChild}
                                />
                            ))}
                        </div>
                        {locked && (
                            <div
                                style={{
                                    position: 'absolute',
                                    top: '5px',
                                    right: '5px',
                                    zIndex: 10001,
                                    background: 'rgba(0,0,0,0.7)',
                                    color: '#fff',
                                    padding: '4px 8px',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    pointerEvents: 'none',
                                }}
                            >
                                <i className="fas fa-lock" /> Locked
                            </div>
                        )}
                    </div>
                </>
            );
        }

        // ================== RENDER ELEMENT KHÁC (Button, Image, Text...) ==================
        // ----- SỬA LỖI CHIỀU CAO: `commonElementStyles` -----
        const { height, width, ...restCommonStyles } = responsiveStyles;
        const commonElementStyles = useMemo(
            () => ({
                position: 'absolute',
                top: responsivePosition.y || 0,
                left: responsivePosition.x || 0,
                zIndex: responsivePosition.z || 1,
                opacity: isDragging ? 0.5 : 1,
                cursor: locked ? 'not-allowed' : isDragging ? 'grabbing' : 'pointer',
                userSelect: type === 'heading' || type === 'paragraph' ? 'text' : 'none',
                ...restCommonStyles, // Áp dụng style (BG, padding, v.v.)
                ...lockedStyles,
                ...animationStyles,
                // Ghi đè height/width từ `responsiveSize` LÊN TRÊN CÙNG
                width: responsiveSize?.width ? `${responsiveSize.width}px` : 'auto',
                height: responsiveSize?.height ? `${responsiveSize.height}px` : 'auto',
            }),
            [responsivePosition, responsiveSize, type, isDragging, locked, responsiveStyles, lockedStyles, animationStyles]
        );

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    if (!locked && type !== 'section' && type !== 'popup') {
                        drag(node);
                    }
                }}
                data-element-id={id}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                style={commonElementStyles} // Đã sửa
                onMouseDown={locked ? undefined : handleMouseDown}
                onContextMenu={handleContextMenu}
                onClick={handleClick}
            >
                {locked && (
                    <div
                        style={{
                            position: 'absolute',
                            top: '5px',
                            right: '5px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '4px 8px',
                            borderRadius: '4px',
                            fontSize: '11px',
                            pointerEvents: 'none',
                        }}
                    >
                        <i className="fas fa-lock" /> Locked
                    </div>
                )}
                {renderComponentContent(
                    type,
                    componentData,
                    responsiveStyles, // Truyền style đã kế thừa
                    children,
                    true,
                    onSelectChild,
                    id,
                    null,
                    false,
                    viewMode,
                    onDeleteChild // Truyền xuống
                )}
            </div>
        );
    }
);

// PropTypes for ChildElement
ChildElement.propTypes = {
    parentId: PropTypes.string.isRequired,
    id: PropTypes.string.isRequired,
    type: PropTypes.string.isRequired,
    element: PropTypes.object.isRequired, // Yêu cầu element đầy đủ
    isSelected: PropTypes.bool.isRequired,
    onSelectChild: PropTypes.func.isRequired,
    viewMode: PropTypes.oneOf(['desktop', 'tablet', 'mobile']).isRequired,
    onDeleteChild: PropTypes.func.isRequired,
    // Bỏ các prop thừa (vì đã có trong `element`)
    componentData: PropTypes.object,
    styles: PropTypes.object,
    position: PropTypes.object,
    size: PropTypes.object,
    visible: PropTypes.bool,
    locked: PropTypes.bool,
};

// PropTypes for Element
Element.propTypes = {
    element: PropTypes.shape({
        id: PropTypes.string.isRequired,
        type: PropTypes.string.isRequired,
        componentData: PropTypes.object,
        position: PropTypes.object,
        size: PropTypes.object,
        styles: PropTypes.object,
        children: PropTypes.array,
        visible: PropTypes.bool,
        locked: PropTypes.bool,
    }).isRequired,
    isSelected: PropTypes.bool.isRequired,
    onSelectElement: PropTypes.func.isRequired,
    onUpdatePosition: PropTypes.func.isRequired,
    onUpdateSize: PropTypes.func.isRequired, // Thêm prop
    viewMode: PropTypes.oneOf(['desktop', 'tablet', 'mobile']).isRequired,
    zoomLevel: PropTypes.number.isRequired,
    gridSize: PropTypes.number.isRequired,
    onContextMenu: PropTypes.func.isRequired,
    canvasBounds: PropTypes.shape({
        width: PropTypes.number,
        height: PropTypes.number,
    }).isRequired,
    canvasRef: PropTypes.object.isRequired,
    onSelectChild: PropTypes.func.isRequired,
    selectedChildId: PropTypes.string,
    onAddChild: PropTypes.func.isRequired,
    onUpdateChildPosition: PropTypes.func.isRequired,
    onMoveChild: PropTypes.func.isRequired,
    showGrid: PropTypes.bool.isRequired,
    setDragPreview: PropTypes.func.isRequired,
    visiblePopups: PropTypes.arrayOf(PropTypes.string), // Đổi tên
    onClosePopup: PropTypes.func.isRequired, // Đổi tên
    onSaveTemplate: PropTypes.func.isRequired,
    onToggleVisibility: PropTypes.func.isRequired,
    onEditElement: PropTypes.func.isRequired,
    onMoveElementUp: PropTypes.func.isRequired,
    onMoveElementDown: PropTypes.func.isRequired,
    onDeleteElement: PropTypes.func.isRequired,
    onDeleteChild: PropTypes.func.isRequired,
};

// Export `Element` và `ChildElement`
export { ChildElement, Element };
