                    // Cache result
                    this.cache.set(cacheKey, {
                        data: data,
                        timestamp: Date.now()
                    });
                    
                    return data;
                } catch (error) {
                    console.error('[LPB] API Error:', error);
                    throw error;
                }
            },
            
            clearCache: function(endpoint) {
                if (endpoint) {
                    for (let key of this.cache.keys()) {
                        if (key.startsWith(endpoint)) {
                            this.cache.delete(key);
                        }
