import { useState, useCallback, useEffect } from 'react';
import api from '@landinghub/api';
import { toast } from 'react-toastify';

export const useImageUpload = (pageId = null) => {
    const [uploading, setUploading] = useState(false);
    const [progress, setProgress] = useState(0);
    const [uploadedImages, setUploadedImages] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    // Load images when pageId changes
    useEffect(() => {
        if (pageId) {
            loadPageImages(pageId);
        } else {
            loadAllImages();
        }
    }, [pageId]);

    // Load images for specific page
    const loadPageImages = useCallback(async (targetPageId) => {
        setLoading(true);
        try {
            const response = await imageService.getPageImages(targetPageId);
            setUploadedImages(response.data || []);
        } catch (err) {
            console.error('Failed to load page images:', err);
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }, []);

    // Load all user images
    const loadAllImages = useCallback(async () => {
        setLoading(true);
        try {
            const response = await imageService.getUserImages();
            setUploadedImages(response.data || []);
        } catch (err) {
            console.error('Failed to load images:', err);
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }, []);

    // Upload single image
    const uploadImage = useCallback(async (file, options = {}) => {
        const { compress = true, validate = true, targetPageId = pageId } = options;

        try {
            // Validate
            if (validate) {
                const validation = imageService.validateImageFile(file);
                if (!validation.valid) {
                    toast.error(validation.error);
                    return null;
                }
            }

            setUploading(true);
            setProgress(0);
            setError(null);

            // Compress if needed
            let fileToUpload = file;
            if (compress) {
                toast.info('Đang nén ảnh...');
                fileToUpload = await imageService.compressImage(file);
            }

            // Upload
            const response = await imageService.uploadImage(
                fileToUpload,
                targetPageId,
                setProgress
            );

            toast.success('Upload ảnh thành công!');
            setUploadedImages(prev => [response.data, ...prev]);

            return response.data;
        } catch (err) {
            console.error('Upload error:', err);
            setError(err.message);
            toast.error(err.message || 'Upload thất bại');
            return null;
        } finally {
            setUploading(false);
            setProgress(0);
        }
    }, [pageId]);

    // Upload multiple images
    const uploadMultiple = useCallback(async (files, options = {}) => {
        const { compress = true, validate = true, targetPageId = pageId } = options;

        try {
            // Validate all files
            if (validate) {
                for (const file of files) {
                    const validation = imageService.validateImageFile(file);
                    if (!validation.valid) {
                        toast.error(`${file.name}: ${validation.error}`);
                        return [];
                    }
                }
            }

            setUploading(true);
            setProgress(0);
            setError(null);

            // Compress if needed
            let filesToUpload = files;
            if (compress) {
                toast.info('Đang nén ảnh...');
                filesToUpload = await Promise.all(
                    files.map(file => imageService.compressImage(file))
                );
            }

            // Upload
            const response = await imageService.uploadMultipleImages(
                filesToUpload,
                targetPageId,
                setProgress
            );

            toast.success(`Upload ${response.data.length} ảnh thành công!`);
            setUploadedImages(prev => [...response.data, ...prev]);

            return response.data;
        } catch (err) {
            console.error('Upload error:', err);
            setError(err.message);
            toast.error(err.message || 'Upload thất bại');
            return [];
        } finally {
            setUploading(false);
            setProgress(0);
        }
    }, [pageId]);

    // Delete image
    const deleteImage = useCallback(async (key) => {
        try {
            await imageService.deleteImage(key);
            setUploadedImages(prev => prev.filter(img => img.key !== key));
            toast.success('Xóa ảnh thành công!');
            return true;
        } catch (err) {
            console.error('Delete error:', err);
            toast.error(err.message || 'Xóa ảnh thất bại');
            return false;
        }
    }, []);

    // Refresh images
    const refreshImages = useCallback(() => {
        if (pageId) {
            loadPageImages(pageId);
        } else {
            loadAllImages();
        }
    }, [pageId, loadPageImages, loadAllImages]);

    return {
        uploading,
        progress,
        uploadedImages,
        error,
        loading,
        loadPageImages,
        loadAllImages,
        uploadImage,
        uploadMultiple,
        deleteImage,
        refreshImages,
        setUploadedImages
    };
};

export default useImageUpload;
