import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import api from '@landinghub/api';

const UserProfile = () => {
    const { user, setUser } = useContext(UserContext);
    const [form, setForm] = useState({ name: '', email: '' });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserInfo = async () => {
            try {
                const res = await api.get('/user/info');
                setForm({ name: res.data.name, email: res.data.email });
                setUser({ ...user, name: res.data.name, email: res.data.email });
            } catch (err) {
                console.error('Error fetching user info:', err);
                setError('Không thể tải thông tin người dùng');
            } finally {
                setLoading(false);
            }
        };
        fetchUserInfo();
    }, []);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        setError(null);
        try {
            const res = await api.put('/user/update', form);
            const newToken = res.data.token;
            localStorage.setItem('token', newToken);
            setUser({ ...user, name: form.name, email: form.email });
            console.log('Thông tin người dùng được cập nhật thành công');
        } catch (err) {
            setError(err.response?.data?.msg || 'Cập nhật thất bại');
        }
    };

    if (loading) return <div>Đang tải thông tin...</div>;

    return (
        <div className="user-profile">
            <h2>Thông tin người dùng</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleUpdate}>
                <label>Tên</label>
                <input type="text" name="name" value={form.name} onChange={handleChange} />
                <label>Email</label>
                <input type="email" name="email" value={form.email} onChange={handleChange} />
                <button type="submit">Cập nhật</button>
            </form>
        </div>
    );
};

export default UserProfile;
