import React, { useState } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import api from '@landinghub/api';

// CSS Variables from renderStaticHTML.js
const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
};

// CSS Styles
const styles = `
    .page-container {
        position: relative;
        width: var(--canvas-desktop);
        min-height: 100vh;
        margin: 0 auto;
    }
    .ladi-section {
        width: 100%;
        max-width: 1200px;
        margin: 0 auto;
        position: relative;
        overflow: hidden;
    }
    .ladi-section-background {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        z-index: 0;
    }
    .ladi-overlay {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        z-index: 1;
        pointer-events: none;
    }
    .ladi-container {
        position: relative;
        z-index: 2;
        padding: 20px;
        min-height: 100%;
        box-sizing: border-box;
    }
    .lpb-canvas-element, .lpb-child-element {
        position: absolute;
        cursor: move;
    }
    .lpb-canvas-element:hover, .lpb-child-element:hover {
        outline: 2px dashed var(--primary);
    }
    @media (max-width: 768px) {
        .page-container {
            width: 100%;
        }
        .ladi-section {
            width: 100%;
            min-width: auto;
        }
        .lpb-child-element {
            left: 0 !important;
            width: 100% !important;
        }
    }
    @media (max-width: 480px) {
        .lpb-child-element {
            left: 0 !important;
            width: 100% !important;
        }
    }
    .ladi-card, .ladi-product, .ladi-testimonial, .ladi-box {
        padding: 16px;
        box-sizing: border-box;
    }
    .ladi-menu {
        display: flex;
        gap: 20px;
        list-style: none;
        padding: 0;
    }
    .ladi-menu a {
        color: inherit;
        text-decoration: none;
    }
    .ladi-grid {
        display: grid;
    }
    .ladi-list {
        list-style: none;
        padding: 0;
    }
    .ladi-video {
        max-width: 100%;
    }
    .ladi-icon {
        display: inline-block;
    }
    .ladi-gallery {
        display: grid;
        gap: 10px;
    }
    .ladi-carousel {
        overflow: hidden;
        position: relative;
    }
    .ladi-tabs {
        display: flex;
        flex-direction: column;
    }
    .ladi-tabs-nav {
        display: flex;
        gap: 10px;
        border-bottom: 1px solid var(--border-color);
    }
    .ladi-tab {
        padding: 8px 16px;
        cursor: pointer;
    }
    .ladi-tab-content {
        padding: 16px;
    }
    .ladi-accordion-item {
        border-bottom: 1px solid var(--border-color);
    }
    .ladi-accordion-title {
        padding: 8px;
        cursor: pointer;
    }
    .ladi-table {
        width: 100%;
        border-collapse: collapse;
    }
    .ladi-table th, .ladi-table td {
        border: 1px solid var(--border-color);
        padding: 8px;
        text-align: left;
    }
    .ladi-survey {
        display: flex;
        flex-direction: column;
        gap: 10px;
    }
    .ladi-html {
        overflow: auto;
    }
    .ladi-collection {
        display: flex;
        flex-direction: column;
        gap: 20px;
    }
    .upload-input {
        margin: 10px 0;
    }
`;

// Item Types for react-dnd
const ItemTypes = {
    COMPONENT: 'component'
};

// Draggable Component
const DraggableComponent = ({ id, type, componentData = {}, styles = {}, size = {}, position = {}, children = [], onMove, onUpdate }) => {
    const [{ isDragging }, drag] = useDrag({
        type: ItemTypes.COMPONENT,
        item: { id },
        collect: (monitor) => ({
            isDragging: monitor.isDragging()
        })
    });

    const [, drop] = useDrop({
        accept: ItemTypes.COMPONENT,
        drop: (item, monitor) => {
            const delta = monitor.getDifferenceFromInitialOffset();
            const x = (position.desktop?.x || 0) + (delta?.x || 0);
            const y = (position.desktop?.y || 0) + (delta?.y || 0);
            onMove(item.id, { x, y });
        }
    });

    const handleFileUpload = async (event, index = null) => {
        const file = event.target.files[0];
        if (file) {
            const params = {
                Bucket: 'your-bucket-name', // Thay bằng tên bucket của bạn
                Key: `uploads/${Date.now()}-${file.name}`,
                Body: file,
                ContentType: file.type,
                ACL: 'public-read'
            };
            try {
                const { Location } = await s3.upload(params).promise();
                console.log('Uploaded to S3:', Location);
                if (index !== null) {
                    // Update item in grid, gallery, or carousel
                    const updatedItems = [...(componentData.items || componentData.images || [])];
                    updatedItems[index] = { ...updatedItems[index], image: Location };
                    onUpdate(id, { ...componentData, items: updatedItems });
                } else {
                    // Update single image or product
                    onUpdate(id, { ...componentData, src: Location, image: Location });
                }
            } catch (error) {
                console.error('Upload failed:', error);
            }
        }
    };

    const normalizedStyles = {
        position: 'absolute',
        left: position.desktop?.x || 0,
        top: position.desktop?.y || 0,
        width: size?.width || 200,
        height: size?.height || 50,
        opacity: isDragging ? 0.5 : 1,
        cursor: 'move',
        ...styles
    };

    const renderComponentContent = (type, componentData, styles, children) => {
        switch (type) {
            case 'section':
                return (
                    <div className="ladi-section" style={{ width: '100%', height: '100%', position: 'relative' }} ref={drop}>
                        <div className="ladi-section-background" style={{ backgroundColor: componentData.backgroundColor || 'transparent', backgroundImage: componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none', backgroundSize: 'cover', backgroundPosition: 'center' }}></div>
                        <div className="ladi-overlay" style={{ backgroundColor: componentData.overlayColor || 'transparent', opacity: componentData.overlayOpacity || 0 }}></div>
                        <div className="ladi-container" style={{ padding: componentData.padding || '20px', width: '100%', height: '100%', position: 'relative' }}>
                            {componentData.title && !children.some(child => child?.type === 'heading') && (
                                <h2 style={{ fontSize: componentData.titleFontSize || '1.8rem', margin: componentData.titleMargin || '0', color: componentData.titleColor || '#1f2937', fontWeight: componentData.titleFontWeight || '700' }}>{componentData.title}</h2>
                            )}
                            {children.map((child, index) => (
                                <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                            ))}
                        </div>
                    </div>
                );
            case 'container':
            case 'box':
                return (
                    <div style={{ display: componentData.layoutType || 'flex', flexDirection: componentData.direction || 'column', gap: componentData.gap || '10px', padding: componentData.padding || '20px', background: componentData.background || 'transparent', ...styles }} ref={drop}>
                        {componentData.title && !children.some(child => child?.type === 'heading') && (
                            <h2 style={{ fontSize: componentData.titleFontSize || '1.8rem', margin: componentData.titleMargin || '0', color: componentData.titleColor || '#1f2937', fontWeight: componentData.titleFontWeight || '700' }}>{componentData.title}</h2>
                        )}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'heading':
                const HeadingLevel = componentData.level && ['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(componentData.level) ? componentData.level : 'h2';
                return (
                    <HeadingLevel style={{ fontSize: componentData.fontSize || '1.5rem', color: componentData.color || '#1f2937', margin: componentData.margin || '0', padding: componentData.padding || '0', fontWeight: componentData.fontWeight || '700', textAlign: componentData.textAlign || 'left', ...styles }}>
                        {componentData.content || 'Tiêu đề'}
                    </HeadingLevel>
                );
            case 'paragraph':
                return (
                    <p style={{ fontSize: componentData.fontSize || '1rem', color: componentData.color || '#1f2937', margin: componentData.margin || '0', padding: componentData.padding || '0', fontWeight: componentData.fontWeight || '400', lineHeight: componentData.lineHeight || '1.6', textAlign: componentData.textAlign || 'left', ...styles }}>
                        {componentData.content || 'Đoạn văn'}
                    </p>
                );
            case 'button':
                return (
                    <button style={{ padding: componentData.padding || '8px 16px', borderRadius: componentData.borderRadius || '8px', background: componentData.background || '#2563eb', color: componentData.color || '#fff', border: componentData.border || 'none', cursor: 'pointer', fontSize: componentData.fontSize || '1rem', fontWeight: componentData.fontWeight || '600', textAlign: componentData.textAlign || 'center', ...styles }}>
                        {componentData.content || 'Nút'}
                    </button>
                );
            case 'image':
                return (
                    <div>
                        <input type="file" className="upload-input" accept="image/*" onChange={handleFileUpload} />
                        <img src={componentData.src || 'https://via.placeholder.com/150?text=Image'} alt={componentData.alt || 'Hình ảnh'} style={{ maxWidth: componentData.maxWidth || '100%', height: componentData.height || 'auto', borderRadius: componentData.borderRadius || '0', ...styles }} />
                    </div>
                );
            case 'form':
                return (
                    <form style={{ display: 'flex', flexDirection: componentData.direction || 'column', gap: componentData.gap || '10px', ...styles }} ref={drop}>
                        {componentData.title && !children.some(child => child?.type === 'heading') && (
                            <h3 style={{ margin: componentData.titleMargin || '0', fontSize: componentData.titleFontSize || '1.2rem', color: componentData.titleColor || '#1f2937', fontWeight: componentData.titleFontWeight || '600' }}>{componentData.title}</h3>
                        )}
                        {Array.isArray(componentData.fields) && componentData.fields.map((field, index) => (
                            <input key={index} type={field.type || 'text'} placeholder={field.placeholder || field.label || 'Nhập...'} style={{ padding: field.padding || '8px', borderRadius: field.borderRadius || '8px', border: field.border || '1px solid #ccc', fontSize: field.fontSize || '1rem', width: '100%', boxSizing: 'border-box', ...styles }} />
                        ))}
                        {componentData.buttonText && !children.some(child => child?.type === 'button') && (
                            <button type="submit" style={{ background: componentData.buttonBackground || '#2563eb', color: componentData.buttonColor || '#fff', padding: componentData.buttonPadding || '8px 16px', borderRadius: componentData.buttonBorderRadius || '8px', border: componentData.buttonBorder || 'none', cursor: 'pointer', fontSize: componentData.buttonFontSize || '1rem', fontWeight: componentData.buttonFontWeight || '600' }}>
                                {componentData.buttonText || 'Gửi'}
                            </button>
                        )}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </form>
                );
            case 'card':
                return (
                    <div className="ladi-card" style={{ padding: componentData.padding || '16px', background: componentData.background || '#ffffff', borderRadius: componentData.borderRadius || '8px', boxShadow: componentData.boxShadow || '0 2px 8px rgba(0,0,0,0.1)', ...styles }}>
                        {componentData.title && !children.some(child => child?.type === 'heading') && (
                            <h3 style={{ fontSize: componentData.titleFontSize || '1.2rem', margin: componentData.titleMargin || '0 0 8px', color: componentData.titleColor || '#1f2937', fontWeight: componentData.titleFontWeight || '600' }}>{componentData.title}</h3>
                        )}
                        {componentData.content && !children.some(child => child?.type === 'paragraph') && (
                            <p style={{ fontSize: componentData.contentFontSize || '1rem', margin: componentData.contentMargin || '0', color: componentData.contentColor || '#4b5563' }}>{componentData.content}</p>
                        )}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'testimonial':
                return (
                    <div className="ladi-testimonial" style={{ padding: componentData.padding || '20px', background: componentData.background || '#f9fafb', borderRadius: componentData.borderRadius || '8px', ...styles }}>
                        {componentData.quote && (
                            <p style={{ fontSize: componentData.quoteFontSize || '1rem', margin: componentData.quoteMargin || '0 0 8px', color: componentData.quoteColor || '#1f2937', fontStyle: 'italic' }}>"{componentData.quote}"</p>
                        )}
                        {componentData.author && (
                            <p style={{ fontSize: componentData.authorFontSize || '0.9rem', margin: componentData.authorMargin || '0', color: componentData.authorColor || '#4b5563', fontWeight: componentData.authorFontWeight || '600' }}>- {componentData.author}</p>
                        )}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'menu':
                return (
                    <ul className="ladi-menu" style={{ display: 'flex', gap: '20px', listStyle: 'none', padding: '0', ...styles }}>
                        {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                            <li key={index}><a href={item.url || '#'} style={{ fontSize: componentData.itemFontSize || '1rem', color: componentData.itemColor || '#1f2937', textDecoration: 'none', ...styles }}>{item.label || 'Menu Item'}</a></li>
                        ))}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </ul>
                );
            case 'grid':
                return (
                    <div className="ladi-grid" style={{ gridTemplateColumns: `repeat(${componentData.columns || 2}, 1fr)`, gap: componentData.gap || '20px', ...styles }} ref={drop}>
                        {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                            <div key={index} style={{ padding: componentData.itemPadding || '16px', background: componentData.itemBackground || '#ffffff', borderRadius: componentData.itemBorderRadius || '8px', boxShadow: componentData.itemBoxShadow || '0 2px 8px rgba(0,0,0,0.1)', ...styles }}>
                                <input type="file" className="upload-input" accept="image/*" onChange={(e) => handleFileUpload(e, index)} />
                                {item.image && <img src={item.image} alt={item.title || 'Item'} style={{ maxWidth: '100%', height: 'auto', borderRadius: componentData.imageBorderRadius || '8px' }} />}
                                {item.title && <h4 style={{ fontSize: componentData.titleFontSize || '1.1rem', margin: componentData.titleMargin || '8px 0 0', color: componentData.titleColor || '#1f2937' }}>{item.title}</h4>}
                                {item.price && <p style={{ fontSize: componentData.priceFontSize || '1rem', margin: componentData.priceMargin || '4px 0 0', color: componentData.priceColor || '#4b5563' }}>{item.price}</p>}
                            </div>
                        ))}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'list':
                return (
                    <ul className="ladi-list" style={{ listStyle: 'none', padding: '0', ...styles }}>
                        {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                            <li key={index} style={{ fontSize: componentData.itemFontSize || '1rem', color: componentData.itemColor || '#1f2937', margin: componentData.itemMargin || '4px 0', ...styles }}>{item.title || item}</li>
                        ))}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </ul>
                );
            case 'video':
                return (
                    <video className="ladi-video" controls style={{ maxWidth: '100%', ...styles }}>
                        <source src={componentData.src || 'https://via.placeholder.com/150?text=Video'} type="video/mp4" />
                    </video>
                );
            case 'icon':
                return (
                    <i className={`${componentData.icon || 'fas fa-star'} ladi-icon`} style={{ ...styles }}></i>
                );
            case 'product':
                return (
                    <div className="ladi-product" style={{ padding: componentData.padding || '16px', background: componentData.background || '#ffffff', borderRadius: componentData.borderRadius || '8px', boxShadow: componentData.boxShadow || '0 2px 8px rgba(0,0,0,0.1)', ...styles }}>
                        <input type="file" className="upload-input" accept="image/*" onChange={handleFileUpload} />
                        {componentData.image && <img src={componentData.image} alt={componentData.title || 'Product'} style={{ maxWidth: '100%', height: 'auto', borderRadius: componentData.imageBorderRadius || '8px' }} />}
                        {componentData.title && <h4 style={{ fontSize: componentData.titleFontSize || '1.1rem', margin: componentData.titleMargin || '8px 0 0', color: componentData.titleColor || '#1f2937' }}>{componentData.title}</h4>}
                        {componentData.price && <p style={{ fontSize: componentData.priceFontSize || '1rem', margin: componentData.priceMargin || '4px 0 0', color: componentData.priceColor || '#4b5563' }}>{item.price}</p>}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'gallery':
                return (
                    <div className="ladi-gallery" style={{ gridTemplateColumns: componentData.gridTemplateColumns || 'repeat(auto-fill, minmax(150px, 1fr))', gap: componentData.gap || '10px', ...styles }} ref={drop}>
                        {Array.isArray(componentData.images) && componentData.images.map((image, index) => (
                            <div key={index}>
                                <input type="file" className="upload-input" accept="image/*" onChange={(e) => handleFileUpload(e, index)} />
                                <img src={image} alt="Gallery Image" style={{ maxWidth: '100%', height: 'auto', borderRadius: componentData.imageBorderRadius || '8px' }} />
                            </div>
                        ))}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'carousel':
                return (
                    <div className="ladi-carousel" style={{ maxWidth: '100%', overflow: 'hidden', ...styles }} ref={drop}>
                        {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                            <div key={index} style={{ padding: componentData.itemPadding || '16px', background: componentData.itemBackground || '#ffffff', ...styles }}>
                                <input type="file" className="upload-input" accept="image/*" onChange={(e) => handleFileUpload(e, index)} />
                                {item.image && <img src={item.image} alt={item.title || 'Slide'} style={{ maxWidth: '100%', height: 'auto', borderRadius: componentData.imageBorderRadius || '8px' }} />}
                                {item.title && <h4 style={{ fontSize: componentData.titleFontSize || '1.1rem', margin: componentData.titleMargin || '8px 0 0', color: componentData.titleColor || '#1f2937' }}>{item.title}</h4>}
                            </div>
                        ))}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'tabs':
                return (
                    <div className="ladi-tabs" style={{ ...styles }} ref={drop}>
                        <div className="ladi-tabs-nav" style={{ display: 'flex', gap: '10px', borderBottom: '1px solid var(--border-color)' }}>
                            {Array.isArray(componentData.tabs) && componentData.tabs.map((tab, index) => (
                                <div key={index} className="ladi-tab" style={{ padding: componentData.tabPadding || '8px 16px' }}>{tab.title}</div>
                            ))}
                        </div>
                        <div className="ladi-tab-content" style={{ padding: componentData.contentPadding || '16px', background: componentData.contentBackground || '#ffffff' }}>
                            {Array.isArray(componentData.tabs) && componentData.tabs.map((tab, index) => (
                                <div key={index} style={{ display: 'none' }}>{tab.content}</div>
                            ))}
                        </div>
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'accordion':
                return (
                    <div className="ladi-accordion" style={{ ...styles }} ref={drop}>
                        {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                            <div key={index} className="ladi-accordion-item">
                                <div className="ladi-accordion-title" style={{ padding: componentData.titlePadding || '8px', background: componentData.titleBackground || '#f9fafb', ...styles }}>{item.title}</div>
                                <div className="ladi-accordion-content" style={{ padding: componentData.contentPadding || '8px', display: 'none', ...styles }}>{item.content}</div>
                            </div>
                        ))}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'table':
                return (
                    <table className="ladi-table" style={{ borderCollapse: 'collapse', width: '100%', ...styles }} ref={drop}>
                        {Array.isArray(componentData.headers) && (
                            <thead>
                            <tr>
                                {componentData.headers.map((header, index) => (
                                    <th key={index} style={{ padding: componentData.headerPadding || '8px', background: componentData.headerBackground || '#f9fafb', ...styles }}>{header}</th>
                                ))}
                            </tr>
                            </thead>
                        )}
                        {Array.isArray(componentData.rows) && (
                            <tbody>
                            {componentData.rows.map((row, rowIndex) => (
                                <tr key={rowIndex}>
                                    {row.map((cell, cellIndex) => (
                                        <td key={cellIndex} style={{ padding: componentData.cellPadding || '8px', ...styles }}>{cell}</td>
                                    ))}
                                </tr>
                            ))}
                            </tbody>
                        )}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </table>
                );
            case 'survey':
                return (
                    <form className="ladi-survey" style={{ display: 'flex', flexDirection: componentData.direction || 'column', gap: componentData.gap || '10px', ...styles }} ref={drop}>
                        {componentData.title && (
                            <h3 style={{ fontSize: componentData.titleFontSize || '1.2rem', margin: componentData.titleMargin || '0', color: componentData.titleColor || '#1f2937', fontWeight: componentData.titleFontWeight || '600' }}>{componentData.title}</h3>
                        )}
                        {Array.isArray(componentData.questions) && componentData.questions.map((question, index) => (
                            <div key={index} style={{ margin: componentData.questionMargin || '8px 0', ...styles }}>
                                <label style={{ fontSize: componentData.labelFontSize || '1rem', color: componentData.labelColor || '#1f2937' }}>{question.label}</label>
                                {question.type === 'text' && (
                                    <input type="text" placeholder={question.placeholder || 'Nhập...'} style={{ padding: question.padding || '8px', borderRadius: question.borderRadius || '8px', border: question.border || '1px solid #ccc', width: '100%', boxSizing: 'border-box', ...styles }} />
                                )}
                                {question.type === 'radio' && Array.isArray(question.options) && question.options.map((option, optIndex) => (
                                    <div key={optIndex}>
                                        <input type="radio" name={`${question.label}-${index}`} value={option} style={{ ...styles }} /> {option}
                                    </div>
                                ))}
                            </div>
                        ))}
                        {componentData.buttonText && (
                            <button type="submit" style={{ background: componentData.buttonBackground || '#2563eb', color: componentData.buttonColor || '#fff', padding: componentData.buttonPadding || '8px 16px', borderRadius: componentData.buttonBorderRadius || '8px', border: componentData.buttonBorder || 'none', cursor: 'pointer', fontSize: componentData.buttonFontSize || '1rem', fontWeight: componentData.buttonFontWeight || '600' }}>
                                {componentData.buttonText || 'Gửi'}
                            </button>
                        )}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </form>
                );
            case 'html':
                return (
                    <div className="ladi-html" style={{ ...styles }} dangerouslySetInnerHTML={{ __html: componentData.content || '<p>Custom HTML content</p>' }}>
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'collection':
                return (
                    <div className="ladi-collection" style={{ display: 'flex', flexDirection: 'column', gap: '20px', ...styles }} ref={drop}>
                        {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                            <div key={index} style={{ padding: componentData.itemPadding || '16px', background: componentData.itemBackground || '#ffffff', borderRadius: componentData.itemBorderRadius || '8px', boxShadow: componentData.itemBoxShadow || '0 2px 8px rgba(0,0,0,0.1)', ...styles }}>
                                {item.title && <h4 style={{ fontSize: componentData.titleFontSize || '1.1rem', margin: componentData.titleMargin || '0 0 8px', color: componentData.titleColor || '#1f2937' }}>{item.title}</h4>}
                                {item.content && <p style={{ fontSize: componentData.contentFontSize || '1rem', margin: componentData.contentMargin || '0', color: componentData.contentColor || '#4b5563' }}>{item.content}</p>}
                            </div>
                        ))}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            case 'divider':
                return (
                    <hr style={{ border: 'none', background: componentData.color || '#e5e7eb', height: componentData.thickness || '2px', width: componentData.width || '100%', margin: componentData.margin || '0', ...styles }} />
                );
            case 'spacer':
                return (
                    <div style={{ background: componentData.background || 'transparent', height: componentData.height || '40px', width: componentData.width || '100%', ...styles }}></div>
                );
            case 'popup':
            case 'modal':
                return (
                    <div style={{ display: componentData.display || styles.display || 'none', flexDirection: componentData.direction || 'column', gap: componentData.gap || '10px', textAlign: componentData.textAlign || 'center', padding: componentData.padding || '20px', background: componentData.background || 'rgba(255, 255, 255, 0.9)', borderRadius: componentData.borderRadius || '12px', boxShadow: componentData.boxShadow || '0 4px 6px rgba(0, 0, 0, 0.1)', zIndex: styles.zIndex || 100, ...styles }} ref={drop}>
                        {componentData.title && !children.some(child => child?.type === 'heading') && (
                            <h3 style={{ fontSize: componentData.titleFontSize || '1.5rem', margin: componentData.titleMargin || '0', color: componentData.titleColor || '#1f2937', fontWeight: componentData.titleFontWeight || '600' }}>{componentData.title}</h3>
                        )}
                        {componentData.content && !children.some(child => child?.type === 'paragraph') && (
                            <p style={{ fontSize: componentData.contentFontSize || '1rem', margin: componentData.contentMargin || '0', color: componentData.contentColor || '#4b5563' }}>{componentData.content}</p>
                        )}
                        {componentData.fields && !children.some(child => child?.type === 'form') && componentData.fields.map((field, index) => (
                            <input key={index} type={field.type || 'text'} placeholder={field.placeholder || field.label || 'Nhập...'} style={{ padding: field.padding || '8px', borderRadius: field.borderRadius || '8px', border: field.border || '1px solid #ccc', fontSize: field.fontSize || '1rem', width: '100%', boxSizing: 'border-box', ...styles }} />
                        ))}
                        {componentData.buttonText && !children.some(child => child?.type === 'button') && (
                            <button style={{ padding: componentData.buttonPadding || '10px 20px', borderRadius: componentData.buttonBorderRadius || '8px', background: componentData.buttonBackground || '#2563eb', color: componentData.buttonColor || '#fff', border: componentData.buttonBorder || 'none', cursor: 'pointer', fontSize: componentData.buttonFontSize || '1rem', fontWeight: componentData.buttonFontWeight || '600' }}>
                                {componentData.buttonText || (type === 'modal' ? 'Đóng' : 'Gửi')}
                            </button>
                        )}
                        {children.map((child, index) => (
                            <DraggableComponent key={child.id || `child-${index}`} {...child} onMove={onMove} onUpdate={onUpdate} />
                        ))}
                    </div>
                );
            default:
                console.warn(`Skipping unknown component type: ${type}`, { componentData });
                return null;
        }
    };

    return (
        <div id={id} className={`ladi-${type} ${type === 'section' ? '' : 'lpb-child-element'}`} style={normalizedStyles} ref={drag}>
            {renderComponentContent(type, componentData, styles, children)}
        </div>
    );
};

// Main Canvas Component
const Canvas = ({ pageData }) => {
    const [elements, setElements] = useState(pageData.elements || []);

    const handleMove = (id, newPosition) => {
        setElements((prevElements) =>
            prevElements.map((element) =>
                element.id === id
                    ? { ...element, position: { ...element.position, desktop: newPosition } }
                    : element
            )
        );
    };

    const handleUpdate = (id, updatedData) => {
        setElements((prevElements) =>
            prevElements.map((element) =>
                element.id === id
                    ? { ...element, componentData: updatedData }
                    : element
            )
        );
    };

    return (
        <DndProvider backend={HTML5Backend}>
            <style>{styles}</style>
            <div className="page-container" style={{ background: pageData.canvas?.background || '#ffffff', width: pageData.canvas?.width || '1200px' }}>
                {elements.map((element, index) => (
                    <DraggableComponent
                        key={element.id || `element-${index}`}
                        {...element}
                        onMove={handleMove}
                        onUpdate={handleUpdate}
                    />
                ))}
            </div>
        </DndProvider>
    );
};

export default Canvas;
