import React, { Suspense, useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import '../styles/Templates.css';
import DogLoader from "../components/Loader";
import '../styles/Dashboard.css';

const Templates = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [sidebarOpen, setSidebarOpen] = useState(false); // ‚úÖ TH√äM STATE N√ÄY
    const [templates, setTemplates] = useState([]);
    const [error, setError] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('T·∫•t c·∫£');
    const [usingTemplateId, setUsingTemplateId] = useState(null);
    const [previewTemplate, setPreviewTemplate] = useState(null);
    const [showPreview, setShowPreview] = useState(false);

    const location = useLocation();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';
    const categories = ['T·∫•t c·∫£', 'B·∫•t ƒë·ªông s·∫£n', 'Th∆∞∆°ng m·∫°i ƒëi·ªán t·ª≠', 'Nh√† h√†ng', 'S·ª©c kh·ªèe', 'Gi√°o d·ª•c', 'Kh√°c'];

    // ‚úÖ ƒê·ªíNG B·ªò: Auth logic gi·ªëng Dashboard
    useEffect(() => {
        if (user?.role) {
            setUserRole(user.role);
        } else if (localStorage.getItem('token')) {
            try {
                const decodedToken = jwtDecode(localStorage.getItem('token'));
                setUserRole(decodedToken.role);
            } catch (err) {
                console.error('Error decoding token:', err);
            }
        }
        setLoading(false);
    }, [user]);

    // ‚úÖ TH√äM: Sync sidebar state v·ªõi localStorage (gi·ªëng Dashboard)
    useEffect(() => {
        const savedSidebarState = localStorage.getItem('sidebarOpen');
        if (savedSidebarState !== null) {
            setSidebarOpen(JSON.parse(savedSidebarState));
        }
    }, []);

    // ‚úÖ TH√äM: Save sidebar state to localStorage
    const handleSidebarToggle = () => {
        setSidebarOpen(!sidebarOpen);
        localStorage.setItem('sidebarOpen', JSON.stringify(!sidebarOpen));
    };

    // Load templates khi c√≥ role
    useEffect(() => {
        if (userRole && ['user', 'admin'].includes(userRole)) {
            loadTemplates();
        }
    }, [userRole]);

    // ... (gi·ªØ nguy√™n loadTemplates, handlePreview, handleUseTemplate, getPlaceholderImage)

    const loadTemplates = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/templates`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setTemplates(response.data || []);
            setError('');
        } catch (err) {
            console.error('Error loading templates:', err);
            setError('Kh√¥ng th·ªÉ t·∫£i danh s√°ch template');
        } finally {
            setLoading(false);
        }
    };

    const filteredTemplates = selectedCategory === 'T·∫•t c·∫£'
        ? templates
        : templates.filter(t => t.category === selectedCategory);

    const handlePreview = async (templateId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${API_BASE_URL}/api/templates/${templateId}/preview`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setPreviewTemplate(response.data);
            setShowPreview(true);
        } catch (err) {
            console.error('Error previewing template:', err);
            alert('Kh√¥ng th·ªÉ xem tr∆∞·ªõc template');
        }
    };

    const handleUseTemplate = async (templateId, templateName) => {
        const confirmed = window.confirm(
            `B·∫°n c√≥ mu·ªën t·∫°o landing page m·ªõi t·ª´ template "${templateName}" kh√¥ng?`
        );
        if (!confirmed) return;

        try {
            setUsingTemplateId(templateId);
            const token = localStorage.getItem('token');
            const response = await axios.post(
                `${API_BASE_URL}/api/templates/${templateId}/use`,
                {
                    name: `${templateName} - Copy`,
                    description: `Landing page t·ª´ template ${templateName}`
                },
                { headers: { 'Authorization': `Bearer ${token}` } }
            );

            if (response.data.success) {
                alert('‚úÖ ƒê√£ t·∫°o landing page th√†nh c√¥ng!');
                window.location.href = `/pages/create?id=${response.data.page.id}`;
            }
        } catch (err) {
            console.error('Error using template:', err);
            const errorMsg = err.response?.data?.error || 'Kh√¥ng th·ªÉ s·ª≠ d·ª•ng template';
            alert(`‚ùå ${errorMsg}`);
        } finally {
            setUsingTemplateId(null);
        }
    };

    const getPlaceholderImage = (templateName, category) => {
        const colors = {
            'B·∫•t ƒë·ªông s·∫£n': 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            'Th∆∞∆°ng m·∫°i ƒëi·ªán t·ª≠': 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
            'Nh√† h√†ng': 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
            'S·ª©c kh·ªèe': 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)',
            'Gi√°o d·ª•c': 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)',
            'Kh√°c': 'linear-gradient(135deg, #30cfd0 0%, #330867 100%)'
        };
        return {
            background: colors[category] || colors['Kh√°c'],
            text: templateName.charAt(0).toUpperCase()
        };
    };

    const isCompact = location.pathname !== '/dashboard';

    if (loading) {
        return (
            <div className="dashboard-container">
                <Header role={userRole} />
                <div className="dashboard-main">
                    <Sidebar
                        role={userRole}
                        isCompact={isCompact}
                        isOpen={sidebarOpen}
                        onToggle={handleSidebarToggle} // ‚úÖ TH√äM N√ÄY
                    />
                    <div className="dashboard-content">
                        <DogLoader />
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="dashboard-container">
            <Header role={userRole} />

            <div className="dashboard-main">
                {/* ‚úÖ FIX: Truy·ªÅn ƒë√∫ng props cho Sidebar */}
                <Sidebar
                    role={userRole}
                    isCompact={isCompact}
                    isOpen={sidebarOpen}
                    onToggle={handleSidebarToggle}
                />

                <div className={`dashboard-content ${sidebarOpen ? '' : 'sidebar-collapsed'}`}>
                    {/* N·ªôi dung templates gi·ªØ nguy√™n... */}
                    <div className="templates-header">
                        <h1>üìö Th∆∞ Vi·ªán Templates</h1>
                        <p>Ch·ªçn template ph√π h·ª£p v√† t·∫°o landing page chuy√™n nghi·ªáp trong v√†i ph√∫t</p>
                    </div>

                    <div className="templates-filters">
                        {categories.map(category => (
                            <button
                                key={category}
                                className={`filter-btn ${selectedCategory === category ? 'active' : ''}`}
                                onClick={() => setSelectedCategory(category)}
                            >
                                {category}
                            </button>
                        ))}
                    </div>

                    {error && (
                        <div className="templates-error">
                            <i className="fas fa-exclamation-circle"></i>
                            <span>{error}</span>
                        </div>
                    )}

                    <div className="templates-grid">
                        {filteredTemplates.length === 0 ? (
                            <div className="templates-empty">
                                <i className="fas fa-folder-open"></i>
                                <p>Kh√¥ng c√≥ template n√†o trong danh m·ª•c n√†y</p>
                            </div>
                        ) : (
                            filteredTemplates.map(template => {
                                const placeholder = getPlaceholderImage(template.name, template.category);
                                return (
                                    <div key={template.id} className="template-card">
                                        <div className="template-thumbnail">
                                            {template.thumbnail_url ? (
                                                <img
                                                    src={template.thumbnail_url}
                                                    alt={template.name}
                                                    onError={(e) => {
                                                        e.target.style.display = 'none';
                                                        e.target.nextSibling.style.display = 'flex';
                                                    }}
                                                />
                                            ) : null}
                                            <div
                                                className="template-placeholder"
                                                style={{
                                                    background: placeholder.background,
                                                    display: template.thumbnail_url ? 'none' : 'flex'
                                                }}
                                            >
                                                <span className="placeholder-text">{placeholder.text}</span>
                                            </div>

                                            <div className="template-overlay">
                                                <button className="btn-preview" onClick={() => handlePreview(template.id)}>
                                                    <i className="fas fa-eye"></i> Xem tr∆∞·ªõc
                                                </button>
                                                <button
                                                    className="btn-use"
                                                    onClick={() => handleUseTemplate(template.id, template.name)}
                                                    disabled={usingTemplateId === template.id}
                                                >
                                                    {usingTemplateId === template.id ? (
                                                        <>
                                                            <i className="fas fa-spinner fa-spin"></i> ƒêang t·∫°o...
                                                        </>
                                                    ) : (
                                                        <>
                                                            <i className="fas fa-rocket"></i> S·ª≠ d·ª•ng ngay
                                                        </>
                                                    )}
                                                </button>
                                            </div>

                                            {template.price > 0 && (
                                                <div className="template-price-badge">
                                                    {template.price.toLocaleString('vi-VN')}ƒë
                                                </div>
                                            )}
                                            {template.price === 0 && (
                                                <div className="template-free-badge">Mi·ªÖn ph√≠</div>
                                            )}
                                        </div>

                                        <div className="template-info">
                                            <h3 className="template-name">{template.name}</h3>
                                            <p className="template-description">
                                                {template.description || 'Kh√¥ng c√≥ m√¥ t·∫£'}
                                            </p>
                                            <div className="template-meta">
                        <span className="template-category">
                          <i className="fas fa-tag"></i> {template.category}
                        </span>
                                            </div>
                                        </div>
                                    </div>
                                );
                            })
                        )}
                    </div>

                    {/* Preview Modal gi·ªØ nguy√™n... */}
                    {showPreview && previewTemplate && (
                        <div className="preview-modal" onClick={() => setShowPreview(false)}>
                            <div className="preview-content" onClick={(e) => e.stopPropagation()}>
                                <div className="preview-header">
                                    <h2>{previewTemplate.template?.name}</h2>
                                    <button className="btn-close" onClick={() => setShowPreview(false)}>
                                        <i className="fas fa-times"></i>
                                    </button>
                                </div>
                                <div className="preview-body">
                                    <iframe
                                        srcDoc={previewTemplate.html}
                                        title="Template Preview"
                                        className="preview-iframe"
                                    />
                                </div>
                                <div className="preview-footer">
                                    <button
                                        className="btn-use-primary"
                                        onClick={() => {
                                            setShowPreview(false);
                                            handleUseTemplate(
                                                previewTemplate.template.id,
                                                previewTemplate.template.name
                                            );
                                        }}
                                    >
                                        <i className="fas fa-rocket"></i> S·ª≠ d·ª•ng template n√†y
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default React.memo(Templates);
