import React, { useRef, useCallback, useState, useEffect } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import Guidelines from './Guidelines';
import Rulers from './Rulers';
import SelectionOverlay from './SelectionOverlay';
import '../../styles/Canvas.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

// Tính toán tọa độ chính xác trong canvas
const getCanvasPosition = (mouseX, mouseY, canvasElement, zoomLevel) => {
    if (!canvasElement) {
        console.error('Canvas element is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }

    const canvasRect = canvasElement.getBoundingClientRect();
    const scrollX = canvasElement.scrollLeft || 0;
    const scrollY = canvasElement.scrollTop || 0;

    const rawX = (mouseX - canvasRect.left + scrollX) / (zoomLevel / 100);
    const rawY = (mouseY - canvasRect.top + scrollY) / (zoomLevel / 100);

    const x = Math.max(0, rawX);
    const y = Math.max(0, rawY);

    return { x, y };
};

// Snap to grid với snap points
const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    // Snap đến các điểm gần nhất (threshold 15px)
    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    return { x: snappedX, y: snappedY };
};

// Tính bounds của element
const getElementBounds = (element) => ({
    left: element.position.desktop.x,
    top: element.position.desktop.y,
    right: element.position.desktop.x + element.size.width,
    bottom: element.position.desktop.y + element.size.height,
    centerX: element.position.desktop.x + element.size.width / 2,
    centerY: element.position.desktop.y + element.size.height / 2,
});

// Component Element có thể kéo thả
const Element = React.memo(({
                                element,
                                isSelected,
                                onSelectElement,
                                onUpdatePosition,
                                viewMode,
                                zoomLevel,
                                gridSize,
                                onContextMenu,
                                canvasBounds,
                                canvasRef,
                            }) => {
    const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {} } = element;
    const elementRef = useRef(null);
    const [isDragStarted, setIsDragStarted] = useState(false);

    // Debug position changes
    useEffect(() => {
        console.log(`Element ${id} position updated:`, position[viewMode]);
    }, [position, viewMode, id]);

    const [{ isDragging }, drag, preview] = useDrag(() => ({
        type: ItemTypes.EXISTING_ELEMENT,
        item: () => {
            setIsDragStarted(true);
            return {
                id,
                elementSize: size,
                elementPosition: position[viewMode] || { x: 0, y: 0 },
                isExisting: true
            };
        },
        end: (item, monitor) => {
            setIsDragStarted(false);

            const dropResult = monitor.getDropResult();

            if (!dropResult || !dropResult.moved) {
                console.log('Drag cancelled or invalid drop for element:', id);
                return;
            }

            console.log('Drag ended successfully for element:', id, 'New position:', dropResult.newPosition);
        },
        collect: (monitor) => ({
            isDragging: monitor.isDragging(),
        }),
    }), [id, size, position, viewMode]);

    // Sử dụng empty image để tránh ghost image mặc định
    useEffect(() => {
        preview(getEmptyImage(), { captureDraggingState: true });
    }, [preview]);

    const handleMouseDown = useCallback((e) => {
        e.stopPropagation();
        onSelectElement(id, e.ctrlKey);
    }, [id, onSelectElement]);

    const handleContextMenu = useCallback((e) => {
        e.preventDefault();
        e.stopPropagation();
        onContextMenu(id, { x: e.clientX, y: e.clientY });
    }, [id, onContextMenu]);

    // Render component theo type
    const renderComponent = useCallback(() => {
        switch (type) {
            case 'heading':
                return (
                    <h2 style={{
                        fontSize: componentData.fontSize || '1.5rem',
                        color: componentData.color || '#1f2937',
                        margin: 0,
                        padding: 0
                    }}>
                        {componentData.content || 'Tiêu đề'}
                    </h2>
                );
            case 'paragraph':
                return (
                    <p style={{
                        fontSize: componentData.fontSize || '1rem',
                        color: componentData.color || '#1f2937',
                        margin: 0,
                        padding: 0
                    }}>
                        {componentData.content || 'Đoạn văn'}
                    </p>
                );
            case 'button':
                return (
                    <button style={{
                        padding: '8px 16px',
                        borderRadius: '8px',
                        background: componentData.background || '#2563eb',
                        color: componentData.color || '#fff',
                        border: 'none',
                        cursor: 'pointer'
                    }}>
                        {componentData.content || 'Nút'}
                    </button>
                );
            case 'image':
                return (
                    <img
                        src={componentData.src || 'https://via.placeholder.com/150'}
                        alt="Image"
                        style={{ maxWidth: '100%', height: 'auto' }}
                        draggable={false}
                    />
                );
            case 'form':
                return (
                    <form style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                        <input
                            type="text"
                            placeholder="Input"
                            style={{ padding: '8px', borderRadius: '8px', border: '1px solid #ccc' }}
                        />
                        <button
                            type="submit"
                            style={{
                                background: '#2563eb',
                                color: '#fff',
                                padding: '8px 16px',
                                borderRadius: '8px',
                                border: 'none',
                                cursor: 'pointer'
                            }}
                        >
                            Submit
                        </button>
                    </form>
                );
            default:
                return <div>{componentData.content || 'Unknown'}</div>;
        }
    }, [type, componentData]);

    return (
        <div
            ref={(node) => {
                elementRef.current = node;
                drag(node);
            }}
            className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
            style={{
                position: 'absolute',
                transform: `translate(${position[viewMode]?.x || 0}px, ${position[viewMode]?.y || 0}px)`,
                width: size.width,
                height: size.height,
                zIndex: position.z || 1,
                opacity: isDragging ? 0.5 : 1,
                cursor: isDragging ? 'grabbing' : 'grab',
                userSelect: 'none',
                ...styles,
            }}
            onMouseDown={handleMouseDown}
            onContextMenu={handleContextMenu}
        >
            {renderComponent()}
        </div>
    );
});

// Component Canvas chính
const Canvas = React.memo(({
                               pageData,
                               selectedIds,
                               onSelectElement,
                               onUpdatePosition,
                               onUpdateSize,
                               onDeleteElement,
                               onAddElement,
                               onGroupElements,
                               viewMode,
                               zoomLevel,
                               gridSize,
                               showGrid,
                           }) => {
    const canvasRef = useRef(null);
    const [guidelines, setGuidelines] = useState([]);
    const [contextMenu, setContextMenu] = useState(null);
    const [dragPreview, setDragPreview] = useState(null);

    const canvasBounds = canvasRef.current
        ? {
            width: canvasRef.current.offsetWidth / (zoomLevel / 100),
            height: canvasRef.current.offsetHeight / (zoomLevel / 100)
        }
        : { width: 1200, height: 1000 };

    // Tạo snap points cho snapping
    const getSnapPoints = useCallback(() => {
        const points = [
            { x: 0, y: 0 }, // Canvas góc trái trên
            { x: canvasBounds.width, y: canvasBounds.height }, // Canvas góc phải dưới
            { x: canvasBounds.width / 2, y: canvasBounds.height / 2 }, // Canvas center
        ];

        // Thêm snap points từ các elements
        pageData.elements.forEach((el) => {
            const bounds = getElementBounds(el);
            points.push(
                { x: bounds.left, y: bounds.top },
                { x: bounds.right, y: bounds.bottom },
                { x: bounds.centerX, y: bounds.centerY },
                { x: bounds.left, y: bounds.centerY },
                { x: bounds.right, y: bounds.centerY },
                { x: bounds.centerX, y: bounds.top },
                { x: bounds.centerX, y: bounds.bottom }
            );
        });

        return points;
    }, [pageData.elements, canvasBounds]);

    // Setup drop zone
    const [{ isOver }, drop] = useDrop(() => ({
        accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
        hover: (item, monitor) => {
            const clientOffset = monitor.getClientOffset();
            if (!clientOffset || !canvasRef.current) return;

            const pos = getCanvasPosition(
                clientOffset.x,
                clientOffset.y,
                canvasRef.current,
                zoomLevel
            );

            const snapPoints = getSnapPoints();
            const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

            // Update drag preview cho visual feedback
            if (monitor.getItemType() === ItemTypes.EXISTING_ELEMENT && item.isExisting) {
                const draggingElement = pageData.elements.find(el => el.id === item.id);
                if (draggingElement) {
                    setDragPreview({
                        id: item.id,
                        x: snapped.x,
                        y: snapped.y,
                        size: draggingElement.size || { width: 200, height: 50 }
                    });
                }
            }

            // Tạo guidelines
            const newGuidelines = [];
            pageData.elements.forEach((el) => {
                if (el.id === item.id) return; // Skip element đang drag
                const bounds = getElementBounds(el);

                // Vertical guidelines
                if (Math.abs(snapped.x - bounds.left) < 15)
                    newGuidelines.push({ vertical: true, position: bounds.left });
                if (Math.abs(snapped.x - bounds.right) < 15)
                    newGuidelines.push({ vertical: true, position: bounds.right });
                if (Math.abs(snapped.x - bounds.centerX) < 15)
                    newGuidelines.push({ vertical: true, position: bounds.centerX });

                // Horizontal guidelines
                if (Math.abs(snapped.y - bounds.top) < 15)
                    newGuidelines.push({ vertical: false, position: bounds.top });
                if (Math.abs(snapped.y - bounds.bottom) < 15)
                    newGuidelines.push({ vertical: false, position: bounds.bottom });
                if (Math.abs(snapped.y - bounds.centerY) < 15)
                    newGuidelines.push({ vertical: false, position: bounds.centerY });
            });

            // Canvas boundary guidelines
            if (Math.abs(snapped.x) < 15)
                newGuidelines.push({ vertical: true, position: 0 });
            if (Math.abs(snapped.x - canvasBounds.width) < 15)
                newGuidelines.push({ vertical: true, position: canvasBounds.width });
            if (Math.abs(snapped.x - canvasBounds.width / 2) < 15)
                newGuidelines.push({ vertical: true, position: canvasBounds.width / 2 });

            if (Math.abs(snapped.y) < 15)
                newGuidelines.push({ vertical: false, position: 0 });
            if (Math.abs(snapped.y - canvasBounds.height) < 15)
                newGuidelines.push({ vertical: false, position: canvasBounds.height });
            if (Math.abs(snapped.y - canvasBounds.height / 2) < 15)
                newGuidelines.push({ vertical: false, position: canvasBounds.height / 2 });

            setGuidelines(newGuidelines);
        },
        drop: (item, monitor) => {
            const clientOffset = monitor.getClientOffset();
            if (!clientOffset || !canvasRef.current) {
                toast.error('Không thể xác định vị trí thả!');
                return { moved: false };
            }

            const pos = getCanvasPosition(
                clientOffset.x,
                clientOffset.y,
                canvasRef.current,
                zoomLevel
            );

            const snapPoints = getSnapPoints();
            const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

            console.group('Canvas Drop Debug');
            console.log('Drop position:', snapped);
            console.log('Item:', item);
            console.log('Item type:', monitor.getItemType());
            console.groupEnd();

            // Handle element mới từ sidebar
            if (item.json && !item.isExisting) {
                onAddElement({
                    id: `${item.id}-${Date.now()}`,
                    type: item.json.type,
                    componentData: item.json.componentData,
                    position: {
                        [viewMode]: snapped,
                        desktop: snapped,
                        tablet: snapped,
                        mobile: snapped
                    },
                    size: item.json.size || { width: 200, height: 50 },
                    styles: item.json.styles || {},
                });
                toast.success('Đã thêm phần tử!');

                // Clear preview and guidelines
                setDragPreview(null);
                setGuidelines([]);

                return { moved: true, newPosition: snapped };
            }
            // Handle di chuyển element existing
            else if (item.id && item.isExisting) {
                const currentElement = pageData.elements.find(el => el.id === item.id);
                const currentPos = currentElement?.position[viewMode] || { x: 0, y: 0 };

                console.group(`Moving Element ${item.id}`);
                console.log('Current position:', currentPos);
                console.log('New position:', snapped);
                console.log('ViewMode:', viewMode);
                console.log('Position data will be:', { [viewMode]: snapped });

                // TEMP FIX: Force absolute position update
                // Tìm element và update trực tiếp
                const elementToUpdate = pageData.elements.find(el => el.id === item.id);
                if (elementToUpdate) {
                    const updatedElement = {
                        ...elementToUpdate,
                        position: {
                            ...elementToUpdate.position,
                            [viewMode]: snapped // GHI ĐÈ trực tiếp
                        }
                    };

                    // Call với updatedElement thay vì delta
                    onUpdatePosition(item.id, updatedElement.position, 'replace');
                    toast.success('Đã di chuyển phần tử!');

                    console.log('onUpdatePosition called with REPLACE mode:', item.id, updatedElement.position);
                } else {
                    console.error('Element not found:', item.id);
                }
                console.groupEnd();

                // Clear preview and guidelines
                setDragPreview(null);
                setGuidelines([]);

                return { moved: true, newPosition: snapped };
            }

            // Invalid drop
            setDragPreview(null);
            setGuidelines([]);
            return { moved: false };
        },
        collect: (monitor) => ({
            isOver: monitor.isOver(),
        }),
    }), [pageData.elements, viewMode, zoomLevel, gridSize, showGrid, canvasBounds, onAddElement, onUpdatePosition]);

    const handleContextMenu = useCallback((id, position) => {
        setContextMenu({ id, position });
    }, []);

    const handleCanvasClick = useCallback((e) => {
        if (e.target === canvasRef.current) {
            onSelectElement([]);
            setContextMenu(null);
        }
    }, [onSelectElement]);

    // Keyboard navigation với absolute positioning
    const handleKeyDown = useCallback((e) => {
        if (!selectedIds.length) return;

        e.preventDefault();
        e.stopPropagation();

        const step = e.shiftKey ? 10 : 1;

        switch (e.key) {
            case 'ArrowUp':
                selectedIds.forEach(id => {
                    const element = pageData.elements.find(el => el.id === id);
                    if (element) {
                        const newY = Math.max(0, (element.position[viewMode]?.y || 0) - step);
                        onUpdatePosition(id, { [viewMode]: {
                                x: element.position[viewMode]?.x || 0,
                                y: newY
                            }}, 'absolute');
                    }
                });
                break;
            case 'ArrowDown':
                selectedIds.forEach(id => {
                    const element = pageData.elements.find(el => el.id === id);
                    if (element) {
                        const newY = (element.position[viewMode]?.y || 0) + step;
                        onUpdatePosition(id, { [viewMode]: {
                                x: element.position[viewMode]?.x || 0,
                                y: newY
                            }}, 'absolute');
                    }
                });
                break;
            case 'ArrowLeft':
                selectedIds.forEach(id => {
                    const element = pageData.elements.find(el => el.id === id);
                    if (element) {
                        const newX = Math.max(0, (element.position[viewMode]?.x || 0) - step);
                        onUpdatePosition(id, { [viewMode]: {
                                x: newX,
                                y: element.position[viewMode]?.y || 0
                            }}, 'absolute');
                    }
                });
                break;
            case 'ArrowRight':
                selectedIds.forEach(id => {
                    const element = pageData.elements.find(el => el.id === id);
                    if (element) {
                        const newX = (element.position[viewMode]?.x || 0) + step;
                        onUpdatePosition(id, { [viewMode]: {
                                x: newX,
                                y: element.position[viewMode]?.y || 0
                            }}, 'absolute');
                    }
                });
                break;
            case 'Delete':
            case 'Backspace':
                selectedIds.forEach(id => onDeleteElement(id));
                break;
            case 'Escape':
                onSelectElement([]);
                setContextMenu(null);
                break;
        }
    }, [selectedIds, onUpdatePosition, onDeleteElement, onSelectElement, viewMode, pageData.elements]);

    // Hotkeys
    useHotkeys('ctrl+a', (e) => {
        e.preventDefault();
        onSelectElement(pageData.elements.map((el) => el.id));
    }, [pageData.elements, onSelectElement]);

    useHotkeys('ctrl+g', (e) => {
        e.preventDefault();
        if (selectedIds.length > 1) {
            onGroupElements(selectedIds);
        }
    }, [selectedIds, onGroupElements]);

    // Click outside để đóng context menu
    useEffect(() => {
        const handleClickOutside = () => setContextMenu(null);
        if (contextMenu) {
            document.addEventListener('click', handleClickOutside);
            return () => document.removeEventListener('click', handleClickOutside);
        }
    }, [contextMenu]);

    return (
        <div
            className="lpb-canvas-wrapper"
            onKeyDown={handleKeyDown}
            tabIndex={0}
            style={{ outline: 'none' }}
        >
            <div
                ref={(node) => {
                    canvasRef.current = node;
                    drop(node);
                }}
                className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                style={{
                    position: 'relative',
                    minHeight: '100%',
                    background: pageData.canvas?.background || '#ffffff',
                    transform: `scale(${zoomLevel / 100})`,
                    transformOrigin: 'top left',
                    overflow: 'hidden',
                }}
                onClick={handleCanvasClick}
            >
                <Rulers canvasWidth={pageData.canvas?.width || 1200} canvasHeight={1000} zoomLevel={zoomLevel} />
                <Guidelines guidelines={guidelines} zoomLevel={zoomLevel} />

                {/* Render elements */}
                {pageData.elements.map((element) => (
                    <Element
                        key={element.id}
                        element={element}
                        isSelected={selectedIds.includes(element.id)}
                        onSelectElement={onSelectElement}
                        onUpdatePosition={onUpdatePosition}
                        onUpdateSize={onUpdateSize}
                        viewMode={viewMode}
                        zoomLevel={zoomLevel}
                        gridSize={showGrid ? gridSize : Infinity}
                        onContextMenu={handleContextMenu}
                        canvasBounds={canvasBounds}
                        canvasRef={canvasRef}
                    />
                ))}

                {/* Drag preview box */}
                {dragPreview && (
                    <div
                        className="lpb-canvas-element lpb-drag-preview"
                        style={{
                            position: 'absolute',
                            transform: `translate(${dragPreview.x}px, ${dragPreview.y}px)`,
                            width: dragPreview.size.width,
                            height: dragPreview.size.height,
                            border: '2px dashed #3b82f6',
                            background: 'rgba(59, 130, 246, 0.1)',
                            pointerEvents: 'none',
                            zIndex: 1000,
                        }}
                    />
                )}

                <SelectionOverlay
                    selectedElements={pageData.elements.filter((el) => selectedIds.includes(el.id))}
                    onResize={onUpdateSize}
                    zoomLevel={zoomLevel}
                />

                {/* Context menu */}
                {contextMenu && (
                    <div
                        className="lpb-context-menu"
                        style={{
                            position: 'fixed',
                            left: contextMenu.position.x,
                            top: contextMenu.position.y,
                            zIndex: 10000,
                            background: '#fff',
                            border: '1px solid #ccc',
                            borderRadius: '4px',
                            boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
                            minWidth: '120px'
                        }}
                    >
                        <div
                            className="lpb-context-menu-item"
                            style={{ padding: '8px 12px', cursor: 'pointer' }}
                            onClick={() => {
                                onDeleteElement(contextMenu.id);
                                setContextMenu(null);
                            }}
                        >
                            Delete
                        </div>
                        <div
                            className="lpb-context-menu-item"
                            style={{ padding: '8px 12px', cursor: 'pointer' }}
                            onClick={() => {
                                onGroupElements([contextMenu.id]);
                                setContextMenu(null);
                            }}
                        >
                            Group
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
});

export default Canvas;
