import React, { useRef, useEffect, useCallback } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import QuickEditToolkit from './QuickEditToolkit';
import SectionToolkit from './toolkitqick/SectionToolkit';
import { ItemTypes, getCanvasPosition, snapToGrid, getElementBounds, renderComponentContent } from './helpers';
import { Lock, Hand, X } from 'lucide-react'; // Thêm Lock, Hand, X từ lucide-react

export const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         componentData = {},
         styles = {},
         isSelected,
         onSelectChild,
         zoomLevel,
         gridSize,
         showGrid,
         onUpdateChildPosition,
         onMoveChild,
         position,
         size,
         viewMode,
         visible = true,
         locked = false,
         onDeleteChild,
         onEditElement, // Thêm prop
         onDuplicateElement, // Thêm prop
         onMoveElementUp, // Thêm prop
         onMoveElementDown, // Thêm prop
     }) => {
        const dragRef = useRef(null);

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.CHILD_ELEMENT,
            canDrag: () => !locked && !componentData.locked,
            item: () => {
                if (locked || componentData.locked) {
                    toast.warning('Child element đã bị khóa!');
                    return null;
                }
                return { childId: id, parentId, isExisting: true, position, size };
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, parentId, position, size, locked, componentData.locked]);

        const handleClick = useCallback(
            (e) => {
                e.stopPropagation();
                if (typeof onSelectChild === 'function') {
                    onSelectChild(parentId, id);
                }
            },
            [parentId, id, onSelectChild]
        );

        useEffect(() => {
            if (!locked && !componentData.locked) {
                drag(dragRef);
            }
        }, [drag, locked, componentData.locked]);

        if (visible === false || componentData.visible === false) {
            return null;
        }

        const lockedStyles = locked || componentData.locked ? { opacity: 0.7, cursor: 'not-allowed', pointerEvents: 'none' } : {};

        return (
            <div
                className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''} ${isDragging ? 'lpb-child-element-dragging' : ''} ${
                    locked || componentData.locked ? 'lpb-element-locked' : ''
                }`}
                style={{
                    position: 'absolute',
                    left: position[viewMode]?.x || 0,
                    top: position[viewMode]?.y || 0,
                    width: size?.width || 200,
                    height: size?.height || 50,
                    zIndex: isDragging ? 1000 : position.z || 10,
                    cursor: locked || componentData.locked ? 'not-allowed' : isDragging ? 'grabbing' : 'grab',
                    opacity: isDragging ? 0.5 : 1,
                    padding: 0,
                    margin: 0,
                    boxSizing: 'border-box',
                    border: 'none',
                    background: 'transparent',
                    ...lockedStyles,
                }}
                onClick={handleClick}
            >
                <div
                    ref={dragRef}
                    style={{
                        width: '100%',
                        height: '100%',
                        padding: 0,
                        margin: 0,
                        boxSizing: 'border-box',
                        border: 'none',
                        background: 'transparent',
                    }}
                >
                    {renderComponentContent(type, componentData, styles)}
                </div>

                {locked || componentData.locked ? (
                    <div
                        style={{
                            position: 'absolute',
                            top: '2px',
                            right: '2px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '2px 6px',
                            borderRadius: '3px',
                            fontSize: '10px',
                            pointerEvents: 'none',
                        }}
                    >
                        <Lock size={12} className="text-white" />
                    </div>
                ) : null}

                {isSelected && (
                    <QuickEditToolkit
                        element={{ id, type, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => onEditElement({ json: { id, type, componentData, styles, position, size }, isChild: true })}
                        onDuplicate={() => onDuplicateElement(id, parentId)} // Truyền parentId để xử lý trong section
                        onDelete={() => onDeleteChild(parentId, id)}
                        onMoveUp={() => onMoveElementUp(id, parentId)} // Truyền parentId để xử lý trong section
                        onMoveDown={() => onMoveElementDown(id, parentId)} // Truyền parentId để xử lý trong section
                        onClose={() => onSelectChild(parentId, null)}
                    />
                )}
            </div>
        );
    }
);
export const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onAddChild,
         onUpdateChildPosition,
         onMoveChild,
         showGrid,
         setDragPreview,
         visiblePopups = [],
         onClosePopup,
         onSaveTemplate,
         onToggleVisibility,
         onEditElement,
         onDuplicateElement,
         onMoveElementUp,
         onMoveElementDown,
         onDeleteElement,
         onDeleteChild,
     }) => {
        const {
            id,
            type,
            componentData = {},
            position = {},
            size = { width: viewMode === 'mobile' ? 375 : 1200, height: 400 },
            styles = {},
            children = [],
            visible = true,
            locked = false,
        } = element;

        const elementRef = useRef(null);
        const containerRef = useRef(null);

        const handleMouseDown = useCallback(
            (e) => {
                if (locked) {
                    toast.warning('Element đã bị khóa!');
                    return;
                }
                e.stopPropagation();
                if (typeof onSelectElement === 'function') {
                    onSelectElement([id], e.ctrlKey);
                }
                onSelectChild(id, null);
            },
            [id, type, locked, onSelectElement, onSelectChild]
        );

        const handleContextMenu = useCallback(
            (e) => {
                e.preventDefault();
                e.stopPropagation();
                onContextMenu(id, { x: e.clientX, y: e.clientY });
            },
            [id, onContextMenu]
        );

        const snapPoints = [
            { x: 0, y: 0 },
            { x: size.width, y: size.height },
            { x: size.width / 2, y: size.height / 2 },
        ];
        children.forEach((child) => {
            const bounds = getElementBounds(child);
            snapPoints.push(
                { x: bounds.left, y: bounds.top },
                { x: bounds.right, y: bounds.bottom },
                { x: bounds.centerX, y: bounds.centerY }
            );
        });

        const [{ isOverContainer, canDropContainer }, dropSection] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
            canDrop: (item, monitor) => {
                if (type !== 'section' || componentData?.structure !== 'ladi-standard') {
                    return false;
                }
                if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT && item.parentId && item.parentId !== id) {
                    return false;
                }
                return true;
            },
            drop: (item, monitor) => {
                if (!monitor.canDrop() || !containerRef.current) return { moved: false };

                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return { moved: false };

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT) {
                    const newId = `${item.id}-${Date.now()}`;
                    const newChild = {
                        id: newId,
                        type: item.json.type,
                        componentData: item.json.componentData || {},
                        position: {
                            [viewMode]: snapped,
                            desktop: snapped,
                            tablet: snapped,
                            mobile: snapped,
                        },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: [],
                        visible: true,
                        locked: false,
                    };
                    onAddChild(id, newChild);
                    toast.success('Đã thêm thành phần con vào section!');
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped };
                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    if (item.parentId === id) {
                        onUpdateChildPosition(id, item.childId, snapped);
                        toast.success('Đã di chuyển thành phần con trong section!');
                    } else {
                        onMoveChild(item.parentId, item.childId, id, snapped);
                        toast.success('Đã di chuyển thành phần con sang section khác!');
                    }
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped };
                }
                setDragPreview(null);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOverContainer: monitor.isOver(),
                canDropContainer: monitor.canDrop(),
            }),
        }), [id, type, componentData, viewMode, zoomLevel, gridSize, showGrid, onAddChild, onUpdateChildPosition, onMoveChild]);

        const [{ isOverPopup, canDropPopup }, dropPopup] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
            canDrop: () => type === 'popup',
            drop: (item, monitor) => {
                if (!containerRef.current) return { moved: false };

                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return { moved: false };

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT) {
                    const newId = `${item.id}-${Date.now()}`;
                    const newChild = {
                        id: newId,
                        type: item.json.type,
                        componentData: item.json.componentData || {},
                        position: {
                            [viewMode]: snapped,
                            desktop: snapped,
                            tablet: snapped,
                            mobile: snapped,
                        },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                        children: [],
                        visible: true,
                        locked: false,
                    };
                    onAddChild(id, newChild);
                    toast.success('Đã thêm thành phần vào popup!');
                    setDragPreview(null);
                    return { moved: true, newPosition: snapped };
                }
                setDragPreview(null);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOverPopup: monitor.isOver(),
                canDropPopup: monitor.canDrop(),
            }),
        }), [id, type, viewMode, zoomLevel, gridSize, showGrid, onAddChild]);

        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            canDrag: () => !locked && type !== 'section' && type !== 'popup',
            item: () => {
                if (locked) {
                    toast.warning('Element đã bị khóa!');
                    return null;
                }
                return {
                    id,
                    elementSize: size,
                    elementPosition: position[viewMode] || { x: 0, y: 0 },
                    isExisting: true,
                };
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, position, viewMode, locked, type]);

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        useEffect(() => {
            if (type === 'section' && componentData?.structure === 'ladi-standard') {
                dropSection(containerRef);
            }
        }, [dropSection, type, componentData]);

        useEffect(() => {
            if (type === 'popup' && containerRef.current) {
                dropPopup(containerRef);
            }
        }, [dropPopup, type]);

        if (visible === false) {
            return null;
        }

        const isPopup = type === 'popup';
        const shouldShowPopup = isSelected || visiblePopups.includes(id);
        if (isPopup && !shouldShowPopup) {
            return null;
        }

        const animationStyles = {
            animation: componentData.animation?.onLoad
                ? `${componentData.animation.onLoad} ${componentData.animation?.duration || 1000}ms ease`
                : 'none',
        };

        // Clean styles for proper rendering
        const cleanStyles = {
            ...styles,
            boxSizing: 'border-box',
        };

        if (type === 'section' && componentData.structure === 'ladi-standard') {
            return (
                <div
                    ref={elementRef}
                    className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                    style={{
                        position: 'absolute',
                        top: position[viewMode]?.y || 0,
                        width: viewMode === 'mobile' ? '375px' : '1200px',
                        height: size.height,
                        left: '50%',
                        transform: `translateX(-50%)`,
                        zIndex: position.z || 1,
                        userSelect: 'none',
                        background: componentData.backgroundType === 'gradient' ? cleanStyles.background : 'none',
                        border: cleanStyles.border || 'none',
                        borderRadius: cleanStyles.borderRadius || '0px',
                        boxShadow: cleanStyles.boxShadow || 'none',
                        padding: 0,
                        margin: 0,
                        boxSizing: 'border-box',
                        ...animationStyles,
                        ...(locked ? { opacity: 0.85, pointerEvents: 'none' } : {}),
                    }}
                    onMouseDown={locked ? undefined : handleMouseDown}
                    onContextMenu={handleContextMenu}
                >
                    {locked && (
                        <div
                            style={{
                                position: 'absolute',
                                top: '10px',
                                right: '10px',
                                zIndex: 10001,
                                background: 'rgba(0,0,0,0.7)',
                                color: '#fff',
                                padding: '6px 12px',
                                borderRadius: '6px',
                                fontSize: '12px',
                                pointerEvents: 'none',
                            }}
                        >
                            <Lock size={12} className="text-white" /> Section Locked {/* Thay fas fa-lock bằng Lock */}
                        </div>
                    )}

                    <div className="ladi-section" style={{ width: '100%', height: '100%', padding: 0, margin: 0, boxSizing: 'border-box' }}>
                        <div
                            className="ladi-section-background"
                            style={{
                                backgroundColor: componentData.backgroundType === 'color' ? cleanStyles.backgroundColor || 'transparent' : 'none',
                                backgroundImage: componentData.backgroundType === 'image' && componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none',
                                backgroundSize: 'cover',
                                backgroundPosition: cleanStyles.backgroundPosition || 'center',
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                zIndex: 0,
                            }}
                        ></div>
                        <div
                            className="ladi-overlay"
                            style={{
                                backgroundColor: componentData.overlayColor || 'transparent',
                                opacity: componentData.overlayOpacity || 0,
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                bottom: 0,
                                zIndex: 0,
                            }}
                        ></div>
                        <div
                            ref={containerRef}
                            className={`ladi-container ${isOverContainer && canDropContainer ? 'lpb-child-element-hover' : ''}`}
                            style={{
                                position: 'relative',
                                zIndex: 1,
                                padding: cleanStyles.padding || '20px',
                                width: '100%',
                                height: '100%',
                                boxSizing: 'border-box',
                                border: isOverContainer && canDropContainer ? '2px dashed #2563eb' : 'none',
                            }}
                        >
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    visible={child.visible !== false}
                                    locked={child.locked || false}
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    zoomLevel={zoomLevel}
                                    gridSize={gridSize}
                                    showGrid={showGrid}
                                    onUpdateChildPosition={onUpdateChildPosition}
                                    onMoveChild={onMoveChild}
                                    viewMode={viewMode}
                                    onDeleteChild={onDeleteChild}
                                    onEditElement={onEditElement} // Truyền prop
                                    onDuplicateElement={onDuplicateElement} // Truyền prop
                                    onMoveElementUp={onMoveElementUp} // Truyền prop
                                    onMoveElementDown={onMoveElementDown} // Truyền prop
                                />
                            ))}
                        </div>
                    </div>

                    {isSelected && (
                        <SectionToolkit
                            element={element}
                            position={{ x: 0, y: 0 }}
                            onEdit={() => onSelectElement([id])}
                            onSaveTemplate={onSaveTemplate}
                            onMoveUp={() => onMoveElementUp(id)}
                            onMoveDown={() => onMoveElementDown(id)}
                            onToggleVisibility={() => onToggleVisibility(id)}
                            onDuplicate={() => onDuplicateElement(id)}
                            onDelete={() => onDeleteElement(id)}
                            onDeleteChildren={() => onDeleteChild(id, null)} // Thêm onDeleteChildren
                        />
                    )}
                </div>
            );
        }

        if (isPopup) {
            return (
                <>
                    <div
                        className="lpb-popup-backdrop"
                        style={{
                            position: 'absolute',
                            top: 0,
                            left: 0,
                            right: 0,
                            bottom: 0,
                            background: 'rgba(0, 0, 0, 0.4)',
                            zIndex: 1000,
                            pointerEvents: 'none',
                        }}
                        onClick={(e) => {
                            e.stopPropagation();
                            if (typeof onClosePopup === 'function') {
                                onClosePopup(id);
                            }
                        }}
                    />
                    <div
                        ref={elementRef}
                        className={`lpb-popup-container ${isSelected ? 'lpb-popup-selected' : ''} ${locked ? 'lpb-element-locked' : ''}`}
                        style={{
                            position: 'absolute',
                            top: '50%',
                            left: '50%',
                            transform: 'translate(-50%, -50%)',
                            width: viewMode === 'mobile' ? '90%' : size?.width || 600,
                            maxWidth: viewMode === 'mobile' ? '90%' : '600px',
                            minHeight: size?.height || 400,
                            maxHeight: '90vh',
                            zIndex: 1001,
                            background: componentData.background || 'rgba(255, 255, 255, 0.95)',
                            borderRadius: componentData.borderRadius || '12px',
                            boxShadow: isSelected ? '0 20px 60px rgba(0, 0, 0, 0.3), 0 0 0 3px #3b82f6' : '0 8px 32px rgba(0, 0, 0, 0.25)',
                            overflow: 'hidden',
                            display: 'flex',
                            flexDirection: 'column',
                            transition: 'all 0.3s ease',
                            cursor: locked ? 'not-allowed' : 'default',
                            padding: 0,
                            margin: 0,
                            boxSizing: 'border-box',
                            ...cleanStyles,
                            ...(locked ? { opacity: 0.85, pointerEvents: 'none' } : {}),
                        }}
                        onClick={handleMouseDown}
                        onContextMenu={handleContextMenu}
                    >
                        <div
                            className="lpb-popup-header"
                            style={{
                                padding: '16px 20px',
                                borderBottom: '1px solid #e5e7eb',
                                background: 'linear-gradient(to bottom, #f9fafb, #ffffff)',
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center',
                                flexShrink: 0,
                                margin: 0,
                                boxSizing: 'border-box',
                            }}
                        >
                            <h3 style={{ margin: 0, fontSize: '16px', fontWeight: '600', color: '#111827' }}>
                                {componentData.title || 'Popup Title'}
                            </h3>
                            <button
                                className="lpb-popup-close"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    if (typeof onClosePopup === 'function') {
                                        onClosePopup(id);
                                    }
                                }}
                                style={{
                                    width: '28px',
                                    height: '28px',
                                    border: 'none',
                                    background: 'transparent',
                                    cursor: 'pointer',
                                    borderRadius: '6px',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                    transition: 'all 0.15s ease',
                                    color: '#6b7280',
                                    padding: 0,
                                    margin: 0,
                                }}
                            >
                                <X size={14} className="text-gray-600" /> {/* Thay fas fa-times bằng X */}
                            </button>
                        </div>

                        <div
                            ref={containerRef}
                            className={`lpb-popup-content ${isOverPopup && canDropPopup ? 'lpb-popup-dropzone-active' : ''}`}
                            style={{
                                position: 'relative',
                                flex: 1,
                                padding: componentData.padding || '20px',
                                overflowY: 'auto',
                                overflowX: 'hidden',
                                minHeight: '200px',
                                border: isOverPopup && canDropPopup ? '2px dashed #2563eb' : 'none',
                                background: isOverPopup && canDropPopup ? 'rgba(37, 99, 235, 0.05)' : 'transparent',
                                transition: 'all 0.2s ease',
                                margin: 0,
                                boxSizing: 'border-box',
                            }}
                        >
                            {children.length === 0 && (
                                <div
                                    className="lpb-popup-empty"
                                    style={{
                                        display: 'flex',
                                        flexDirection: 'column',
                                        alignItems: 'center',
                                        justifyContent: 'center',
                                        minHeight: '200px',
                                        color: '#9ca3af',
                                        textAlign: 'center',
                                        padding: '40px 20px',
                                        margin: 0,
                                        boxSizing: 'border-box',
                                    }}
                                >
                                    <Hand size={48} className="text-gray-300" /> {/* Thay fas fa-hand-pointer bằng Hand */}
                                    <p style={{ margin: '0 0 8px', fontSize: '14px', fontWeight: '500', color: '#6b7280' }}>
                                        Kéo thả elements vào đây
                                    </p>
                                    <small style={{ fontSize: '12px', color: '#9ca3af', margin: 0 }}>
                                        Popup có thể chứa bất kỳ element nào
                                    </small>
                                </div>
                            )}
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    visible={child.visible !== false}
                                    locked={child.locked || false}
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    zoomLevel={zoomLevel}
                                    gridSize={gridSize}
                                    showGrid={showGrid}
                                    onUpdateChildPosition={onUpdateChildPosition}
                                    onMoveChild={onMoveChild}
                                    viewMode={viewMode}
                                    onDeleteChild={onDeleteChild}
                                />
                            ))}
                        </div>

                        {locked && (
                            <div
                                style={{
                                    position: 'absolute',
                                    top: '5px',
                                    right: '5px',
                                    zIndex: 10001,
                                    background: 'rgba(0,0,0,0.7)',
                                    color: '#fff',
                                    padding: '4px 8px',
                                    borderRadius: '4px',
                                    fontSize: '11px',
                                    pointerEvents: 'none',
                                    margin: 0,
                                }}
                            >
                                <Lock size={12} className="text-white" /> Locked {/* Thay fas fa-lock bằng Lock */}
                            </div>
                        )}
                    </div>
                </>
            );
        }

        const lockedStyles = locked ? { opacity: 0.7, cursor: 'not-allowed', pointerEvents: 'none' } : {};

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    if (!locked && type !== 'section' && type !== 'popup') {
                        drag(node);
                    }
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''} ${
                    locked ? 'lpb-element-locked' : ''
                }`}
                style={{
                    position: 'absolute',
                    top: position[viewMode]?.y || 0,
                    width: viewMode === 'mobile' ? '375px' : '1200px',
                    height: size.height,
                    left: '50%',
                    transform: 'translateX(-50%)',
                    zIndex: position.z || 1,
                    opacity: isDragging ? 0.5 : 1,
                    cursor: locked ? 'not-allowed' : isDragging ? 'grabbing' : 'grab',
                    userSelect: 'none',
                    padding: 0,
                    margin: 0,
                    boxSizing: 'border-box',
                    ...cleanStyles,
                    ...lockedStyles,
                }}
                onMouseDown={locked ? undefined : handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {locked && (
                    <div
                        style={{
                            position: 'absolute',
                            top: '5px',
                            right: '5px',
                            zIndex: 10001,
                            background: 'rgba(0,0,0,0.7)',
                            color: '#fff',
                            padding: '4px 8px',
                            borderRadius: '4px',
                            fontSize: '11px',
                            pointerEvents: 'none',
                            margin: 0,
                        }}
                    >
                        <Lock size={12} className="text-white" /> Locked {/* Thay fas fa-lock bằng Lock */}
                    </div>
                )}

                <div style={{ width: '100%', height: '100%', padding: 0, margin: 0, boxSizing: 'border-box' }}>
                    {renderComponentContent(type, componentData, cleanStyles, children)}
                </div>

                {isSelected && type !== 'section' && (
                    <QuickEditToolkit
                        element={{ id, type, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => onEditElement({ json: element, isChild: false })}
                        onDuplicate={() => onDuplicateElement(id)}
                        onDelete={() => onDeleteElement(id)}
                        onMoveUp={() => onMoveElementUp(id)}
                        onMoveDown={() => onMoveElementDown(id)}
                        onClose={() => onSelectElement([])}
                    />
                )}
            </div>
        );
    }
);
