import React, { useState, useMemo, useCallback, useEffect, useRef } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Toolbar from './create-page/Toolbar';
import ComponentLibrary from './create-page/ComponentLibrary';
import Canvas from './create-page/Canvas';
import PropertiesPanel from './create-page/PropertiesPanel';
import '../styles/CreateLanding.css';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

const CreateLandingPage = () => {
    const [pageStructure, setPageStructure] = useState([]);
    const [selectedElement, setSelectedElement] = useState(null);
    const [leftSidebarCollapsed, setLeftSidebarCollapsed] = useState(false);
    const [rightSidebarCollapsed, setRightSidebarCollapsed] = useState(false);
    const [quickEditElement, setQuickEditElement] = useState(null);
    const [quickEditPosition, setQuickEditPosition] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [history, setHistory] = useState([[]]);
    const [historyIndex, setHistoryIndex] = useState(0);
    const [copiedElement, setCopiedElement] = useState(null);
    const fileInputRef = useRef(null);

    const memoizedPageStructure = useMemo(() => pageStructure, [pageStructure]);

    const updatePosition = useCallback((elementId, sectionId, newPosition) => {
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.id === sectionId) {
                    return {
                        ...section,
                        elements: section.elements.map((el) =>
                            el.id === elementId
                                ? {
                                    ...el,
                                    json: {
                                        ...el.json,
                                        styles: {
                                            ...el.json.styles,
                                            position: {
                                                x: Math.round(newPosition.x),
                                                y: Math.round(newPosition.y),
                                            },
                                        },
                                    },
                                }
                                : el
                        ),
                    };
                }
                return section;
            });
            updateHistory(newStructure);
            return newStructure;
        });
        toast.success('Đã cập nhật vị trí phần tử!');
    }, []);

    const addSection = useCallback((sectionData, index) => {
        const newSection = {
            ...sectionData,
            id: `${sectionData.id}-${Date.now()}`,
            elements: [],
            json: {
                ...sectionData.json,
                styles: {
                    ...sectionData.json.styles,
                    width: '1200px',
                    height: 'auto',
                },
            },
        };
        setPageStructure((prev) => {
            const newStructure = [...prev];
            newStructure.splice(index, 0, newSection);
            updateHistory(newStructure);
            return newStructure;
        });
        toast.success('Đã thêm section!');
    }, []);

    const addElementToSection = useCallback((sectionId, elementData, index) => {
        const newElement = {
            ...elementData,
            id: `${elementData.id}-${Date.now()}`,
            json: {
                ...elementData.json,
                styles: {
                    ...elementData.json.styles,
                    position: { x: 50, y: index * 100 + 50 },
                    width: elementData.json.styles.width || 'auto',
                    height: elementData.json.styles.height || 'auto',
                },
            },
        };
        setPageStructure((prev) => {
            const newStructure = prev.map((section) =>
                section.id === sectionId
                    ? {
                        ...section,
                        elements: [...section.elements.slice(0, index), newElement, ...section.elements.slice(index)],
                    }
                    : section
            );
            updateHistory(newStructure);
            return newStructure;
        });
        toast.success('Đã thêm phần tử vào section!');
    }, []);

    const moveElement = useCallback((sectionId, elementId, newIndex) => {
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.id === sectionId) {
                    const currentIndex = section.elements.findIndex((el) => el.id === elementId);
                    if (currentIndex === -1 || currentIndex === newIndex) return section;
                    const newElements = [...section.elements];
                    const [movedElement] = newElements.splice(currentIndex, 1);
                    newElements.splice(newIndex, 0, {
                        ...movedElement,
                        json: {
                            ...movedElement.json,
                            styles: {
                                ...movedElement.json.styles,
                                position: { x: movedElement.json.styles.position.x, y: newIndex * 100 + 50 },
                            },
                        },
                    });
                    return { ...section, elements: newElements };
                }
                return section;
            });
            updateHistory(newStructure);
            return newStructure;
        });
        toast.info('Đã di chuyển phần tử!');
    }, []);

    const updateElement = useCallback((elementId, sectionId, updates) => {
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.id === sectionId) {
                    return {
                        ...section,
                        elements: section.elements.map((el) =>
                            el.id === elementId
                                ? {
                                    ...el,
                                    json: {
                                        ...el.json,
                                        ...updates,
                                    },
                                }
                                : el
                        ),
                    };
                }
                return section;
            });
            updateHistory(newStructure);
            return newStructure;
        });
        toast.success('Đã cập nhật phần tử!');
    }, []);

    const handleInlineEdit = useCallback((elementId, sectionId, newContent) => {
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.id === sectionId) {
                    return {
                        ...section,
                        elements: section.elements.map((el) =>
                            el.id === elementId
                                ? {
                                    ...el,
                                    json: {
                                        ...el.json,
                                        content: newContent,
                                    },
                                }
                                : el
                        ),
                    };
                }
                return section;
            });
            updateHistory(newStructure);
            return newStructure;
        });
        toast.success('Đã cập nhật nội dung!');
    }, []);

    const handleCopyElement = useCallback((element) => {
        if (!element) return;
        setCopiedElement({ ...element, id: `${element.id}-copy-${Date.now()}` });
        toast.success('Đã sao chép phần tử!');
    }, []);

    const handlePasteElement = useCallback((sectionId, index) => {
        if (!copiedElement) {
            toast.error('Không có phần tử nào được sao chép!');
            return;
        }
        setPageStructure((prev) => {
            const newStructure = prev.map((section) =>
                section.id === sectionId
                    ? {
                        ...section,
                        elements: [
                            ...section.elements.slice(0, index),
                            {
                                ...copiedElement,
                                id: `${copiedElement.id}-paste-${Date.now()}`,
                                json: {
                                    ...copiedElement.json,
                                    styles: {
                                        ...copiedElement.json.styles,
                                        position: {
                                            x: (copiedElement.json.styles.position?.x || 0) + 10,
                                            y: (copiedElement.json.styles.position?.y || 0) + 10,
                                        },
                                    },
                                },
                            },
                            ...section.elements.slice(index),
                        ],
                    }
                    : section
            );
            updateHistory(newStructure);
            return newStructure;
        });
        toast.success('Đã dán phần tử!');
    }, [copiedElement]);

    const handleExportPage = useCallback(() => {
        const exportData = {
            pageStructure,
            viewMode,
            zoomLevel,
            exportedAt: new Date().toISOString(),
        };
        const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `landing-page-${Date.now()}.json`;
        a.click();
        URL.revokeObjectURL(url);
        toast.success('Đã xuất trang thành công!');
    }, [pageStructure, viewMode, zoomLevel]);

    const handleImportPage = useCallback((event) => {
        const file = event.target.files[0];
        if (!file) return;
        const reader = new FileReader();
        reader.onload = (e) => {
            try {
                const data = JSON.parse(e.target.result);
                setPageStructure(data.pageStructure || []);
                setViewMode(data.viewMode || 'desktop');
                setZoomLevel(data.zoomLevel || 100);
                updateHistory(data.pageStructure || []);
                toast.success('Đã nhập trang thành công!');
            } catch (error) {
                toast.error('Lỗi khi nhập trang: ' + error.message);
            }
        };
        reader.readAsText(file);
        event.target.value = null;
    }, []);

    const undo = useCallback(() => {
        if (historyIndex > 0) {
            setHistoryIndex((prev) => prev - 1);
            setPageStructure([...history[historyIndex - 1]]);
            toast.info('Đã hoàn tác!');
        }
    }, [history, historyIndex]);

    const redo = useCallback(() => {
        if (historyIndex < history.length - 1) {
            setHistoryIndex((prev) => prev + 1);
            setPageStructure([...history[historyIndex + 1]]);
            toast.info('Đã khôi phục!');
        }
    }, [history, historyIndex]);

    const handleSelectElement = useCallback((element) => {
        setSelectedElement(element);
        setQuickEditElement(null);
        setQuickEditPosition(null);
    }, []);

    const handleQuickEdit = useCallback((element, position) => {
        setQuickEditElement(element);
        setQuickEditPosition(position);
    }, []);

    const handleCloseQuickEdit = useCallback(() => {
        setQuickEditElement(null);
        setQuickEditPosition(null);
    }, []);

    const handleDuplicateElement = useCallback((element) => {
        if (!element) return;
        const duplicated = {
            ...element,
            id: `${element.id}-copy-${Date.now()}`,
            json: {
                ...element.json,
                styles: {
                    ...element.json.styles,
                    position: {
                        x: (element.json.styles.position?.x || 0) + 10,
                        y: (element.json.styles.position?.y || 0) + 10,
                    },
                },
            },
        };
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.elements.some((el) => el.id === element.id)) {
                    const index = section.elements.findIndex((el) => el.id === element.id);
                    return {
                        ...section,
                        elements: [...section.elements.slice(0, index + 1), duplicated, ...section.elements.slice(index + 1)],
                    };
                }
                return section;
            });
            updateHistory(newStructure);
            return newStructure;
        });
        toast.success('Đã sao chép phần tử!');
    }, []);

    const handleDeleteElement = useCallback((element) => {
        if (!element) return;
        setPageStructure((prev) => {
            const newStructure = prev.map((section) =>
                section.id === (element.id.split('-')[0] === 'section' ? element.id : element.sectionId)
                    ? {
                        ...section,
                        elements: section.elements.filter((el) => el.id !== element.id),
                    }
                    : section
            ).filter((section) => section.elements.length > 0 || section.id === element.id);
            updateHistory(newStructure);
            return newStructure;
        });
        if (selectedElement?.id === element.id) {
            setSelectedElement(null);
        }
        setQuickEditElement(null);
        setQuickEditPosition(null);
        toast.success('Đã xóa phần tử!');
    }, [selectedElement]);

    const handleMoveElement = useCallback((element, direction) => {
        if (!element) return;
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.elements.some((el) => el.id === element.id)) {
                    const currentIndex = section.elements.findIndex((el) => el.id === element.id);
                    if (currentIndex === -1) return section;
                    const newIndex =
                        direction === 'up' ? Math.max(0, currentIndex - 1) : Math.min(section.elements.length - 1, currentIndex + 1);
                    if (newIndex === currentIndex) return section;
                    const newElements = [...section.elements];
                    const [movedElement] = newElements.splice(currentIndex, 1);
                    newElements.splice(newIndex, 0, {
                        ...movedElement,
                        json: {
                            ...movedElement.json,
                            styles: {
                                ...movedElement.json.styles,
                                position: { x: movedElement.json.styles.position.x, y: newIndex * 100 + 50 },
                            },
                        },
                    });
                    return { ...section, elements: newElements };
                }
                return section;
            });
            updateHistory(newStructure);
            return newStructure;
        });
        toast.info('Đã di chuyển phần tử!');
    }, []);

    const updateHistory = useCallback((newStructure) => {
        setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
        setHistoryIndex((prev) => prev + 1);
    }, [historyIndex]);

    useHotkeys('ctrl+z', undo, [undo], { preventDefault: true });
    useHotkeys('ctrl+y', redo, [redo], { preventDefault: true });
    useHotkeys('ctrl+c', () => selectedElement && handleCopyElement(selectedElement), [selectedElement, handleCopyElement], { preventDefault: true });
    useHotkeys('ctrl+v', () => selectedElement && handlePasteElement(selectedElement.sectionId, selectedElement.index + 1), [selectedElement, handlePasteElement], { preventDefault: true });
    useHotkeys('delete', () => selectedElement && handleDeleteElement(selectedElement), [selectedElement, handleDeleteElement], { preventDefault: true });

    useEffect(() => {
        const handleKeyDown = (e) => {
            if (e.key === 'Escape') {
                setSelectedElement(null);
                setQuickEditElement(null);
                setQuickEditPosition(null);
            }
        };
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, []);

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container">
                <Toolbar
                    viewMode={viewMode}
                    setViewMode={setViewMode}
                    zoomLevel={zoomLevel}
                    setZoomLevel={setZoomLevel}
                    onExport={handleExportPage}
                    onImport={() => fileInputRef.current.click()}
                    fileInputRef={fileInputRef}
                    onImportPage={handleImportPage}
                />
                <div className="lpb-main-content">
                    <ComponentLibrary
                        collapsed={leftSidebarCollapsed}
                        onToggleCollapse={() => setLeftSidebarCollapsed(!leftSidebarCollapsed)}
                        onDragEnd={() => setLeftSidebarCollapsed(true)}
                        addSection={addSection}
                        addElement={addElementToSection}
                    />
                    <Canvas
                        pageStructure={memoizedPageStructure}
                        selectedElement={selectedElement}
                        onSelectElement={handleSelectElement}
                        quickEditElement={quickEditElement}
                        quickEditPosition={quickEditPosition}
                        onQuickEdit={handleQuickEdit}
                        onCloseQuickEdit={handleCloseQuickEdit}
                        onDuplicateElement={handleDuplicateElement}
                        onDeleteElement={handleDeleteElement}
                        onMoveElement={handleMoveElement}
                        viewMode={viewMode}
                        addSection={addSection}
                        addElement={addElementToSection}
                        moveElement={moveElement}
                        ItemTypes={ItemTypes}
                        zoomLevel={zoomLevel}
                        setPageStructure={setPageStructure}
                        updatePosition={updatePosition}
                        onInlineEdit={handleInlineEdit}
                    />
                    <PropertiesPanel
                        collapsed={rightSidebarCollapsed}
                        onToggleCollapse={() => setRightSidebarCollapsed(!rightSidebarCollapsed)}
                        selectedElement={selectedElement}
                        onUpdateElement={updateElement}
                        onDeleteElement={handleDeleteElement}
                        onDuplicateElement={handleDuplicateElement}
                        onMoveElement={handleMoveElement}
                    />
                </div>
                <input
                    type="file"
                    ref={fileInputRef}
                    style={{ display: 'none' }}
                    accept=".json"
                    onChange={handleImportPage}
                />
                <ToastContainer position="top-right" autoClose={3000} />
            </div>
        </DndProvider>
    );
};

export default CreateLandingPage;
