// F:\landinghub-iconic\apps\web\src\components\Header.js
import React from 'react';
import { useContext } from 'react';
import { UserContext } from '../context/UserContext';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

const Header = () => {
    const { user, setUser } = useContext(UserContext);
    const navigate = useNavigate();
    const token = localStorage.getItem('token');
    const decodedToken = token ? jwtDecode(token) : null;

    const handleLogout = () => {
        localStorage.removeItem('token'); // Xóa token
        setUser(null); // Xóa user từ context
        window.location.href = '/auth'; // Reload và quay lại trang đăng nhập
    };

    return (
        <header className="dashboard-header">
            <div className="logo">LandingHub</div>
            <div className="user-info">
                {user?.name || decodedToken?.name ? (
                    <>
                        <span>Chào {user?.name || decodedToken?.name}</span>
                        <button onClick={handleLogout}>Đăng xuất</button>
                    </>
                ) : (
                    <button onClick={() => navigate('/auth')}>Đăng nhập</button>
                )}
            </div>
        </header>
    );
};

export default React.memo(Header);
