// CreateLanding.js
import React, { useState, useEffect, useCallback } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import { parse } from 'node-html-parser';
import Canvas from '../components/create-page/Canvas';
import ComponentLibrary from '../components/create-page/ComponentLibrary';
import PropertiesPanel from '../components/create-page/PropertiesPanel';
import Toolbar from './create-page/Toolbar';
import api from '@landinghub/api';
import '../styles/CreateLanding.css';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import _ from 'lodash';

const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--border-radius-lg': '12px',
    '--border-radius-xl': '16px',
    '--shadow-sm': '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--shadow-lg': '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
    '--shadow-xl': '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
};

const DataStore = {
    variables: {},
    apiEndpoints: {},
    formData: {},
};

const resolveDataBinding = (text, dataStore) => {
    return text.replace(/\{\{([^}]+)\}\}/g, (match, path) => {
        return _.get(dataStore.variables, path) || match;
    });
};

const executeAction = (action, params) => {
    const actions = {
        submitForm: (params) => {
            fetch(params.endpoint, { method: 'POST', body: JSON.stringify(DataStore.formData) })
                .then(res => res.json())
                .then(data => toast.success('Form submitted!'))
                .catch(err => toast.error('Error submitting form'));
        },
        navigate: (params) => window.location.href = params.url,
        showModal: (params) => { toast.info('Modal shown'); },
    };
    return actions[action]?.(params);
};

const parseStyles = (styleString) => {
    if (!styleString) return {};
    return styleString.split(';').reduce((acc, style) => {
        const [key, value] = style.split(':').map(s => s.trim());
        if (key && value) {
            const camelKey = key.replace(/-([a-z])/g, (g) => g[1].toUpperCase());
            acc[camelKey] = value;
        }
        return acc;
    }, {});
};

const parseHTMLToPageData = (html) => {
    try {
        const root = parse(html, { blockTextElements: { style: true } });
        const pageContainer = root.querySelector('.page-container');
        if (!pageContainer) {
            return { canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] }, elements: [], meta: {} };
        }
        const elements = [];
        pageContainer.querySelectorAll('.lpb-canvas-element, .ladi-section').forEach((node, index) => {
            const styles = parseStyles(node.getAttribute('style') || '');
            const position = {
                desktop: { x: parseFloat(styles.left) || 0, y: parseFloat(styles.top) || 0 },
                tablet: { x: parseFloat(styles.left) || 0, y: parseFloat(styles.top) || 0 },
                mobile: { x: parseFloat(styles.left) || 0, y: parseFloat(styles.top) || 0 },
            };
            const size = {
                width: parseFloat(styles.width) || 420,
                height: parseFloat(styles.height) || 400,
            };
            let type = node.getAttribute('data-type') || 'container';
            if (node.classList.contains('ladi-section')) {
                type = 'container';
            }
            const children = [];
            node.childNodes.forEach((child, childIndex) => {
                if (child.nodeType !== 1) return;
                const childStyles = parseStyles(child.getAttribute('style') || '');
                const childPosition = {
                    desktop: { x: parseFloat(childStyles.left) || 0, y: parseFloat(childStyles.top) || 0 },
                    tablet: { x: parseFloat(childStyles.left) || 0, y: parseFloat(childStyles.top) || 0 },
                    mobile: { x: parseFloat(childStyles.left) || 0, y: parseFloat(childStyles.top) || 0 },
                };
                const childSize = {
                    width: parseFloat(childStyles.width) || 200,
                    height: parseFloat(childStyles.height) || 50,
                };
                let childType;
                if (child.tagName === 'H2') childType = 'heading';
                else if (child.tagName === 'P') childType = 'paragraph';
                else if (child.tagName === 'BUTTON') childType = 'button';
                else if (child.tagName === 'IMG') childType = 'image';
                if (childType) {
                    children.push({
                        id: `child-${index}-${childIndex}-${Date.now()}`,
                        type: childType,
                        componentData: { content: child.textContent.trim() || (childType === 'image' ? { src: child.getAttribute('src') } : '') },
                        styles: childStyles,
                        position: childPosition,
                        size: childSize,
                        events: {},
                    });
                }
            });
            elements.push({
                id: `element-${index}-${Date.now()}`,
                type,
                componentData: { layout: node.classList.contains('ladi-section') ? 'ladi-standard' : '' },
                position,
                size,
                styles,
                children,
                events: {},
            });
        });
        const pageData = {
            canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] },
            elements,
            meta: { title: 'Landing Page', description: 'Mô tả trang', keywords: 'keywords, landing, page' },
        };
        return pageData;
    } catch (error) {
        return { canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] }, elements: [], meta: {} };
    }
};

import ReactDOMServer from 'react-dom/server';
import { minify } from 'html-minifier-terser'; // Đổi sang html-minifier-terser

const renderStaticHTML = async (pageData) => { // Thêm async
    console.log('Rendering static HTML with pageData:', JSON.stringify(pageData, null, 2));
    const content = pageData.elements.map(element => (
        <div key={element.id}>{renderComponentContent(element.type, element.componentData, element.styles, element.children, DataStore, resolveDataBinding)}</div>
    ));
    const html = `
        <!DOCTYPE html>
        <html lang="vi">
        <head>
            <meta charset="UTF-8">
            <meta name="viewport" content="width=device-width, initial-scale=1.0">
            <title>${pageData.meta.title || 'Landing Page'}</title>
            <meta name="description" content="${pageData.meta.description || 'Mô tả trang'}">
            <meta name="keywords" content="${pageData.meta.keywords || 'keywords, landing, page'}">
            <link rel="stylesheet" href="/styles/CreateLanding.css">
        </head>
        <body>
            <div class="page-container">
                ${ReactDOMServer.renderToStaticMarkup(content)}
            </div>
            <script>
                document.addEventListener('DOMContentLoaded', () => {
                    document.querySelectorAll('[data-events]').forEach(el => {
                        const events = JSON.parse(el.getAttribute('data-events'));
                        for (let event in events) {
                            el.addEventListener(event.replace('on', '').toLowerCase(), () => {
                                executeAction(events[event].action, events[event].params);
                            });
                        }
                    });
                });
            </script>
        </body>
        </html>
    `;
    const minifiedHTML = await minify(html, { // Thêm await
        removeComments: true,
        collapseWhitespace: true,
        minifyCSS: true,
        minifyJS: true,
    });
    return minifiedHTML;
};

class ErrorBoundary extends React.Component {
    state = { error: null };
    static getDerivedStateFromError(error) {
        return { error };
    }
    render() {
        if (this.state.error) {
            return <div>Lỗi: {this.state.error.message}</div>;
        }
        return this.props.children;
    }
}

const CreateLanding = () => {
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const pageId = searchParams.get('id');

    const [pageData, setPageData] = useState({
        canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] },
        elements: [],
        meta: { title: 'Landing Page', description: 'Mô tả', keywords: 'keywords' },
    });
    const [selectedIds, setSelectedIds] = useState([]);
    const [selectedChildId, setSelectedChildId] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [gridSize, setGridSize] = useState(10);
    const [showGrid, setShowGrid] = useState(true);
    const [history, setHistory] = useState([]);
    const [historyIndex, setHistoryIndex] = useState(-1);
    const [isLoading, setIsLoading] = useState(true);
    const [isLibraryCollapsed, setIsLibraryCollapsed] = useState(false);
    const [isPropertiesCollapsed, setIsPropertiesCollapsed] = useState(false);

    const handleToggleLibrary = useCallback(() => {
        setIsLibraryCollapsed((prev) => !prev);
    }, []);

    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                toast.error('Vui lòng đăng nhập để tiếp tục');
                navigate('/auth');
                return;
            }
            let decoded;
            try {
                decoded = jwtDecode(token);
                if (decoded.exp && decoded.exp < Math.floor(Date.now() / 1000)) {
                    localStorage.removeItem('token');
                    toast.error('Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.');
                    navigate('/auth');
                    return;
                }
                api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            } catch (err) {
                toast.error('Phiên đăng nhập không hợp lệ');
                navigate('/auth');
                return;
            }
        };
        initializeAuth();
    }, [navigate]);

    useEffect(() => {
        if (!pageId || !pageId.match(/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i)) {
            toast.error('ID trang không hợp lệ.');
            navigate('/pages');
            return;
        }
        const fetchPageContent = async () => {
            setIsLoading(true);
            try {
                const response = await api.get(`/api/pages/${pageId}/content`);
                if (response.data.html) {
                    const parsedPageData = parseHTMLToPageData(response.data.html);
                    setPageData(parsedPageData);
                    setHistory([parsedPageData]);
                    setHistoryIndex(0);
                } else {
                    setPageData({ canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] }, elements: [], meta: {} });
                }
            } catch (error) {
                const errorMessage = error.response?.data?.error || error.message;
                toast.error('Lỗi khi lấy nội dung trang: ' + errorMessage);
                if (error.response?.status === 401) {
                    localStorage.removeItem('token');
                    navigate('/auth');
                } else {
                    navigate('/pages');
                }
            } finally {
                setIsLoading(false);
            }
        };
        fetchPageContent();
    }, [pageId, navigate]);

    const handleSelectElement = useCallback((ids, append = false) => {
        setSelectedIds(append ? [...new Set([...selectedIds, ...(Array.isArray(ids) ? ids : [ids])])] : (Array.isArray(ids) ? ids : [ids]));
        setSelectedChildId(null);
    }, [selectedIds]);

    const handleSelectChild = useCallback((parentId, childId) => {
        if (!parentId) {
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        if (!pageData.elements.find((el) => el.id === parentId)) {
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        setSelectedIds([parentId]);
        setSelectedChildId(childId);
    }, [pageData.elements]);

    const handleAddElement = useCallback((element) => {
        setPageData((prev) => {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    desktop: element.position?.desktop || { x: 0, y: 0 },
                    tablet: element.position?.tablet || { x: 0, y: 0 },
                    mobile: element.position?.mobile || { x: 0, y: 0 },
                },
                styles: {
                    ...element.styles,
                    borderRadius: element.styles?.borderRadius || CSS_VARIABLES['--border-radius'],
                    backgroundColor: element.styles?.backgroundColor || CSS_VARIABLES['--white'],
                    boxShadow: element.styles?.boxShadow || CSS_VARIABLES['--shadow'],
                    zIndex: element.type === 'section' ? 1 : 10,
                },
                children: element.children || [],
                events: element.events || {},
            };
            const newPageData = { ...prev, elements: [...prev.elements, newElement] };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã thêm phần tử!');
    }, [history, historyIndex]);

    const handleAddChild = useCallback((parentId, newChild) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        return {
                            ...el,
                            children: [...(el.children || []), {
                                ...newChild,
                                position: {
                                    desktop: newChild.position?.desktop || { x: 0, y: 0 },
                                    tablet: newChild.position?.tablet || { x: 0, y: 0 },
                                    mobile: newChild.position?.mobile || { x: 0, y: 0 },
                                },
                                styles: {
                                    ...newChild.styles,
                                    zIndex: 2,
                                },
                                events: newChild.events || {},
                            }],
                        };
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã thêm thành phần con!');
    }, [history, historyIndex]);

    const debouncedUpdatePosition = useCallback(
        _.debounce((id, position, mode) => {
            setPageData((prev) => {
                const newPageData = {
                    ...prev,
                    elements: prev.elements.map((el) =>
                        el.id === id
                            ? {
                                ...el,
                                position: mode === 'replace' ? position : { ...el.position, ...position },
                            }
                            : el
                    ),
                };
                setHistory([...history.slice(0, historyIndex + 1), newPageData]);
                setHistoryIndex(historyIndex + 1);
                return newPageData;
            });
        }, 50),
        [history, historyIndex]
    );

    const handleUpdateSize = useCallback((id, size) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id ? { ...el, size } : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    }, [history, historyIndex]);

    const handleUpdateCanvas = useCallback((canvasUpdates) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                canvas: {
                    ...prev.canvas,
                    ...canvasUpdates,
                },
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã cập nhật cài đặt canvas!');
    }, [history, historyIndex]);

    const handleDeleteElement = useCallback((id) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.filter((el) => el.id !== id),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        setSelectedIds((prev) => prev.filter((selId) => selId !== id));
        setSelectedChildId(null);
        toast.success('Đã xóa phần tử!');
    }, [history, historyIndex]);

    const handleEditElement = useCallback((updatedElement) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === selectedIds[0]
                        ? updatedElement.isChild
                            ? {
                                ...el,
                                children: el.children.map((child) =>
                                    child.id === selectedChildId
                                        ? { ...child, ...updatedElement.json }
                                        : child
                                ),
                            }
                            : { ...el, ...updatedElement.json }
                        : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã cập nhật phần tử!');
    }, [history, historyIndex, selectedIds, selectedChildId]);

    const handleDuplicateElement = useCallback((id) => {
        const element = pageData.elements.find((el) => el.id === id);
        if (element) {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    desktop: {
                        x: (element.position.desktop?.x || 0) + 10,
                        y: (element.position.desktop?.y || 0) + 10,
                    },
                    tablet: {
                        x: (element.position.tablet?.x || 0) + 10,
                        y: (element.position.tablet?.y || 0) + 10,
                    },
                    mobile: {
                        x: (element.position.mobile?.x || 0) + 10,
                        y: (element.position.mobile?.y || 0) + 10,
                    },
                },
            };
            setPageData((prev) => {
                const newPageData = {
                    ...prev,
                    elements: [...prev.elements, newElement],
                };
                setHistory([...history.slice(0, historyIndex + 1), newPageData]);
                setHistoryIndex(historyIndex + 1);
                return newPageData;
            });
            toast.success('Đã sao chép phần tử!');
        }
    }, [history, historyIndex, pageData.elements]);

    const handleMoveElementUp = useCallback((id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index <= 0) return prev;
            const newElements = [...prev.elements];
            [newElements[index - 1], newElements[index]] = [newElements[index], newElements[index - 1]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã di chuyển phần tử lên!');
    }, [history, historyIndex]);

    const handleMoveElementDown = useCallback((id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index >= prev.elements.length - 1) return prev;
            const newElements = [...prev.elements];
            [newElements[index], newElements[index + 1]] = [newElements[index + 1], newElements[index]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã di chuyển phần tử xuống!');
    }, [history, historyIndex]);

    const handleGroupElements = useCallback((ids) => {
        const groupId = `group-${Date.now()}`;
        setPageData((prev) => {
            const selectedElements = prev.elements.filter((el) => ids.includes(el.id));
            if (selectedElements.length === 0) return prev;
            const newPageData = {
                ...prev,
                elements: [
                    ...prev.elements.filter((el) => !ids.includes(el.id)),
                    {
                        id: groupId,
                        type: 'group',
                        position: {
                            desktop: { x: 0, y: 0 },
                            tablet: { x: 0, y: 0 },
                            mobile: { x: 0, y: 0 },
                        },
                        size: { width: 300, height: 200 },
                        styles: {
                            borderRadius: CSS_VARIABLES['--border-radius'],
                            backgroundColor: CSS_VARIABLES['--white'],
                            boxShadow: CSS_VARIABLES['--shadow'],
                            zIndex: 10,
                        },
                        children: selectedElements.map((el) => ({
                            ...el,
                            position: {
                                desktop: { x: 0, y: 0 },
                                tablet: { x: 0, y: 0 },
                                mobile: { x: 0, y: 0 },
                            },
                        })),
                    },
                ],
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        setSelectedIds([groupId]);
        toast.success('Đã nhóm các phần tử!');
    }, [history, historyIndex]);

    const debouncedUpdateChildPosition = useCallback(
        _.debounce((parentId, childId, newPosition) => {
            setPageData((prev) => {
                const newPageData = {
                    ...prev,
                    elements: prev.elements.map((el) => {
                        if (el.id === parentId) {
                            return {
                                ...el,
                                children: el.children.map((child) =>
                                    child.id === childId
                                        ? { ...child, position: { ...child.position, [viewMode]: newPosition } }
                                        : child
                                ),
                            };
                        }
                        return el;
                    }),
                };
                setHistory([...history.slice(0, historyIndex + 1), newPageData]);
                setHistoryIndex(historyIndex + 1);
                return newPageData;
            });
            toast.success('Đã cập nhật vị trí thành phần con!');
        }, 50),
        [history, historyIndex, viewMode]
    );

    const handleMoveChild = useCallback((sourceParentId, childId, targetParentId, newElementOrPosition) => {
        setPageData((prev) => {
            let newElements = [...prev.elements];
            let childElement;
            if (sourceParentId) {
                newElements = newElements.map((el) => {
                    if (el.id === sourceParentId) {
                        const childIndex = el.children.findIndex((child) => child.id === childId);
                        if (childIndex !== -1) {
                            childElement = {
                                ...el.children[childIndex],
                                position: {
                                    ...el.children[childIndex].position,
                                    [viewMode]:
                                        typeof newElementOrPosition === 'object' && newElementOrPosition.position
                                            ? newElementOrPosition.position[viewMode]
                                            : newElementOrPosition,
                                },
                            };
                            return { ...el, children: el.children.filter((child) => child.id !== childId) };
                        }
                    }
                    return el;
                });
            } else {
                const childIndex = newElements.findIndex((el) => el.id === childId);
                if (childIndex !== -1) {
                    childElement = {
                        ...newElements[childIndex],
                        position: {
                            ...newElements[childIndex].position,
                            [viewMode]:
                                typeof newElementOrPosition === 'object' && newElementOrPosition.position
                                    ? newElementOrPosition.position[viewMode]
                                    : newElementOrPosition,
                        },
                    };
                    newElements.splice(childIndex, 1);
                }
            }
            if (targetParentId) {
                newElements = newElements.map((el) => {
                    if (el.id === targetParentId) {
                        return { ...el, children: [...(el.children || []), childElement] };
                    }
                    return el;
                });
            } else {
                newElements.push(
                    typeof newElementOrPosition === 'object' && newElementOrPosition.id
                        ? newElementOrPosition
                        : childElement
                );
            }
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success(targetParentId ? 'Đã di chuyển thành phần con!' : 'Đã chuyển thành phần con thành phần tử độc lập!');
    }, [history, historyIndex, viewMode]);

    const handleUpdateChildren = useCallback((parentId, dragIndex, dropIndex) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        const newChildren = [...(el.children || [])];
                        if (dragIndex >= 0 && dragIndex < newChildren.length && dropIndex >= 0 && dropIndex < newChildren.length) {
                            const [movedChild] = newChildren.splice(dragIndex, 1);
                            newChildren.splice(dropIndex, 0, movedChild);
                            return { ...el, children: newChildren };
                        }
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã sắp xếp lại thành phần con!');
    }, [history, historyIndex]);

    const handleUndo = useCallback(() => {
        if (historyIndex >= 0) {
            setPageData(history[historyIndex]);
            setHistoryIndex(historyIndex - 1);
            toast.info('Đã hoàn tác!');
        }
    }, [history, historyIndex]);

    const handleRedo = useCallback(() => {
        if (historyIndex < history.length - 1) {
            setPageData(history[historyIndex + 1]);
            setHistoryIndex(historyIndex + 1);
            toast.info('Đã khôi phục!');
        }
    }, [history, historyIndex]);

    const handleSave = useCallback(async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang.');
            return;
        }
        if (!pageData.elements.length) {
            toast.warn('Trang trống. Vui lòng thêm nội dung.');
            return;
        }
        try {
            const htmlContent = await renderStaticHTML(pageData); // Thêm await
            const response = await api.put(`/api/pages/${pageId}`, { html: htmlContent });
            if (response.data.success) {
                toast.success(`Lưu trang thành công! (${pageData.elements.length} phần tử)`);
            } else {
                toast.error('Lưu trang thất bại: ' + (response.data.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Lỗi khi lưu trang: ' + errorMessage);
            if (error.response?.status === 401) {
                localStorage.removeItem('token');
                navigate('/auth');
            }
        }
    }, [pageId, pageData, navigate]);

    const handlePreview = useCallback(async () => { // Thêm async
        const htmlContent = await renderStaticHTML(pageData); // Thêm await
        const newWindow = window.open('', '_blank');
        newWindow.document.write(htmlContent);
        newWindow.document.close();
    }, [pageData]);

    const handleGenerateCode = useCallback(async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang để triển khai.');
            return;
        }
        try {
            const htmlContent = await renderStaticHTML(pageData); // Thêm await
            await api.put(`/api/pages/${pageId}`, { html: htmlContent });
            const response = await api.post(`/api/pages/${pageId}/publish`);
            if (response.data.success && response.data.page?.url) {
                toast.success(`Triển khai thành công! Truy cập tại: ${response.data.page.url}`);
            } else {
                toast.error('Triển khai thất bại: ' + (response.data.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Lỗi khi triển khai trang: ' + errorMessage);
            if (error.response?.status === 401) {
                localStorage.removeItem('token');
                navigate('/auth');
            }
        }
    }, [pageId, pageData, navigate]);

    const selectedElement = (() => {
        if (selectedChildId) {
            const parent = pageData.elements.find((el) => el.id === selectedIds[0]);
            if (!parent || !parent.children) return null;
            const child = parent.children.find((child) => child.id === selectedChildId);
            return child ? { json: child, isChild: true } : null;
        }
        const element = pageData.elements.find((el) => el.id === selectedIds[0]);
        return element ? { json: element, isChild: false } : null;
    })();

    if (isLoading) {
        return (
            <div className="lpb-loading">
                <div className="lpb-loading-spinner"></div>
                <p>Đang tải trang...</p>
            </div>
        );
    }

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container flex h-screen">
                <ErrorBoundary>
                    <link
                        rel="stylesheet"
                        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
                        crossOrigin="anonymous"
                        referrerPolicy="no-referrer"
                    />
                    <ComponentLibrary
                        isCollapsed={isLibraryCollapsed}
                        onToggle={handleToggleLibrary}
                        onAddElement={handleAddElement}
                        onAddChild={handleAddChild}
                        pageData={pageData}
                    />
                </ErrorBoundary>
                <div className="lpb-main-content flex-1 flex flex-col">
                    <ErrorBoundary>
                        <Toolbar
                            onSave={handleSave}
                            onPreview={handlePreview}
                            onGenerateCode={handleGenerateCode}
                            viewMode={viewMode}
                            onViewModeChange={setViewMode}
                            onUndo={handleUndo}
                            onRedo={handleRedo}
                            canUndo={historyIndex >= 0}
                            canRedo={historyIndex < history.length - 1}
                            zoomLevel={zoomLevel}
                            onZoom={(direction) => setZoomLevel(direction === 'in' ? Math.min(200, zoomLevel + 10) : Math.max(50, zoomLevel - 10))}
                            onToggleGrid={() => setShowGrid(!showGrid)}
                            showGrid={showGrid}
                            gridSize={gridSize}
                            onSetGridSize={setGridSize}
                        />
                    </ErrorBoundary>
                    <ErrorBoundary>
                        <Canvas
                            pageData={pageData}
                            selectedIds={selectedIds}
                            onSelectElement={handleSelectElement}
                            onUpdatePosition={debouncedUpdatePosition}
                            onUpdateSize={handleUpdateSize}
                            onDeleteElement={handleDeleteElement}
                            onAddElement={handleAddElement}
                            onGroupElements={handleGroupElements}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={gridSize}
                            showGrid={showGrid}
                            onEditElement={handleEditElement}
                            onDuplicateElement={handleDuplicateElement}
                            onMoveElementUp={handleMoveElementUp}
                            onMoveElementDown={handleMoveElementDown}
                            onSelectChild={handleSelectChild}
                            selectedChildId={selectedChildId}
                            onUpdateChildren={handleUpdateChildren}
                            onAddChild={handleAddChild}
                            onUpdateChildPosition={debouncedUpdateChildPosition}
                            onMoveChild={handleMoveChild}
                            dataStore={DataStore}
                            resolveDataBinding={resolveDataBinding}
                            executeAction={executeAction}
                        />
                    </ErrorBoundary>
                </div>
                <ErrorBoundary>
                    <div className="lpb-sidebar-right">
                        <PropertiesPanel
                            selectedElement={selectedElement}
                            onUpdateElement={handleEditElement}
                            onUpdateCanvas={handleUpdateCanvas}
                            isCollapsed={isPropertiesCollapsed}
                            onToggle={() => setIsPropertiesCollapsed(!isPropertiesCollapsed)}
                            pageData={pageData}
                        />
                    </div>
                </ErrorBoundary>
            </div>
        </DndProvider>
    );
};

export default CreateLanding;
