import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import UserProfile from './UserProfile';
import api from '@landinghub/api';
import '../styles/UserProfile.css';

const Header = () => {
    const [userData, setUserData] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await api.get('/api/user/info');
                setUserData(res.data);
            } catch (err) {
                console.error('Error fetching user data:', err);
                setError('Không thể tải dữ liệu người dùng.');
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        window.location.href = '/auth';
    };

    const displayName = userData.name || (localStorage.getItem('token') ? `User ${jwtDecode(localStorage.getItem('token')).userId}` : 'Khách');

    const [showProfile, setShowProfile] = useState(false);

    if (loading) return <div>Đang tải...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <header className="dashboard-header">
            <div className="logo">LandingHub</div>
            <div className="user-info">
                {localStorage.getItem('token') ? (
                    <>
                        <span>Chào {displayName}</span>
                        <button onClick={() => setShowProfile(!showProfile)}>Thông tin</button>
                        <button onClick={handleLogout}>Đăng xuất</button>
                        {showProfile && (
                            <div className="profile-popup">
                                <UserProfile />
                                <button onClick={() => setShowProfile(false)}>Đóng</button>
                            </div>
                        )}
                    </>
                ) : (
                    <button onClick={() => navigate('/auth')}>Đăng nhập</button>
                )}
            </div>
        </header>
    );
};

export default React.memo(Header);
