import React, { useRef, useMemo, useCallback, useState } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import QuickEditToolkit from './QuickEditToolkit';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

const Element = React.memo(
    ({ component, index, isSelected, hasQuickEdit, onSelectElement, handleElementHover, onDuplicateElement, onDeleteElement, onMoveElement, updatePosition, canvasRef, sectionId, onInlineEdit }) => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: { id: component.id },
            end: (item, monitor) => {
                const delta = monitor.getDifferenceFromInitialOffset();
                if (delta && canvasRef.current && sectionId) {
                    const rect = canvasRef.current.getBoundingClientRect();
                    const newPosition = {
                        x: (component.json.styles.position?.x || 0) + delta.x,
                        y: (component.json.styles.position?.y || 0) + delta.y,
                    };
                    updatePosition(component.id, sectionId, newPosition); // Cập nhật vị trí sau drag
                }
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        const handleMouseDown = (e) => {
            e.stopPropagation();
            onSelectElement(component);
        };

        const handleInlineEdit = (e) => {
            if (onInlineEdit && e.target.contentEditable === 'true') {
                onInlineEdit(component.id, sectionId, e.target.innerText);
            }
        };

        const renderContent = () => {
            const { type, content, styles } = component.json;
            const styleProps = { ...styles, maxWidth: '100%', boxSizing: 'border-box' };
            switch (type) {
                case 'heading':
                    return <h2 style={styleProps} contentEditable suppressContentEditableWarning onBlur={handleInlineEdit}>{content}</h2>;
                case 'paragraph':
                    return <p style={styleProps} contentEditable suppressContentEditableWarning onBlur={handleInlineEdit}>{content}</p>;
                case 'button':
                    return <button style={styleProps} contentEditable suppressContentEditableWarning onBlur={handleInlineEdit}>{content}</button>;
                case 'button-group':
                    return (
                        <div style={styleProps}>
                            {content.map((btn, idx) => (
                                <button key={idx} style={{ ...styles, marginRight: idx < content.length - 1 ? '10px' : 0 }}>
                                    {btn}
                                </button>
                            ))}
                        </div>
                    );
                case 'form':
                    return (
                        <form style={styleProps}>
                            {content.fields.map((field, idx) => (
                                <input
                                    key={idx}
                                    type="text"
                                    placeholder={field}
                                    style={{ marginBottom: '8px', width: '100%', ...styles }}
                                />
                            ))}
                            <button type="submit" style={{ ...styles, marginTop: '8px' }}>Submit</button>
                        </form>
                    );
                case 'image':
                    return <img src={content} alt="Image" style={{ ...styles, maxWidth: '100%', height: 'auto' }} />;
                case 'gallery':
                    return (
                        <div style={styleProps}>
                            {content.map((src, idx) => (
                                <img
                                    key={idx}
                                    src={src}
                                    alt={`Gallery ${idx}`}
                                    style={{ width: '100px', marginRight: '8px', ...styles }}
                                />
                            ))}
                        </div>
                    );
                case 'product-list':
                    return (
                        <div style={styleProps}>
                            {content.map((prod, idx) => (
                                <div key={idx} style={{ marginBottom: '10px', ...styles }}>
                                    {prod.title}
                                </div>
                            ))}
                        </div>
                    );
                case 'popup':
                    return <div style={styleProps}>{content}</div>;
                default:
                    return <div style={styleProps}>{component.name || 'Unknown'}</div>;
            }
        };

        return (
            <div
                ref={drag}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                onMouseEnter={(e) => handleElementHover(e, component)}
                onMouseDown={handleMouseDown}
                style={{
                    position: 'absolute',
                    left: component.json.styles.position?.x || 0,
                    top: component.json.styles.position?.y || 0,
                }}
            >
                {renderContent()}
            </div>
        );
    }
);

const Section = React.memo(
    ({ section, index, selectedElement, onSelectElement, handleElementHover, onDuplicateElement, onDeleteElement, onMoveElement, updatePosition, addElement, canvasRef, moveElement, onInlineEdit }) => {
        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && canvasRef.current) {
                    const rect = canvasRef.current.getBoundingClientRect();
                    const y = clientOffset.y - rect.top;
                    // Logic to determine element drop index within section
                }
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả. Vui lòng thử lại!');
                    return;
                }
                const rect = canvasRef.current.getBoundingClientRect();
                const position = {
                    x: clientOffset.x - rect.left,
                    y: clientOffset.y - rect.top,
                };
                if (item.componentData) {
                    addElement(section.id, { ...item.componentData, json: { ...item.componentData.json, styles: { ...item.componentData.json.styles, position } } }, section.elements.length);
                } else {
                    // Di chuyển existing element đến vị trí mới
                    updatePosition(item.id, section.id, position);
                    moveElement(section.id, item.id, section.elements.length); // Cập nhật index nếu cần
                }
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        return (
            <div ref={drop} className={`lpb-canvas-section ${isOver ? 'lpb-canvas-section-over' : ''}`}>
                <h3>{section.json.content.title || 'Section'}</h3>
                <div className="lpb-canvas-section-content">
                    {section.elements.map((element, idx) => (
                        <Element
                            key={element.id}
                            component={element}
                            index={idx}
                            isSelected={selectedElement?.id === element.id}
                            hasQuickEdit={false}
                            onSelectElement={onSelectElement}
                            handleElementHover={handleElementHover}
                            onDuplicateElement={onDuplicateElement}
                            onDeleteElement={onDeleteElement}
                            onMoveElement={onMoveElement}
                            updatePosition={updatePosition}
                            canvasRef={canvasRef}
                            sectionId={section.id}
                            onInlineEdit={onInlineEdit}
                        />
                    ))}
                </div>
            </div>
        );
    }
);

const Canvas = React.memo(
    ({
         pageStructure,
         selectedElement,
         onSelectElement,
         quickEditElement,
         quickEditPosition,
         onQuickEdit,
         onCloseQuickEdit,
         onDuplicateElement,
         onDeleteElement,
         onMoveElement,
         viewMode,
         addSection,
         addElement,
         moveElement,
         ItemTypes,
         zoomLevel,
         setPageStructure,
         updatePosition,
         onInlineEdit, // Thêm prop cho inline edit
     }) => {
        const canvasRef = useRef(null);
        const [dropIndex, setDropIndex] = useState(null);

        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.SECTION],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && canvasRef.current) {
                    const rect = canvasRef.current.getBoundingClientRect();
                    const y = clientOffset.y - rect.top;
                    const index = Math.min(pageStructure.length, Math.floor(y / 200));
                    setDropIndex(index);
                    if (item.componentData) {
                        item.componentData.json.styles = {
                            ...item.componentData.json.styles,
                            position: { x: clientOffset.x - rect.left, y },
                        };
                    }
                }
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả. Vui lòng thử lại!');
                    return;
                }
                const rect = canvasRef.current.getBoundingClientRect();
                const position = {
                    x: clientOffset.x - rect.left,
                    y: clientOffset.y - rect.top,
                };
                const index = Math.min(pageStructure.length, Math.floor(position.y / 200));
                addSection({ ...item.componentData, json: { ...item.componentData.json, styles: { ...item.componentData.json.styles, position } } }, index);
                setDropIndex(null);
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        const handleElementHover = useCallback(
            (e, component) => {
                if (quickEditElement?.id === component.id) return;
                const rect = e.currentTarget.getBoundingClientRect();
                const canvasRect = canvasRef.current?.getBoundingClientRect() || { left: 0, top: 0 };
                onQuickEdit(component, {
                    x: rect.left - canvasRect.left,
                    y: rect.top - canvasRect.top + 20,
                });
            },
            [quickEditElement, onQuickEdit]
        );

        useHotkeys(
            'delete',
            () => {
                if (selectedElement) {
                    onDeleteElement(selectedElement);
                }
            },
            [selectedElement, onDeleteElement]
        );

        return (
            <div className="lpb-canvas-wrapper">
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    onMouseLeave={onCloseQuickEdit}
                    style={{ zoom: `${zoomLevel / 100}`, position: 'relative' }}
                >
                    <div className="lpb-canvas-content">
                        {pageStructure.length === 0 ? (
                            <div className="lpb-canvas-empty-state">
                                <i className="fas fa-layer-group"></i>
                                <p>Kéo thả section để bắt đầu</p>
                            </div>
                        ) : (
                            <>
                                {pageStructure.map((section, index) => (
                                    <React.Fragment key={section.id}>
                                        {dropIndex === index && <div className="lpb-canvas-drop-zone" />}
                                        <Section
                                            section={section}
                                            index={index}
                                            selectedElement={selectedElement}
                                            onSelectElement={onSelectElement}
                                            handleElementHover={handleElementHover}
                                            onDuplicateElement={onDuplicateElement}
                                            onDeleteElement={onDeleteElement}
                                            onMoveElement={onMoveElement}
                                            updatePosition={updatePosition}
                                            addElement={addElement}
                                            canvasRef={canvasRef}
                                            moveElement={moveElement}
                                            onInlineEdit={onInlineEdit}
                                        />
                                    </React.Fragment>
                                ))}
                                {dropIndex === pageStructure.length && <div className="lpb-canvas-drop-zone" />}
                            </>
                        )}
                    </div>
                    <QuickEditToolkit
                        element={quickEditElement}
                        position={quickEditPosition}
                        onEdit={() => onSelectElement(quickEditElement)}
                        onDuplicate={() => onDuplicateElement(quickEditElement)}
                        onDelete={() => onDeleteElement(quickEditElement)}
                        onMoveUp={() => onMoveElement(quickEditElement, 'up')}
                        onMoveDown={() => onMoveElement(quickEditElement, 'down')}
                        onClose={onCloseQuickEdit}
                    />
                </div>
            </div>
        );
    }
);

export default Canvas;
