        // ========== API MANAGER ==========
        LPB.api = {
            cache: new Map(),
            
            getCacheKey: function(endpoint, params) {
                return endpoint + JSON.stringify(params || {});
            },
            
            fetch: async function(endpoint, options = {}) {
                const cacheKey = this.getCacheKey(endpoint, options.params);
                
